                .WithTextLine("Immune to player card effects.")
                .WithTextLine("Forced: At the beginning of the encounter phase, discard the top X cards of the encounter deck. Each player adds an enemy discarded from this effect to the staging area. X is 2 more than the number of players in the game.")
                .WithFlavorLine("...but that steep as were its sides men could reach the summit by climbing a stair cut into the rock...")
                .WithFlavorLine("-The Children of Húrin") //(7/23)
                .WithInfo(178, 1, Artist.Anke_Eissmann);
            addLocation("Amon Rûdh", EncounterSet.TheBetrayalOfMim, 2, Card.VALUE_NA)
                .WithUnique()
                .WithSlugSuffix("Hidden")
                .WithTraits("Hidden.", "Dor-Cúarthol.")
                .WithTextLine("Immune to player card effects.")
                .WithTextLine("While Amon Rûdh is the active location, it gains: \"After a character readies during the refresh phase, heal 1 damage from the character.\"")
                .WithFlavorLine("\"We have seen it, but never nearer...\"")
                .WithFlavorLine("-Androg, The Children of Húrin")
                .WithInfo(178, 1, Artist.Unknown); //(7/23)
            addLocation("Gate of the Garth", EncounterSet.TheBetrayalOfMim, 4, 8)
                .WithTraits("Dor-Cúarthol.")
                .WithTextLine("Forced: After an encounter card is added to the staging area of Gate of the Garth outside of the quest phase, each player raises he threat by 2.")
                .WithTextLine("Response: After Gate of the Garth is explored, reduce each player's threat by 5.")
                .WithFlavorLine("\"Not the door of the house, but the gate of the garth...\"")
                .WithFlavorLine("-Mîm, The Children of Húrin")
                .WithInfo(179, 1, Artist.Unknown); //Mr. Cube (8/23)
            addLocation("Stony Heath", EncounterSet.TheBetrayalOfMim, 5, 6)
                .WithTraits("Dor-Cúarthol.")
                .WithTextLine("While Stony Heath is the active location, enemies get +1 Attack.")
                .WithTextLine("Response: After Stony Heath is explored, add 1 resource to the resource pool of each hero.")
                .WithFlavorLine("They passed over the tumbled stones, and began to climb; for Amon Rûdh stood upon the eastern edge of the high moorlands that rose between the vales of Sirion and Narog...")
                .WithFlavorLine("-The Children of Húrin")
                .WithInfo(180, 1, Artist.Unknown); //(9/23)
            addLocation("Broken Land", EncounterSet.TheBetrayalOfMim, 3, 9)
                .WithTraits("Dor-Cúarthol.")
                .WithTextLine("While Broken Lands is the active location, it gainsd, \"Forced: After any amount of progress is placed on Broken Lands, deal 1 damage to each questing character.\"")
                .WithTextLine("Response: After Broken Land is explored, each player draws 3 cards.")
                .WithFlavorLine("Upon the eastern side a broken land climbed slowly up to the high ridges among knots of birch and rowan, and ancient thorn-tress rooted in rock.")
                .WithFlavorLine("-The Children of Húrin")
                .WithInfo(181, 1, Artist.Unknown); //Concept Cube (10/23)
            addLocation("Holly-thicket", EncounterSet.TheBetrayalOfMim, 3, 10)
                .WithTraits("Dor-Cúarthol.", "Forest.")
                .WithTextLine("While Holly-thicket is the active location, the player attack step of the combat phase happens first.")
                .WithTextLine("Travel: Each player must discard an attachment he controls.")
                .WithFlavorLine("Soon after, it chanced that as the grey light of a day of rain was falling Túrin and his men were sheltering in a holly-thicket...")
                .WithFlavorLine("-The Children of Húrin")
                .WithInfo(182, 1, Artist.Unknown); // (11/23)
            addLocation("Dark Dells", EncounterSet.TheBetrayalOfMim, 4, 7)
                .WithTraits("Dor-Cúarthol.")
                .WithTextLine("While Dark Dells is the active location, each non-objective ally's text box is treated as if it were blank, except for traits.")
                .WithTextLine("Response: After Dark Dells is explored, you may look at the facedown encounter cards in an adjacent location's staging area.")
                .WithInfo(183, 1, Artist.Unknown); //Sinate (12/23)
            addTreachery("Secret Stair", EncounterSet.TheBetrayalOfMim)
