using System;
using HallOfBeorn.Models;

namespace HallOfBeorn.Services.LotR.Templates
{
    public class FrenchTemplateRepository : TemplateRepository
    {
        public FrenchTemplateRepository()
            : base(Language.FR)
        {
        }

        protected override void Initialize()
        {
            CoreSet();

            ChildrenOfEorl();
            TheAldburgPlot();
            FireOnTheEastemnet();
            TheGapOfRohan();
            TheGlitteringCaves();
            MusteringOfTheRohirrim();

            TheScouringOfTheShire();
        }

        private void CoreSet()
        {
            AddHtml("Aragorn-Core", "<p class='main-text'>{keyword:Sentinelle.}</p><p class='main-text'><b>Réponse:</b> quand {self} s'engage dans une quête, dépensez I ressource de sa réserve pour le redresser.</p><p class='flavor-text'>&laquo;Je suis Aragorn, fils d'Ararthorn, et si, par la vie ou par la mort, je puis vous sauver, je le ferai.&raquo;<br>&ndash;La Communaute de l'Anneau</p>");
            AddHtml("Beorn-Core", "<p class='main-text'><b>Action:</b> {self} gagne +5 {Attack} jusqu'a la fin de la phase. A la fin de la phase ou vous avez declenche cet effet, melangez {self} dans votre deck. (Limite: une fois par tour).</p><p class='flavor-text'>&laquo; Je n'ai pas besoin de vos services, merci... mais sans doute avez-vous besoin des miens. &raquo;<br>&ndash; Le Hobbit</p>");
            AddHtml("Guard-of-the-Citadel-Core", "<p class='flavor-text'>&laquo;Mais les Seigneurs de Minas Tirith continuent de se battre, defiant nos ennemis, gardant le passage de la riviere, d'Argonath a la mer.&laquo;<br>&ndash;Elrond, La Communaute de l'Anneau</p>");
        }

        private void ChildrenOfEorl()
        {
            //Children of Eorl
            AddHtml("Ingold-CoE", "<p class='main-text'><b>Réponse:</b> quand un allié <b><i>Gondor</i></b> ou <b><i>Guerrier</i></b> entre en jeu sous votre contrôle, piochez une carte. Ensuite, vous devez dépenser une ressource de la réserve d’Ingold ou augmenter de 1 votre niveau de menace. (Limite : une fois par phase.)</p><p class='flavor-text'>“Nous ne désirons aucun étranger dans le pays en ce moment, sauf de vigoureux hommes d’armes, en la loyauté et l’aide desquels nous puissions avoir confiance.” — Le Retour du Roi</p>");
            AddHtml("Celador-CoE", "<p class='main-text'>À distance.</p><p class='main-text'>Diminuez de 1 le total de {Threat} dans la zone de cheminement pour chaque carte <b><i>Piège</i></b> que vous contrôlez qui n’est pas attachée.</p><p class='main-text'><b>Réponse</b> : quand une carte <b><i>Piège</i></b> que vous contrôlez est attachée à un ennemi, choisissez entre : redresser Celador ou diminuer de 1 votre niveau de menace.</p><p class='flavor-text'>“Un sage ne se fie pas à une rencontre par hasard dans ce pays” — Faramir, Les Deux Tours</p>");
            AddHtml("Thengel-CoE", "<p class='main-text'><b>Réponse</b> : quand un allié en jeu est défaussé, le contrôleur de cette carte cherche parmi les 5 premières cartes de son deck un allié partageant au moins 1 <b><i>Trait</i></b> avec l’allié défaussé, le met en jeu incliné, et mélange son deck. (Limite : une fois par tour.)</p><p class='flavor-text'>“Mon ami, dit Gandalf, vous aviez des chevaux, des faits d’armes et des champs libres” — Le Retour du Roi</p>");
            AddHtml("Herubrand-CoE", "<p class='main-text'>Herubrand gagne +1 {Willpower} pour chaque héros que vous contrôlez ayant au moins 1 attachement Restreint.</p><p class='main-text'><b>Réponse</b> : quand un ennemi est détruit dans la zone de cheminement, piochez une carte.</p><p class='flavor-text'>“Quel fardeau portez-vous, Hommes de Rohan ?” — Imrahil, Le Retour du Roi</p>");
            AddHtml("Deeping-Bowman-CoE", "<p class='main-text'>Dévoué. À distance.</p><p class='main-text'><b>Action</b> : défaussez l’Archer du Gouffre pour choisir un ennemi. Jusqu’à la fin de la phase, cet ennemi ne compte pas sa {Defense} quand vous déterminez les dégâts de combat.</p><p class='flavor-text'>“Nous avons lâché jusqu’à notre dernière flèche, et rempli le Fossé d’Orques” — Les Deux Tours</p>");
            AddHtml("Open-the-Gates-CoE", "<p class='main-text'><b>Action</b> : cherchez parmi les 5 premières cartes de votre deck un allié partageant au moins 1 <b><i>Trait</i></b> avec chacun de vos héros et mettez-le en jeu. Mélangez votre deck. À la fin de la phase, si cet allié est encore en jeu, ajoutez-le à votre main.</p><p class='flavor-text'>“Il semblait que la nouvelle de leur venue les avait devancés, et ils furent admis aussitôt, en silence et sans questions.” — Le Retour du Roi</p>");
            AddHtml("Wealth-of-Adventure-CoE", "<p class='main-text'><b>Action</b> : cherchez dans votre deck 1 carte <b><i>Compétence</i></b> et ajoutez-la à votre main. Mélangez votre deck.</p><p class='flavor-text'>“C’est une affaire dangereuse de passer ta porte, Frodon, me disait-il. Tu vas sur la route et, si tu ne retiens pas tes pieds, Dieu sait jusqu’où tu pourrais être emporté”<br/>— Frodon, La Communauté de l’Anneau</p>");
            AddHtml("Feast-of-Plenty-CoE", "<p class='main-text'>Le coût en ressources de Festin d’Abondance doit être payé avec la réserve d’un seul héros.</p><p class='main-text'><b>Action</b> : redressez jusqu’à 3 héros et ajoutez 1 ressource à la réserve de chacun de ces héros. Seul 1 exemplaire de Festin d’Abondance peut être joué par les joueurs à chaque tour.</p><p class='flavor-text'>“Le festin était joyeux et la chère répondait à tout ce que sa faim pouvait désirer” — La Communauté de l’Anneau</p>");
            AddHtml("Deeping-Defender-CoE", "<p class='main-text'>Dévoué.</p><p class='main-text'><b>Réponse</b> : après que le Défenseur du Gouffre a défendu contre une attaque, renvoyez l’ennemi attaquant dans la zone de cheminement et infligez-lui 1 point de dégâts.</p><p class='flavor-text'>“Le soleil couchant dorait leurs lances et leurs casques, et étincelait dans leurs cheveux pâles et flottants.”<br/>— Les Deux Tours</p>");
            AddHtml("Morwen-Steelsheen-CoE", "<p class='main-text'><b>Action</b> : mettez Morwen Lustre d’Acier en jeu depuis votre main. Ensuite, défaussez un allié <b><i>Rohan</i></b> ou <b><i>Gondor</i></b> que vous contrôlez.</p><p class='main-text'><b>Réponse</b> : quand un allié <b><i>Rohan</i></b> ou <b><i>Gondor</i></b> quitte le jeu, inclinez Morwen Lustre d’Acier pour choisir entre : soigner 2 points de dégâts sur un personnage ou diminuer de 1 votre niveau de menace.</p>");
            AddHtml("It-Should-Be-Spared-CoE", "<p class='main-text'><b>Réponse</b> : quand une carte <b><i>Piège</i></b> que vous contrôlez est attachée à un ennemi, ramenez-la dans votre main. Ensuite, jouez-la de votre main gratuitement.</p><p class='flavor-text'>“Mais je n’abats ni homme ni bête sans nécessité, et je ne le fais pas de gaieté de cœur quand je le dois.” — Faramir, Les Deux Tours</p>");
            AddHtml("Spring-the-Trap-CoE", "<p class='main-text'><b>Réponse</b> : quand une carte <b><i>Piège</i></b> que vous contrôlez est attachée à un ennemi, mettez en jeu un allié <b><i>Rôdeur</i></b> depuis votre main.</p><p class='flavor-text'>“La route passe peut-être, mais eux ne passeront pas ! Pas tant que Faramir sera Capitaine.”<br/>— Mablung, Les Deux Tours</p>");
            AddHtml("Squire-of-the-Mark-CoE", "<p class='main-text'><b>Réponse</b> : quand un allié <b><i>Rohan</i></b> que vous contrôlez est défaussé, ajoutez l’Écuyer de la Marche à la pile de victoire pour mettre en jeu cet allié incliné.</p><p class='flavor-text'>“Je ne puis rendre que peu de services à votre seigneur, mais ce que je peux faire, je le ferai”<br/>— Pippin, Le Retour du Roi</p>");
            AddHtml("Rammas-Lookout-CoE", "<p class='main-text'>Dévoué.</p><p class='main-text'>Tant que chaque héros que vous contrôlez a au moins 1 ressource dans sa réserve, le Guetteur du Rammas gagne +1 {Willpower} et n’est pas incliné pour s’engager dans une quête.</p><p class='flavor-text'>“Quoi qu’il advienne, vous êtes parvenus à la fin du Gondor que vous avez connu. Laissez-moi passer !”<br/>— Gandalf, Le Retour du Roi</p>");
            AddHtml("Theodwyn-CoE", "<p class='main-text'><b>Réponse</b> : quand Théodwyn entre en jeu, choisissez entre : piocher 1 carte ou ajouter une ressource à la réserve d’un héros <b><i>Noble</i></b>.</p><p class='flavor-text'>“Elle est intrépide et a le cœur haut placé. Elle est aimée de tous. Qu’elle soit le seigneur des Eorlingas, pendant notre absence.” — Háma, Les Deux Tours</p>");
            AddHtml("Light-footed-Steed-CoE", "<p class='main-text'>Attachez cette carte à un héros <b><i>Rohan</i></b>. Restreint.</p><p class='main-text'><b>Réponse</b> : quand un personnage que vous contrôlez quitte le jeu, inclinez le Coursier au Pied Léger pour redresser le héros attaché. Ensuite, si le personnage ayant quitté le jeu était unique, ce héros gagne +1 {Attack} jusqu’à la fin du tour.</p><p class='flavor-text'>“… et n’oublie pas leurs chevaux ! Ils peuvent voir le vent nocturne, ou en tout cas on le dit.”<br/>— Uglúk, Les Deux Tours</p>");
            AddHtml("Rammas-Sentry-CoE", "<p class='main-text'>Dévoué.</p><p class='main-text'>Tant que chaque héros que vous contrôlez a au moins 1 ressource dans sa réserve, la Sentinelle du Rammas gagne : “<b>Réponse</b> : annulez 1 point de dégâts tout juste infligé à un personnage <b><i>Gondor</i></b>. (Limite : une fois par phase.)”</p><p class='flavor-text'>“Car si nous tombons, qui tiendra ?”<br/>— Beregond, Le Retour du Roi</p>");
            AddHtml("Live-Bait-CoE", "<p class='main-text'>Vous ne pouvez jouer cette carte que si vous contrôlez une carte <b><i>Piège</i></b> qui n’est pas attachée.</p><p class='main-text'><b>Action</b> : choisissez un ennemi non-unique en jeu. Jusqu’à la fin du tour, cet ennemi perd le texte imprimé dans sa boîte de texte (à l’exception des <b><i>Traits</i></b>). Ensuite, attachez à cet ennemi une carte <b><i>Piège</i></b> que vous contrôlez qui n’est pas attachée.</p>");
            AddHtml("Worthy-of-Remembrance-CoE", "<p class='main-text'><b>[Combat] Action</b> : défaussez un allié <b><i>Rohan</i></b> que vous contrôlez pour choisir un ennemi. Infligez 2 points de dégâts à cet ennemi (4 points de dégâts si l’allié défaussé était unique).</p><p class='flavor-text'>“Alors, le cœur d’Eowyn changea [...] Et soudain son hiver passa et le Soleil brilla sur elle.”<br/>— Le Retour du Roi</p>");
            AddHtml("Need-Brooks-No-Delay-CoE", "<p class='main-text'>Le coût pour jouer cette carte est réduit de 1 pour chaque héros <b><i>Rohan</i></b> que vous contrôlez.</p><p class='main-text'><b>[Organisation] Action:</b> cherchez parmi les 10 premières cartes de votre deck une carte ayant un coût imprimé de 2 ou moins (3 ou moins si elle est unique) et mettez-la de côté. Mélangez votre deck. Ensuite, jouez la carte mise de côté gratuitement comme si elle était dans votre main.</p>");
            AddHtml("Wild-Country-CoE", "<p class='main-text'>Maudit 1. Vaste.</p><p class='main-text'><b>Forcé</b> : à la fin de la phase de voyage, si le lieu actif n’est pas vaste, chaque joueur inflige 1 point de dégâts à un personnage qu’il contrôle.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : si le lieu actif est vaste, infligez 1 point de dégâts au personnage défenseur.</p>");
            AddHtml("Endless-Grasslands-CoE", "<p class='main-text'>Vaste.</p><p class='main-text'><b>Forcé</b> : à la fin de la phase de voyage, si le lieu actif n’est pas vaste, chaque joueur incline un allié qu’il contrôle.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : si le lieu actif est vaste, l’ennemi attaquant effectue une attaque supplémentaire après celle-ci.</p>");
            AddHtml("Windworn-Villages-CoE", "<p class='main-text'>Vaste.</p><p class='main-text'><b>Forcé</b> : à la fin de la phase de voyage, si le lieu actif n’est pas vaste, chaque joueur défausse 1 carte de sa main.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : si le lieu actif est vaste, défaussez un attachement du personnage défenseur.</p>");
            AddHtml("Humpbacked-Downs-CoE", "<p class='main-text'>Vaste.</p><p class='main-text'><b>Forcé</b> : à la fin de la phase de voyage, si le lieu actif n’est pas vaste, chaque joueur augmente de 1 son niveau de menace.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : si le lieu actif est vaste, augmentez de 2 votre niveau de menace.</p>");
            AddHtml("Ride-Through-the-Night-CoE", "<p class='main-text'><b>Une fois révélée</b> : inclinez chaque attachement Restreint que les joueurs contrôlent. Si aucun attachement n’est incliné par cet effet, Chevauchée Nocturne gagne Renfort.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : si le lieu actif est vaste, le personnage défenseur ne peut pas se redresser jusqu’à la fin du tour.</p>");
            AddHtml("The-Green-Sea-CoE", "<p class='flavor-text'>L’arrière-pays est vaste et sans fin, épuisant même les chevaux les plus robustes de l’Ouestfolde.</p><p class='main-text'>Les lieux vastes gagnent +3 points de quête et sont immunisés aux effets de carte des joueurs.</p><p class='main-text'><b>Forcé</b> : au début de la phase de voyage, s’il n’y a pas de lieu vaste dans la zone de cheminement, défaussez des cartes du sommet du deck de rencontre jusqu’à ce qu’un lieu vaste soit défaussé et placez-le dans la zone de cheminement.</p>{victory:10}");
            AddHtml("Cold-from-the-Mountains-CoE", "<p class='main-text'><b>Une fois révélée</b> : chaque joueur qui contrôle plus de 2 alliés doit défausser un allié qu’il contrôle (2 alliés à la place, s’il en contrôle plus de 5).</p>{shadow}<p class='shadow-text'><b>Ombre</b> : inclinez un personnage que vous contrôlez.</p><p class='flavor-text'>“Que ceux qui le veulent appellent cela le vent.”<br/>—Boromir, La Communauté de l’Anneau</p>");
            AddHtml("Exhausting-Winds-CoE", "<p class='main-text'><b>Une fois révélée</b> : infligez 1 point de dégâts à chaque personnage à qui il reste plus de 1 point de vie. Inclinez chaque personnage qui n’a pas reçu de dégâts par cet effet.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : après cette attaque, infligez X points de dégâts au personnage défenseur. X est égal au nombre de points de vie restant au personnage défenseur moins 1.</p>");
            AddHtml("Tired-and-Hungry-CoE", "<p class='main-text'>Tant qu’elle est attachée à un héros, compte comme un attachement <b><i><b><i>Condition</i></b></i></b> avec le texte : “<b>Forcé</b> : quand un lieu est exploré, inclinez le héros attaché.”.</p><p class='main-text'><b>Une fois révélée</b> : attachez cette carte à un héros redressé n’ayant pas déjà une carte “Fatigué et Affamé” attachée. Si Fatigué et Affamé n’est pas attachée, elle gagne Renfort.</p><p class='flavor-text'>“Ennemis devant, derrière la peur, sous les cieux sera notre lit.” — La Communauté de l’Anneau</p>");
            AddHtml("Exposed-to-the-Elements-CoE", "<p class='flavor-text'>Votre groupe se trouve à la merci de la pluie, de la grêle et de la faim. Vous devez trouver un abri afin d’obtenir un soupçon de répit.</p><p class='main-text'><b>Forcé</b> : à la fin de l’étape de renforcement, défaussez des cartes du sommet du deck de rencontre jusqu’à ce qu’une traîtrise soit défaussée. Révélez cette carte.</p>{victory:10}");
            AddHtml("Old-Farmhouse-CoE", "<p class='main-text'><b>Forcé</b> : lorsque la Vieille Grange est explorée, défaussez des cartes du sommet du deck de rencontre jusqu’à ce qu’un lieu soit défaussé. Ajoutez ce lieu à la zone de cheminement.</p><p class='flavor-text'>À environ un furlong de la route principale, ils se trouvèrent devant une forte barricade faite de vieilles charrettes renversées. Cela les arrêta.<br/>—Le Retour du Roi</p>");
            AddHtml("Abandoned-Mill-CoE", "<p class='main-text'>Maudit 1. Immunisé aux effets de carte des joueurs.</p><p class='main-text'><b>Forcé</b> : lorsque le Moulin Abandonné est exploré, défaussez des cartes du sommet du deck de rencontre jusqu’à ce qu’un ennemi soit défaussé. Ajoutez cet ennemi à la zone de cheminement.</p><p class='flavor-text'>Il faut avoir du blé pour moudre, et il n’y en avait pas.<br/>— Le Retour du Roi</p>");
            AddHtml("Dunland-Chieftain-CoE", "<p class='main-text'><b>Forcé:</b> lorsque le Chef de Clan de Dun vous engage au combat, défaussez une carte au hasard de votre main. Ensuite, défaussez chaque autre copie de cette carte qui est dans votre main ou en jeu sous votre contrôle.</p><p class='flavor-text'>“Ils nous haïssent, et ils sont contents.”<br/>— Gamelin, Les Deux Tours</p>{victory:1}");
            AddHtml("Wild-Ravager-CoE", "<p class='main-text'><b>Forcé:</b> au début de la phase de rencontre, sélectionnez un joueur au hasard. Le Ravageur Sauvage engage ce joueur.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> l’ennemi attaquant ne peut pas subir de dégâts jusqu’à la fin du tour.</p>");
            AddHtml("Dunlending-Berserker-CoE", "<p class='main-text'><b>Forcé</b> : lorsque le Berserker Dunlending vous engage au combat, infligez 1 point de dégâts à un héros que vous contrôlez et 1 point de dégâts à un ennemi non-unique engagé au combat avec vous.</p><p class='flavor-text'>“Ils ne fléchiront pas maintenant, que ce soit le crépuscule ou l’aube.” — Gamelin, Les Deux Tours</p>");
            AddHtml("Hillman-Scout-CoE", "<p class='main-text'>Archerie 2.</p><p class='main-text'><b>Forcé</b> : lorsque l’Éclaireur des Collines vous engage au combat, inclinez l’allié que vous contrôlez ayant le coût imprimé le plus élevé.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : inclinez un personnage que vous contrôlez ayant 3 {Attack} ou plus.</p>");
            AddHtml("Surprise-Attack-CoE", "<p class='main-text'>Renfort.</p><p class='main-text'><b>Une fois révélée</b> : le prochain ennemi engagé au combat durant ce tour ne peut pas subir de dégâts jusqu’à la fin du tour. Renvoyez-le dans la zone de cheminement après la résolution de son attaque.</p><p class='flavor-text'>Il semblait n’avoir aucune chance d’atteindre le Gué avant que le chemin ne soit coupé par ceux qui étaient restés embusqués. Il les voyait clairement à présent.<br/>— La Communauté de l’Anneau</p>");
            AddHtml("A-History-of-Strife-CoE", "<p class='flavor-text'>Les Dunlendings nourrissent une vieille rancune envers les étrangers. Ils cherchent à vous surprendre, pillant vos provisions avant de vous laisser pour mort.</p><p class='main-text'>Renfort.</p><p class='main-text'>Chaque ennemi <b><i>Pays de Dun</i></b> gagne “<b>Forcé</b> : après avoir engagé au combat cet ennemi, défaussez une ressource de la réserve d’un de vos héros.”</p>{victory:10}");
            AddHtml("Recreant-Rider-CoE", "<p class='main-text'><b>Forcé</b> : lorsque le Cavalier Renégat entre dans la zone de cheminement, il effectue immédiatement une attaque contre le premier joueur.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : renvoyez l’ennemi attaquant dans la zone de cheminement après la résolution de son attaque.</p>");
            AddHtml("Deadly-Defector-CoE", "<p class='main-text'><b>Forcé</b> : à la fin de la phase de combat, si le Meurtrier Transfuge est dans la zone de cheminement, il effectue immédiatement une attaque contre le dernier joueur.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : si l’ennemi attaquant est dans la zone de cheminement, il effectue une attaque supplémentaire après celle-ci.</p>");
            AddHtml("Orc-Battalion-CoE", "<p class='main-text'>Le Bataillon Orque ne peut subir de dégâts de combat tant qu’il est attaqué par moins de 3 personnages.</p><p class='main-text'><b>Forcé</b> : après avoir déclaré un personnage comme attaquant contre le Bataillon Orque, infligez 1 point de dégâts à ce personnage.</p>");
            AddHtml("Servant-of-the-Eye-CoE", "<p class='main-text'>Maudit 1.</p><p class='main-text'><b>Forcé</b> : quand le Serviteur de l’Œil vous attaque, augmentez de 1 votre niveau de menace.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : augmentez de 2 votre niveau de menace.</p>");
            AddHtml("Black-Land-Scouts-CoE", "<p class='main-text'><b>Forcé</b> : au début de la phase de combat, soit vous augmentez de 1 le niveau de menace de chaque joueur, soit vous répartissez 4 points de dégâts parmi les personnages en jeu.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : répartissez 2 points de dégâts parmi les personnages que vous contrôlez.</p>");
            AddHtml("Orcish-Horse-butcher-CoE", "<p class='main-text'><b>Forcé</b> : quand le Boucher Chevalin Orque inflige des dégâts à un personnage, choisissez entre : défausser un attachement de ce personnage ou le joueur engagé au combat avec le Boucher Chevalin Orque augmente de 1 son niveau de menace.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : soit vous défaussez un attachement que vous contrôlez, soit vous augmentez de 3 votre niveau de menace.</p>");
            AddHtml("Crimson-Banner-CoE", "<p class='main-text'>Renfort.</p><p class='main-text'>Tant qu’elle est attachée à un ennemi, compte comme un attachement <b><i><b><i>Condition</i></b></i></b> avec le texte “<b>Forcé</b> : à la fin du tour, augmentez de 1 le niveau de menace de chaque joueur pour chaque ennemi <b><i>Orque</i></b> en jeu.”</p><p class='main-text'><b>Une fois révélée</b> : attachez cette carte à l’ennemi <b><i>Orque</i></b> non-unique ayant le coût d’engagement au combat le plus élevé.</p>");
            AddHtml("White-Mountain-Reinforcements-CoE", "<p class='flavor-text'>D’autres orques descendent de leurs cavernes dans les montagnes, renforçant les assauts de vos adversaires. Si vous ne parvenez pas à les stopper, ils pourraient bien vous submerger.</p><p class='main-text'><b>Une fois révélée</b> : chaque joueur qui n’est pas engagé au combat avec un ennemi doit chercher dans le deck de rencontre ou dans la pile de défausse de rencontre un ennemi et le mettre en jeu, engagé au combat avec lui. Mélangez le deck de rencontre.</p><p class='main-text'>Chaque carte ombre sans effet ombre gagne : “<b>Ombre</b> : augmentez de 1 votre niveau de menace.”</p>{victory:10}");
            AddHtml("Brigand-Ringleader-CoE", "<p class='main-text'>Maraude 3. Immunisé aux effets de carte des joueurs. Ne peut pas recevoir plus de 5 marqueurs de dégâts par tour.</p><p class='main-text'><b>Forcé</b> : à la fin du tour, le joueur engagé au combat avec le Meneur Brigand ajoute la première carte de son deck à sa pile de butin, puis soigne 1 point de dégâts sur le Meneur Brigand pour chaque coût de carte différent dans sa pile de butin.</p>{victory:10}");
            AddHtml("Plundered-Goods-CoE", "<p class='main-text'>Ajoutez 1 au total de {Threat} de la zone de cheminement pour chaque carte dans la pile de butin des Biens Pillés.</p><p class='main-text'><b>Forcé</b> : quand un ennemi quitte le jeu, ajoutez une carte de sa pile de butin à la pile de butin des Biens Pillés, ensuite ajoutez une autre carte de sa pile de butin à la main de son propriétaire. Défaussez le reste.</p><p class='flavor-text'>Les brigands ont dévalisé le stock de lances et de boucliers du village et ont retourné les armes contre les villageois.</p>");
            AddHtml("Pillaging-Swordsman-CoE", "<p class='main-text'>Maraude 1.</p><p class='main-text'>Le Pillard Bretteur gagne +X {Attack}, où X est le coût imprimé le plus élevé parmi les cartes de sa pile de butin.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : ajoutez les deux premières cartes de votre deck à la pile de butin de l’ennemi attaquant.</p>");
            AddHtml("Grasping-Slaver-CoE", "<p class='main-text'>Maraude 2.</p><p class='main-text'><b>Forcé</b> : quand un allié subit des dégâts ou est détruit par l’attaque du Chasseur d’Esclaves, ajoutez cet allié à sa pile de butin.</p><p class='main-text'><b>Forcé</b> : quand le Chasseur d’Esclaves attaque, infligez X points de dégâts parmi les personnages que vous contrôlez. X est le nombre d’alliés dans sa pile de butin.</p>");
            AddHtml("Vengeful-Bowman-CoE", "<p class='main-text'>Maraude 2. Archerie X.</p><p class='main-text'>X est le nombre de types de carte différents dans la pile de butin de l’Archer Vengeur.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : si cette attaque inflige des dégâts, ajoutez la première carte de votre deck à la pile de butin de l’ennemi attaquant.</p>");
            AddHtml("Sneaky-Thief-CoE", "<p class='main-text'>Maraude 0. Renfort.</p><p class='main-text'><b>Forcé</b> : quand le Voleur Sournois devrait subir des dégâts, s’il possède des cartes dans sa pile de butin, défaussez chacune de ces cartes à la place.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : si cette attaque détruit un personnage, l’ennemi attaquant effectue une attaque supplémentaire après celle-ci.</p>");
            AddHtml("Erelas-Gates-CoE", "<p class='main-text'><b>Trajet</b> : chaque joueur doit choisir un ennemi et ajouter la première carte de son deck à sa pile de butin.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : augmentez de 1 votre niveau de menace pour chaque carte dans la pile de butin de l’ennemi attaquant.</p><p class='flavor-text'>Des colonnes tordues et penchées dressaient leurs fûts ébréchés.<br/>— Les Deux Tours</p>");
            AddHtml("Crossroads-CoE", "<p class='main-text'><b>Trajet</b> : mélangez la pile de défausse de rencontre dans le deck de rencontre et défaussez des cartes du sommet du deck de rencontre jusqu’à ce qu’un ennemi soit défaussé. Mettez en jeu cet ennemi, engagé au combat avec le premier joueur.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : l’ennemi attaquant gagne +X {Attack}, où X est le nombre de sphères différentes parmi les cartes de sa pile de butin.</p>");
            AddHtml("Erelas-Stables-CoE", "<p class='main-text'><b>Trajet</b> : renvoyez dans la zone de cheminement un ennemi engagé au combat avec un joueur.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : si l’ennemi attaquant est non-unique, renvoyez-le dans la zone de cheminement après la résolution de son attaque.</p>");
            AddHtml("Chaos-in-the-Hills-CoE", "<p class='main-text'><b>Une fois révélée</b> : renvoyez dans la zone de cheminement l’ennemi engagé au combat ayant le coût d’engagement au combat le plus élevé. Si aucun ennemi n’est renvoyé dans la zone de cheminement par cet effet, Chaos dans les Collines gagne Renfort.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : après cette attaque, l’ennemi attaquant engage au combat le joueur suivant et effectue immédiatement une attaque.</p>");
            AddHtml("Looted-Weapons-CoE", "<p class='main-text'>Tant qu’elles sont attachées à un ennemi, les Armes Dérobées comptent comme un attachement <b><i><b><i>Condition</i></b></i></b> avec le texte : “L’ennemi attaché gagne +1 {Threat} et +1 {Attack} pour chaque attachement dans sa pile de butin.”</p><p class='main-text'><b>Une fois révélée</b> : attachez cette carte à l’ennemi en jeu ayant le coût d’engagement au combat le plus élevé. Ensuite, chaque joueur ajoute un attachement <b><i>Objet</i></b> qu’il contrôle à la pile de butin de cet ennemi, si possible. S’il n’y pas d’ennemi en jeu, Armes Dérobées gagne Renfort.</p>");
            AddHtml("Stockpile-Barricade-CoE", "<p class='flavor-text'>Des chariots et des carrioles renversés formaient une barricade solide en travers de la route.</p><p class='main-text'>Renfort.</p><p class='main-text'>Chaque ennemi gagne +1 {Defense} pour chaque attachement dans sa pile de butin.</p><p class='main-text'><b>Forcé </b>: après avoir résolu le mot-clé Maraude d’un ennemi, s’il n’y a pas d’attachement dans sa pile de butin, il effectue immédiatement une attaque contre le joueur engagé au combat avec lui.</p>{victory:10}");
            AddHtml("Erelas-Beacon-CoE", "<p class='main-text'><b>Forcé</b> : à la fin de la phase de combat, infligez 1 point de dégâts au Feu d’Alarme de l’Erelas pour chaque ennemi dans la zone de cheminement.</p><p class='main-text'><b>Le Feu d’Alarme de l’Erelas a X Points de Vie, où X est égal à 2, plus 3{PerPlayer}. Si le Feu d’Alarme de l’Erelas n’a plus de Points de Vie, les joueurs ont perdu la partie.</b></p><p class='flavor-text'>Toutes les terres étaient grises et silencieuses, l’ombre s’épaississait toujours devant eux, et l’espoir s’affaiblissait dans tous les cœurs.<br/>— Le Retour du Roi</p>");
            AddHtml("Host-of-Rohan-CoE", "<p class='main-text'>Sentinelle.</p><p class='main-text'><b>Action</b> : les joueurs en tant que groupe peuvent dépenser 2 ressources pour redresser l’Ost du Rohan. (Limite : une fois par phase.)</p><p class='flavor-text'>“La guerre est ouverte, et les Rohirrim, seigneurs des chevaux, ne dorment pas.”<br/>— Gandalf, Les Deux Tours</p>");
            AddHtml("Enraged-Dunlending-CoE", "<p class='main-text'><b>Forcé</b> : quand le Dunlending Enragé vous engage au combat, infligez 1 point de dégâts au Feu d’Alarme de l’Erelas.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : les points de dégâts infligés au personnage défenseur par cette attaque doivent être assignés au Feu d’Alarme de l’Erelas.</p>");
            AddHtml("Bald-Hill-CoE", "<p class='main-text'>Tant que les joueurs contrôlent le feu d’alarme, la Colline Pelée gagne +3 {Threat}.</p><p class='main-text'><b>Trajet</b> : si les joueurs contrôlent le feu d’alarme, infligez 2 points de dégâts au Feu d’Alarme de l’Erelas.</p><p class='flavor-text'>Ils finirent par atteindre ainsi le sommet de la colline grise, et une brise soudaine souffla dans leurs cheveux et agita leurs capes : le vent froid de l’aube. — Les Deux Tours</p>");
            AddHtml("Wooden-Barricade-CoE", "<p class='main-text'>Tant que la Barricade en Bois est dans la zone de cheminement, elle gagne: “<b>Forcé</b> : à la fin de la phase de combat, infligez 1 point de dégâts au Feu d’Alarme de l’Erelas et placez un marqueur de progression sur la Barricade en Bois.”</p>{shadow}<p class='shadow-text'><b>Ombre</b> : soit vous défaussez 2 attachements que vous contrôlez, soit vous infligez 1 point de dégâts au Feu d’Alarme de l’Erelas.</p>");
            AddHtml("Rocky-Slope-CoE", "<p class='main-text'>Quand les effets d’une carte de rencontre sont annulés, infligez 1 point de dégâts au Feu d’Alarme de l’Erelas.</p><p class='main-text'><b>Trajet</b> : le premier joueur doit défausser chaque événement de sa main.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : soit vous défaussez chaque ressource de la réserve de chacun de vos héros, soit vous infligez 1 point de dégâts au Feu d’Alarme de l’Erelas.</p>");
            AddHtml("Surrounding-Grassland-CoE", "<p class='main-text'>Archerie 1.</p><p class='main-text'><b>Forcé</b> : quand les Prairies Environnantes quittent le jeu en tant que lieu exploré, s’il n’y a pas de copie de Prairies Environnantes dans la zone de cheminement, renvoyez ce lieu dans la zone de cheminement.</p><p class='flavor-text'>Ils voyaient par-delà la rivière les prairies vertes de Rohan se perdre dans le gris lointain. — Les Deux Tours</p>");
            AddHtml("Narrow-Stair-CoE", "<p class='main-text'>Chaque joueur ne peut déclarer qu’une seule attaque et qu’un seul attaquant par phase.</p><p class='main-text'>Tant que l’Escalier Étroit est le lieu actif, chaque joueur ne peut défendre qu’une seule attaque par phase (les autres attaques sont considérées sans défense).</p>{shadow}<p class='shadow-text'><b>Ombre</b> : soit l’ennemi attaquant gagne +3 {Attack}, soit vous infligez 1 point de dégâts au Feu d’Alarme de l’Erelas.</p>");
            AddHtml("From-All-Sides-CoE", "<p class='main-text'><b>Une fois révélée</b> : chaque joueur doit choisir entre : soit infliger 1 point de dégâts à 4 personnages différents qu’il contrôle, soit infliger 1 point de dégâts au Feu d’Alarme de l’Erelas.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : après cette attaque, soit vous défaussez le personnage défenseur, soit vous infligez 1 point de dégâts au Feu d’Alarme de l’Erelas.</p>");
            AddHtml("Another-Wave-CoE", "<p class='main-text'>Maudit 1. Renfort.</p><p class='main-text'><b>Une fois révélée</b> : si les joueurs contrôlent le feu d’alarme, choisissez entre : infliger 2 points de dégâts au Feu d’Alarme de l’Erelas ou révéler une carte du deck de rencontre.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : soit l’ennemi attaquant effectue une attaque supplémentaire après celle-ci, soit vous infligez 1 point de dégâts au Feu d’Alarme de l’Erelas.</p>");
            AddHtml("Assault-Upon-Erelas-CoE", "<p class='main-text'><b>Une fois révélée</b> : le premier joueur choisit un ennemi engagé au combat avec un joueur et le renvoie dans la zone de cheminement. Si aucun ennemi n’est renvoyé dans la zone de cheminement par cet effet, choisissez entre : infliger 1 point de dégâts au Feu d’Alarme de l’Erelas ou Assaut sur l’Erelas gagne Renfort.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : soit vous augmentez de 4 votre niveau de menace, soit vous infligez 1 point de dégâts au Feu d’Alarme de l’Erelas.</p>");
            AddHtml("Reclaim-the-Beacon-CoE", "<p class='flavor-text'>Reprenez le feu d’alarme, avant que les Dunlendings ne l’endommagent, ou pire.</p><p class='main-text'>Les Dunlendings contrôlent le feu d’alarme. Les ennemis dans la zone de cheminement ne peuvent pas être engagés intentionnellement au combat ou subir de dégâts.</p><p class='main-text'>Il ne peut pas y avoir plus de X marqueurs de progression sur cette étape, où X est égal à 2, plus 1{PerPlayer}. Quand il y a X marqueurs de progression sur cette étape, retournez-la (conservez les marqueurs de progression).</p>");
            AddHtml("Stolen-Steeds-CoE", "<p class='main-text'>Les objectifs <b><i>Monture</i></b> ne peuvent pas être défaussés ou quitter le jeu. Si un objectif <b><i>Monture</i></b> n’est pas attaché, un joueur doit le prendre et l’attacher à un héros qu’il contrôle.</p><p class='main-text'><b>Forcé</b> : à la fin du tour, ajoutez 1 marqueur de progression sur la quête principale pour chaque objectif <b><i>Monture</i></b> que les joueurs contrôlent. Ensuite, augmentez de 1 le niveau de menace de chaque joueur pour pour chaque objectif <b><i>Monture</i></b> en jeu que les joueurs ne contrôlent pas.</p>");
            AddHtml("Flinthoof-CoE", "<p class='main-text'>Protégé (ennemi). Restreint.</p><p class='main-text'>L’ennemi protégeant Sabotdur gagne +1 {Attack}.</p><p class='main-text'>Le héros attaché gagne +1 {Attack}.</p><p class='main-text'><b>[Combat] Action</b> : inclinez Sabotdur pour infliger 1 point de dégâts à un ennemi en jeu.</p><p class='flavor-text'>Leur longue queue flottait au vent, leur crinière était nattée sur leur fière encolure. —Les Deux Tours</p>");
            AddHtml("Brightmane-CoE", "<p class='main-text'>Protégé (lieu). Restreint.</p><p class='main-text'>Le lieu ou l’ennemi protégeant Claircrin gagne +1 {Threat}.</p><p class='main-text'>Le héros attaché gagne +1 {Willpower}.</p><p class='main-text'><b>[Quête] Action:</b> inclinez Claircrin pour regarder la première carte du deck de rencontre. Si cette carte est un lieu, vous pouvez la défausser.</p>");
            AddHtml("Felarof-CoE", "<p class='main-text'>Protégé (ennemi). Restreint.</p><p class='main-text'><b>Forcé</b> : quand l’ennemi protégeant Felaróf attaque, attribuez-lui 2 cartes ombre supplémentaires.</p><p class='main-text'><b>[Combat] Action</b> : inclinez Felaróf pour défausser une carte ombre face cachée en jeu.</p><p class='flavor-text'>“Mais un autre avance devant : un très Grand Cheval. Je n’ai jamais vu son pareil.” — Legolas, Les Deux Tours</p>");
            AddHtml("Thruk-CoE", "<p class='main-text'>Immunisé aux effets de carte des joueurs. Thruk engage au combat le premier joueur.</p><p class='main-text'><b>Forcé</b> : au début de la phase de combat, si Thruk ne protège pas un objectif, il effectue immédiatement une attaque contre le joueur engagé au combat avec lui.</p>{victory:10}");
            AddHtml("Saddle-slasher-CoE", "<p class='main-text'>Archerie X. X est égal à 2, plus 1 pour chaque objectif <b><i>Monture</i></b> que les joueurs contrôlent.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : si vous contrôlez un objectif <b><i>Monture</i></b>, choisissez entre : attacher cet objectif à l’ennemi attaquant en tant qu’objectif protégé ou défausser le personnage défenseur après la résolution de cette attaque.</p>");
            AddHtml("Rein-ripper-CoE", "<p class='main-text'><b>Forcé</b> : quand le Rompeur-de-Rêne attaque, attribuez-lui 2 cartes ombre supplémentaires. Vous pouvez attacher un objectif <b><i>Monture</i></b> que vous contrôlez au Rompeur-de-Rêne en tant qu’objectif protégé pour annuler cet effet.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : l’ennemi attaquant gagne +1 {Attack} pour chaque objectif <b><i>Monture</i></b> que les joueurs contrôlent.</p>");
            AddHtml("Bridle-biter-CoE", "<p class='main-text'>Renfort.</p><p class='main-text'><b>Une fois révélée</b> : le Broyeur-de-Bride engage le joueur qui contrôle le plus d’objectifs <b><i>Monture</i></b>.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : mettez en jeu le Broyeur-de-Bride engagé au combat avec vous et attribuez-lui une carte ombre.</p>");
            AddHtml("Pommel-pouncer-CoE", "<p class='main-text'>Tant que l’Arracheur-d’Arçon est dans la zone de cheminement et ne protège pas d’attachement <b><i>Monture</i></b>, il gagne : “<b>Forcé</b> : quand un joueur devrait prendre le contrôle d’un objectif <b><i>Monture</i></b>, attachez cet objectif <b><i>Monture</i></b> à l’Arracheur-d’Arçon en tant qu’objectif protégé à la place.”</p>");
            AddHtml("Spoiled-Pasture-CoE", "<p class='main-text'>X est égal à 2, plus 2 pour chaque objectif <b><i>Monture</i></b> que les joueurs contrôlent.</p><p class='main-text'><b>Forcé</b> : quand un joueur perd le contrôle d’un objectif <b><i>Monture</i></b>, augmentez de 1 son niveau de menace.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : augmentez de 1 votre niveau de menace pour chaque objectif <b><i>Monture</i></b> que vous contrôlez.</p>");
            AddHtml("Horse-Thieves'-Hovel-CoE", "<p class='main-text'>Quand les joueurs ont la possibilité de voyager, ils doivent voyager vers un lieu non-vaste.</p><p class='main-text'><b>Forcé</b> : quand la Masure des Voleurs de Chevaux quitte le jeu en tant que lieu exploré, s’il y a moins de 1{PerPlayer} ennemi en jeu, défaussez des cartes du sommet du deck de rencontre jusqu’à ce qu’un ennemi soit défaussé et placez-le dans la zone de cheminement.</p>{victory:2}");
            AddHtml("Horse-Lord's-Lodge-CoE", "<p class='main-text'>Quand les joueurs ont la possibilité de voyager, ils doivent voyager vers un lieu non-vaste.</p><p class='main-text'><b>Forcé</b> : quand le Logis des Seigneurs des Chevaux quitte le jeu en tant que lieu exploré, s’il y a moins de 1{PerPlayer} lieu en jeu, défaussez des cartes du sommet du deck de rencontre jusqu’à ce qu’un lieu soit défaussé. Placez ce lieu dans la zone de cheminement.</p>{victory:2}");
            AddHtml("Thirst-for-Horse-Blood-CoE", "<p class='main-text'><b>Une fois révélée</b> : chaque joueur qui contrôle un objectif <b><i>Monture</i></b> doit choisir entre : soit révéler une carte de rencontre, soit attacher à l’ennemi sans attachement ayant le coût d’engagement au combat le plus élevé un objectif <b><i>Monture</i></b> qu’il contrôle en tant qu’objectif protégé. Si aucun joueur ne contrôle d’objectif <b><i>Monture</i></b>, Soif de Sang Équin gagne Renfort.</p><p class='flavor-text'>“Nous festoierons encore de chair de cheval ou de quelque chose de mieux.” — Uglúk, Les Deux Tours</p>");
            AddHtml("Overburdened-CoE", "<p class='main-text'><b>Une fois révélée</b> : chaque joueur qui contrôle plus de 2 attachements doit défausser 1 attachement qu’il contrôle, si possible (à la place, si un joueur contrôle plus de 5 attachements, il doit en défausser 2, si possible). Si aucun attachement n’est défaussé par cette effet, Surchargé gagne Renfort.</p>{shadow}<p class='shadow-text'><b>Ombre</b> : défaussez 1 carte prise au hasard dans votre main.</p>");
            AddHtml("The-Last-Alliance-CoE", "<p class='main-text'>Choisissez deux traits, <b><i>A</i></b> et <b><i>B</i></b>. Chacun de vos héros de départ et chaque allié de votre deck doit avoir le trait imprimé <b><i>A</i></b> ou le trait imprimé <b><i>B</i></b>, mais ne peut pas avoir les deux traits. Vous devez avoir au moins 1 héros de départ et 10 alliés avec le trait <b><i>A</i></b> dans votre deck. De même pour le trait <b><i>B</i></b>.</p><p class='main-text'><b>Mise en place:</b> retournez cette carte.</p><p class='flavor-text'>“La force des Elfes pour lui résister était plus grande, il y a longtemps ; et tous les Hommes ne leur étaient pas aliénés.” — Gandalf, La Communauté de l’Anneau</p>");
            AddHtml2("The-Last-Alliance-CoE", "<p class='main-text'>Tant que vous contrôlez plus de personnages <b><i>A</i></b> que de personnages <b><i>B</i></b>, réduisez de 1 le coût du premier allié <b><i>B</i></b> que vous jouez à chaque tour, et vice versa.</p><p class='main-text'><b>Action</b> : inclinez La Dernière Alliance pour choisir une carte de votre main ou en jeu sous votre contrôle. Remplacez chaque instance de “<b><i>A</i></b>” ou “<b><i>B</i></b>” sur la capacité de cette carte par “<b><i>A</i></b> ou <b><i>B</i></b>” jusqu’à la fin du tour.</p>");
            AddHtml("Drive-them-Away-CoE", "<p class='flavor-text'>Le long de la Grande Route de l’Ouest, vous arrivez près d’un camp de Rohirrim dans l’ombre de la colline nue du feu d’alarme de l’Erelas. Mais le calme de votre voyage est interrompu par une attaque soudaine ! Le choc de l’acier et les cris des hommes atteignent bientôt vos oreilles. Vous devez les chasser et protéger le camp de nouvelles destructions !</p><p class='main-text'><b></b> <b>Mise en place</b> : mettez de côté le Meneur Brigand, hors jeu. Placez les Biens Pillés dans la zone de cheminement. Chaque joueur cherche un ennemi différent dans le deck de rencontre et le place dans la zone de cheminement. Mélangez le deck de rencontre.</p>");
            AddHtml2("Drive-them-Away-CoE", "<p class='main-text'></b> <b>Forcé</b> : quand un ennemi est détruit, placez 1 marqueur de ressource sur cette carte.</p><p class='main-text'><b>[Rencontre] Action</b> : retirez 5 marqueurs de progression de cette étape pour choisir une carte dans une pile de butin et la défausser. Limite : une fois par tour et par joueur.</p><p class='main-text'><b>S’il y a au moins X marqueurs de ressource sur Repoussez-les, passez à l’étape suivante. X est égal à 1, plus 2{PerPlayer} (2 par joueur).</b></p>");
            AddHtml("Ambush-CoE", "<p class='flavor-text'>Vous vous êtes battu au cœur du campement, mais les brigands se rassemblent autour de leur chef et lancent une violente attaque.</p><p class='main-text'><b></b> <b>Une fois révélée</b> : mettez fin à la phase en cours. Placez le Meneur Brigand dans la zone de cheminement. Chaque joueur qui n’est pas engagé au combat avec un ennemi cherche dans le deck de rencontre ou dans la pile de défausse de rencontre un ennemi et le met en jeu, engagé au combat avec lui. Mélangez le deck de rencontre.</p>");
            AddHtml2("Ambush-CoE", "<p class='main-text'></b> <b>Forcé</b> : quand un ennemi entre en jeu, chaque joueur fait un test d’engagement au combat contre lui.</p><p class='main-text'><b>[Organisation] Action</b>: retirez 5 marqueurs de progression de cette étape pour choisir une carte dans une pile de butin. Le propriétaire de cette carte peut la défausser ou la jouer gratuitement. Limite : quatre fois par tour pour le groupe.</p><p class='main-text'><b>Quand le Meneur Brigand est éliminé, les joueurs ont gagné la partie.</b></p>");
            AddHtml("Battle-for-the-Hill-CoE", "<p class='flavor-text'>Des Dunlendings et des Rohirrim renégats se sont emparés du feu d’alarme de l’Erelas. Reprenez la colline à tout prix !</p><p class='main-text'><b></b> <b>Mise en place</b> : mettez de côté l’Ost du Rohan, hors jeu. Placez le Feu d’Alarme de l’Erelas et 1{PerPlayer} Dunlending Enragé dans la zone de cheminement. Placez la quête annexe double-face dans la zone de cheminement, du côté Reprendre le Feu d’Alarme. Cherchez un exemplaire de Prairies Environnantes dans le deck de rencontre et faites-en le lieu actif. Mélangez le deck de rencontre.</p>");
            AddHtml2("Battle-for-the-Hill-CoE", "<p class='main-text'></b> <b>Forcé</b> : après avoir placé n’importe quel nombre de marqueurs de progression sur cette étape, chaque joueur doit choisir entre : soit incliner un personnage qu’il contrôle, soit infliger 1 point de dégâts au Feu d’Alarme de l’Erelas.</p><p class='main-text'><b>Cette étape ne peut être passée tant que les Dunlendings contrôlent le feu d’alarme.</b></p>");
            AddHtml("Hold-the-Hilltop-CoE", "<p class='flavor-text'>Alors que vos ennemis se rassemblent pour un nouvel assaut sur le feu d’alarme, vous pouvez apercevoir les torches des troupes de Gálmód se massant en contrebas.</p><p class='main-text'><b></b> <b>Une fois révélée</b> : placez des marqueurs de progression sur Défendre le Feu d’Alarme (en ignorant tout lieu actif) jusqu’à ce qu’il y ait X marqueurs de progression dessus, où X est égal à 2, plus 1{PerPlayer}. Mélangez la pile de défausse de rencontre dans le deck de rencontre. Chaque joueur défausse des cartes du sommet du deck de rencontre jusqu’à ce qu’il défausse un ennemi et le place dans la zone de cheminement.</p>");
            AddHtml2("Hold-the-Hilltop-CoE", "<p class='main-text'></b> Temps 3.</p><p class='main-text'><b>Forcé</b> : à la fin du tour, infligez 1 point de dégâts au Feu d’Alarme de l’Erelas (2 points de dégâts s’il y a au moins 3 joueurs dans la partie). Ensuite, s’il n’y a plus de compteur de temps sur cette étape, passez immédiatement à l’étape 3.</p><p class='main-text'><b>Forcé</b> : quand Défendre le Feu d’Alarme devient la quête en cours, révélez une carte de rencontre.</p><p class='main-text'><b>Si les Dunlendings contrôlent le feu d’alarme, les joueurs ont perdu la partie.</b></p>");
            AddHtml("The-Last-Push-CoE", "<p class='flavor-text'>Alors que les forces de Gálmód chevauchent pour encercler l’ennemi, vous devez vous armer pour une ultime offensive.</p><p class='main-text'><b></b> <b>Une fois révélée</b> : retirez tous les marqueurs de progression sur Défendre le Feu d’Alarme et retournez-la. Mettez l’Ost du Rohan en jeu sous le contrôle du premier joueur. Mélangez la pile de défausse de rencontre dans le deck de rencontre. Chaque joueur défausse des cartes du sommet du deck de rencontre jusqu’à ce qu’il défausse un ennemi et le place dans la zone de cheminement.</p>");
            AddHtml2("The-Last-Push-CoE", "<p class='main-text'></b> Ajoutez 2 au total de {Threat} dans la zone de cheminement pour chaque dégât sur le Feu d’Alarme de l’Erelas.</p><p class='main-text'><b>Forcé</b> : au début de la phase de combat, chaque ennemi engagé au combat effectue immédiatement une attaque.</p><p class='main-text'><b>Forcé</b> : quand un personnage quitte le jeu, infligez 1 point de dégâts au Feu d’Alarme de l’Erelas.</p><p class='main-text'><b>À la fin du tour, si les Dunlendings contrôlent le feu d’alarme, les joueurs ont perdu la partie, et si les joueurs contrôlent le feu d’alarme, ils ont gagné la partie.</b></p>");
            AddHtml("Thieves-and-Coursers-CoE", "<p class='flavor-text'>Side A: Des orques ont envahi l’Estfolde, ravageant le pays et capturant les chevaux de guerre préférés du roi Thengel. Side B: Si vous récupérez Sabotdur et Claircrin, vous pourrez peut-être arrêter l’ost des orques avant qu’il n’atteigne Aldburg.</p><p class='main-text'><b></b> <b>Mise en place</b> : placez Coursiers Volés dans la zone de cheminement. Mettez Thruk, les objectifs <b><i>Monture</i></b>, et les 8 ennemis du set de rencontre Ost des Orques de côté, hors jeu. Mélangez le deck de rencontre. Le premier joueur révèle Sabotdur et Claircrin et les place dans la zone de cheminement. Chaque autre joueur défausse des cartes du sommet du deck de rencontre jusqu’à ce qu’il défausse un ennemi ou un lieu et le place dans la zone de cheminement.</p>");
            AddHtml2("Thieves-and-Coursers-CoE", "<p class='main-text'></b> <b>Cette étape ne peut être passée tant que les joueurs ne contrôlent pas au moins 2 objectifs <b><i>Monture</i></b>.</b></p>");
            AddHtml("The-King's-Stallion-CoE", "<p class='flavor-text'>Le chef de guerre Thruk est arrivé dans la bataille, chevauchant le puissant Felaróf et hurlant des ordres à sa troupe. “Rassemblement ! Ce soir, nous festoierons de chair de cheval !”</p><p class='main-text'><b></b> <b>Une fois révélée</b> : s’il s’agit de la phase de combat, mettez fin à la phase. Mélangez la pile de défausse de rencontre dans le deck de rencontre. Le premier joueur place Thruk dans la zone de cheminement et lui attache Felaróf en tant qu’objectif protégé. Chaque autre joueur défausse des cartes du sommet du deck de rencontre jusqu’à ce qu’il défausse un ennemi et le met en jeu, engagé au combat avec lui.</p>");
            AddHtml2("The-King's-Stallion-CoE", "<p class='main-text'></b> Thruk ne peut pas subir de dégâts, sauf s’il y a au moins 4 marqueurs de progression sur cette étape.</p><p class='main-text'><b>Cette étape ne peut être passée tant que les joueurs ne contrôlent pas 3 objectifs <b><i>Monture</i></b>.</b></p>");
            AddHtml("The-Sweeping-Tide-CoE", "<p class='flavor-text'>L’ost des orques tout entier a répondu au cri de ralliement de Thruk. Vous devez vous unir pour une charge finale afin de les disperser dans les collines.</p><p class='main-text'><b></b> <b>Une fois révélée</b> : s’il s’agit de la phase de combat, mettez fin à la phase. Mélangez la pile de défausse de rencontre dans le deck de rencontre. Cherchez Thruk dans la pile de victoire et placez le dans la zone de cheminement. Chaque joueur choisit 2 ennemis du set de rencontre mis de côté Ost des Orques et les met en jeu, engagés au combat avec lui. Un des ennemis mis en jeu de cette façon doit être le Bataillon Orque.</p>");
            AddHtml2("The-Sweeping-Tide-CoE", "<p class='main-text'></b> Sautez l’étape de renforcement de la phase de Quête. À tout moment, si le deck de rencontre est vide, mélangez la pile de défausse dans le deck de rencontre.</p><p class='main-text'><b>Forcé</b> : quand un ennemi effectue une attaque, attribuez-lui 1 carte ombre supplémentaire.</p><p class='main-text'><b>Si un objectif <b><i>Monture</i></b> est attaché à un ennemi, les joueurs ont perdu la partie. S’il n’y a plus d’ennemis en jeu, les joueurs ont gagné la partie.</b></p>");
            //AddHtml("Reclaim-the-Beacon-CoE", "<p class='main-text'>Les joueurs contrôlent le feu d’alarme. Les cartes ombre sont immunisées aux effets de carte des joueurs.</p><p class='main-text'>Il ne peut pas y avoir plus de X marqueurs de progression sur cette étape, où X est égal à 2, plus 1{PerPlayer}.</p><p class='main-text'><b>Réponse</b> : retirez n’importe quel nombre de marqueurs de progression sur Défendre le Feu d’Alarme pour annuler autant de dégâts tout juste infligés au Feu d’Alarme de l’Erelas. Ensuite, si cette étape a 0 marqueur de progression, retournez-la.</p>");

        }

        private void TheAldburgPlot()
        {
            AddHtml("Dunlending-Agents-TAP", "<p class='main-text'>Si les joueurs contrôlent au moins 2 objectifs <b><i>Indice</i></b>, les Agents Dunlending gagnent +2 {Attack}.</p><p class='main-text'><b>Forcé:</b> quand les Agents Dunlending attaquent et détruisent un personnage que vous contrôlez, infligez 1 point de dégâts à chaque héros que vous contrôlez ayant un objectif <b><i>Indice</i></b> attaché.</p><p class='flavor-text'>Ceux du Pays de Dun et de nombreux hommes de la garnison du Fort étaient au travail. — Le Retour du Roi</p>");
            AddHtml("Aldburg-Malcontent-TAP", "<p class='main-text'><b>Une fois révélée:</b> si les joueurs contrôlent au moins 3 objectifs <b><i>Indice</i></b>, l’Aldburgeois Mécontent gagne Renfort.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> si les joueurs contrôlent au moins 2 objectifs <b><i>Indice</i></b>, renvoyez l’ennemi attaquant dans la zone de cheminement après la résolution de son attaque.</p>");
            AddHtml("Spreading-Inferno-TAP", "<p class='main-text'>Protégé (lieu).</p><p class='main-text'><b>Forcé:</b> au début de la phase de voyage, défaussez le lieu attaché, augmentez de 1 le niveau de menace de chaque joueur, et chaque joueur défausse l’allié qu’il contrôle ayant le coût imprimé le plus faible. Ensuite, révélez le Brasier Dévorant.</p><p class='main-text'><b>Forcé:</b> quand le lieu attaché est exploré, retournez cette carte et révélez-la.</p>");
            AddHtml("Ruby-Cloak-pin-TAP", "<p class='main-text'>Si cette carte n’est pas attachée, attachez-la au héros ayant le moins d’objectifs <b><i>Indice</i></b> attachés.</p><p class='main-text'><b>Forcé:</b> quand le héros attaché se redresse, défaussez les 4 premières cartes du dessus de votre deck.</p><p class='flavor-text'>Parmi les cendres, vous trouvez une broche que vous reconnaissez immédiatement. Elle appartient au roquet Gálmód, le conseiller de confiance de la reine Morwen.</p>");
            AddHtml("Garrison-Barracks-TAP", "<p class='main-text'>Immunisé aux effets de cartes des joueurs. X est le double du nombre d’objectifs <b><i>Indice</i></b> que les joueurs contrôlent.</p><p class='main-text'><b>Trajet:</b> mélangez dans le deck de rencontre tous les ennemis <b><i>Pays de Dun</i></b> mis de côté.</p><p class='main-text'><b>Forcé:</b> quand la Caserne est explorée, retournez cette carte et révélez-la.</p><p class='flavor-text'>La garde de la ville a des yeux et des oreilles sur tout à Aldburg. Mais qui les surveille ?</p>");
            AddHtml("Raven-Signet-Ring-TAP", "<p class='main-text'><b>Une fois révélée:</b> placez L’Homme à la Lèvre Tordue dans la zone de cheminement.</p><p class='main-text'>Si cette carte n’est pas attachée, attachez-la au héros ayant le moins d’objectifs <b><i>Indice</i></b> attachés.</p><p class='main-text'><b>Forcé:</b> quand le héros attaché se redresse, défaussez toutes les ressources de sa réserve.</p><p class='flavor-text'>Le prisonnier a parlé aux gardiens d’un agent appelé le Maître-Espion. La seule piste actuellement est cette bague, censée lui appartenir.</p>");
            AddHtml("Whisperer-in-the-Darkness-TAP", "<p class='flavor-text'>La taverne était en proie à des rumeurs au sujet d’un assassin appelé “La Chuchoteuse”. Si vous voulez percer ce mystère, vous devrez la trouver et l’arrêter avant qu’elle ne frappe.</p><p class='main-text'><b>Forcé:</b> quand un lieu est exploré, chaque joueur augmente de 1 son niveau de menace.</p><p class='main-text'><b>Forcé:</b> quand cette étape est passée, placez la Chuchoteuse dans la zone de cheminement.</p>{victory:10}");
            AddHtml("Prison-Key-TAP", "<p class='main-text'><b>Une fois révélée:</b> placez Un Début d’Incendie et Le Prisonnier en Blanc dans la zone de cheminement.</p><p class='main-text'>Si cette carte n’est pas attachée, attachez-la au héros ayant le moins d’objectifs <b><i>Indice</i></b> attachés.</p><p class='main-text'><b>Forcé:</b> quand le héros attaché se redresse, défaussez un attachement non-<b><i>Indice</i></b> qu’il possède.</p><p class='flavor-text'>Blessé et cherchant à fuir, le Maître-Espion laisse échapper un détail clé : un de ses agents est déjà en mouvement et se dirige vers la prison de la ville.</p>");
            AddHtml("Traitor's-Confession-TAP", "<p class='main-text'>Si cette carte n’est pas attachée, attachez-la au héros ayant le moins d’objectifs <b><i>Indice</i></b> attachés.</p><p class='main-text'><b>Forcé:</b> quand le héros attaché se redresse, défaussez une carte au hasard de votre main.</p><p class='flavor-text'>Dans son dernier souffle, le traître Ossian révèle que Gálmód lui a ordonné de tuer l’un des membres de sa propre éored : un témoin d’une terrible trahison.</p>");
            AddHtml("Silver-Dagger-TAP", "<p class='main-text'><b>Une fois révélée:</b> placez Une Affaire d’Identité et Moisson Rouge dans la zone de cheminement.</p><p class='main-text'>Si cette carte n’est pas attachée, attachez-la au héros ayant le moins d’objectifs <b><i>Indice</i></b> attachés.</p><p class='main-text'><b>Forcé:</b> quand le héros attaché se redresse, infligez-lui 1 point de dégâts.</p><p class='flavor-text'>La Chuchoteuse s’est échappée dans les rues d’Aldburg, mais elle a laissé derrière elle une arme distinctive et coûteuse.</p>");
            AddHtml("Fear-Fire-Foes-TAP", "<p class='main-text'>Secret 1. Vous ne pouvez pas jouer cette carte si vous contrôlez un héros <b><i>Guerrier</i></b>.</p><p class='main-text'><b>Action:</b> choisissez un ennemi non-unique et un attribut ({Threat}, {Attack}, ou {Defense}). Jusqu’à la fin de la phase, réduisez l’attribut choisi de cet ennemi à 0.</p><p class='flavor-text'>Au loin, on entendit répondre d’autres cors. L’alerte se répandait. — La Communauté de l’Anneau</p>");
            AddHtml("Sudden-Ambush-TAP", "<p class='main-text'><b>Une fois révélée:</b> le premier joueur doit choisir entre : augmenter de 4 son niveau de menace, ou mettre en jeu, engagé au combat avec lui, l’ennemi le plus proche du sommet de la pile de défausse de rencontre. Cet ennemi effectue immédiatement une attaque. Le premier joueur doit déclarer comme défenseur le personnage redressé qu’il contrôle ayant la {Willpower} la plus élevée, si possible.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> résolvez l’effet “Une fois révélée” de cette carte.</p>");
            AddHtml("Taken-by-the-Night-TAP", "<p class='main-text'><b>Une fois révélée:</b> chaque joueur doit défausser l’allié qu’il contrôle ayant le coût imprimé le plus faible. Si aucun allié ayant un coût imprimé de 2 ou plus n’est défaussé par cet effet, Emporté dans la Nuit gagne Renfort.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> l’ennemi attaquant gagne +1 {Attack} (+2 {Attack} s’il attaque depuis la zone de cheminement).</p>");
            AddHtml("False-Lead-TAP", "<p class='main-text'>Maudit 1.</p><p class='main-text'><b>Une fois révélée:</b> chaque joueur retire de la quête un héros qu’il contrôle ayant un objectif <b><i>Indice</i></b> attaché, si possible. Ensuite, chaque joueur choisit un héros qu’il contrôle qui n’est pas engagé dans la quête et l’incline.</p><p class='flavor-text'>“…essayez-vous maintenant de m’attraper grâce à un mensonge ?” — Frodon Sacquet, Les Deux Tours</p>");
            AddHtml("Red-Fletched-Arrow-TAP", "<p class='main-text'>Si cette carte n’est pas attachée, attachez-la au héros ayant le moins d’objectifs <b><i>Indice</i></b> attachés.</p><p class='main-text'><b>Forcé:</b> quand le héros attaché se redresse, augmentez de 1 votre niveau de menace.</p><p class='flavor-text'>Le quartier-maître de l’armurerie vous informe qu’il a personnellement forgé la dague, ainsi que cette flèche inhabituelle, pour Lord Gálmód.</p>");
            AddHtml("Town-Guard-TAP", "<p class='main-text'>Si les joueurs contrôlent au moins 2 objectifs <b><i>Indice</i></b>, la Garde de la Ville gagne +2 {Threat}.</p><p class='main-text'><b>Forcé:</b> quand la Garde de la Ville attaque et détruit un personnage que vous contrôlez, défaussez 1 ressource de la réserve de chaque héros que vous contrôlez ayant un objectif <b><i>Indice</i></b> attaché.</p><p class='flavor-text'>“Espéré-je en vain que vous m’avez été envoyé en aide dans le doute et le besoin ?” — Éomer, Les Deux Tours</p>");
            AddHtml("Gavin-TAP", "<p class='main-text'>Ne peut pas avoir d’attachements.</p><p class='main-text'><b>Réponse:</b> quand une carte de rencontre n’ayant pas le mot-clé Renfort est révélée, inclinez Gavin pour donner à cette carte le mot-clé Renfort et ajouter 1 ressource à la réserve d’un héros <b><i>Éclaireur</i></b> que vous contrôlez. Si la prochaine carte de rencontre révélée n’est pas un lieu, défaussez-la sans résoudre ses effets.</p>");
            AddHtml("Great-Mead-Hall-TAP", "<p class='main-text'>Immunisé aux effets de cartes des joueurs.</p><p class='main-text'><b>Trajet:</b> chaque joueur doit augmenter de X son niveau de menace, X étant le nombre d’ennemis dans la zone de cheminement.</p><p class='main-text'><b>Forcé:</b> quand le Grand Hall à Hydromel est exploré, retournez cette carte et révélez-la.</p><p class='flavor-text'>Vous avez traqué les agents de la Chuchoteuse jusque dans les couloirs mêmes du Maréchal Eomund. Quelles peuvent être les limites de ce complot ?</p>");
            AddHtml("Fatty-Bolger-TAP", "<p class='main-text'><b>Mise en place:</b> diminuez de 2 votre niveau de menace.</p><p class='main-text'><b>Réponse:</b> augmentez de 4 votre niveau de menace et inclinez Gros Bolger pour annuler une carte Ennemi non-unique tout juste révélée du deck de rencontre et l’ajouter à la pile de victoire. (Limite : une fois par partie pour le groupe.)</p><p class='flavor-text'>Gros Bolger n’était pas resté inactif. Dès qu’il eut vu les formes noires ramper du jardin, il avait su qu’il devait se sauver au plus vite ou mourir. — La Communauté de l’Anneau</p>");
            AddHtml("Well-lit-Hall-TAP", "<p class='main-text'>Tant que le Hall Lumineux est dans la zone de cheminement, pas plus de 3 marqueurs de progression ne peuvent être placés sur une carte quête suite à un succès de quête.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> l’ennemi attaquant gagne +1 {Attack} (+2 {Attack} si le personnage défenseur a un attachement <b><i>Indice</i></b>).</p>");
            AddHtml("The-Armoury-TAP", "<p class='main-text'>Immunisé aux effets de cartes des joueurs.</p><p class='main-text'><b>Trajet:</b> chaque joueur doit incliner le personnage redressé qu’il contrôle ayant la {Defense} la plus élevée.</p><p class='main-text'><b>Forcé:</b> quand l’Armurerie est explorée, retournez cette carte et révélez-la.</p><p class='flavor-text'>La Chuchoteuse brandissait un poignard d’apparence familière. Peut-être que le quartier-maître de l’armurerie pourra vous en dire plus.</p>");
            AddHtml("The-Man-with-the-Twisted-Lip-TAP", "<p class='flavor-text'>Vous avez commencé à traquer le Maître-Espion d’Aldburg, bien qu’il soit difficile de dire lequel de vous est le chat et lequel est la souris.</p><p class='main-text'><b>Forcé:</b> à la fin du tour, chaque joueur augmente de 1 son niveau de menace.</p><p class='main-text'><b>Forcé:</b> quand cette étape est passée, placez le Maître-Espion dans la zone de cheminement.</p>{victory:10}");
            AddHtml("The-Whisperer-TAP", "<p class='main-text'><b>Forcé:</b> à la fin du tour, renvoyez la Chuchoteuse dans la zone de cheminement, si possible. Elle effectue immédiatement une attaque contre le premier joueur. Les dégâts infligés par cette attaque sont annulés ; à la place, le joueur en défense augmente d’autant son niveau de menace.</p><p class='main-text'><b>Forcé:</b> quand la Chuchoteuse est éliminée, retournez cette carte et révélez-la.</p>");
            AddHtml("The-Horse's-Tail-TAP", "<p class='main-text'>Immunisé aux effets de cartes des joueurs. X est le double du nombre d’objectifs <b><i>Indice</i></b> que les joueurs contrôlent.</p><p class='main-text'><b>Trajet:</b> mélangez dans le deck de rencontre tous les ennemis <b><i>Garnison</i></b> mis de côté.</p><p class='main-text'><b>Forcé:</b> quand Le Crin de Cheval est exploré, retournez cette carte et révélez-la.</p><p class='flavor-text'>Cela attirera peut-être les soupçons de la garde de la ville, mais ce mystère ne pourra pas être résolu par les moyens habituels.</p>");
            AddHtml("The-Spy-Master-TAP", "<p class='main-text'><b>Forcé:</b> si la carte ombre attribuée au Maître-Espion n’a aucun effet ombre, infligez 1 point de dégâts au personnage défenseur et attribuez une nouvelle carte ombre au Maître-Espion (Limite 2 fois par attaque).</p><p class='main-text'><b>Forcé:</b> quand le Maître-Espion est éliminé, retournez cette carte et révélez-la.</p>");
            AddHtml("Evil-Under-the-Moon-TAP", "<p class='flavor-text'>Sachant qu’une faction de rebelles a élu domicile à Aldburg, Ealdwulf vous supplie de les dénicher. </p><p class='main-text'> <b>Mise en place:</b> mettez de côté chaque quête annexe de rencontre, chaque exemplaire de Garde de la Ville, et chaque exemplaire de Agents Dunlending, hors jeu. Mettez de côté chaque quête annexe de rencontre double-face, hors jeu, côté <b><i>Indice</i></b> face cachée. Placez la Caserne et Le Crin de Cheval dans la zone de cheminement. Cherchez un ennemi dans le deck de rencontre et placez-le dans la zone de cheminement (2 ennemis différents s’il y a 3 joueurs ou plus dans la partie). Mélangez le deck de rencontre.</p>");
            AddHtml2("Evil-Under-the-Moon-TAP", "<p class='flavor-text'>Où commencer la chasse ? Allez-vous chercher de l’aide auprès des gardes de la ville ou enquêter sur les criminels des bas-fonds dans une taverne sordide ?</p><p class='main-text'> Les ennemis ne peuvent pas être intentionnellement engagés au combat.</p><p class='main-text'><b>Forcé:</b> à la fin du tour, augmentez de 1 le niveau de menace de chaque joueur.</p><p class='main-text'>Cette étape ne peut pas être passée tant que les joueurs ne contrôlent pas au moins un objectif <b><i>Indice</i></b>.</p>");
            AddHtml("The-Prisoner-in-White-TAP", "<p class='flavor-text'>Afin de tenter de dissimuler ses traces, l’agent du Maître-Espion s’est infiltré dans la prison.</p><p class='main-text'>Les joueurs ne peuvent pas voyager ou rendre un lieu actif par un effet de carte de joueur.</p><p class='main-text'><b>Forcé:</b> quand cette étape est passée, placez Ossian dans la zone de cheminement.</p>{victory:10}");
            AddHtml("Letter-from-Dunland-TAP", "<p class='main-text'>Si cette carte n’est pas attachée, attachez-la au héros ayant le moins d’objectifs <b><i>Indice</i></b> attachés.</p><p class='main-text'><b>Forcé:</b> quand le héros attaché se redresse, retirez 2 marqueurs de progression de chaque carte lieu et quête en jeu.</p><p class='flavor-text'>Dans une cachette, vous trouvez une lettre scellée écrite dans un rohirrique douteux, signée “Grimrede”. Elle est adressée à un homme que vous pensiez être votre ami – Gálmód.</p>");
            AddHtml("Lily-Cotton-TAP", "<p class='main-text'><b>Action:</b> inclinez Lily Chaumine pour redresser un autre allié <b><i>Hobbit</i></b> et ajouter respectivement le total de {Willpower}, d’{Attack} et de {Defense} de Lily Chaumine à la {Willpower}, l’{Attack} et la {Defense} de ce personnage jusqu’à la fin de la phase. À la fin de la phase, si cet allié est toujours en jeu, ramenez-le dans la main de son propriétaire. (Limite une fois par phase.)</p><p class='flavor-text'>“Il n’y a guère de magie chez eux que celle, toute ordinaire et courante, qui leur permet de disparaître sans bruit et rapidement…” — Le Hobbit</p>");
            AddHtml("Red-Harvest-TAP", "<p class='flavor-text'>Brusquement, les hommes de la Chuchoteuse vous tombent dessus. Vous devrez vous battre bec et ongles pour vous rendre à l’armurerie.</p><p class='main-text'><b>Forcé:</b> quand un ennemi engage au combat un joueur, ce joueur doit infliger 2 points de dégâts à un personnage qu’il contrôle.</p><p class='main-text'><b>Forcé:</b> quand cette étape est passée, mélangez la pile de défausse de rencontre dans le deck de rencontre. Chaque joueur défausse des cartes du sommet du deck de rencontre jusqu’à ce qu’il défausse un ennemi et le place dans la zone de cheminement. Placez l’Armurerie dans la zone de cheminement.</p>{victory:10}");
            AddHtml("Ossian-TAP", "<p class='main-text'>Immunisé aux effets de cartes des joueurs. Ossian ne peut pas subir plus de X points de dégât par tour, X étant le nombre d’objectifs <b><i>Indice</i></b> que les joueurs contrôlent.</p><p class='main-text'><b>Forcé:</b> quand Ossian est éliminé, retournez cette carte et révélez-la.</p><p class='flavor-text'>Vous trouvez Ossian penché sur un cadavre vêtu de blanc. Entendant votre approche, il se retourne.</p>");
            AddHtml("Rohirric-Coins-TAP", "<p class='main-text'><b>Une fois révélée:</b> placez Chuchotement dans les Ténèbres dans la zone de cheminement.</p><p class='main-text'>Si cette carte n’est pas attachée, attachez-la au héros ayant le moins d’objectifs <b><i>Indice</i></b> attachés.</p><p class='main-text'><b>Forcé:</b> quand le héros attaché se redresse, inclinez un autre personnage que vous contrôlez.</p><p class='flavor-text'>Un inconnu heurte votre table. Il s’excuse abondamment puis s’évanouit dans la nuit. Au moment où vous remarquez la bourse remplies de pièces qu’il a laissé, il est déjà loin.</p>");
            AddHtml("Gathering-Clues-TAP", "<p class='flavor-text'>Il semble que la ville d’Aldburg regorge de mystères. À chaque coin de rue se cache une nouvelle énigme, et derrière chaque énigme se trouve une lame aiguisée et désespérée. Avant que le soleil ne se lève, vous devrez aller plus loin pour découvrir la vérité. </p>");
            AddHtml2("Gathering-Clues-TAP", "<p class='flavor-text'>Il semblerait qu’Aldburg soit l’hôte d’un mystère à deux têtes. Si seulement vous pouvez en saisir un fil, peut-être pourrez-vous démêler cette toile corrompue et amère. Il est minuit passé. Le temps presse.</p><p class='main-text'> <b>Réponse:</b> à la fin du tour, si les joueurs contrôlent au moins 4 objectifs <b><i>Indice</i></b>, ils peuvent passer à l’étape 3A.</p>");
            AddHtml("Weep-No-More-TAP", "<p class='main-text'>Limite : 3 exemplaires de Renonçons aux Larmes ! dans la pile de victoire.</p><p class='main-text'><b>Réponse:</b> après avoir résolu l’effet “une fois révélée” ou l’effet ombre d’une carte Traîtrise, ajoutez Renonçons aux Larmes ! à la pile de victoire pour ajouter cette carte Traîtrise à la pile de victoire.</p><p class='flavor-text'>“Au moins pourrons-nous encore être vengés. Ceignons nos reins et renonçons aux larmes !”<br/>— Aragorn, La Communauté de l’Anneau</p>");
            AddHtml("Misty-Street-TAP", "<p class='main-text'><b>Une fois révélée:</b> défaussez tous les attachements et marqueurs de l’allié contrôlé par le premier joueur ayant le coût le plus élevé. Retournez-le et attachez-le face cachée sous la Ruelle Embrumée.</p><p class='main-text'><b>Forcé:</b> quand la Ruelle Embrumée quitte le jeu, retournez l’allié attaché et renvoyez-le sous le contrôle de son propriétaire, redressé.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> défaussez un attachement non-<b><i>Indice</i></b> que vous contrôlez.</p>");
            AddHtml("Buckland-Shirriff-TAP", "<p class='main-text'>Dévoué. À distance.</p><p class='main-text'><b>Réponse:</b> quand un ennemi qui partage un <b><i>Trait</i></b> avec un ennemi dans la pile de victoire est placé dans la zone de cheminement, dépensez 1 ressource {sphere:Lore} pour mettre le Shirriff du Pays de Bouc en jeu depuis votre main, sous votre contrôle.</p>");
            AddHtml("Horn-call-of-Buckland-TAP", "<p class='main-text'>Attachez cette carte à un héros {sphere:Lore} ou <b><i>Hobbit</i></b>.</p><p class='main-text'><b>Réponse:</b> quand vous engagez au combat un ennemi qui partage un <b><i>Trait</i></b> avec un ennemi dans la pile de victoire, inclinez la Sonnerie de Cor du Pays de Bouc pour redresser le héros attaché. Ensuite, cet ennemi gagne –1 {Attack} et –1 {Defense} jusqu’à la fin du tour.</p><p class='flavor-text'>À ce moment, parmi les arbres proches, un cor sonna. Il déchira la nuit comme un feu au sommet d’une colline.<br/>— La Communauté de l’Anneau</p>");
            AddHtml("Decrepit-Hovel-TAP", "<p class='main-text'>Le coût en ressources des alliés doit être payé avec la réserve d’un seul héros.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> défaussez 1 ressource de la réserve d’un héros que vous contrôlez.</p><p class='flavor-text'>“ Ils en partent pour vagabonder alentour, à voler ou à “ ramasser ” comme ils appellent ça. ”<br/>— Tom Chaumine, Le Retour du Roi</p>");
            AddHtml("Young-Tom-TAP", "<p class='main-text'>Durant le tour où Tom le Jeune entre en jeu, le coût d’engagement au combat de chaque ennemi est augmenté de 5.</p><p class='flavor-text'>“Tout n’y est pas parfait, dit Frodon, mais on y honore certainement les jardiniers.” — Les Deux Tours</p>");
            AddHtml("There-and-Back-Again-TAP", "<p class='main-text'>Secret 1.</p><p class='main-text'><b>Réponse:</b> quand un allié <b><i>Hobbit</i></b> quitte le jeu, cherchez dans votre main ou pile de défausse une copie de cet allié, et mettez-le en jeu sous le contrôle de n’importe quel joueur.</p><p class='flavor-text'>Les ennuis juridiques durèrent des années. Il fallut longtemps pour faire admettre en fait que M. Sacquet était de nouveau vivant. — Le Hobbit</p>");
            AddHtml("A-Fire-Rises-TAP", "<p class='flavor-text'>Quelque chose de terrible se trame à Aldbourg. Alors que vous commencez à assembler les pièces du puzzle, vous voyez de la fumée et des flammes s’élever d’un bâtiment voisin.</p><p class='main-text'>Le coût pour jouer chaque évènement est augmenté de 1.</p><p class='main-text'><b>Forcé:</b> quand cette étape est passée, révélez le Brasier Dévorant.</p>{victory:10}");
            AddHtml("A-Case-of-Identity-TAP", "<p class='flavor-text'>Le mystère de la véritable identité de la Chuchoteuse demeure. Vous devez être sur la bonne piste, à en juger par ses égorgeurs qui rôdent dans les ruelles.</p><p class='main-text'><b>Forcé:</b> à la fin de l’étape de renforcement, défaussez des cartes du sommet du deck de rencontre jusqu’à ce qu’un ennemi soit défaussé. Révélez cet ennemi.</p><p class='main-text'><b>Forcé:</b> quand cette étape est passée, placez le Grand Hall à Hydromel dans la zone de cheminement.</p>{victory:10}");
            AddHtml("The-Uncertain-Dawn-TAP", "<p class='flavor-text'>Alors que le soleil se lève sur les rues d’Aldburg, vos ennemis acculés se révèlent. Vous avez rassemblé vos indices. Maintenant, faites-les porter pendant que vous vous battez pour votre vie — et pour l’âme du Rohan.</p><p class='main-text'> <b>Une fois révélée:</b> retirez de la partie toutes les quêtes annexes de rencontre et toutes les cartes <b><i>Suspect</i></b>. Placez chaque ennemi <b><i>Pays de Dun</i></b> et <b><i>Garnison</i></b> mis de côté dans la zone de cheminement, si possible.</p>");
            AddHtml2("The-Uncertain-Dawn-TAP", "<p class='main-text'> Une Aube Incertaine gagne –5 points de quête pour chaque objectif <b><i>Indice</i></b> que les joueurs contrôlent.</p><p class='main-text'><b>Forcé:</b> au début de la phase de quête, défaussez des cartes du sommet du deck de rencontre jusqu’à ce qu’un ennnemi soit défaussé. Revélez cet ennemi et placez-le dans la zone de cheminement.</p><p class='main-text'><b>Vous ne pouvez pas passer cette étape sauf si c’est la fin du tour. Si les joueurs passent cette étape, ils ont gagné la partie.</b></p>");
            AddHtml("A-Lesson-in-Caution-TAP", "<p class='main-text'>Secret 1.</p><p class='main-text'><b>Action:</b> piochez 1 carte. Ensuite, choisissez entre : réduire de 2 votre niveau de menace ou la prochaine carte que vous jouerez durant cette phase gagne Secret 1.</p><p class='flavor-text'>Les Hobbits ont le pas léger et leurs empreintes ne sont pas faciles à interpréter, même pour un Rôdeur… — Les Deux Tours</p>");
            AddHtml("Widfast-TAP", "<p class='main-text'><b>Mise en place:</b> cherchez un allié <b><i>Créature</i></b> dans votre collection et ajoutez-le à votre main. Durant le premier tour, son coût est réduit de 2.</p><p class='main-text'><b>[Voyage] Action:</b> s’il y a un lieu actif, dépensez 1 ressource de la réserve de Widfast pour voyager vers un autre lieu en résolvant son effet Trajet. (Il y a maintenant deux lieux actifs.)</p>");
        }

        private void FireOnTheEastemnet()
        {
            AddHtml("To-the-River-FotE", "<p class='flavor-text'>Les derniers orques cherchent à se rallier à leur chef, Morghak. C’est le moment de briser leur formation et de les presser avec la force des armes et de la volonté.</p><p class='main-text'> <b>Une fois révélée:</b> mélangez la pile de défausse de rencontre dans le deck de rencontre. Le premier joueur place les Rivages de l’Anduin et Morghak dans la zone de cheminement. Chaque autre joueur défausse des cartes du sommet du deck de rencontre jusqu’à ce qu’il défausse un ennemi et place cet ennemi dans la zone de cheminement.</p>");
            AddHtml2("To-the-River-FotE", "<p class='main-text'> Morgahk ne peut pas quitter la zone de cheminement tant qu’il n’y a pas au moins 15 marqueurs de progression sur cette étape.</p><p class='main-text'><b>Forcé:</b> à la fin du tour, renvoyez dans la zone de cheminement chaque ennemi engagé au combat. S’il y a plus de Coursiers Sauvages que de Coursiers Capturés, chaque ennemi dans la zone de cheminement capture ses coursiers.</p><p class='main-text'><b>Les joueurs ne peuvent pas passer cette étape tant que Morgahk est en jeu. Une fois que Morgahk est détruit, s’il y a plus de Coursiers Sauvages que de Coursiers Capturés, les joueurs ont gagné la partie. Sinon, les joueurs ont perdu la partie.</b></p>");
            AddHtml("Alagos-FotE", "<p class='main-text'>Ne peut pas avoir d’attachements Restreint.</p><p class='main-text'><b>Réponse:</b> quand un allié <b><i>Aigle</i></b> entre dans la zone de jeu d’un joueur, ajoutez 1 ressource à la réserve d’un héros que ce joueur contrôle. (Limite une fois par tour.)</p><p class='flavor-text'>“Qu’est-ce que tout ce tumulte dans la forêt, ce soir ?”<br/>— Le Seigneur des Aigles, Le Hobbit</p>");
            AddHtml("Box-of-Earth-FotE", "<p class='main-text'>Vous ne pouvez jouer cette carte que si vous contrôlez un héros <b><i>Noldor</i></b> ou <b><i>Sylvain</i></b>.</p><p class='main-text'>Attachez cette carte à un héros.</p><p class='main-text'><b>Action:</b> inclinez la Boîte de Terre pour placer 1 marqueur de progression sur un lieu. Celui-ci gagne le trait <b><i>Forêt</i></b> jusqu’à la fin du tour.</p><p class='flavor-text'>“Cela ne vous gardera pas sur votre route et ne vous défendra contre aucun danger ; mais si vous la conservez… peut-être y trouverez-vous votre récompense.”<br/>— Galadriel, La Communauté de l’Anneau</p>");
            AddHtml("Poachers-in-the-Night-FotE", "<p class='flavor-text'>Armé de torches et de fouets, l’ost des orques s’est regroupé afin de voler au Rohan ses précieux coursiers. </p><p class='main-text'> <b>Mise en place:</b> mettez Rivages de l’Anduin, Bataillon Orque, et Morgahk de côté, hors jeu. Cherchez un exemplaire des Écuries Pillées dans le deck de rencontre et faites-en le lieu actif. Placez Coursiers Sauvages, Coursiers Capturés, et un exemplaire de Pâturage de l’Estemnet dans la zone de cheminement. Placez 1 plus 3{PerPlayer} coursiers sur Coursiers Sauvages. Chaque joueur cherche un ennemi différent dans le deck de rencontre et le place dans la zone de cheminement. Mélangez le deck de rencontre.</p>");
            AddHtml2("Poachers-in-the-Night-FotE", "<p class='flavor-text'>À la sortie des écuries, vous attaquez de front les voleurs de chevaux ! Repoussez-les avant qu’ils ne s’enfuient avec leurs bêtes volées.</p><p class='main-text'> Coursiers 3{PerPlayer}.</p><p class='main-text'><b>Forcé:</b> à la fin du tour, placez 1{PerPlayer} coursier de cette étape sur Coursiers Capturés. S’il n’y a plus de coursier sur cette étape, passez à l’étape suivante.</p>");
            AddHtml("Hidden-Orc-Camp-FotE", "<p class='main-text'>Coursiers 1.</p><p class='main-text'>Le Camp Orque Caché gagne +1 {Threat} pour chaque coursier sur lui.</p><p class='main-text'><b>Forcé:</b> à la fin de la phase de combat, placez un coursier d’un lieu non-<b><i>Camp</i></b> sur le Camp Orque Caché.</p><p class='main-text'><b>Trajet:</b> chaque joueur incline un personnage qu’il contrôle.</p>{victory:1}");
            AddHtml("Panicked-Horses-FotE", "<p class='main-text'><b>Une fois révélée:</b> placez un coursier de Coursiers Sauvages sur chaque ennemi en jeu. S’il y a moins de 1{PerPlayer} ennemi en jeu, cherchez un ennemi dans le deck de rencontre ou dans la pile de défausse de rencontre et placez-le dans la zone de cheminement. Mélangez le deck de rencontre.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> l’ennemi attaquant gagne +1 {Attack} (+2 {Attack} à la place s’il y a plus de Coursiers Sauvages que de Coursiers Capturés).</p>");
            AddHtml("Golden-Crown-FotE", "<p class='main-text'>Attachez cette carte à un héros <b><i>Aigle</i></b>. Celui-ci gagne le trait <b><i>Noble</i></b>.</p><p class='main-text'><b>Action:</b> inclinez la Couronne d’Or pour choisir un allié <b><i>Aigle</i></b> dont vous êtes le propriétaire (il peut être sous le contrôle de n’importe quel joueur). Soit vous donnez le contrôle de cet allié à un autre joueur, soit vous le défaussez et ajoutez 1 ressource à la réserve du héros attaché.</p>");
            AddHtml("Captured-Steeds-FotE", "<p class='main-text'><b>Forcé:</b> quand un ennemi quitte le jeu sans avoir été détruit, placez les coursiers de cet ennemi sur Coursiers Capturés.</p><p class='main-text'><b>Forcé:</b> quand un lieu quitte le jeu sans avoir été exploré, placez les coursiers de ce lieu sur Coursiers Capturés.</p><p class='flavor-text'>Leurs chevaux étaient de grande stature, forts et bien découplés… — Les Deux Tours</p>");
            AddHtml("Wild-Steeds-FotE", "<p class='main-text'><b>Forcé:</b> quand un ennemi entre en jeu, placez un coursier de Coursiers Sauvages sur cet ennemi.</p><p class='main-text'><b>À la fin du tour, s’il n’y a aucun coursier sur Coursiers Sauvages, les joueurs ont perdu la partie.</b></p><p class='flavor-text'>“Je parie qu’il y a des tas de bonnes choses qui poussent à l’état sauvage dans ce pays.” — Sam, Les Deux Tours</p>");
            AddHtml("Fang-FotE", "<p class='main-text'>(Limite : 1 par deck). Ne peut pas avoir d’attachements. Le coût d’engagement au combat de chaque ennemi augmente de 1.</p><p class='main-text'><b>Réponse:</b> quand Croc entre en jeu, cherchez dans votre deck un allié <b><i>Créature</i></b> et ajoutez-le à votre main. Jusqu’à la fin de la phase, cet allié n’a pas besoin de ressource adéquate pour être joué. Mélangez votre deck.</p>");
            AddHtml("Pillaged-Stables-FotE", "<p class='main-text'>Coursiers 4.</p><p class='main-text'><b>Forcé:</b> à la fin du tour, s’il reste au moins un coursier sur les Écuries Pillées, placez un coursier des Écuries Pillées sur Coursiers Capturés. Le premier joueur peut augmenter de 2 son niveau de menace pour annuler cet effet.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> si cette attaque détruit un personnage, l’ennemi attaquant capture ses coursiers.</p>{victory:1}");
            AddHtml("Orcish-Wrangler-FotE", "<p class='main-text'>Renfort.</p><p class='main-text'><b>Forcé:</b> quand l’Éleveur Orque attaque, placez un coursier du lieu actif sur cet ennemi.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> s’il y a plus de Coursiers Sauvages que de Coursiers Capturés, l’ennemi attaquant capture ses coursiers.</p>");
            AddHtml("Grip-FotE", "<p class='main-text'>(Limite : 1 par deck). Ne peut pas avoir d’attachements. Le coût d’engagement au combat de chaque ennemi augmente de 1.</p><p class='main-text'><b>Réponse:</b> quand Étau entre en jeu, cherchez dans votre deck un allié <b><i>Créature</i></b> et ajoutez-le à votre main. Jusqu’à la fin de la phase, cet allié n’a pas besoin de ressource adéquate pour être joué. Mélangez votre deck.</p>");
            AddHtml("Faunith-FotE", "<p class='main-text'>Ne peut pas avoir d’attachements Restreint.</p><p class='main-text'><b>[Quête] Action</b> : jouez un allié <b><i>Aigle</i></b> de votre main. Ensuite, vous pouvez incliner cet allié pour soit l’engager dans la quête, soit infliger 1 point de dégâts à un ennemi dans la zone de cheminement.</p><p class='flavor-text'>Ils piquèrent droit sur les Nazgûl, plongeant soudain du haut des airs… — Le Retour du Roi</p>");
            AddHtml("Relentless-Forces-FotE", "<p class='flavor-text'>Rageant après la défaite du chef de guerre Thruk, l’ost des orques se masse dans la campagne, faisant des ravages. “Allez, allez !” crient-ils, avides de chair de cheval. </p><p class='main-text'> <b>Une fois révélée:</b> mélangez la pile de défausse de rencontre dans le deck de rencontre. Le premier joueur place le Bataillon Orque dans la zone de cheminement. Chaque autre joueur défausse des cartes du sommet du deck de rencontre jusqu’à ce qu’il défausse un ennemi et place cet ennemi dans la zone de cheminement.</p>");
            AddHtml2("Relentless-Forces-FotE", "<p class='flavor-text'>Les orques ne seront pas satisfaits tant qu’ils n’auront pas arraché à ces champs chaque étalon et chaque jument. Prenez-les de flanc et dispersez-les à l’est !</p><p class='main-text'> Coursiers 3{PerPlayer}.</p><p class='main-text'><b>Forcé:</b> à la fin du tour, s’il y a au moins un ennemi en jeu, placez 1{PerPlayer} coursiers de cette étape sur Coursiers Capturés.</p><p class='main-text'><b>Les joueurs ne peuvent pas passer cette étape tant que le Bataillon Orque est en jeu.</b></p>");
            AddHtml("Cracking-Horse-Whip-FotE", "<p class='main-text'><b>Une fois révélée:</b> pour chaque personnage redressé qu’un joueur contrôle, il doit choisir entre : soit incliner ce personnage, soit lui infliger 1 point de dégâts. S’il y a plus de Coursiers Sauvages que de Coursiers Capturés, Fouet Claquant gagne Renfort.</p><p class='flavor-text'>“Où il y a un fouet, il y a du cœur, mes loches.”<br/>— Le Retour du Roi</p>");
            AddHtml("Orc-Lookout-FotE", "<p class='main-text'><b>Forcé:</b> à la fin de la phase de combat, le Guetteur Orque capture ses coursiers.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> soit l’ennemi attaquant ne peut pas subir de dégâts ce tour, soit il capture ses coursiers.</p>");
            AddHtml("Guided-by-Fate-FotE", "<p class='main-text'>Attachez cette carte à un héros.</p><p class='main-text'><b>Réponse:</b> quand vous devriez mélanger dans le deck de rencontre une carte mise de côté, mélangez-la dans les 5 premières cartes à la place.</p><p class='main-text'><b>Action:</b> si le deck de rencontre contient 5 cartes ou plus, inclinez Guidé par le Destin et augmentez de 1 votre niveau de menace pour mélanger la première carte de la pile de défausse de rencontre dans les 5 premières cartes du deck de rencontre.</p>");
            AddHtml("Roving-Herbmaster-FotE", "<p class='main-text'><b>Réponse:</b> quand un joueur diminue son niveau de menace, soignez 1 point de dégâts sur un personnage que vous contrôlez. Ensuite, vous pouvez donner le contrôle de l’Herboriste Itinérante à un autre joueur. (Limite une fois par tour.)</p><p class='flavor-text'>“Broyez-en une dans l’eau et lavez bien la blessure ; je la panserai. À votre tour.”<br/>— Aragorn, La Communauté de l’Anneau</p>");
            AddHtml("Wolf-FotE", "<p class='main-text'>(Limite : 1 par deck). Ne peut pas avoir d’attachements. Le coût d’engagement au combat de chaque ennemi augmente de 1.</p><p class='main-text'><b>Réponse:</b> quand Loup entre en jeu, cherchez dans votre deck un allié <b><i>Créature</i></b> et ajoutez-le à votre main. Jusqu’à la fin de la phase, cet allié n’a pas besoin de ressource adéquate pour être joué. Mélangez votre deck.</p><p class='flavor-text'>Le plus gros et le plus féroce des trois… — La Communauté de l’Anneau</p>");
            AddHtml("White-Mountain-Beastmaster-FotE", "<p class='main-text'><b>Forcé:</b> quand le Maître des Bêtes des Montagnes Blanches vous engage au combat, choisissez entre : il effectue immédiatement une attaque contre vous ou il capture ses coursiers.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> soit l’ennemi attaquant effectue une attaque supplémentaire après celle-ci, soit il capture ses coursiers.</p>");
            AddHtml("Messenger-of-Manwe-FotE", "<p class='main-text'>Ne peut pas défendre ou avoir d’attachements Restreint.</p><p class='main-text'><b>Réponse:</b> quand le Messager de Manwë entre dans votre zone de jeu, donnez +1 {Willpower} à un héros <b><i>Aigle</i></b> ou <b><i>Noble</i></b> que vous contrôlez jusqu’à la fin du tour.</p><p class='main-text'><b>Réponse:</b> quand le Messager de Manwë s’engage dans une quête, donnez le contrôle de cette carte à un autre joueur.</p>");
            AddHtml("Morgahk-FotE", "<p class='main-text'>Immunisé aux effets de carte des joueurs.</p><p class='main-text'><b>Forcé:</b> au début de la phase de quête, Morgahk effectue immédiatement une attaque contre le premier joueur.</p><p class='main-text'><b>Forcé:</b> quand Morgahk attaque et détruit un personnage, chaque ennemi dans la zone de cheminement capture ses coursiers.</p>{victory:5}");
            AddHtml("Eastemnet-Pasture-FotE", "<p class='main-text'>Coursiers 1{PerPlayer}.</p><p class='main-text'>Vous ne pouvez pas placer de marqueurs de progression sur les lieux de la zone de cheminement.</p><p class='flavor-text'>Les herbages onduleux s’élevaient et descendaient comme une vaste mer grise. — Les Deux Tours</p>{victory:1}");
            AddHtml("Hidden-Roosts-FotE", "<p class='main-text'><b>Réponse:</b> quand un allié <b><i>Aigle</i></b> quitte le jeu, le joueur qui le contrôle diminue de X son niveau de menace, où X est le coût imprimé de cet allié. Si X est inférieur ou égal à 2, ce joueur pioche également une carte.</p><p class='flavor-text'>“Que le vent sous vos ailes vous porte où le soleil fait route et où la lune chemine !” — Gandalf, Le Hobbit</p>");
            AddHtml("Exhausted-Pursuit-FotE", "<p class='main-text'><b>Une fois révélée:</b> soit tous les ennemis en jeu capturent leurs coursiers, soit vous inclinez chaque personnage blessé.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> inclinez un personnage blessé que vous contrôlez.</p><p class='flavor-text'>Tout étourdi qu’il était par la souffrance et la fatigue, Sam se réveilla… — Le Retour du Roi</p>");
            AddHtml("Open-Meadows-FotE", "<p class='main-text'>Coursiers 2.</p><p class='main-text'>Tant que les Prairies Dégagées sont dans la zone de cheminement, elles gagnent: “<b>Forcé:</b> à la fin de la phase de combat, placez le premier ennemi de la pile de défausse de rencontre dans la zone de cheminement.”</p>{victory:1}");
            AddHtml("Expanded-Search-FotE", "<p class='flavor-text'>Les orques semblent avoir détecté une odeur et commencent à fouiller le sol près d’un endroit où vous vous êtes autrefois arrêtés pour vous reposer.</p><p class='main-text'><b>Forcé:</b> quand vous engagez un ennemi au combat, soit vous placez un coursier de Coursiers Sauvages sur Coursiers Capturés, soit cet ennemi gagne +3 {Attack} jusqu’à la fin du tour.</p>{victory:10}");
            AddHtml("Shores-of-Anduin-FotE", "<p class='main-text'>Immunisé aux effets de carte des joueurs.</p><p class='main-text'>Tant que les Rivages de l’Anduin sont dans la pile de victoire, la quête en cours gagne : “<b>Réponse:</b> quand un ennemi est détruit, placez X coursiers de Coursiers Capturés sur Coursiers Sauvages, où X est la {Threat} imprimée de l’ennemi détruit.”</p><p class='main-text'><b>Trajet:</b> chaque ennemi dans la zone de cheminement capture ses coursiers.</p>{victory:5}");
            AddHtml("Breath-of-Arda-FotE", "<p class='main-text'><b>[Rencontre] Action</b> : ramenez dans votre main X alliés <b><i>Aigle</i></b> dont vous êtes le propriétaire (ils peuvent être sous le contrôle de n’importe quel joueur). Ensuite, mettez en jeu chacun de ces alliés sous le contrôle de n’importe quel joueur, un à la fois. Retirez Souffle d’Arda de la partie.</p><p class='flavor-text'>“Car nous avons besoin d’une vitesse plus grande que celle de tous les vents.”<br/>— Gandalf, Le Retour du Roi</p>");
            AddHtml("Chieftain-of-the-Skies-FotE", "<p class='main-text'>Dévoué.</p><p class='main-text'>Ne peut pas avoir d’attachements Restreint.</p><p class='main-text'><b>Réponse:</b> quand le Souverain du Ciel entre dans votre zone de jeu, redressez un allié que vous contrôlez.</p><p class='flavor-text'>Derrière eux, en longues files rapides, s’avançaient tous leurs vassaux des montagnes du nord. — Le Retour du Roi</p>");
            AddHtml("Entwash-Crossings-FotE", "<p class='main-text'>Coursiers 3.</p><p class='main-text'><b>Trajet:</b> cherchez un ennemi dans la pile de défausse de rencontre et mettez-le en jeu, engagé au combat avec le premier joueur.</p><p class='flavor-text'>Les bouviers y avaient beaucoup vagabondé, vivant en campement et sous la tente, même en hiver. Mais à présent toute la région était vide. — Les Deux Tours</p>{victory:1}");
            AddHtml("Veteran-Eagle-FotE", "<p class='main-text'>Ne peut pas avoir d’attachements Restreint.</p><p class='main-text'><b>Réponse:</b> quand le Vétéran Aigle entre dans votre zone de jeu, chaque exemplaire de Vétéran Aigle que vous contrôlez gagne +1 {Willpower}, +1 {Attack} et +1 {Defense} jusqu’à la fin du tour.</p><p class='flavor-text'>Mais les aigles ont des yeux perçants, et ils sont capables de voir de petites choses à très grande distance. — Le Hobbit</p>");
        }

        private void TheGapOfRohan()
        {
            AddHtml("Dunlending-Rearguard-TGoR", "<p class='main-text'><b>Forcé:</b> à la fin de la phase de rencontre, si l’Arrière-Garde Dunlending est dans la zone de cheminement, retirez 1 compteur de temps de Gálmód.</p><p class='flavor-text'>“Mort aux Têtes de Paille ! Mort aux voleurs du Nord !” — Les Deux Tours</p>");
            AddHtml("Bob-TGoR", "<p class='main-text'><b>Réponse:</b> quand Bob entre en jeu, cherchez dans votre deck 1 carte <b><i>Monture</i></b> ou <b><i>Poney</i></b> et ajoutez-la à votre main. Mélangez votre deck.</p><p class='flavor-text'>“Une pauvre vieille créature famélique, mais il ne veut pas s’en séparer à moins du triple de ce qu’elle vaut.”<br/>— La Communauté de l’Anneau</p>");
            AddHtml("Slippery-Hill-TGoR", "<p class='main-text'>X est le nombre d’alliés contrôlés par le joueur ayant le plus d’alliés, plus 1.</p><p class='flavor-text'>“Commencez à me suivre, vous verrez le chemin que je prendrai.”<br/>— Gandalf, Le Hobbit</p>");
            AddHtml("Scheming-Staddler-TGoR", "<p class='main-text'><b>Réponse:</b> quand une attaque sans défense inflige des dégâts à un héros que vous contrôlez, infligez 1 point de dégâts à l’ennemi attaquant. (Limite: une fois par phase.)</p><p class='flavor-text'>“Il vendrait n’importe quoi à n’importe qui, ou il ferait du mal par simple plaisir.”<br/>— Aragorn, La Communauté de l’Anneau</p>");
            AddHtml("Snowbank-TGoR", "<p class='main-text'><b>Forcé:</b> quand des marqueurs de progression devraient être placés sur une carte Quête ou sur le lieu actif, placez-les d’abord sur Congère.</p><p class='main-text'>[Quête] <b>Action</b>: inclinez un héros que vous contrôlez pour placer X marqueurs de progression sur ce lieu, X étant la valeur d’{Attack} imprimée du héros incliné.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> placez la Congère dans la zone de cheminement.</p>");
            AddHtml("Exposed-Ridge-TGoR", "<p class='main-text'>Tant que la Crête Exposée est dans la zone de cheminement, aucun marqueur de progression ne peut être placé sur ce lieu.</p><p class='main-text'><b>Trajet:</b> infligez 1 point de dégâts à chaque personnage incliné contrôlé par le premier joueur.</p><p class='flavor-text'>“Que j’ai horreur de regarder de haut dans le vide ! Mais regarder vaut mieux que descendre.”<br/>— Sam Gamegie, Les Deux Tours</p>");
            AddHtml("Seasoned-Forager-TGoR", "<p class='main-text'>Attachez cette carte à un héros que vous contrôlez. (Limite: 1 par joueur.)</p><p class='main-text'>Vos héros ne peuvent pas gagner de ressources grâce aux effets de carte des joueurs.</p><p class='main-text'><b>Action</b>: inclinez Cueilleur Averti pour réduire de 1 le coût de la prochaine carte que vous jouerez à cette phase appartenant à la sphère d’influence du héros attaché.</p>");
            AddHtml("Half-pint-TGoR", "<p class='main-text'>Attachez cette carte à un personnage.</p><p class='main-text'>[Restauration] <b>Action</b>: défaussez la Demi-Pinte pour soigner jusqu’à 2 points de dégâts sur le personnage attaché. Ensuite, mettez un attachement <b><i>Objet</i></b> de votre pile de défausse sur le sommet de votre deck.</p><p class='flavor-text'>“J’ai une goutte de bonne bière en perce, si vous et vos amis êtes d’accord.”<br/>— Père Maggotte, La Communauté de l’Anneau</p>");
            AddHtml("Gift-of-Foresight-TGoR", "<p class='main-text'>Attachez cette carte à un héros ayant une {Willpower} imprimée de 2 ou plus. (Limite: 1 par héros.)</p><p class='main-text'>Le héros attaché gagne +2 {Defense} s’il ne s’est pas engagé dans la quête durant ce tour.</p><p class='flavor-text'>“N’imaginez pas que c’est seulement par des chants dans les arbres, ni même par les minces flèches des arcs elfiques que ce pays de Lothlorien est maintenu et défendu.”<br/>— Galadriel, La Communauté de l’Anneau</p>");
            AddHtml("Frantic-Dunlending-TGoR", "<p class='main-text'>Renfort.</p><p class='main-text'><b>Forcé:</b> à la fin du tour, renvoyez le Dunlending Acharné dans la zone de cheminement.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> l’ennemi attaquant gagne +X {Attack} pour cette attaque, X étant la valeur de Gelure de la quête principale.</p>");
            AddHtml("Galmod's-Escort-TGoR", "<p class='main-text'>Immunisé aux effets de carte des joueurs. L’Escorte de Gálmód ne peut pas quitter la zone de cheminement (sauf en étant détruite) mais est considérée comme étant engagée au combat avec chaque joueur.</p><p class='main-text'>Les dégâts qui devraient être infligés à l’Escorte de Gálmód doivent d’abord être placés comme marqueurs de progression sur les cartes Congère dans la zone de cheminement, si possible.</p>{victory:5}");
            AddHtml("Unnatural-Cold-TGoR", "<p class='main-text'>Tant que cette carte est attachée à une quête, elle compte comme un attachement <b><i><b><i>Condition</i></b></i></b> avec le texte: “<b>Forcé</b>: quand un allié ou un ennemi entre en jeu, infligez-lui 1 point de dégâts. Si c’est un allié, son contrôleur peut l’incliner pour annuler cet effet.”</p><p class='main-text'><b>Une fois révélée:</b> attachez cette carte à la quête principale si elle n’a pas déjà un exemplaire de Froid Contre Nature attaché.</p>");
            AddHtml("Galmod-TGoR", "<p class='main-text'>Temps 7.</p><p class='main-text'><b>Si le dernier compteur de temps est retiré de Gálmód, les joueurs ont perdu la partie.</b></p><p class='flavor-text'>Le parjure a fui vers l’ouest en plein milieu d’une violente tempête.</p>");
            AddHtml("Harry-Goatleaf-TGoR", "<p class='main-text'><b>Réponse:</b> annulez 1 point de dégâts de combat tout juste infligé à un héros que vous contrôlez. (Limite: une fois par tour par héros.)</p><p class='flavor-text'>“Vos affaires ne regardent que vous, c’est entendu, mais la mienne est de poser des questions après la tombée de la nuit.” — La Communauté de l’Anneau</p>");
            AddHtml("Frozen-Lake-TGoR", "<p class='main-text'>Tant que le Lac Gelé est le lieu actif, chaque exemplaire de Congère dans la zone de cheminement perd le texte imprimé dans sa boîte de texte.</p><p class='main-text'><b>Forcé:</b> quand le Lac Gelé quitte le jeu en tant que lieu exploré, défaussez la première carte du deck de rencontre. Si cette carte est une traîtrise, chaque joueur défausse un allié qu’il contrôle.</p>");
            AddHtml("White-Wall-TGoR", "<p class='flavor-text'>Le violent blizzard semble être sans fin. Les bourrasques sur les sommets et les vents cruels apportent avec eux d’implacables murs de neige.</p><p class='main-text'>Renfort.</p><p class='main-text'>Mur Blanc gagne +4{PerPlayer} points de quête.</p><p class='main-text'><b>Forcé:</b> au début de la phase de quête, cherchez 1 exemplaire de Congère dans la pile de défausse de rencontre et placez-le dans la zone de cheminement.</p>{victory:1}");
            AddHtml("Nob-TGoR", "<p class='main-text'>Réduisez de 1 le coût du premier attachement <b><i>Objet</i></b> que vous jouez sur un autre personnage <b><i>Bree</i></b> ou <b><i>Hobbit</i></b> à chaque tour.</p><p class='main-text'><b>Action</b>: inclinez Nob pour piocher 1 carte. Si cette carte est un attachement <b><i>Objet</i></b>, redressez un personnage <b><i>Bree</i></b>. (Limite: une fois par tour.)</p><p class='flavor-text'>“Où es-tu, clampin aux pieds laineux ?”<br/>— Prosper Poiredebeurré, La Communauté de l’Anneau</p>");
            AddHtml("Giving-Chase-TGoR", "<p class='flavor-text'>Vous avez poursuivi le traître Gálmód jusqu’à la Trouée du Rohan. Cependant, une neige inhabituelle pour la saison rend le passage difficile. </p><p class='main-text'> <b>Mise en place:</b> mettez de côté l’Escorte de Gálmód, hors jeu. Placez Gálmód et 1{PerPlayer} exemplaire de Congère dans la zone de cheminement.</p>");
            AddHtml2("Giving-Chase-TGoR", "<p class='flavor-text'>Alors que le blizzard arrive, vous devez à tout prix capturer le félon Gálmód.</p><p class='main-text'> Les personnages ne peuvent pas être redressés par des effets de carte des joueurs.</p>");
            AddHtml("Pint-TGoR", "<p class='main-text'>Vous ne pouvez jouer cette carte que si vous contrôlez un personnage unique ayant le trait <b><i>Bree</i></b>.</p><p class='main-text'>Attachez cette carte à un héros.</p><p class='main-text'><b>Action</b>: inclinez la Pinte pour soigner 1 point de dégâts sur le héros attaché.</p><p class='main-text'><b>Action</b>: défaussez la Pinte pour soigner 5 points de dégâts sur le héros attaché.</p><p class='flavor-text'>“Alors, mes joyeux amis, voulez-vous boire avec moi avant de partir ?” — Sylvebarbe, Le Retour du Roi</p>");
            AddHtml("Bree-Pony-TGoR", "<p class='main-text'>Attachez cette carte à un personnage <b><i>Bree</i></b> ou <b><i>Hobbit</i></b>. Restreint.</p><p class='main-text'>Le personnage attaché gagne +1 Point de Vie.</p><p class='main-text'><b>Réponse:</b> quand le personnage attaché subit des dégâts d’une attaque sans défense, inclinez le Poney de Bree pour réduire de 1 ces dégâts.</p><p class='flavor-text'>On ne pouvait obtenir à quelque condition que ce fût dans tout le voisinage ni cheval ni poney, hormis un seul.<br/>— La Communauté de l’Anneau</p>");
            AddHtml("Frozen-Solid-TGoR", "<p class='main-text'><b>Une fois révélée:</b> chaque joueur incline chaque attachement qu’il contrôle. Les attachements ne peuvent pas être redressés jusqu’à la fin du tour. Si aucun attachement n’est incliné par cet effet, Pris par le Gel gagne Renfort.</p><p class='flavor-text'>“Soit nous arrêter où nous sommes, soit retourner en arrière. Il ne sert à rien de continuer.”<br/>— Gandalf, La Communauté de l’Anneau</p>");
            AddHtml("Barliman-Butterbur-TGoR", "<p class='main-text'><b>Réponse:</b> quand Prosper Poiredebeurré subit des dégâts d’une attaque sans défense, piochez 1 carte.</p><p class='flavor-text'>“Je suis débordé; mais je vais voir ce que je peux faire pour vous.” — La Communauté de l’Anneau</p>");
            AddHtml("Bree-land-Protector-TGoR", "<p class='main-text'>Dévoué.</p><p class='main-text'><b>Réponse:</b> inclinez la Protectrice du Pays de Bree pour annuler un effet ombre qui vient d’être déclenché par l’attaque ennemie et attribuez à l’ennemi attaquant une autre carte ombre. Si cette attaque n’inflige pas de dégâts, ramenez la Protectrice du Pays de Bree dans la main de son propiétaire après l’attaque.</p>");
            AddHtml("Snow-Squall-TGoR", "<p class='main-text'><b>Une fois révélée:</b> cherchez 2 exemplaires de Congère dans la pile de défausse de rencontre et placez-les dans la zone de cheminement. Si moins de 2 exemplaires de Congère sont placés dans la zone de cheminement par cet effet, Rafale de Neige gagne Renfort.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> cherchez 1 exemplaire de Congère dans la pile de défausse de rencontre et placez-le dans la zone de cheminement.</p>");
            AddHtml("Recapture-the-Traitor-TGoR", "<p class='flavor-text'>Alors que le cheval du félon s’écroule juste devant le col, un des alliés de Gálmód l’arrache à sa selle. </p><p class='main-text'> <b>Une fois révélée:</b> ajoutez 1 compteur de temps sur Gálmód. Le premier joueur place l’Escorte de Gálmód dans la zone de cheminement et lui attache Gálmód en tant qu’objectif protégé. Chaque autre joueur cherche un exemplaire de Congère dans le deck de rencontre, la pile de défausse de rencontre ou la pile de victoire, et le place dans la zone de cheminement. Mélangez le deck de rencontre.</p>");
            AddHtml2("Recapture-the-Traitor-TGoR", "<p class='flavor-text'>Gálmód et sa garde se sont presque échappés dans la nature! Bientôt il sera complètement hors d’atteinte.</p><p class='main-text'> L’Escorte de Gálmód ne peut pas subir de dégâts tant qu’il n’y a pas au moins 6 marqueurs de progression sur cette étape.</p><p class='main-text'><b>Les joueurs ne peuvent pas passer cette étape tant que Gálmód est protégé par un ennemi. Quand Gálmód n’est plus attaché à une carte de rencontre, il est capturé et les joueurs ont gagné la partie.</b></p>");
            AddHtml("Stumble-in-the-Snow-TGoR", "<p class='main-text'><b>Une fois révélée:</b> chaque joueur incline un personnage qu’il contrôle et lui inflige X points de dégâts, X étant la valeur de Gelure de la quête principale.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> le personnage défenseur ne peut pas être redressé jusqu’à la fin du tour.</p>");
            AddHtml("A-Stout-Heart-TGoR", "<p class='main-text'><b>Réponse:</b> annulez un effet ombre tout juste déclenché durant une attaque sans défense. Après que cette attaque a infligé des dégâts à un seul héros, le contrôleur de ce héros peut dépenser 1 ressource de sa réserve pour réduire de 3 sa menace.</p><p class='flavor-text'>“Nous arrivons au bord du gouffre, où espoir et désespoir ne font qu’un.”<br/>— Aragorn, Le Retour du Roi</p>");
            AddHtml("A-Dangerous-Pursuit-TGoR", "<p class='flavor-text'>Plus loin, Gálmód est rejoint par une petite bande de rebelles. Alors qu’il continue sa chevauchée, ceux-ci ressèrent les rangs après son passage pour ralentir votre poursuite. </p><p class='main-text'> <b>Une fois révélée:</b> ajoutez 1 compteur de temps sur Gálmód. Mélangez la pile de défausse de rencontre dans le deck de rencontre. Défaussez des cartes du sommet du deck de rencontre jusqu’à ce que 1{PerPlayer} ennemi soit défaussé. Placez chaque ennemi défaussé dans la zone de cheminement.</p>");
            AddHtml2("A-Dangerous-Pursuit-TGoR", "<p class='flavor-text'>De violentes bourrasques hurlent dans le col de la montagne, et même vos assaillants semblent déroutés par la férocité de la tempête. Le brouillard blanc de neige balayée par le vent vous aveugle alors que vous faites face à vos ennemis.</p><p class='main-text'> <b>Forcé:</b> au début de la phase de quête, infligez 1 point de dégâts à chaque personnage incliné.</p>");
            AddHtml("Archet-Alekeeper-TGoR", "<p class='main-text'>Dévoué.</p><p class='main-text'><b>Réponse:</b> quand une attaque sans défense inflige des dégâts à un héros que vous contrôlez, redressez le Veilleur de Bière d’Archet et donnez-lui +1 {Attack} jusqu’à la fin de la phase.</p><p class='flavor-text'>“Cela me donnait l’occasion de me balader dans le pays, de voir des gens, d’entendre les nouvelles et de savoir où on trouvait la bonne bière.” — Robin Petitterrier, Le Retour du Roi</p>");
            AddHtml("Biting-Winds-TGoR", "<p class='flavor-text'>Alors que Gálmód galope au travers des bourrasques, le mauvais temps empire. Il pousse son étalon au-delà de ses limites, déterminé à échapper à la sanglante justice réservée aux traîtres. </p><p class='main-text'> <b>Une fois révélée:</b> ajoutez 1 compteur de temps sur Gálmód. Le premier joueur cherche un exemplaire de Froid Contre Nature dans le deck de rencontre, la pile de défausse de rencontre ou la pile de victoire, et l’attache à la quête principale. Mélangez le deck de rencontre. Chaque autre joueur révèle une carte de rencontre.</p>");
            AddHtml2("Biting-Winds-TGoR", "<p class='flavor-text'>Rapidement, même votre groupe commence à ralentir et chanceler. Si le traître s’échappe par le col, il sera hors de portée du Rohan.</p><p class='main-text'> Chaque personnage blessé perd le texte imprimé dans sa boîte de texte (à l’exception des <b><i>Traits</i></b>).</p><p class='main-text'>Froid Contre Nature est immunisé aux effets de carte des joueurs et gagne Renfort.</p>");
        }

        private void TheGlitteringCaves()
        {
            AddHtml("Balin-TGC", "<p class='main-text'>Dévoué.</p><p class='main-text'><b>Réponse:</b> quand une carte est défaussée du sommet de votre deck, redressez Balin. (Limite: une fois par tour.)</p><p class='flavor-text'>Les autres ne firent aucun semblant d’offre, sauf le vieux Balin. — Le Hobbit</p>");
            AddHtml("Radiant-Caverns-Jewelled-Cavern-TGC", "<p class='main-text'>Immunisé aux effets de carte des joueurs.</p><p class='main-text'><b>Forcé:</b> quand les Cavernes Rayonnantes sont explorées, attribuez-les en tant que carte ombre, face cachée, à Helm Main-Marteau.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> l’ennemi attaquant gagne +1 {Attack} pour chaque carte ombre qui lui est attribuée. Si cette attaque détruit un personnage, augmentez de 2 votre niveau de menace.</p>{cave:Grotte des Gemmes}");
            AddHtml("Radiant-Caverns-Vast-Cave-TGC", "<p class='main-text'>Immunisé aux effets de carte des joueurs.</p><p class='main-text'><b>Forcé:</b> quand les Cavernes Rayonnantes sont explorées, attribuez-les en tant que carte ombre, face cachée, à Helm Main-Marteau.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> l’ennemi attaquant gagne +1 {Attack} pour chaque carte ombre qui lui est attribuée. Si cette attaque détruit un personnage, augmentez de 2 votre niveau de menace.</p>{cave:Grande Salle}");
            AddHtml("Radiant-Caverns-Crossroads-TGC", "<p class='main-text'>Immunisé aux effets de carte des joueurs.</p><p class='main-text'><b>Forcé:</b> quand les Cavernes Rayonnantes sont explorées, attribuez-les en tant que carte ombre, face cachée, à Helm Main-Marteau.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> l’ennemi attaquant gagne +1 {Attack} pour chaque carte ombre qui lui est attribuée. Si cette attaque détruit un personnage, augmentez de 2 votre niveau de menace.</p>{cave:Croisement}");
            AddHtml("Alluring-Paths-TGC", "<p class='main-text'>Quand les joueurs ont la possibilité de voyager, ils doivent voyager vers les Chemins Attirants s’il n’y a pas de lieu actif.</p><p class='main-text'><b>Forcé:</b> quand les Chemins Attirants quittent le jeu, si Helm Main-Marteau est dans une grotte occupée par un joueur, déplacez-le dans la grotte occupée par le joueur suivant.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> si vous ne vous êtes pas déplacé à ce tour, l’ennemi attaquant effectue une attaque supplémentaire contre vous après celle-ci.</p>");
            AddHtml("Twisting-Paths-TGC", "<p class='main-text'>Renfort.</p><p class='main-text'><b>Une fois révélée:</b> attachez cette carte à une grotte en jeu. Compte comme un attachement avec le texte: “<b>[Voyage] Action</b>: un joueur dans la grotte attachée peut défausser les Chemins Tortueux pour échanger la position de la grotte attachée (ainsi que ses marqueurs et attachements) avec une grotte <b>adjacente</b>.”</p>{shadow}<p class='shadow-text'><b>Ombre:</b> résolvez l’effet “une fois révélée” de cette carte.</p>{cave:Porte Verrouillée}");
            AddHtml("Helm's-Horn-TGC", "<p class='main-text'>Les marqueurs de joueur ne peuvent pas être déplacés.</p><p class='main-text'><b>Une fois révélée:</b> mélangez la pile de défausse de rencontre dans le deck de rencontre. Si Helm Main-Marteau ne se trouve pas dans la grotte sans marqueur de dégâts, déplacez-le dans celle-ci. Ensuite, retournez la carte Helm Main-Marteau.</p><p class='flavor-text'>Une nuit, les hommes entendirent sonner le cor, mais Helm point ne revint. — Le Retour du Roi</p>");
            AddHtml("Crossroads-TGC", "<p class='main-text'><b>Forcé:</b> quand vous vous déplacez dans cette grotte, augmentez de 3 votre niveau de menace.</p><p class='main-text'><b>[Voyage] Action</b>: augmentez de 3 votre niveau de menace pour fouiller cette grotte.</p>");
            AddHtml("Dead-End-TGC", "<p class='main-text'>Tant que vous êtes dans cette grotte, vous ne pouvez pas piocher de cartes et vos héros ne peuvent pas gagner de ressources.</p><p class='main-text'><b>[Voyage] Action</b>: retirez de la partie tous les évènements de votre main et de votre pile de défausse pour fouiller cette grotte.</p>");
            AddHtml("Echoes-in-the-Deep-Dark-Pit-TGC", "<p class='main-text'><b>Une fois révélée:</b> déplacez Helm Main-Marteau dans une grotte <b>adjacente</b> vers le joueur le plus proche, si possible. Ensuite, Helm Main-Marteau effectue immédiatement une attaque contre le joueur dans cette grotte ayant le niveau de menace le plus élevé. Si aucune attaque n’est effectuée par cet effet, Échos dans le Gouffre gagne Renfort.</p>{cave:Puits Sombre}");
            AddHtml("Echoes-in-the-Deep-Secret-Passage-TGC", "<p class='main-text'><b>Une fois révélée:</b> déplacez Helm Main-Marteau dans une grotte <b>adjacente</b> vers le joueur le plus proche, si possible. Ensuite, Helm Main-Marteau effectue immédiatement une attaque contre le joueur dans cette grotte ayant le niveau de menace le plus élevé. Si aucune attaque n’est effectuée par cet effet, Échos dans le Gouffre gagne Renfort.</p>{cave:Passage Secret}");
            AddHtml("Stairwell-TGC", "<p class='main-text'><b>Forcé:</b> quand vous vous déplacez dans cette grotte, défaussez des attachements que vous contrôlez jusqu’à ce qu’aucun des personnages que vous contrôlez n’ait plus de 2 attachements.</p><p class='main-text'><b>[Voyage] Action</b>: défaussez 1 attachement de chaque personnage que vous contrôlez ayant un attachement, si possible. Ensuite, fouillez cette grotte.</p>");
            AddHtml("Wandering-Spirit-TGC", "<p class='main-text'>Ne peut pas entrer en jeu.</p><p class='main-text'><b>Action</b>: si l’Esprit Errant est dans votre pile de défausse, déplacez-le d’1 ou 2 cartes vers le haut ou vers le bas. Ensuite, s’il est à côté d’un allié <b><i>Noldor</i></b> ayant un coût imprimé de 2 ou moins, vous pouvez dépenser 1 ressource {sphere:Spirit} et retirer l’Esprit Errant de la partie pour mettre en jeu cet allié. (Limite : une fois par tour.)</p>");
            AddHtml("Killing-With-Fear-Guarded-Hall-TGC", "<p class='main-text'><b>Une fois révélée:</b> infligez 1 point de dégâts à chaque personnage incliné contrôlé par les joueurs qui sont dans la même grotte que Helm Main-Marteau ou dans une grotte <b>adjacente</b>, ainsi qu’à chaque ennemi <b><i>Pays de Dun</i></b> engagé au combat avec ces joueurs. Déplacez Helm Main-Marteau dans une grotte <b>adjacente</b> vers le joueur le plus proche.</p>{cave:Salle Gardée}");
            AddHtml("Killing-With-Fear-Stairwell-TGC", "<p class='main-text'><b>Une fois révélée:</b> infligez 1 point de dégâts à chaque personnage incliné contrôlé par les joueurs qui sont dans la même grotte que Helm Main-Marteau ou dans une grotte <b>adjacente</b>, ainsi qu’à chaque ennemi <b><i>Pays de Dun</i></b> engagé au combat avec ces joueurs. Déplacez Helm Main-Marteau dans une grotte <b>adjacente</b> vers le joueur le plus proche.</p>{cave:Escalier}");
            AddHtml("Killing-With-Fear-Cave-Opening-TGC", "<p class='main-text'><b>Une fois révélée:</b> infligez 1 point de dégâts à chaque personnage incliné contrôlé par les joueurs qui sont dans la même grotte que Helm Main-Marteau ou dans une grotte <b>adjacente</b>, ainsi qu’à chaque ennemi <b><i>Pays de Dun</i></b> engagé au combat avec ces joueurs. Déplacez Helm Main-Marteau dans une grotte <b>adjacente</b> vers le joueur le plus proche.</p>{cave:Ouverture}");
            AddHtml("Search-the-Caves-TGC", "<p class='flavor-text'>Fuyant le violent blizzard, vous êtes poursuivis par le fantôme de Helm Main-Marteau. Il vous talonne inexorablement, cherchant vos prisonniers dunlendings pour assouvir sa soif de sang. Vous devez parcourir les cavernes sous le Gouffre de Helm afin de retrouver le formidable cor de guerre de Helm. Souffler dedans pourrait être votre seule chance de guérir l’esprit du spectre.</p><p class='main-text'> <b>Mise en place:</b> mettez le Cor de Helm de côté, hors jeu. Placez Helm Main-Marteau dans la zone de cheminement, face <b><i>Traqueur</i></b> visible. Mettez en place la Carte des Grottes et les Indices (cf. feuillet).</p>");
            AddHtml2("Search-the-Caves-TGC", "<p class='main-text'> Les marqueurs de progression qui devraient être placés sur cette étape doivent à la place être répartis parmi les grottes ayant des marqueurs de joueur.</p><p class='main-text'><b>[Voyage] Action</b>: n’importe quel joueur peut retirer 4 marqueurs de progression de la grotte qu’il occupe pour se déplacer dans une grotte <b>connectée</b>. Il n’y a pas de limite au nombre de fois qu’un joueur peut déclencher cet effet.</p><p class='main-text'><b>Quand il reste exactement 1 seule grotte sans marqueur de dégâts, passez immédiatement à l’étape 2A</b>.</p>");
            AddHtml("Gaeronel-TGC", "<p class='main-text'>Tant qu’il y a une carte au sommet de votre pile de défausse :</p><p class='main-text'>Gaeronel gagne +2 {Willpower} si c’est un évènement;</p><p class='main-text'>Gaeronel gagne +2 {Attack} si c’est un allié;</p><p class='main-text'>Gaeronel gagne +2 {Defense} si c’est un attachement.</p><p class='flavor-text'>“Je resterai auprès des plages grises pour garder les Ports jusqu’au dernier navire.” — Círdan, Le Silmarillion</p>");
            AddHtml("Guard-the-Entrance-TGC", "<p class='main-text'><b>Une fois révélée:</b> chaque joueur augmente de X son niveau de menace, X étant le nombre de grottes <b>connectées</b> à la grotte qu’il occupe.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> défaussez une ressource de la réserve de chacun de vos héros.</p>");
            AddHtml("Lurking-Ghul-TGC", "<p class='main-text'>Tant qu’elle est dans la zone de cheminement, la Ghûl à l’Affût gagne: “<b>Forcé:</b> quand un joueur se déplace, la Ghûl à l’Affût effectue immédiatement une attaque contre ce joueur depuis la zone de cheminement. (Limite: une fois par tour.)”</p><p class='flavor-text'>“Va-t’en, immonde Dwimmerlaik, seigneur de la charogne ! Laisse les morts en paix !” — Éowyn, Le Retour du Roi</p>");
            AddHtml("Glorfindel-TGC", "<p class='main-text'>Glorfindel gagne +2 {Attack} quand il attaque seul.</p><p class='main-text'>Glorfindel gagne +2 {Attack} quand il attaque un ennemi unique.</p><p class='flavor-text'>Alors Glorfindel bondit en avant sur le Balrog et son armure dorée luisit étrangement sous la lune…<br/>— La Chute de Gondolin</p>");
            AddHtml("Vast-Cave-TGC", "<p class='main-text'>Les personnages dans cette grotte ne peuvent pas être redressés par des effets de carte des joueurs.</p><p class='main-text'><b>[Voyage] Action</b>: retirez 8 marqueurs de progression de cette grotte pour fouiller cette grotte.</p>");
            AddHtml("Jewelled-Cavern-TGC", "<p class='main-text'>Tant que vous êtes dans cette grotte, les cartes que vous jouez coûtent 1 ressource adéquate supplémentaire.</p><p class='main-text'><b>[Voyage] Action</b>: défaussez toutes les ressources de la réserve de chacun de vos héros pour fouiller cette grotte.</p>");
            AddHtml("Helm-Hammerhand-A-TGC", "<p class='main-text'>Immunisé aux effets de carte des joueurs. Ne peut pas être engagé au combat.</p><p class='main-text'><b>Forcé:</b> à la fin de la phase de combat, déplacez Helm Main-Marteau dans une grotte <b>adjacente</b> vers le joueur le plus proche, si possible. Ensuite, il effectue immédiatement une attaque contre chaque joueur dans cette grotte. Les joueurs dans d’autres grottes ne peuvent pas utiliser de personnage Sentinelle pour défendre contre ces attaques.</p>");
            AddHtml("Helm-Hammerhand-B-TGC", "<p class='main-text'>Immunisé aux effets de carte des joueurs. Ne peut pas quitter la zone de cheminement (sauf en étant détruit) mais est considéré comme engagé au combat avec chaque joueur. Quand Helm Main-Marteau attaque, attribuez-lui une carte ombre supplémentaire.</p><p class='main-text'><b>Forcé:</b> quand un ennemi <b>Pays de Dun</b> est attribué à Helm Main-Marteau en tant que carte ombre, annulez son effet ombre (s’il y en a un) et attribuez à Helm Main-Marteau une autre carte ombre.</p>{victory:10}");
            AddHtml("Legacy-of-Gondolin-TGC", "<p class='main-text'>Attachez cette carte à un héros <b><i>Noldor</i></b> que vous contrôlez. (Limite : 1 par héros.)</p><p class='main-text'>Vous ne pouvez pas jouer d’alliés ou d’attachements de votre main tant que vous avez plus de 2 cartes en main.</p><p class='main-text'><b>Action</b>: inclinez Héritage de Gondolin pour piocher 1 carte ou défausser Héritage de Gondolin.</p>");
            AddHtml("Fork-TGC", "<p class='main-text'><b>Forcé:</b> quand vous vous déplacez dans cette grotte, défaussez des cartes du sommet du deck de rencontre jusqu’à ce qu’un ennemi soit défaussé. Engagez au combat cet ennemi.</p><p class='main-text'><b>[Voyage] Action</b>: défaussez l’allié ou l’attachement que vous contrôlez ayant le coût imprimé le plus élevé pour fouiller cette grotte.</p>");
            AddHtml("The-Horn's-Discovery-TGC", "<p class='flavor-text'>Pourtant on dit dans le Gouffre que résonne parfois encore le cor de Helm et que sa grande ire poursuit toujours les ennemis du Rohan, foudroyant de peur les Hommes. — Le Retour du Roi</p><p class='main-text'> <b>Une fois révélée:</b> attachez le Cor de Helm, en tant qu’indice, face cachée, à la grotte restante sans marqueur de dégâts.</p>");
            AddHtml2("The-Horn's-Discovery-TGC", "<p class='main-text'> Tous les joueurs doivent être dans la grotte sans marqueur de dégâts avant qu’un joueur puisse fouiller cette grotte. Les marqueurs de progression qui devraient être placés sur cette étape doivent à la place être répartis parmi les grottes en jeu (même celles n’ayant pas de marqueurs de joueur).</p><p class='main-text'><b>[Voyage] Action</b>: n’importe quel joueur peut retirer 4 marqueurs de progression de la grotte qu’il occupe pour se déplacer dans une grotte <b>connectée</b>. Il n’y a pas de limite au nombre de fois qu’un joueur peut déclencher cet effet.</p><p class='main-text'><b>Quand Helm Main-Marteau est détruit, les joueurs ont gagné la partie.</b></p>");
            AddHtml("Lay-of-the-Nauglamir-TGC", "<p class='main-text'>Dévoué.</p><p class='main-text'><b>Action</b>: défaussez les 10 premières cartes de votre deck. Choisissez jusqu’à 2 alliés <b><i>Noldor</i></b> ou <b><i>Nain</i></b> différents défaussés par cet effet, et mettez-les en jeu. Chaque allié choisi doit avoir un coût imprimé de 2 ou moins. Vous pouvez mélanger dans votre deck n’importe quel nombre des autres cartes défaussées par cet effet. Retirez Lai du Nauglamír de la partie.</p>");
            AddHtml("Wonders-of-Aglarond-TGC", "<p class='main-text'><b>Forcé:</b> quand les Merveilles d’Aglarond quittent le jeu, placez 4 marqueurs de progression sur n’importe quelle grotte n’ayant pas de marqueur de joueur et révélez 1 carte de rencontre.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> soit Helm Main-Marteau se déplace dans une grotte <b>adjacente</b> vers le joueur le plus proche, soit le joueur en défense augmente de 2 son niveau de menace.</p>");
            AddHtml("Forgotten-Shade-Fork-TGC", "<p class='main-text'>Lors des tests d’engagement au combat contre un joueur, diminuez de 30 le coût d’engagement au combat de l’Ombre Oubliée si ce joueur ne s’est pas déplacé à ce tour.</p><p class='main-text'>Tant qu’elle est engagée au combat avec un joueur qui ne s’est pas déplacé à ce tour, l’Ombre Oubliée ne peut pas subir de dégâts.</p>{cave:Intersection}");
            AddHtml("Forgotten-Shade-Dead-End-TGC", "<p class='main-text'>Lors des tests d’engagement au combat contre un joueur, diminuez de 30 le coût d’engagement au combat de l’Ombre Oubliée si ce joueur ne s’est pas déplacé à ce tour.</p><p class='main-text'>Tant qu’elle est engagée au combat avec un joueur qui ne s’est pas déplacé à ce tour, l’Ombre Oubliée ne peut pas subir de dégâts.</p>{cave:Cul-de-Sac}");
            AddHtml("Forgotten-Shade-Tunnel-TGC", "<p class='main-text'>Lors des tests d’engagement au combat contre un joueur, diminuez de 30 le coût d’engagement au combat de l’Ombre Oubliée si ce joueur ne s’est pas déplacé à ce tour.</p><p class='main-text'>Tant qu’elle est engagée au combat avec un joueur qui ne s’est pas déplacé à ce tour, l’Ombre Oubliée ne peut pas subir de dégâts.</p>{cave:Tunnel}");
            AddHtml("Cave-Opening-TGC", "<p class='main-text'><b>Forcé:</b> quand vous vous déplacez dans cette grotte, défaussez un allié ou un attachement que vous contrôlez ayant un coût imprimé de 2 ou plus.</p><p class='main-text'><b>[Voyage] Action</b>: augmentez de 1 votre niveau de menace pour chaque indice en jeu pour fouiller cette grotte.</p>");
            AddHtml("Secret-Passage-TGC", "<p class='main-text'><b>Forcé:</b> quand vous vous déplacez dans cette grotte, choisissez une traîtrise dans la pile de défausse de rencontre et révélez-la.</p><p class='main-text'><b>[Voyage] Action</b>: retirez 6 marqueurs de progression de cette grotte pour choisir entre: soit fouiller cette grotte, soit vous déplacer directement d’1 ou 2 grottes <b>adjacentes</b>.</p>");
            AddHtml("Small-Stream-TGC", "<p class='main-text'><b>Trajet:</b> chaque joueur doit défausser 1 carte prise au hasard dans sa main pour voyager vers ce lieu.</p><p class='main-text'><b>[Voyage] Action</b>: si cette carte est dans la pile de victoire, un joueur dans une grotte <b><i>Ruisseau</i></b> peut retirer cette carte du jeu pour se déplacer dans une grotte <b>adjacente</b>.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> l’ennemi attaquant gagne +1 {Attack}. Si vous ne vous êtes pas déplacé à ce tour, attribuez-lui une carte ombre supplémentaire.</p>{victory:1}");
            AddHtml("Locked-Door-TGC", "<p class='main-text'><b>Forcé:</b> quand vous vous déplacez dans cette grotte, inclinez 2 personnages que vous contrôlez.</p><p class='main-text'><b>[Voyage] Action</b>: annoncez un type de carte et une sphère, et défaussez une carte prise au hasard dans votre main. Si la carte défaussée correspond à l’ensemble des deux critères annoncés, fouillez cette grotte.</p>");
            AddHtml("Onward-Into-Battle-TGC", "<p class='main-text'>Attachez cette carte à un héros <b><i>Guerrier</i></b>.</p><p class='main-text'><b>[Rencontre] Action</b>: inclinez Premier Au Combat pour redresser le héros attaché.</p><p class='flavor-text'>“…et quoique nous considérions toujours qu’un guerrier doit avoir d’autres talents que la seule adresse à manier les armes et à tuer, nous ne l’en plaçons pas moins dans notre estime au-dessus des hommes des autres professions. Ainsi le veut la nécessité de nos jours.” — Faramir, Les Deux Tours</p>");
            AddHtml("Salvaged-Supplies-TGC", "<p class='main-text'><b>Réponse:</b> à la fin de la phase de restauration, si aucun de vos héros n’a gagné de ressource grâce à un effet de carte des joueurs à ce tour, ajoutez 1 ressource à la réserve d’un héros que vous contrôlez.</p><p class='main-text'><b>Action</b>: inclinez un personnage que vous contrôlez pour ramener Provisions Récupérées dans votre main depuis votre pile de défausse.</p>");
            AddHtml("Dark-Pit-TGC", "<p class='main-text'>Les personnages dans cette grotte perdent –1 {Attack} et –1 {Defense}, et sont retirés de la partie quand ils quittent le jeu.</p><p class='main-text'><b>[Voyage] Action</b>: révélez 1 carte de rencontre pour fouiller cette grotte.</p>");
            AddHtml("Relic-of-Nargothrond-TGC", "<p class='main-text'>Attachez cette carte à un personnage <b><i>Noldor</i></b> ou <b><i>Nain</i></b>. Restreint. (Limite : 1 par personnage.) La Relique de Nargothrond peut être jouée depuis votre pile de défausse.</p><p class='main-text'>Le personnage attaché gagne +1 {Defense}.</p><p class='main-text'><b>Action</b>: défaussez la Relique de Nargothrond pour donner au personnage attaché +2 {Defense} jusqu’à la fin de la phase.</p><p class='flavor-text'>Les Nains des Montagnes Bleues aidèrent aux travaux.<br/>— Le Silmarillion</p>");
            AddHtml("Guarded-Hall-TGC", "<p class='main-text'>Tant que vous êtes dans cette grotte, attribuez aux ennemis engagés au combat avec vous 1 carte ombre supplémentaire lorsqu’ils attaquent.</p><p class='main-text'><b>[Voyage] Action</b>: engagez au combat le premier ennemi de la pile de défausse de rencontre pour fouiller cette grotte.</p>");
            AddHtml("Tunnel-TGC", "<p class='main-text'><b>Forcé:</b> quand vous vous déplacez dans cette grotte, défaussez des personnages que vous contrôlez jusqu’à avoir 6 personnages ou moins.</p><p class='main-text'><b>[Voyage] Action</b>: inclinez tous les personnages redressés que vous contrôlez pour fouiller cette grotte.</p>");
            AddHtml("Harlond-Lookout-TGC", "<p class='main-text'>Dévoué.</p><p class='main-text'><b>Réponse:</b> quand la Vigie de Harlond est détruite, redressez un personnage que vous contrôlez.</p><p class='flavor-text'>Les navires disparurent lentement à la vue des guetteurs dans les ports, leurs feux s’amoindrirent dans le lointain. — Le Silmarillion</p>");
        }

        private void MusteringOfTheRohirrim()
        {
            AddHtml("Raven-Clan-Sharpshooter-MotR", "<p class='main-text'>Tant qu’un joueur a au moins 3 cartes en main, l’Archer du Clan du Corbeau gagne Archerie 3.</p><p class='main-text'>Tant qu’un joueur a au moins 5 cartes en main, l’Archer du Clan du Corbeau ne peut pas être engagé intentionnellement au combat.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> l’ennemi attaquant gagne +2 {Attack}.</p>");
            AddHtml("Assassin-at-Edoras-MotR", "<p class='flavor-text'>Menacé d’assassinat, Thengel a placé Edoras en état d’alerte maximale.</p><p class='main-text'><b>Une fois révélée:</b> le premier joueur prend au hasard un ennemi <b><i>Assassin</i></b> mis de côté et le place dans la zone de cheminement. Chaque autre joueur révèle une carte de rencontre.</p><p class='main-text'>Les ennemis <b><i>Assassin</i></b> sont immunisés aux effets de carte des joueurs. Tant qu’il n’y a pas au moins 6 marqueurs de progression sur Assassin à Edoras, les ennemis <b><i>Assassin</i></b> ne peuvent pas subir de dégâts.</p><p class='main-text'><b>Cette étape ne peut pas être passée tant qu’un ennemi <b><i>Assassin</i></b> est en jeu.</b></p>");
            AddHtml("Woodmen's-Outpost-MotR", "<p class='main-text'>Attachez cette carte à un lieu. (Limite : 1 par lieu.)</p><p class='main-text'><b>Réponse:</b> quand le lieu attaché est exploré, chaque personnage contrôlé par le premier joueur gagne +1 {Attack} et +1 {Defense} jusqu’à la fin du tour.</p><p class='flavor-text'>“…même les Wargs n’osaient les attaquer en plein jour s’il y en avait beaucoup ensemble.” — Le Hobbit</p>");
            AddHtml("Birna-MotR", "<p class='main-text'><b>Action</b>: inclinez Birna et dépensez 1 ressource {sphere:Tactics} pour mettre en jeu un allié <b><i>Beornide</i></b> depuis votre pile de défausse. À la fin de la phase, si cet allié est toujours en jeu ou dans votre pile de défausse, mélangez-le dans votre deck.</p><p class='flavor-text'>En cette dernière heure, Beorn lui-même avait apparu — nul ne savait comment ni d’où il avait surgi. — Le Hobbit</p>");
            AddHtml("Spiteful-Hunter-MotR", "<p class='main-text'>Le Chasseur Rancunier gagne +1 {Attack} pour chaque héros incliné que le joueur en défense contrôle.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> soit vous placez 1 marqueur de dégâts sur Regroupement en Bord d’Isen, soit l’ennemi attaquant gagne +3 {Attack}.</p>");
            AddHtml("Raven-Clan-Warchief-MotR", "<p class='main-text'>Ne peut pas avoir d’attachements.</p><p class='main-text'><b>Forcé:</b> quand le Chef de Guerre du Clan du Corbeau attaque, placez 1 marqueur de dégâts sur Regroupement en Bord d’Isen.</p><p class='main-text'><b>Forcé:</b> quand le Chef de Guerre du Clan du Corbeau est éliminé, mélangez-le, ainsi que la pile de défausse de rencontre, dans le deck de rencontre.</p>");
            AddHtml("Seeking-Support-MotR", "<p class='flavor-text'>Alors que vous traversez le Rohan, le pays est en détresse. Personne ne pourra vous venir en aide avant que vous n’ayez trouvé l’éored partie pour les Montagnes Blanches, demandé le soutien de Thengel, et chassé les voleurs de chevaux orques qui écument la campagne. Où que vous alliez, les agents d’Ealdwulf cherchent à vous ralentir et à vous duper. </p><p class='main-text'> <b>Une fois révélée:</b> le premier joueur choisit une quête annexe <b><i>Rassemblement</i></b> mise de côté et la révèle.</p>");
            AddHtml2("Seeking-Support-MotR", "<p class='flavor-text'>À chaque aube, l’armée d’Ealdwulf devient plus organisée. Vous devez vous dépêcher.</p><p class='main-text'> Chercher de l’Aide ne peut pas être choisie comme quête en cours lors de la phase de quête.</p><p class='main-text'><b>Forcé:</b> quand une quête annexe <b><i>Rassemblement</i></b> est passée, retournez-la et révélez-la. Ensuite, le premier joueur choisit une autre quête annexe <b><i>Rassemblement</i></b> mise de côté et la révèle. <b>Si le premier joueur ne peut pas le faire, passez à l’étape 3A.</b></p>");
            AddHtml("Hunting-Dogs-MotR", "<p class='main-text'>Dévoué. Ne peuvent pas avoir d’attachements.</p><p class='main-text'><b>Réponse:</b> quand un lieu avec un attachement est exploré, redressez les Chiens de Chasse.</p><p class='flavor-text'>“Le chien courant est affamé, le lièvre peureux.”<br/>— Sylvebarbe, Les Deux Tours</p>");
            AddHtml("Pernicious-Plotter-MotR", "<p class='main-text'><b>Forcé:</b> à la fin du tour, placez 1 marqueur de dégâts sur Regroupement en Bord d’Isen.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> soit vous placez 1 marqueur de dégâts sur Regroupement en Bord d’Isen, soit vous renvoyez l’ennemi attaquant dans la zone de cheminement après cette attaque.</p>");
            AddHtml("Lie-of-the-Land-MotR", "<p class='main-text'>Attachez cette carte à un lieu.</p><p class='main-text'><b>Action</b>: inclinez un personnage <b><i>Éclaireur</i></b> pour placer un marqueur de progression sur le lieu attaché. Si ce lieu n’est pas exploré par cet effet, vous pouvez attacher Connaissance du Terrain à un autre lieu.</p><p class='flavor-text'>“Il nous faudra battre le pays à de longues lieues à la ronde avant de faire le moindre mouvement.”<br/>— Gandalf, La Communauté de l’Anneau</p>");
            AddHtml("Coup-MotR", "<p class='main-text'><b>Une fois révélée:</b> chaque joueur doit choisir entre : soit augmenter de 6 son niveau de menace, soit défausser un héros qu’il contrôle. Ajoutez Coup dans le Dos à la pile de victoire. Cet effet ne peut pas être annulé.</p><p class='flavor-text'>“En apprenant que Gripoil était revenu sans cavalier, je me suis réjoui du retour du cheval, mais encore davantage de l’absence du cavalier.”<br/>— Gríma, Les Deux Tours</p>{victory:1}");
            AddHtml("Cutthroat's-Strike-MotR", "<p class='main-text'>Renfort.</p><p class='main-text'>Tant qu’il est attaché à un ennemi, le Coupe-Gorge compte comme un attachement <b><i><b><i>Condition</i></b></i></b> avec le texte : “<b>Forcé:</b> quand l’ennemi attaché attaque, il gagne +5 {Attack} pour cette attaque. Ensuite, défaussez Coupe-Gorge.” <b>Une fois révélée:</b> attachez cette carte à l’ennemi en jeu ayant le coût d’engagement au combat le plus faible et qui n’a pas déjà un exemplaire de Coupe-Gorge attaché.</p>");
            AddHtml("Road-to-Ruin-MotR", "<p class='flavor-text'>La bande de guerriers orques se regroupe et menace les dresseurs de chevaux qui leur font face sur la route.</p><p class='main-text'><b>Une fois révélée:</b> le premier joueur place l’Ost des Orques en Déroute dans la zone de cheminement et lui inflige 4 points de dégâts. Chaque autre joueur révèle une carte de rencontre.</p><p class='main-text'><b>Forcé:</b> à la fin du tour, infligez 1 point de dégâts à l’Ost des Orques en Déroute.</p><p class='main-text'><b>Cette étape ne peut pas être passée tant que l’Ost des Orques en Déroute n’est pas dans la pile de victoire.</b></p>");
            AddHtml("Sniveling-Courtier-MotR", "<p class='main-text'>Indestructible.</p><p class='main-text'><b>Forcé:</b> quand le Courtisan Médisant effectue une attaque sans défense contre vous, choisissez entre : placer 1 marqueur de dégâts sur Regroupement en Bord d’Isen ou augmenter de 3 votre niveau de menace.</p><p class='main-text'>[Restauration] <b>Action</b>: le joueur engagé au combat peut dépenser 2 ressources pour défausser le Courtisan Médisant.</p>");
            AddHtml("Curuwyn-The-Whisperer-MotR", "<p class='main-text'><b>Forcé:</b> au début de la phase de rencontre, Curuwyn La Chuchoteuse engage au combat le joueur qui a le niveau de menace le plus élevé.</p><p class='main-text'><b>Forcé:</b> quand Curuwyn La Chuchoteuse attaque, le joueur en défense doit soit placer un marqueur de dégâts sur Regroupement en Bord d’Isen, soit augmenter de 4 son niveau de menace.</p>{victory:5}");
            AddHtml("Descendants-of-Beorn-MotR", "<p class='main-text'><b>Réponse:</b> quand un allié <b><i>Beornide</i></b> que vous contrôlez quitte le jeu, ajoutez 1 ressource à la réserve d’un héros <b><i>Beornide</i></b> pour chaque marqueur de dégâts qui était sur cet allié (avec un maximum égal aux Points de Vie imprimés de cet allié).</p><p class='flavor-text'>Il rejetait les loups et les gobelins de son chemin comme fétus. — Le Hobbit</p>");
            AddHtml("Scattered-to-the-Winds-MotR", "<p class='main-text'><b>Une fois révélée:</b> chaque joueur choisit la moitié des alliés non-objectif qu’il contrôle (arrondie au supérieur) et les mélange dans le deck de leur propriétaire respectif. Ajoutez Dispersés aux Vents à la pile de victoire. Cet effet ne peut pas être annulé.</p><p class='flavor-text'>“Nous allons être tous dispersés et perdus.”<br/>— Aragorn, La Communauté de l’Anneau</p>{victory:1}");
            AddHtml("Felberen-Scion-of-Freca-MotR", "<p class='main-text'><b>Forcé:</b> au début de la phase de rencontre, Felberen Rejeton de Freca engage au combat le joueur qui a le plus de cartes dans sa main.</p><p class='main-text'><b>Forcé:</b> quand Felberen Rejeton de Freca attaque, le joueur en défense doit soit placer un marqueur de dégâts sur Regroupement en Bord d’Isen, soit défausser sa main.</p>{victory:5}");
            AddHtml("Halls-of-Ealdwulf-MotR", "<p class='flavor-text'>Vous rentrez à Aldburg pour confronter Ealdwulf. L’armée du félon est déjà partie, mais il a laissé derrière lui un cadeau d’adieu: deux assassins à l’air mauvais, leurs lames affûtées et étincelantes. </p><p class='main-text'> <b>Une fois révélée:</b> placez 1{PerPlayer} marqueur de dégâts sur Regroupement en Bord d’Isen. Placez dans la zone de cheminement les ennemis <b><i>Assassin</i></b> restants mis de côté.</p>");
            AddHtml2("Halls-of-Ealdwulf-MotR", "<p class='flavor-text'>Les assassins d’Ealdwulf se jettent sur vous sauvagement, frappant de leurs lames en marmonnant des paroles chargées d’une haine froide.</p><p class='main-text'> Les ennemis <b><i>Assassin</i></b> sont immunisés aux effets de carte des joueurs.</p><p class='main-text'><b>Cette étape ne peut pas être passée tant qu’un ennemi <b><i>Assassin</i></b> est en jeu. Quand cette étape est passée, les joueurs ont gagné la partie.</b></p>");
            AddHtml("Winter-in-Westemnet-MotR", "<p class='flavor-text'>Les éclaireurs d’Éomund sont allés très loin dans leur traque pour retrouver Gálmód. La nouvelle de sa capture apportera sûrement de nouveaux alliés à votre cause.</p><p class='main-text'><b>Une fois révélée:</b> le premier joueur place l’Írensaga dans la zone de cheminement. Chaque autre joueur révèle une carte de rencontre.</p><p class='main-text'>Les ennemis gagnent +1 {Threat} et ne font pas de tests d’engagement au combat.</p><p class='main-text'><b>Cette étape ne peut pas être passée tant que l’Írensaga n’est pas dans la pile de victoire.</b></p>");
            AddHtml("Irensaga-MotR", "<p class='main-text'>Immunisé aux effets de carte des joueurs.</p><p class='main-text'>L’Írensaga gagne +2{PerPlayer} points de quête. Tant que l’Írensaga est le lieu actif, les joueurs ne peuvent pas jouer d’alliés.</p><p class='main-text'><b>Forcé:</b> à la fin du tour, si l’Írensaga est dans la zone de cheminement, placez 1 marqueur de dégâts sur Regroupement en Bord d’Isen.</p>{victory:5}");
            AddHtml("Beorn's-Welcome-MotR", "<p class='main-text'>Vous ne pouvez jouer cette carte que si vous contrôlez un héros <b><i>Beornide</i></b>. Attachez cette carte à un héros. (Limite : 1 par héros).</p><p class='main-text'><b>Réponse:</b> quand vous dépensez n’importe quel nombre de ressources de la réserve du héros attaché pour autre chose que jouer une carte, inclinez L’Accueil de Beorn pour ajouter 1 ressource à la réserve du héros attaché.</p><p class='flavor-text'>“Je suis Gandalf”, dit le magicien. “Je n’ai jamais entendu parler de lui”, grogna l’homme — Le Hobbit</p>");
            AddHtml("The-Far-Bank-MotR", "<p class='main-text'>Immunisé aux effets de carte des joueurs. Les joueurs ne peuvent pas voyager vers ce lieu.</p><p class='main-text'>Le coût d’engagement au combat des ennemis en jeu est augmenté de 5.</p><p class='main-text'><b>Forcé:</b> quand un joueur engage intentionnellement au combat un ennemi, cet ennemi effectue immédiatement une attaque contre lui.</p>");
            AddHtml("Subdued-MotR", "<p class='main-text'>Attachez cette carte à un ennemi blessé engagé au combat avec vous. (Limite : 1 par ennemi).</p><p class='main-text'>L’ennemi attaché perd –4 {Attack}.</p><p class='flavor-text'>Son misérable adversaire se traînait à plat ventre sur les pierres, gémissant. — Les Deux Tours</p>");
            AddHtml("Osbera-SideA-MotR", "<p class='main-text'>Réduisez de 1 le total de {Threat} de la zone de cheminement pour chaque personnage <b><i>Beornide</i></b> blessé que vous contrôlez. (Limite : –5 {Threat}).</p><p class='main-text'><b>Réponse:</b> quand vous engagez au combat un ennemi, dépensez 1 ressource de la réserve d’Osbera pour la retourner.</p><p class='flavor-text'>“Je n’ai pas besoin de vos services, merci ; mais sans doute avez-vous besoin des miens.” — Beorn, Le Hobbit</p>");
            AddHtml("Osbera-SideB-MotR", "<p class='main-text'>Ne peut pas avoir d’attachements Restreint. Si vous n’êtes pas engagé au combat avec un ennemi, retournez Osbera.</p><p class='main-text'><b>Réponse:</b> infligez 1 point de dégâts à un personnage <b><i>Beornide</i></b> que vous contrôlez pour annuler 2 points de dégâts tout juste infligés à un personnage que vous contrôlez. (Limite : une fois par tour.)</p><p class='flavor-text'>“Vous n’avez pas encore été dévoré par les Wargs, les gobelins ou les méchants ours, à ce que je vois.” — Beorn, Le Hobbit</p>");
            AddHtml("Orc-host-Remnant-MotR", "<p class='main-text'>X est le nombre de marqueurs de dégâts sur cette carte. Ne peut pas avoir d’attachements. Seul le joueur engagé au combat peut déclarer des attaquants ou des défenseurs contre l’Ost des Orques en Déroute.</p><p class='main-text'><b>Forcé:</b> quand l’Ost des Orques en Déroute subit n’importe quel nombre de dégâts, renvoyez-le dans la zone de cheminement.</p>{victory:5}");
            AddHtml("Host-of-Rohan-MotR", "<p class='main-text'>Sentinelle. Immunisé aux effets de carte des joueurs.</p><p class='main-text'>Le premier joueur gagne le contrôle de l’Ost du Rohan.</p><p class='main-text'><b>Action</b>: les joueurs en tant que groupe peuvent dépenser 2 ressources pour redresser l’Ost du Rohan. (Limite : une fois par phase.)</p><p class='main-text'><b>Si l’Ost du Rohan quitte le jeu, retirez-le de la partie.</b></p><p class='flavor-text'>Le danger écarté, le roi peut librement dépêcher ses armées.</p>");
            AddHtml("Plains-of-Strife-MotR", "<p class='main-text'>Vaste.</p><p class='main-text'>Tant que les Plaines du Conflit sont dans la zone de cheminement, les ennemis <b><i>Rohan</i></b> gagnent +1 {Threat}.</p><p class='main-text'>Tant que les Plaines du Conflit sont le lieu actif, les ennemis <b><i>Rohan</i></b> gagnent +1 {Attack}.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> si l’ennemi attaquant a le trait <b><i>Rohan</i></b>, il effectue une attaque supplémentaire contre vous après celle-ci.</p>");
            AddHtml("Beorning-Pony-MotR", "<p class='main-text'>Ne peut pas avoir d’attachements.</p><p class='main-text'><b>Réponse:</b> quand le Poney Beornide entre en jeu, défaussez les 5 premières cartes de votre deck. Ensuite, vous pouvez dépenser 1 ressource {sphere:Tactics} pour ajouter l’une des cartes défaussées par cet effet à votre main.</p><p class='flavor-text'>L’ouverture était juste assez large pour y pousser les poneys après les avoir débarrassés de leur chargement et de leurs selles.<br/>— Le Hobbit</p>");
            AddHtml("Aldburg-Provocateur-MotR", "<p class='main-text'>Chaque carte jouée par le joueur engagé au combat gagne Maudit 1.</p><p class='flavor-text'>“Il y a une troisième sorte : les ramasseurs d’ossements, ceux qui se mêlent des chagrins des autres, les charognards qui s’engraissent de la guerre.”<br/>— Gríma, Les Deux Tours</p>");
            AddHtml("Traitor's-Redoubt-MotR", "<p class='main-text'>Immunisé aux effets de carte des joueurs.</p><p class='main-text'><b>Trajet:</b> chaque joueur cherche un ennemi <b><i>Traître</i></b> dans le deck de rencontre ou dans la pile de défausse de rencontre, et le met en jeu, engagé au combat avec lui.</p><p class='main-text'><b>Réponse:</b> quand la Redoute du Traître quitte le jeu en tant que lieu exploré, retirez 1 marqueur de dégâts de Regroupement en Bord d’Isen.</p>{victory:1}");
            AddHtml("Massing-at-the-Isen-MotR", "<p class='main-text'><b>Forcé:</b> à la fin du tour, placez un marqueur de dégâts sur cette carte.</p><p class='main-text'><b>S’il y a au moins 5 marqueurs de dégâts sur cette carte, retirez-en 5 et révélez la première carte du deck Regroupement. Si vous ne le pouvez pas, les joueurs ont perdu la partie.</b></p><p class='flavor-text'>Ealdwulf prépare son armée pour la guerre sur les rives de l’Isen.</p>");
            AddHtml("Devoted-Determination-MotR", "<p class='main-text'>Le premier allié joué par chaque joueur à chaque tour ne nécessite pas d’avoir une ressource adéquate.</p><p class='flavor-text'>Avec le conspirateur Gálmód enchaîné, les alliés viennent honorer leurs serments.</p>");
            AddHtml("Deeping-stream-MotR", "<p class='main-text'>La Rivière du Gouffre gagne +3{PerPlayer} points de quête.</p><p class='main-text'><b>Forcé:</b> à la fin du tour, placez un marqueur de dégâts sur Regroupement en Bord d’Isen.</p><p class='flavor-text'>Vous devez quitter votre camp de fortune près des Grottes Scintillantes avant qu’Ealdwulf ne divise le royaume du Rohan avec son ambition.</p>{victory:5}");
            AddHtml("Rowan-MotR", "<p class='main-text'>Sentinelle.</p><p class='main-text'><b>Réponse:</b> quand vous jouez un attachement sur un lieu, ajoutez 1 ressource à la réserve d’un héros que vous contrôlez. (Limite : une fois par tour.)</p><p class='main-text'><b>[Quête] Action:</b> choisissez un joueur pour prendre le contrôle du marqueur premier joueur. Chaque joueur ne peut être choisi qu’une fois par partie.</p>");
            AddHtml("Orator's-Hall-MotR", "<p class='main-text'>Les joueurs ne peuvent pas voyager vers la Salle d’Audience. Aucun marqueur de progression ne peut être placé sur cette carte par des effets de carte des joueurs.</p><p class='main-text'>[Organisation] <b>Action</b>: inclinez un héros que vous contrôlez pour placer sur la Salle d’Audience un nombre de marqueurs de progression égal à sa {Willpower} imprimée. Si la Salle d’Audience est explorée par cet effet, retirez 1 marqueur de dégâts de Regroupement en Bord d’Isen. N’importe quel joueur peut déclencher cette action.</p>{victory:1}");
            AddHtml("Without-My-Hat-MotR", "<p class='main-text'>Limite : 1 exemplaire de Sans Chapeau dans la pile de victoire.</p><p class='main-text'><b>Action</b>: placez Sans Chapeau dans la pile de victoire pour chercher un attachement dans votre collection. Si aucun exemplaire de cet attachement n’était dans votre deck à la mise en place, ajoutez-le à votre main.</p>{victory:1}");
            AddHtml("Oaths-of-the-Rohirrim-MotR", "<p class='flavor-text'>Le traître Ealdwulf lève une armée pour usurper le trône du roi Thengel. </p><p class='main-text'> <b>Mise en place:</b> mélangez ensemble les 5 cartes avec le trait <b><i>Regroupement</i></b> pour créer le deck Regroupement. Cherchez dans le deck de rencontre l’Írensaga, l’Ost des Orques en Déroute, chaque ennemi <b><i>Assassin</i></b>, et chaque quête annexe <b><i>Rassemblement</i></b> et mettez-les de côté, hors jeu. Le premier joueur place Regroupement en Bord d’Isen dans la zone de cheminement et fait de la Rivière du Gouffre le lieu actif. Chaque joueur cherche dans le deck de rencontre 1 ennemi <b><i>Traître</i></b> différent et le place dans la zone de cheminement. Mélangez le deck de rencontre.</p>");
            AddHtml2("Oaths-of-the-Rohirrim-MotR", "<p class='flavor-text'>Si vous espérez relever le défi d’Ealdwulf, vous devez passer les mois à venir à rassembler les alliés dispersés du Rohan. Sa conspiration planifiée de longue date lui donne une longueur d’avance, mais vos chevaux rapides peuvent encore vous transporter sur de nombreuses lieues pour avertir les seigneurs qui restent fidèles à la bannière de Thengel.</p><p class='main-text'> <b>Une fois révélée:</b> chaque joueur doit choisir entre : placer 1 marqueur de dégâts sur Regroupement en Bord d’Isen, ou révéler une carte de rencontre.</p>");
            AddHtml("Smeohbrand-Rogue-of-Orthanc-MotR", "<p class='main-text'><b>Forcé:</b> au début de la phase de rencontre, Smeóhbrand Truand d’Orthanc engage au combat le joueur qui contrôle le moins de personnages.</p><p class='main-text'><b>Forcé:</b> quand Smeóhbrand Truand d’Orthanc attaque pour la première fois à chaque tour, il effectue une attaque supplémentaire après celle-ci. Le joueur engagé au combat avec lui peut placer un marqueur de dégâts sur Regroupement en Bord d’Isen pour annuler cet effet.</p>{victory:5}");
            AddHtml("Bitter-Betrayal-MotR", "<p class='main-text'><b>Une fois révélée:</b> l’allié que chaque joueur contrôle ayant le coût le plus élevé effectue immédiatement une attaque contre le joueur qui le contrôle. Jusqu’à la fin de ces attaques, chacune de ces cartes perd le type de carte Allié et gagne le type de carte Ennemi. Ensuite, retirez ces alliés de la partie. Ajoutez Trahison Amère à la pile de victoire. Cet effet ne peut pas être annulé.</p>{victory:1}");
        }

        private void TheScouringOfTheShire()
        {
            AddHtml("Escorted-Prisoners-TSotS", "<p class='flavor-text'>Après une brève rencontre sur la route avec Saroumane, tombé en disgrâce, Frodon et ses compagnons rencontrent la résistance et l’hostilité des shirriffs de la Comté. </p><p class='main-text'> <b>Une fois révélée:</b> faites de la Porte du Brandevin le lieu actif et placez la Pierre des Trois Quartiers dans la zone de cheminement.</p>");
            AddHtml2("Escorted-Prisoners-TSotS", "<p class='flavor-text'>“Les ordres du Chef sont que vous devez venir sans esclandre. Nous allons vous emmener à Lézeau et vous remettre entre les mains des Hommes du Chef.” — Le Retour du Roi</p><p class='main-text'> Il ne peut pas y avoir plus de 12 marqueurs de progression sur cette étape.</p><p class='main-text'><b>Forcé:</b> à la fin de la phase de quête, si aucun marqueur de progression n’a été placé sur cette carte suite à un succès de quête, le premier joueur augmente de 3 son niveau de menace. Si plus de 3 marqueurs de progression ont été placés, chaque joueur engagé au combat avec plus de 1 ennemi <b><i>Shirriff</i></b> peut défausser l’un d’entre eux.</p><p class='main-text'><b>Les joueurs ne peuvent pas passer cette étape tant que la Pierre des Trois Quartiers est en jeu.</i></b></p>");
            AddHtml("Into-the-West-TSotS", "<p class='main-text'> Vous ne pouvez utiliser ce contrat que si vous avez détruit l’Anneau Unique et gagné le scénario <i>Le Nettoyage de la Comté</i> en mode campagne.</p><p class='main-text'>Votre deck doit contenir un nombre équivalent de cartes avec un coût imprimé de 1, 2, 3, 4, et 5.</p><p class='main-text'><b>Mise en place:</b> cherchez dans les 10 premières cartes de votre deck un allié ayant un coût imprimé de 3 ou moins et mettez-le en jeu. Mélangez votre deck et retournez cette carte.</p><p class='flavor-text'>“J’ai tenté de sauver la Comté, et elle l’a été, mais pas pour moi.” — Frodon Sacquet, Le Retour du Roi</p>");
            AddHtml2("Into-the-West-TSotS", "<p class='main-text'> À l’Ouest est immunisé aux effets des autres cartes et ne peut pas quitter le jeu ou votre contrôle. Les alliés que vous jouez de votre main ne peuvent pas avoir leur coût réduit de plus de 2.</p><p class='main-text'><b>Forcé:</b> au début de la phase de quête, attachez À l’Ouest à un allié que vous contrôlez, si possible.</p><p class='main-text'><b>Forcé:</b> à la fin de la phase de restauration, répartissez X ressources parmi les héros que vous contrôlez, où X est égal au coût imprimé de l’allié attaché. Retirez de la partie la carte attachée et piochez une carte.</p>");
            AddHtml("Gang-of-Ruffians-TSotS", "<p class='main-text'><b>Réponse:</b> les dégâts infligés par la Bande de Coquins peuvent être répartis parmi les personnages contrôlés par le joueur en défense.</p><p class='main-text'><b>Forcé:</b> quand la Bande de Coquins attaque et détruit au moins un personnage, défaussez au hasard une carte de votre main et augmentez de 1 votre niveau de menace.</p>");
            AddHtml("Brick-Building-TSotS", "<p class='main-text'>Tant que le Bâtiment de Brique est dans la zone de cheminement, les personnages ne peuvent pas être soignés.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> soit vous infligez 2 points de dégâts au personnage défenseur, soit vous augmentez de 2 votre niveau de menace.</p><p class='flavor-text'>En passant entre des rangées de nouvelles et vilaines maisons, ils virent le nouveau moulin dans toute sa rébarbative et sale laideur. — Le Retour du Roi</p>");
            AddHtml("Wooden-Shed-TSotS", "<p class='main-text'>Tant que la Cabane en Bois est dans la zone de cheminement, aucun joueur ne peut diminuer son niveau de menace de plus de 1 à chaque tour.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> soit vous augmentez de 1 votre niveau de menace, soit vous inclinez un personnage que vous contrôlez.</p><p class='flavor-text'>Il y avait des tas d’ordures de tous côtés. La porte était tailladée, la chaîne de la sonnette pendillait librement, et la sonnette ne donna aucun son. — Le Retour du Roi</p>");
            AddHtml("Thieving-Ruffian-TSotS", "<p class='main-text'><b>Forcé:</b> quand la Canaille attaque, augmentez de 1 votre niveau de menace pour chaque attachement sur le personnage défenseur.</p><p class='flavor-text'>“Ah, c’est là votre ton ? Eh bien changez-en, ou on le changera pour vous.” — Le Retour du Roi</p>");
            AddHtml("Against-the-Rules-TSotS", "<p class='main-text'><b>Une fois révélée:</b> inclinez chaque personnage ayant une {Attack} imprimée de 3 ou plus. Les joueurs qui n’ont incliné aucun personnage par cet effet doivent choisir et incliner un personnage qu’ils contrôlent.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> si votre niveau de menace est supérieur au coût d’engagement au combat de l’ennemi attaquant, celui-ci gagne +2 {Attack}. Sinon, augmentez de 2 votre niveau de menace.</p>");
            AddHtml("Scatha's-Horn-TSotS", "<p class='main-text'>Attachez cette carte à un héros.</p><p class='main-text'><b>Action:</b> inclinez le Cor de Scatha pour choisir un ennemi attaquant. Jusqu’à la fin de la phase, cet ennemi perd −1 {Attack} (si le personnage défenseur a le trait <b><i>Hobbit</i></b>, cet ennemi perd −2 {Attack} à la place).</p><p class='flavor-text'>“Celui qui en sonnera dans le besoin inspirera la peur au cœur de ses ennemis.” — Éowyn, Le Retour du Roi</p>");
            AddHtml("Awake-Fire-Foes-Awake-TSotS", "<p class='flavor-text'>Il n’était pas encore bien loin, qu’il entendit soudain retentir un clair appel de cor, qui se répercuta par-dessus collines et champs. — Le Retour du Roi</p><p class='main-text'><b>[Rencontre] Action:</b> engagez au combat un ennemi <b><i>Bandit</i></b> de la pile de défausse de rencontre. N’importe quel joueur peut déclencher cet effet.</p><p class='main-text'><b>Réponse:</b> quand le premier joueur détruit un ennemi, placez 2 marqueurs de progression sur cette carte.</p><p class='main-text'><b>Forcé:</b> quand cette étape est passée, retournez cette carte et révélez-la.</p>");
            AddHtml("Set-up-Barriers-TSotS", "<p class='flavor-text'>D’autres, sous les ordres de Merry, dressaient des barrières en travers de la route aux deux extrémités du village. — Le Retour du Roi</p><p class='main-text'>Les joueurs ne peuvent pas piocher de cartes.</p><p class='main-text'><b>Action:</b> les joueurs, en tant que groupe, peuvent dépenser 1{PerPlayer} ressource pour placer 3 marqueurs de progression sur cette carte.</p><p class='main-text'><b>Forcé:</b> quand cette étape est passée, le premier joueur doit défausser chaque ressource de la réserve de chacun de ses héros. Ensuite, retournez cette carte et révélez-la.</p>");
            AddHtml("To-the-Road-Again-Worm-TSotS", "<p class='main-text'><b>Une fois révélée:</b> défaussez des cartes du sommet du deck de rencontre jusqu’à ce qu’un lieu soit défaussé et placez-le dans la zone de cheminement. Répartissez X points de dégâts entre les personnages en jeu. X est égal à 2 fois le nombre de lieux en jeu (3 fois le nombre de lieux en jeu s’il y a 3 joueurs ou plus dans la partie).</p><p class='flavor-text'>Saroumane rit. “Tu fais ce que Sharcoux dit toujours, n’est-ce pas, Langue de Serpent ?” — Le Retour du Roi</p>");
            AddHtml("Rouse-the-Cottons-TSotS", "<p class='flavor-text'>“Mais allons-y, les gars ! Lézeau se lève ! Il faut être dans le coup !”<br>— Tom Chaumine, Le Retour du Roi</p><p class='main-text'>Tant que Éveiller les Chaumine est la quête en cours, les alliés ne peuvent pas être engagés dans la quête.</p><p class='main-text'><b>Forcé:</b> quand cette étape est passée, retournez cette carte et révélez-la.</p>");
            AddHtml("Escorted-Prisoners-TSotS", "<p class='main-text'><b>Réponse:</b> quand Frodon Sacquet s’engage dans une quête, dépensez 1 ressource de sa réserve pour redresser un autre personnage unique engagé dans la quête. Si vous obtenez un succès de quête durant cette phase, diminuez de 1 votre niveau de menace.</p>");
            AddHtml("Frodo-Baggins-Fellowship-TSotS", "<p class='main-text'>Le premier joueur gagne le contrôle de Frodon Sacquet.</p><p class='main-text'><b>Réponse:</b> quand Frodon Sacquet est déclaré comme défenseur, dépensez 1 ressource [fellowship] pour réduire votre niveau de menace de la {Threat} de l’ennemi attaquant. Jusqu’à la fin du tour, cet ennemi ne peut pas subir de dégâts.</p><p class='main-text'><b>Si Frodon Sacquet quitte le jeu, les joueurs ont perdu la partie.</b></p>");
            AddHtml("Frodo-Baggins-TSotS", "<p class='main-text'><b>Réponse:</b> quand Frodon Sacquet s’engage dans une quête, dépensez 1 ressource de sa réserve pour redresser un autre personnage unique engagé dans la quête. Si vous obtenez un succès de quête durant cette phase, diminuez de 1 votre niveau de menace.</p>");
            AddHtml("Star-like-Gem-TSotS", "<p class='main-text'>Attachez cette carte à un héros.</p><p class='main-text'>Celui-ci gagne +1 {Willpower}.</p><p class='main-text'>Si ce héros a le trait <b><i>Hobbit</i></b>, il ne s’incline pas pour s’engager dans une quête.</p><p class='flavor-text'>“Quand vous serez troublé par le souvenir de la peur et des ténèbres, dit-elle, ceci vous apportera de l’aide.” — Arwen, Le Retour du Roi</p>");
            AddHtml("Heightened-Stature-TSotS", "<p class='main-text'>Attachez cette carte à un héros.</p><p class='main-text'>Celui-ci gagne +2 Points de Vie (si ce héros a le trait <b><i>Hobbit</i></b>, il gagne +3 Points de Vie à la place).</p><p class='main-text'><b>Action:</b> inclinez Haute Stature pour soigner 1 point de dégâts sur le héros attaché.</p><p class='flavor-text'>“Les mortels ne peuvent pas aller prendre des boissons d’Ents et s’attendre à n’en rien voir sortir de plus que d’un pot de bière.” — Gimli, Le Retour du Roi</p>");
            AddHtml("Roused-Hobbits-TSotS", "<p class='main-text'>Tant qu’elle est attachée, compte comme un attachement <b><i><b><i>Condition</i></b></i></b> avec le texte : “La carte attachée perd le mot-clé Indestructible et le type de carte Ennemi et gagne le type de carte Allié avec une {Willpower} égal à sa {Threat}. Si la carte attachée ou Hobbits Éveillés quitte le jeu, retirez les deux cartes de la partie.”</p><p class='main-text'><b>Une fois révélée:</b> un joueur au choix prend le contrôle d’un ennemi <b><i>Shirriff</i></b> engagé au combat avec lui et lui attache Hobbits Éveillés (s’il n’y a aucun ennemi <b><i>Shirriff</i></b> engagé au combat, retirez Hobbits Éveillés de la partie).</p>");
            AddHtml("Roused-Hobbits-2-TSotS", "<p class='main-text'>Tant qu’elle est attachée, compte comme un attachement <b><i><b><i>Condition</i></b></i></b> avec le texte : “La carte attachée perd le mot-clé Indestructible et le type de carte Ennemi et gagne le type de carte Allié avec une {Willpower} égal à sa {Threat}. Si la carte attachée ou Hobbits Éveillés quitte le jeu, retirez les deux cartes de la partie.”</p><p class='main-text'><b>Une fois révélée:</b> un joueur au choix prend le contrôle d’un ennemi <b><i>Shirriff</i></b> engagé au combat avec lui et lui attache Hobbits Éveillés (s’il n’y a aucun ennemi <b><i>Shirriff</i></b> engagé au combat, retirez Hobbits Éveillés de la partie).</p>");
            AddHtml("Roused-Hobbits-3-TSotS", "<p class='main-text'>Tant qu’elle est attachée, compte comme un attachement <b><i><b><i>Condition</i></b></i></b> avec le texte : “La carte attachée perd le mot-clé Indestructible et le type de carte Ennemi et gagne le type de carte Allié avec une {Willpower} égal à sa {Threat}. Si la carte attachée ou Hobbits Éveillés quitte le jeu, retirez les deux cartes de la partie.”</p><p class='main-text'><b>Une fois révélée:</b> un joueur au choix prend le contrôle d’un ennemi <b><i>Shirriff</i></b> engagé au combat avec lui et lui attache Hobbits Éveillés (s’il n’y a aucun ennemi <b><i>Shirriff</i></b> engagé au combat, retirez Hobbits Éveillés de la partie).</p>");
            AddHtml("Roused-Hobbits-4-TSotS", "<p class='main-text'>Tant qu’elle est attachée, compte comme un attachement <b><i><b><i>Condition</i></b></i></b> avec le texte : “La carte attachée perd le mot-clé Indestructible et le type de carte Ennemi et gagne le type de carte Allié avec une {Willpower} égal à sa {Threat}. Si la carte attachée ou Hobbits Éveillés quitte le jeu, retirez les deux cartes de la partie.”</p><p class='main-text'><b>Une fois révélée:</b> un joueur au choix prend le contrôle d’un ennemi <b><i>Shirriff</i></b> engagé au combat avec lui et lui attache Hobbits Éveillés (s’il n’y a aucun ennemi <b><i>Shirriff</i></b> engagé au combat, retirez Hobbits Éveillés de la partie).</p>");
            AddHtml("Roused-Hobbits-5-TSotS", "<p class='main-text'>Tant qu’elle est attachée, compte comme un attachement <b><i><b><i>Condition</i></b></i></b> avec le texte : “La carte attachée perd le mot-clé Indestructible et le type de carte Ennemi et gagne le type de carte Allié avec une {Willpower} égal à sa {Threat}. Si la carte attachée ou Hobbits Éveillés quitte le jeu, retirez les deux cartes de la partie.”</p><p class='main-text'><b>Une fois révélée:</b> un joueur au choix prend le contrôle d’un ennemi <b><i>Shirriff</i></b> engagé au combat avec lui et lui attache Hobbits Éveillés (s’il n’y a aucun ennemi <b><i>Shirriff</i></b> engagé au combat, retirez Hobbits Éveillés de la partie).</p>");
            AddHtml("Roused-Hobbits-6-TSotS", "<p class='main-text'>Tant qu’elle est attachée, compte comme un attachement <b><i><b><i>Condition</i></b></i></b> avec le texte : “La carte attachée perd le mot-clé Indestructible et le type de carte Ennemi et gagne le type de carte Allié avec une {Willpower} égal à sa {Threat}. Si la carte attachée ou Hobbits Éveillés quitte le jeu, retirez les deux cartes de la partie.”</p><p class='main-text'><b>Une fois révélée:</b> un joueur au choix prend le contrôle d’un ennemi <b><i>Shirriff</i></b> engagé au combat avec lui et lui attache Hobbits Éveillés (s’il n’y a aucun ennemi <b><i>Shirriff</i></b> engagé au combat, retirez Hobbits Éveillés de la partie).</p>");
            AddHtml("Roused-Hobbits-7-TSotS", "<p class='main-text'>Tant qu’elle est attachée, compte comme un attachement <b><i><b><i>Condition</i></b></i></b> avec le texte : “La carte attachée perd le mot-clé Indestructible et le type de carte Ennemi et gagne le type de carte Allié avec une {Willpower} égal à sa {Threat}. Si la carte attachée ou Hobbits Éveillés quitte le jeu, retirez les deux cartes de la partie.”</p><p class='main-text'><b>Une fois révélée:</b> un joueur au choix prend le contrôle d’un ennemi <b><i>Shirriff</i></b> engagé au combat avec lui et lui attache Hobbits Éveillés (s’il n’y a aucun ennemi <b><i>Shirriff</i></b> engagé au combat, retirez Hobbits Éveillés de la partie).</p>");
            AddHtml("Sharkey's-Men-TSotS", "<p class='main-text'><b>Une fois révélée:</b> chaque ennemi en jeu gagne +1 {Attack} jusqu’à la fin du tour. S’il y a moins de 1{PerPlayer} ennemi <b><i>Bandit</i></b> en jeu, défaussez des cartes du sommet du deck de rencontre jusqu’à ce qu’un ennemi <b><i>Bandit</i></b> soit défaussé et placez-le dans la zone de cheminement.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> si l’ennemi attaquant est un <b><i>Bandit</i></b>, il effectue une attaque supplémentaire après celle-ci. Le joueur en défense peut défausser un allié qu’il contrôle pour annuler cet effet.</p>");
            AddHtml("The-Battle-of-Bywater-TSotS", "<p class='main-text'> <b>Une fois révélée:</b> mélangez la pile de défausse de rencontre dans le deck de rencontre. Retirez chaque ennemi <b><i>Shirriff</i></b> en jeu et dans le deck de rencontre de la partie. Le premier joueur cherche un exemplaire de Bande de Coquins dans le deck de rencontre ou dans la pile de victoire et l’engage au combat. Ensuite, chaque joueur qui n’est pas engagé au combat avec un ennemi <b><i>Bandit</i></b> cherche dans le deck de rencontre un ennemi <b><i>Bandit</i></b> et l’engage au combat. Mélangez le deck de rencontre. Défaussez des cartes du sommet du deck de rencontre jusqu’à ce que X ennemis aient été défaussés, X étant le nombre de quêtes annexes de rencontre en jeu. Placez ces ennemis dans la zone de cheminement. Ensuite, retirez les quêtes annexes de rencontre de la partie.</p>");
            AddHtml2("The-Battle-of-Bywater-TSotS", "<p class='flavor-text'>Mais les bandits ne se laissèrent pas aussi aisément intimider, à présent. Quelques-uns obéirent, mais ils furent aussitôt invectivés par leurs camarades. Une vingtaine ou davantage se ruèrent en arrière et chargèrent les charrettes. — Le Retour du Roi</p><p class='main-text'> Tant qu’il y a un ennemi <b><i>Bandit</i></b> en jeu avec au moins un point de vie restant, chaque ennemi <b><i>Bandit</i></b> gagne Indestructible.</p><p class='main-text'><b>S’il n’y a plus d’ennemis en jeu, passez à l’étape suivante.</i></b></p>");
            AddHtml("Hidden-Blade-TSotS", "<p class='main-text'><b>Une fois révélée:</b> Saroumane effectue immédiatement une attaque contre chaque joueur dans l’ordre du tour. Les alliés ne peuvent pas défendre contre Saroumane.</p><p class='flavor-text'>Un poignard étincela dans sa main, et il en porta un coup rapide. — Le Retour du Roi</p>");
            AddHtml("Grima's-Choice-TSotS", "<p class='flavor-text'>“Langue de Serpent ! appela Frodon. Vous n’êtes pas obligé de le suivre. Je ne sache pas que vous m’ayez fait aucun mal. Vous pouvez avoir ici repos et nourriture pendant quelque temps, jusqu’à ce que à vous ayez repris des forces et soyez en état de suivre votre propre chemin.”<br>— Le Retour du Roi</p><p class='main-text'>X est égal à 3{PerPlayer}. Le Choix de Gríma ne peut pas être choisie comme quête en cours tant qu’il y a des points de quête restants sur Le Tout Dernier Coup.</p><p class='main-text'><b>Forcé:</b> quand cette étape est passée, défaussez Saroumane.</b></p>");
            AddHtml("The-Scouring-of-the-Shire-Campaign-TSotS", "<p class='main-text'>Vous jouez en mode Campagne.</p><p class='main-text'><b>Mise en place:</b> chaque joueur peut changer de cartes héros sans subir la pénalité de +1 du niveau de menace. Les cartes Avantage suivantes ont été gagnées par les joueurs : Gemme Étincelante, Mailles Brillantes, Cor de Scatha, et Haute Stature. Chaque joueur en choisit une et l’ajoute à sa main. Si certaines de ces cartes Avantage n’ont pas été choisies, un joueur peut choisir l’une d’entre elles et la mélanger dans son deck. Retirez de la partie les cartes Avantage non choisies. Retirez Gwaihir et chaque carte Fardeau de la réserve de campagne. Attachez Blessure Grave de La Route s’Assombrit à Frodon Sacquet.</p><p class='flavor-text'>La nuit était tombée quand, mouillés et las, les voyageurs finirent par atteindre le Brandevin, et ils trouvèrent le chemin barré.<br/>— Le Retour du Roi </p>");
            AddHtml2("The-Scouring-of-the-Shire-Campaign-TSotS", "<p class='main-text'><b>Résolution:</b> chaque joueur a gagné la carte Contrat À l’Ouest et peut maintenant l’utiliser dans ses decks.</p><p class='flavor-text'>Alors, Elrond et Galadriel poursuivirent leur route, car le Tiers Âge était fini, les Jours des Anneaux étaient passés, et la fin était venue de l’histoire et du chant de ces temps. Avec eux, partirent de nombreux Elfes de Haute Lignée qui ne voulaient plus demeurer en Terre du Milieu, et, parmi eux, emplis d’une tristesse pourtant bienheureuse et dépourvue d’amertume, chevauchaient Sam, Frodon, Bilbon et les Elfes enchantés de leur rendre honneur.<br/>— Le Retour du Roi</p>");
            AddHtml("The-Very-Last-Stroke-TSotS", "<p class='flavor-text'>Les forces de Sharcoux étant dispersées, Frodon entreprend de l’affronter. À sa surprise, il est accueilli par nul autre que Saroumane, Gríma Langue-de-Serpent se recroquevillant à proximité. </p><p class='main-text'> <b>Une fois révélée:</b> si c’est la phase de combat, mettez fin à la phase. Placez Saroumane et Le Choix de Gríma dans la zone de cheminement.</p>");
            AddHtml2("The-Very-Last-Stroke-TSotS", "<p class='flavor-text'>“Vous êtes sage, et cruel. Vous avez retiré toute douceur à la vengeance, et maintenant, il me faut partir d’ici l’amertume au cœur.” — Saroumane, Le Retour du Roi</p><p class='main-text'> X est égal à 6{PerPlayer}. Il ne peut pas y avoir plus de marqueurs de progression sur cette étape que le nombre de ressources sur Saroumane.</p><p class='main-text'><b>Action:</b> inclinez un héros et défaussez 1 ressource de sa réserve pour ajouter 1 ressource sur Saroumane.</p><p class='main-text'><b>Les joueurs ne peuvent pas passer cette étape tant que Saroumane est en jeu. Si les joueurs passent cette étape, ils ont gagné la partie.</i></b>.</p>");
            AddHtml("The-Lockholes-TSotS", "<p class='main-text'>Fatalité.</p><p class='main-text'><b>Une fois révélée:</b> défaussez tous les attachements et marqueurs sur l’allié que vous contrôlez ayant le coût le plus élevé et attachez-le, face cachée, sous Les Trous Prisons.</p><p class='main-text'><b>Forcé:</b> quand Les Trous Prisons sont explorés, retournez l’allié attaché face visible et renvoyez-le sous le contrôle de son propriétaire, redressé.</p><p class='flavor-text'>“Et puis, il y a les Trous prisons, qu’ils appellent ça : les anciens tunnels d’entreposage à Grand’Cave, qu’ils ont transformés en prisons pour ceux qui leur tiennent tête.” — Tom Chaumine, Le Retour du Roi</p>");
            AddHtml("Once-Beloved-Place-TSotS", "<p class='main-text'>Renfort.</p><p class='main-text'><b>Une fois révélée:</b> chaque lieu gagne +1 {Threat} jusqu’à la fin du tour. S’il y a moins de 1{PerPlayer} lieu dans la zone de cheminement, défaussez des cartes du sommet du deck de rencontre jusqu’à ce qu’un lieu soit défaussé et placez-le dans la zone de cheminement.</p><p class='flavor-text'>Tous les châtaigniers avaient disparu. Les berges et les bordures de haies étaient défoncées. — Le Retour du Roi</p>");
            AddHtml("Bright-Mail-TSotS", "<p class='main-text'>Attachez cette carte à un héros.</p><p class='main-text'>Celui-ci gagne +1 {Defense} (si ce héros a le trait <b><i>Hobbit</i></b>, il gagne +2 {Defense} à la place).</p><p class='flavor-text'>“Ils ne se lanceraient pas contre des gens armés, munis d’épées, de casques, de boucliers et de tout ça. Ça leur donnerait à réfléchir.”<br/>— Prosper Poiredebeurré, Le Retour du Roi</p>");
            AddHtml("Shirriff-House-TSotS", "<p class='main-text'><b>Forcé:</b> quand la Maison du Shirriff devient le lieu actif, inclinez chaque personnage avec une {Attack} imprimée de 2 ou plus.</p><p class='main-text'><b>Forcé:</b> quand la Maison du Shirriff quitte le jeu, le premier joueur cherche dans le deck de rencontre ou dans la pile de défausse de rencontre un ennemi <b><i>Shirriff</i></b> et l’engage au combat, si possible. Mélangez le deck de rencontre.</p><p class='flavor-text'>À l’intérieur, elle était humide et triste. — Le Retour du Roi</p>");
            AddHtml("Curse-Upon-the-Shire-TSotS", "<p class='main-text'><b>Une fois révélée:</b> chaque joueur doit choisir entre : soit augmenter de 1 son niveau de menace pour chaque allié qu’il contrôle, soit défausser 2 alliés qu’il contrôle.</p><p class='flavor-text'>“Puisse votre pays manquer longtemps d’herbe !” — Saroumane, Le Retour du Roi</p>");
            AddHtml("Many-Partings-TSotS", "<p class='flavor-text'>Comme ils ressortaient en terrain découvert au coucher du Soleil, ils rattrapèrent un vieillard, appuyé sur un bâton, il était vêtu de haillons gris ou d’un blanc sale, et sur ses talons allait un autre mendiant, qui traînait le pas en gémissant. — Le Retour du Roi </p><p class='main-text'> <b>Mise en place:</b> créez le deck de Sharcoux (cf. feuillet). Mettez Saroumane, la Porte du Brandevin, la Pierre des Trois Quartiers, et chaque quête annexe de rencontre de côté, hors jeu. Mélangez le deck de rencontre.</p>");
            AddHtml2("Many-Partings-TSotS", "<p class='flavor-text'>“Vous vous moquez bien de ce qui manque à un mendiant, hein ? Car vous avez tout ce qu’il vous faut, de la nourriture, de beaux vêtements et la meilleure herbe pour vos pipes. Oh oui, je sais ! Je sais d’où elle vient. Vous n’en donneriez pas une pipée, non ?” — Saroumane, Le Retour du Roi</p><p class='main-text'> <b>Action:</b> inclinez un héros non-[fellowship] et défaussez 1 ressource de sa réserve. Ensuite, ajoutez 2 ressources sur Saroumane, hors-jeu.</p><p class='main-text'><b>À la fin de la phase d’organisation, passez à l’étape suivante.</i></b></p>");
            AddHtml("Three-Farthing-Stone-TSotS", "<p class='main-text'>X est égal à 3{PerPlayer}. Immunisé aux effets de carte des joueurs. Les joueurs ne peuvent voyager vers ce lieu que s’il y a 12 marqueurs de progression sur “Prisonniers” Escortés.</p><p class='main-text'><b>Réponse:</b> quand la Pierre des Trois Quartiers est explorée, chaque joueur diminue de 3 son niveau de menace.</p><p class='flavor-text'>Ils avaient faim, très mal aux pieds, et ils ne pouvaient soutenir l’allure. — Le Retour du Roi</p>{victory:1}");
            AddHtml("Brandywine-Gate-TSotS", "<p class='main-text'>Immunisé aux effets de cartes des joueurs.</p><p class='main-text'><b>Forcé:</b> quand la Porte du Brandevin est explorée, chaque joueur cherche un Shirriff au Chapeau à Plumes dans le deck de rencontre et le met en jeu, engagé au combat avec lui. Mélangez le deck de rencontre.</p><p class='flavor-text'>À chaque extrémité du Pont, il y avait une grande grille garnie de pointes. — Le Retour du Roi</p>{victory:1}");
            AddHtml("Muster-the-Tooks-TSotS", "<p class='flavor-text'>“Ça ne fait que quatorze miles environ par les champs. Je vous ramènerai une armée de Touque dans la matinée.” — Pippin, Le Retour du Roi</p><p class='main-text'>X est égal à 4{PerPlayer}. Tant que Rassembler les Touque est la quête en cours, les héros ne peuvent pas être engagés dans la quête.</p><p class='main-text'><b>Forcé:</b> quand cette étape est passée, retournez cette carte et révélez-la.</p>");
            AddHtml("Hobbiton-Road-TSotS", "<p class='main-text'><b>Forcé:</b> quand la Route de Hobbitebourg quitte le jeu, mettez en jeu un ennemi <b><i>Bandit</i></b> de la pile de défausse de rencontre engagé au combat avec un joueur.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> soit vous infligez 1 point de dégâts au personnage défenseur, soit vous augmentez de 1 votre niveau de menace.</p>");
            AddHtml("East-Road-TSotS", "<p class='main-text'>Fatalité.</p><p class='main-text'><b>Une fois révélée:</b> augmentez de 2 votre niveau de menace ou la Route de l’Est gagne Renfort.</p><p class='flavor-text'>Derrière un coude, à environ un furlong de la route principale, ils se trouvèrent devant une forte barricade faite de vieilles charrettes renversées. Cela les arrêta.<br/>— Le Retour du Roi</p>");
            AddHtml("Take-Him-Lads-TSotS", "<p class='main-text'><b>Une fois révélée:</b> augmentez de 4 le niveau de menace de chaque joueur. Ensuite, chaque joueur défausse un allié qu’il contrôle et diminue de X son niveau de menace, où X est égal au coût imprimé de l’allié défaussé.</p>{shadow}<p class='shadow-text'><b>Ombre:</b> si votre niveau de menace est supérieur au coût d’engagement au combat de l’ennemi attaquant, celui-ci gagne +2 {Attack}. Sinon, augmentez de 2 votre niveau de menace.</p>");
            AddHtml("Saruman-TSotS", "<p class='main-text'>Immunisé aux effets de cartes des joueurs. Ne peut pas être engagé au combat. Les effets des cartes <b><i>Sharcoux</i></b> ne peuvent pas être annulés.</p><p class='main-text'><b>Forcé:</b> à la fin de l’étape de renforcement, ajoutez 1{PerPlayer} ressource sur Saroumane et révélez la première carte du deck de Sharcoux. Ensuite, placez la carte révélée sous le deck de Sharcoux.</p>");
            AddHtml("Feather-capped-Shirriff-TSotS", "<p class='main-text'>Indestructible. Les dégâts infligés par les attaques du Shirriff au Chapeau à Plumes sont annulés ; à la place, le joueur en défense augmente d’autant son niveau de menace.</p><p class='main-text'>Tant que le niveau de menace du joueur engagé au combat est plus élevé que le coût d’engagement au combat du Shirriff au Chapeau à Plumes, celui-ci gagne +1 {Attack}.</p>");
            AddHtml("Raise-the-Shire-TSotS", "<p class='flavor-text'>La Comté est tombée sous l’emprise d’une bande de voyous dirigée par un homme connu sous le nom de “Sharcoux”. Frodon et ses amis doivent fortifier Lézeau et chercher des appuis afin de s’opposer à lui. </p><p class='main-text'> <b>Une fois révélée:</b> sélectionnez au hasard 3 des quêtes annexes non-<b><i>Destin</i></b> mises de côté et mettez-les en jeu (4 quêtes annexes à la place s’il y a 3 joueurs ou plus dans la partie).</p>");
            AddHtml2("Raise-the-Shire-TSotS", "<p class='flavor-text'>Aussitôt qu’ils virent ce qui se passait, la plupart retirèrent leurs plumes et se joignirent à la révolte. — Le Retour du Roi</p><p class='main-text'> <b>Forcé:</b> quand une quête annexe est passée, cherchez dans le deck de rencontre ou la pile de défausse de rencontre un ennemi <b><i>Shirriff</i></b> et placez-le dans la zone de cheminement. Mélangez le deck de rencontre.</p><p class='main-text'><b>Forcé:</b> à la fin du tour, placez 1 ressource sur cette carte et augmentez de 1 le niveau de menace de chaque joueur pour chaque ressource sur cette carte. Ensuite, les joueurs peuvent choisir de passer à l’étape suivante.</p>");
            AddHtml("Watch-the-East-Road-TSotS", "<p class='flavor-text'>“Il y a une grande bande à environ quatre milles … ils doivent être bien près d’une centaine, et ils incendient tout au passage.” — Merry, Le Retour du Roi</p><p class='main-text'>Ne peut pas être choisie comme quête en cours. X est égal à 1 plus 1{PerPlayer}.</p><p class='main-text'><b>Réponse:</b> au début de la phase de renforcement, placez n’importe quel nombre de marqueurs de progression sur cette carte, puis révélez un nombre équivalent de cartes de rencontre.</p><p class='main-text'><b>Forcé:</b> quand cette étape est passée, retournez cette carte et révélez-la.</p>");
            AddHtml("Ambush-Sharkey's-Men-TSotS", "<p class='flavor-text'>“ Je vous avais averti de ne pas revenir. Je vous préviens de nouveau : vous êtes en pleine lumière.” — Merry, Le Retour du Roi</p><p class='main-text'><b>Réponse:</b> quand un exemplaire de Bande de Coquins est détruit, retournez cette carte et révélez-là.</p><p class='main-text'><b>Forcé:</b> à la fin de la phase de quête, si aucun marqueur de progression n’a été placé sur cette carte ce tour, cherchez dans le deck de rencontre ou dans la pile de défausse de rencontre un ennemi <b><i>Bandit</i></b> et placez-le dans la zone de cheminement. Mélangez le deck de rencontre.</p>");
            AddHtml("Find-the-Gaffer-TSotS", "<p class='flavor-text'>“Mais je veux le voir. Le Patron et ce Sharcoux, dont ils ont parlé, ils pourraient faire quelque malheur là-bas avant le matin.” — Sam, Le Retour du Roi</p><p class='main-text'><b>[Voyage] Action:</b> inclinez un héros que vous contrôlez pour placer 2 marqueurs de progression sur cette carte. Vous ne pouvez pas déclarer de personnage attaquant ou défenseur jusqu’à la fin du tour. N’importe quel joueur peut déclencher cet effet.</p><p class='main-text'><b>Forcé:</b> quand cette étape est passée, retournez cette carte et révélez-la.</p>");
            AddHtml("One-Ill-Turn-TSotS", "<p class='main-text'><b>Une fois révélée:</b> infligez 1 point de dégâts à chaque personnage incliné. Les personnages ne peuvent pas être soignés jusqu’à la fin du tour.</p><p class='flavor-text'>“La leçon aurait été plus dure si seulement vous m’aviez laissé un peu plus de temps.”<br/>— Saroumane, Le Retour du Roi</p>");
            AddHtml("Voice-of-Saruman-TSotS", "<p class='main-text'><b>Une fois révélée:</b> chaque joueur sélectionne au hasard 1 allié qu’il contrôle, le défausse, et inflige X points de dégâts à l’un de ses héros, où X est égal à l’{Attack} de l’allié défaussé. Si un joueur ne contrôle aucun allié, il défausse à la place tous les attachements du héros qu’il contrôle ayant le plus d’attachements.</p><p class='flavor-text'>“Il a perdu tout pouvoir, sauf sa voix qui peut encore vous intimider et vous abuser, si vous le laissez faire.”<br/>— Frodon Sacquet, Le Retour du Roi</p>");
            AddHtml("Highway-Robber-TSotS", "<p class='main-text'><b>Forcé:</b> quand le Voleur de Grand Chemin attaque, défaussez une ressource de la réserve d’un héros que vous contrôlez ou le Voleur de Grand Chemin gagne +2 {Attack}.</p><p class='flavor-text'>Leur affaire avait été d’effrayer les paysans de Bree et de houspiller des hobbits désorientés.<br/>— Le Retour du Roi</p>");
        }
    }
}