using System;
using HallOfBeorn.Models;

namespace HallOfBeorn.Services.LotR.Templates
{
    public class ItalianTemplateRepository
        : TemplateRepository
    {
        public ItalianTemplateRepository()
            : base(Language.IT)
        {
        }

        protected override void Initialize()
        {
            ChildrenOfEorl();
            TheAldburgPlot();
            FireOnTheEastemnet();
            TheGapOfRohan();
            TheGlitteringCaves();
            MusterinfOfTheRohirrim();
            BloodInTheIsen();
            TheScouringOfTheShire();
        }

        private void ChildrenOfEorl()
        {
            AddHtml("Defend-the-Beacon-CoE", "<p class='main-text'>I giocatori controllano il faro. Le carte ombra sono immuni agli effetti delle carte dei giocatori.</p><p class='main-text'>Non può avere più di X segnalini progesso, dove X è pari a 2 più 1{PerPlayer}.</p><p class='main-text'><b>Risposta:</b> Rimuovi un qualsiasi ammontare di segnalini progresso da Difendere Il Faro per cancellare un pari ammontare di danni appena inflitti a Faro di Erelas. Poi, se Difendere il Faro ha 0 segnalini progresso, giralo.</p>");
            AddHtml("Ingold-CoE", "<p class='main-text'><b>Risposta:</b> Dopo che un alleato <b><i>Gondor</i></b> o <b><i>Guerriero</i></b> è entrato in gioco sotto il tuo controllo, pesca 1 carta. Poi, spendi una risorsa dalla riserva di Ingold oppure incrementa di 1 la tua minaccia. (Solo una volta per fase.)</p><p class='flavor-text'>“Non desideriamo stranieri nel nostro paese, in questi tempi, a meno che non si tratti di poderosi uomini d’arme fedeli e valorosi.”<br/>—Il Ritorno del Re</p>");
            AddHtml("Celador-CoE", "<p class='main-text'>Gittata.</p><p class='main-text'>Riduci di 1 la {Threat} totale dell’area di allestimento per ogni <b><i>Trappola</i></b> non aggregata che controlli.</p><p class='main-text'><b>Risposta:</b> Dopo che una carta <b><i>Trappola</i></b> che controlli viene aggregata a un nemico, rendi pronto Celador oppure riduci di 1 la tua minaccia.</p><p class='flavor-text'>“Un uomo saggio non si fida degli incontri casuali fatti lungo le strade di questo paese.” —Faramir, Le Due Torri</p>");
            AddHtml("Thengel-CoE", "<p class='main-text'><b>Risposta:</b> Dopo che un alleato è stato scartato dal gioco, il proprietario di quell’alleato cerca tra le prime 5 carte del suo mazzo un alleato che condivide un <b><i>Tratto</i></b> con l’alleato scartato, lo mette in gioco esausto e rimescola il suo mazzo. (Solo una volta per turno.)</p><p class='flavor-text'>“Amico,” disse Gandalf, “tu avevi cavalli, vantavi azioni di guerra e liberi campi.” —Il Ritorno del Re</p>");
            AddHtml("Herubrand-CoE", "<p class='main-text'>Herubrand prende +1 {Willpower} per ogni eroe che controlli con almeno un aggregato limitato.</p><p class='main-text'><b>Risposta:</b> Dopo che un nemico è stato distrutto nell’area di allestimento, pesca 1 carta.</p><p class='flavor-text'>“Quale fardello trasportate, Uomini di Rohan?”<br/>—Imrahil, Il Ritorno del Re</p>");
            AddHtml("Deeping-Bowman-CoE", "<p class='main-text'>Devozione. Gittata.</p><p class='main-text'><b>Azione:</b> Scarta Arciere del Fossato per scegliere un nemico. Fino alla fine della fase, quel nemico non conta la propria {Defense} durante il calcolo del danno da combattimento.</p><p class='flavor-text'>“Scoccammo ogni freccia disponibile, riempiendo la Diga di Orchi.” —Le Due Torri</p>");
            AddHtml("Open-the-Gates-CoE", "<p class='main-text'><b>Azione:</b> Cerca tra le prime 5 carte del tuo mazzo un alleato che condivide un <b><i>Tratto</i></b> con ciascuno dei tuoi eroi e mettilo in gioco. Rimescola il tuo mazzo. Alla fine della fase, se quell’alleato non ha lasciato il gioco nel frattempo, aggiungilo alla tua mano.</p><p class='flavor-text'>Pareva che la notizia del loro arrivo li avesse preceduti; furono fatti entrare immediatamente, in silenzio e senza interrogatorio. —Il Ritorno del Re</p>");
            AddHtml("Wealth-of-Adventure-CoE", "<p class='main-text'><b>Azione:</b> Cerca nel tuo mazzo una carta con il tratto <b><i>Abilità</i></b> e aggiungila alla tua mano. Rimescola il tuo mazzo.</p><p class='flavor-text'>“È pericoloso e impegnativo uscire di casa, Frodo”, mi ripeteva sempre. “Cammini per la strada e, se non fai attenzione, chissà fin dove sei trascinato.”<br/>—Frodo, La Compagnia dell’Anello</p>");
            AddHtml("Feast-of-Plenty-CoE", "<p class='main-text'>Devi pagare il costo di Banchetto Generoso dalla riserva di un singolo eroe.</p><p class='main-text'><b>Azione:</b> Rendi pronti fino a 3 eroi e aggiungi 1 risorsa alla riserva di ciascuno di quegli eroi. I giocatori possono giocare solo 1 copia di Banchetto Generoso per turno.</p><p class='flavor-text'>Il banchetto era allegro e il cibo vario e abbondante come non ne aveva mai visto.<br/>—La Compagnia dell’Anello</p>");
            AddHtml("Deeping-Defender-CoE", "<p class='main-text'>Devozione.</p><p class='main-text'><b>Risposta:</b> Dopo che Difensore del Fossato ha difeso contro un attacco, fai tornare il nemico attaccante nell’area di allestimento e infliggi 1 danno a quel nemico.</p><p class='flavor-text'>Il tramonto indorava le lance e gli elmi e giocava tra i loro pallidi capelli al vento. —Le Due Torri</p>");
            AddHtml("Morwen-Steelsheen-CoE", "<p class='main-text'><b>Azione:</b> Metti in gioco Morwen Bagliore d’Acciaio dalla tua mano. Poi, scarta un alleato <b><i>Rohan</i></b> o <b><i>Gondor</i></b> che controlli.</p><p class='main-text'><b>Risposta:</b> Dopo che un alleato <b><i>Rohan</i></b> o <b><i>Gondor</i></b> lascia il gioco, esaurisci Morwen Bagliore d’Acciaio per curare 2 danni a un personaggio oppure ridurre di 1 la tua minaccia.</p>");
            AddHtml("It-Should-Be-Spared-CoE", "<p class='main-text'><b>Risposta:</b> Dopo che una carta <b><i>Trappola</i></b> che controlli viene aggregata a un nemico, riprendila in mano. Poi, giocala dalla tua mano senza pagarne il costo.</p><p class='flavor-text'>“Ma non uccido uomo o bestia inutilmente, e mai con piacere, anche se necessario.”<br/>—Faramir, Le Due Torri</p>");
            AddHtml("Spring-the-Trap-CoE", "<p class='main-text'><b>Risposta:</b> Dopo che una carta <b><i>Trappola</i></b> che controlli viene aggregata a un nemico, metti in gioco un alleato <b><i>Ranger</i></b> dalla tua mano.</p><p class='flavor-text'>“Forse la strada l’attraversa, ma essi non l’attraverseranno mai! Perlomeno finché Faramir è Capitano.” —Mablung, Le Due Torri</p>");
            AddHtml("Squire-of-the-Mark-CoE", "<p class='main-text'><b>Risposta:</b> Dopo che un alleato <b><i>Rohan</i></b> che controlli è stato scartato dal gioco, aggiungi Scudiero del Mark al riquadro vittoria per rimettere quell’alleato in gioco esausto.</p><p class='flavor-text'>“Ben pochi servigi posso offrire al tuo signore, ma tutto ciò che mi sarà possibile lo farò…”<br/>—Pipino, Il Ritorno del Re</p>");
            AddHtml("Rammas-Lookout-CoE", "<p class='main-text'>Devozione.</p><p class='main-text'>Fintanto che ogni eroe che controlli ha almeno 1 risorsa nella sua riserva, Vedetta del Rammas prende +1 {Willpower} e non si esaurisce per essere coinvolto in una ricerca.</p><p class='flavor-text'>“Accada quel che potrà, è giunta ormai la fine del paese di Gondor quale lo conoscevate voi. Lasciatemi passare!”<br/>—Gandalf, Il Ritorno del Re</p>");
            AddHtml("Theodwyn-CoE", "<p class='main-text'><b>Risposta:</b> Dopo che Théodwyn entra in gioco, pesca 1 carta oppure aggiungi 1 risorsa alla riserva di un eroe <b><i>Nobile</i></b>.</p><p class='flavor-text'>“Dama dal cuore nobile e senza paura. Tutti la amano. Fa che prenda lei il posto del sire degli Eorlingas durante la nostra assenza.”<br/>—Háma, Le Due Torri</p>");
            AddHtml("Light-footed-Steed-CoE", "<p class='main-text'>Aggregalo a un eroe <b><i>Rohan</i></b>. Limitato.</p><p class='main-text'><b>Risposta:</b> Dopo che un personaggio che controlli ha lasciato il gioco, esaurisci Lesto Destriero per rendere pronto l’eroe aggregato. Poi, se il personaggio che ha lasciato il gioco era unico, l’eroe aggregato prende +1 {Attack} fino alla fine del turno.</p><p class='flavor-text'>“…e non dimenticare i loro cavalli! Quelli vedono anche la brezza di notte, o così ho sentito dire.”<br/>—Ugluk, Le Due Torri</p>");
            AddHtml("Rammas-Sentry-CoE", "<p class='main-text'>Devozione.</p><p class='main-text'>Fintanto che ogni eroe che controlli ha almeno 1 risorsa nella sua riserva, Sentinella del Rammas guadagna: “<b>Risposta:</b> Cancella 1 danno appena inflitto a un personaggio <b><i>Gondor</i></b>. (Solo una volta per fase).”</p><p class='flavor-text'>“Se cadiamo noi, chi rimarrà in piedi?”<br/>—Beregond, Il Ritorno del Re</p>");
            AddHtml("Live-Bait-CoE", "<p class='main-text'>Gioca solo se controlli una carta <b><i>Trappola</i></b> non aggregata.</p><p class='main-text'><b>Azione:</b> Scegli un nemico non unico in gioco. Tratta il riquadro di testo di quel nemico come se fosse vuoto (ad eccezione dei <b><i>Tratti</i></b>) fino alla fine del turno. Poi, aggrega una carta <b><i>Trappola</i></b> non aggregata che controlli a quel nemico.</p>");
            AddHtml("Worthy-of-Remembrance-CoE", "<p class='main-text'>Azione di Combattimento: Scarta un alleato <b><i>Rohan</i></b> che controlli per scegliere un nemico. Infliggi 2 danni a quel nemico (4 danni invece se l’alleato scartato era unico).</p><p class='flavor-text'>“Allora il cuore di Éowyn cambiò ad un tratto, e fu ella finalmente a comprenderlo; e improvvisamente il suo inverno scomparve e il sole brillò in lei.”<br/>—Il Ritorno del Re</p>");
            AddHtml("Need-Brooks-No-Delay-CoE", "<p class='main-text'>Riduci di 1 il costo per giocare Il Bisogno non Tollera Attesa per ogni eroe <b><i>Rohan</i></b> che controlli.</p><p class='main-text'>Azione di Pianificazione: Cerca tra le prime 10 carte del tuo mazzo una carta con costo stampato di 2 o inferiore (3 o inferiore se la carta è unica) e mettila da parte. Rimescola il tuo mazzo. Poi, gioca la carta messa da parte senza pagarne il costo e come se fosse nella tua mano.</p>");
            AddHtml("Wild-Country-CoE", "<p class='main-text'>Condanna 1. Vasto.</p><p class='main-text'><b>Obbligato:</b> Alla fine della fase di viaggio, se il luogo attivo non è vasto, ogni giocatore infligge 1 danno ad un personaggio che controlla.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Se il luogo attivo è vasto, infliggi 1 danno al personaggio in difesa.</p>");
            AddHtml("Endless-Grasslands-CoE", "<p class='main-text'>Vasto.</p><p class='main-text'><b>Obbligato:</b> Alla fine della fase di viaggio, se il luogo attivo non è vasto, ogni giocatore esaurisce un alleato che controlla.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Se il luogo attivo è vasto, il nemico attaccante compie un attacco addizionale dopo questo.</p>");
            AddHtml("Windworn-Villages-CoE", "<p class='main-text'>Vasto.</p><p class='main-text'><b>Obbligato:</b> Alla fine della fase di viaggio, se il luogo attivo non è vasto, ogni giocatore sceglie e scarta 1 carta dalla propria mano.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Se il luogo attivo è vasto, scarta 1 aggregato dal personaggio in difesa.</p>");
            AddHtml("Humpbacked-Downs-CoE", "<p class='main-text'>Vasto.</p><p class='main-text'><b>Obbligato:</b> Alla fine della fase di viaggio, se il luogo attivo non è vasto, ogni giocatore incrementa di 1 la propria minaccia.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Se il luogo attivo è vasto, incrementa di 2 la tua minaccia.</p>");
            AddHtml("Ride-Through-the-Night-CoE", "<p class='main-text'><b>Quando Rivelata:</b> Esaurisci tutti gli aggregati limitati controllati dai giocatori. Se questo effetto non fa esaurire alcun aggregato limitato, Attraverso la Notte guadagna impeto.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Se il luogo attivo è vasto, il personaggio in difesa non può essere reso pronto fino alla fine del turno.</p>");
            AddHtml("The-Green-Sea-CoE", "<p class='flavor-text'>L’entroterra è vasto e senza fine, in grado di sfiancare anche i più vigorosi destrieri dell’Ovestfalda.</p><p class='main-text'>Ogni luogo vasto prende +3 punti progresso ed è immune agli effetti delle carte dei giocatori.</p><p class='main-text'><b>Obbligato:</b> All’inizio della fase di viaggio, se non c’è almeno un luogo vasto nell’area di allestimento, scarta carte dalla cima del mazzo incontri fino a che non viene scartato un luogo vasto e aggiungilo all’area di allestimento.</p>{victory:10}");
            AddHtml("Cold-from-the-Mountains-CoE", "<p class='main-text'><b>Quando Rivelata:</b> Ogni giocatore che controlla più di 2 alleati deve scartare 1 alleato che controlla (ne deve scartare 2 se controlla più di 5 alleati).</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Esaurisci un personaggio che controlli.</p><p class='flavor-text'>“Chi vuole lo chiami pur vento…”<br/>—Boromir, La Compagnia dell’Anello</p>");
            AddHtml("Exhausting-Winds-CoE", "<p class='main-text'><b>Quando Rivelata:</b> Infliggi 1 danno ad ogni personaggio con più di 1 punto ferita rimanente. Esaurisci ogni personaggio non danneggiato da questo effetto.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Dopo aver risolto questo attacco, infliggi X danni al personaggio in difesa. X è 1 in meno dei punti ferita rimanenti di quel personaggio.</p>");
            AddHtml("Tired-and-Hungry-CoE", "<p class='main-text'>Fintanto che è aggregata ad un eroe conta come un aggregato <b><i>Condizione</i></b> con il testo: “<b>Obbligato:</b> Quando un luogo viene esplorato, esaurisci l’eroe aggregato.”</p><p class='main-text'><b>Quando Rivelata:</b> Aggregala ad un eroe pronto senza una copia di Stanchi ed Affamati aggregata. Altrimenti Stanchi ed Affamati guadagna impeto.</p><p class='flavor-text'>“Davanti a noi i nemici e dietro lo spavento, il nostro letto sarà sotto il cielo e nel vento.”<br/>—La Compagnia dell’Anello</p>");
            AddHtml("Exposed-to-the-Elements-CoE", "<p class='flavor-text'>Il vostro gruppo si trova esposto all’inclemenza degli elementi e della fame. Dovete cercare riparo se volete avere anche un solo attimo di tregua.</p><p class='main-text'><b>Obbligato:</b> Alla fine del passo di allestimento, scarta carte dal mazzo incontri fino a che non viene scartata una carta perfidia. Rivela quella carta.</p>{victory:10}");
            AddHtml("Old-Farmhouse-CoE", "<p class='main-text'><b>Obbligato:</b> Dopo che Vecchia Fattoria ha lasciato il gioco come luogo esplorato, scarta carte dal mazzo incontri fino a che non scarti un luogo. Aggiungi quel luogo all’area di allestimento.</p><p class='flavor-text'>A un paio di centinaia di metri dalla strada maestra, incontrarono una robusta barricata fatta di vecchi carri capovolti. Furono costretti a fermarsi.<br/>—Il Ritorno del Re</p>");
            AddHtml("Abandoned-Mill-CoE", "<p class='main-text'>Condanna 1. Immune agli effetti delle carte dei giocatori.</p><p class='main-text'><b>Obbligato:</b> Dopo che Mulino Abbandonato ha lasciato il gioco come luogo esplorato, scarta carte dal mazzo incontri fino a che non scarti un nemico. Aggiungi quel nemico all’area di allestimento.</p><p class='flavor-text'>Per macinare ci vuole grano, e non ve n’era certo più…<br/>—Il Ritorno del Re</p>");
            AddHtml("Dunland-Chieftain-CoE", "<p class='main-text'><b>Obbligato:</b> Dopo che Capitano Dunlandiano ti ha ingaggiato, scarta 1 carta casuale dalla tua mano. Poi, scarta ogni altra copia di quella carta che si trova nella tua mano o sotto il tuo controllo.</p><p class='flavor-text'>“Ci odiano e sono contenti…”<br/>—Gamling, Le Due Torri</p>{victory:1}");
            AddHtml("Wild-Ravager-CoE", "<p class='main-text'><b>Obbligato:</b> All’inizio della fase incontri, scegli un giocatore casuale. Devastatore Selvaggio ingaggia quel giocatore.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Il nemico attaccante non può subire danni fino alla fine del turno.</p>");
            AddHtml("Dunlending-Berserker-CoE", "<p class='main-text'><b>Obbligato:</b> Dopo che Berserker del Dunland ti ha ingaggiato, infliggi 1 danno ad un eroe che controlli e 1 danno ad un nemico non unico ingaggiato con te.</p><p class='flavor-text'>“Alba e crepuscolo non li faranno indietreggiare…”<br/>—Gamling, Le Due Torri</p>");
            AddHtml("Hillman-Scout-CoE", "<p class='main-text'>Arciere 2.</p><p class='main-text'><b>Obbligato:</b> Dopo che Esploratore delle Montagne ti ha ingaggiato, esaurisci l’alleato pronto che controlli con il costo stampato più alto.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Esaurisci un personaggio che controlli con 3 o più {Attack}.</p>");
            AddHtml("Surprise-Attack-CoE", "<p class='main-text'>Impeto.</p><p class='main-text'><b>Quando Rivelata:</b> Il prossimo nemico che viene ingaggiato questo turno non può subire danni fino alla fine del turno e ritorna nell’area di allestimento dopo che ha attaccato questo turno.</p><p class='flavor-text'>Era stato raggiunto dagli altri che gli avevano tagliato la strada. Li vedeva distintamente…<br/>—La Compagnia dell’Anello</p>");
            AddHtml("A-History-of-Strife-CoE", "<p class='flavor-text'>I Dunlandiani covano un antico rancore contro gli estranei che si aggirano nelle loro terre. Cercheranno di cogliervi alla sprovvista e di saccheggiare le vostre provviste prima di lasciarvi ad una morte certa.</p><p class='main-text'>Impeto.</p><p class='main-text'>Ogni nemico <b><i>Dunland</i></b> guadagna “<b>Obbligato:</b> Dopo che questo nemico ti ha ingaggiato, scarta 1 risorsa dalla riserva di un eroe che controlli.”</p>{victory:10}");
            AddHtml("Recreant-Rider-CoE", "<p class='main-text'><b>Obbligato:</b> Quando entra nell’area di allestimento, Cavaliere Meschino compie un attacco immediato contro il primo giocatore.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Il nemico attaccante ritorna nell’area di allestimento dopo questo attacco.</p>");
            AddHtml("Deadly-Defector-CoE", "<p class='main-text'><b>Obbligato:</b> Alla fine della fase di combattimento, se Disertore Funesto è nell’area di allestimento, esso compie un attacco immediato contro l’ultimo giocatore.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Se il nemico attaccante si trova nell’area di allestimento, esso compie un attacco addizionale dopo questo.</p>");
            AddHtml("Orc-Battalion-CoE", "<p class='main-text'>Battaglione Orchesco non può subire danni da combattimento quando viene attaccato da meno di 3 personaggi.</p><p class='main-text'><b>Obbligato:</b> Dopo che un personaggio viene dichiarato come attaccante contro Battaglione Orchesco, infliggi 1 danno a quel personaggio.</p>");
            AddHtml("Servant-of-the-Eye-CoE", "<p class='main-text'>Condanna 1.</p><p class='main-text'><b>Obbligato:</b> Quando Servitore dell’Occhio ti attacca, incrementa di 1 la tua minaccia.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Incrementa di 2 la tua minaccia.</p>");
            AddHtml("Black-Land-Scouts-CoE", "<p class='main-text'><b>Obbligato:</b> All’inizio della fase di combattimento, scegli se incrementare di 1 la minaccia di ogni giocatore o se infliggere 4 danni suddivisi tra i personaggi in gioco.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Infliggi 2 danni suddivisi tra i personaggi che controlli.</p>");
            AddHtml("Orcish-Horse-butcher-CoE", "<p class='main-text'><b>Obbligato:</b> Quando Macellatore di Cavalli Orchesco infligge danno a un personaggio, scarta un aggregato da quel personaggio oppure incrementa di 1 la minaccia del giocatore ingaggiato.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Scarta un aggregato che controlli oppure incrementa di 3 la tua minaccia.</p>");
            AddHtml("Crimson-Banner-CoE", "<p class='main-text'>Impeto.</p><p class='main-text'>Fintanto che è aggregata ad un nemico, conta come un aggregato <b><i>Condizione</i></b> con il testo: “<b>Obbligato:</b> Alla fine del turno, ogni giocatore incrementa di 1 la propria minaccia per ogni nemico <b><i>Orco</i></b> in gioco.”</p><p class='main-text'><b>Quando Rivelata:</b> Aggregala al nemico <b><i>Orco</i></b> non unico con il valore di ingaggio più alto.</p>");
            AddHtml("White-Mountain-Reinforcements-CoE", "<p class='flavor-text'>Gli Orchi stanno sciamando dalle caverne sulle montagne, rinforzando l’assalto dei vostri nemici. Se non li bloccherete, presto vi potrebbero soverchiare.</p><p class='main-text'><b>Quando Rivelata:</b> Ogni giocatore non ingaggiato con un nemico deve cercare un nemico nel mazzo incontri e nella pila degli scarti degli incontri e poi metterlo in gioco ingaggiato con sé. Rimescola il mazzo incontri.</p><p class='main-text'>Ogni carta ombra senza effetto ombra guadagna: “<b>Ombra:</b> Incrementa di 1 la tua minaccia.”</p>{victory:10}");
            AddHtml("Brigand-Ringleader-CoE", "<p class='main-text'>Saccheggiare 3. Immune agli effetti delle carte dei giocatori. Non può subire più di 5 danni ogni turno.</p><p class='main-text'><b>Obbligato:</b> Alla fine del turno, il giocatore ingaggiato aggiunge la prima carta del proprio mazzo alla pila di saccheggio di Capobanda dei Briganti e guarisce 1 danno da Capobanda dei Briganti per ogni costo differente tra le carte nella sua pila di saccheggio.</p>{victory:10}");
            AddHtml("Plundered-Goods-CoE", "<p class='main-text'>Aggiungi 1 alla {Threat} totale dell’area di allestimento per ogni carta nella pila di saccheggio di Beni Trafugati.</p><p class='main-text'><b>Obbligato:</b> Quando un nemico lascia il gioco, aggiungi 1 carta dalla sua pila di saccheggio alla pila di saccheggio di Beni Trafugati, poi aggiungi un’altra carta dalla sua pila di saccheggio alla mano del proprietario della carta. Scarta il resto delle carte.</p><p class='flavor-text'>I briganti hanno razziato le scorte di lance e scudi del villaggio, per poi rivolgere le armi contro gli abitanti.</p>");
            AddHtml("Pillaging-Swordsman-CoE", "<p class='main-text'>Saccheggiare 1.</p><p class='main-text'>Spadaccino Saccheggiatore prende +X {Attack}, dove X è il costo stampato più alto tra le carte nella sua pila di saccheggio.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Aggiungi le prime 2 carte del tuo mazzo alla pila di saccheggio del nemico attaccante.</p>");
            AddHtml("Grasping-Slaver-CoE", "<p class='main-text'>Saccheggiare 2.</p><p class='main-text'><b>Obbligato:</b> Dopo che un alleato viene danneggiato o distrutto da un attacco di Schiavista Avido, aggiungi quell’alleato alla pila di saccheggio di Schiavista Avido.</p><p class='main-text'><b>Obbligato:</b> Dopo che Schiavista Avido attacca, infliggi X danni suddivisi tra i personaggi che controlli. X è il numero di carte alleato nella pila di saccheggio di Schiavista Avido.</p>");
            AddHtml("Vengeful-Bowman-CoE", "<p class='main-text'>Saccheggiare 2. Arciere X.</p><p class='main-text'>X è il numero di tipi differenti di carte dei giocatori nella pila di saccheggio di Arciere Vendicativo.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Se questo attacco infligge danni, aggiungi la prima carta del tuo mazzo alla pila di saccheggio del nemico attaccante.</p>");
            AddHtml("Sneaky-Thief-CoE", "<p class='main-text'>Saccheggiare 0. Impeto.</p><p class='main-text'><b>Obbligato:</b> Quando Ladro Furtivo subirebbe un qualsiasi ammontare di danni, se ha delle carte nella sua pila di saccheggio, invece scarta quelle carte.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Se questo attacco distrugge un personaggio, il nemico attaccante compie un attacco addizionale dopo questo.</p>");
            AddHtml("Erelas-Gates-CoE", "<p class='main-text'><b>Viaggio:</b> Ogni giocatore sceglie un nemico e aggiunge la prima carta del proprio mazzo alla pila di saccheggio di quel nemico.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Incrementa di 1 la tua minaccia per ogni carta nella pila di saccheggio del nemico attaccante.</p><p class='flavor-text'>Pilastri contorti e sradicati s’impennavano coi loro fusti scheggiati… —Le Due Torri</p>");
            AddHtml("Crossroads-CoE", "<p class='main-text'><b>Viaggio:</b> Mescola la pila degli scarti degli incontri nel mazzo incontri e scarta carte fino a che non viene scartato un nemico. Metti quel nemico in gioco ingaggiato con il primo giocatore.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Il nemico attaccante prende +X {Attack}, dove X è il numero di sfere differenti tra le carte nella sua pila di saccheggio.</p>");
            AddHtml("Erelas-Stables-CoE", "<p class='main-text'><b>Viaggio:</b> Fai ritornare un nemico ingaggiato con un giocatore nell’area di allestimento.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Se il nemico attaccante non è unico, fallo ritornare nell’area di allestimento dopo questo attacco.</p>");
            AddHtml("Chaos-in-the-Hills-CoE", "<p class='main-text'><b>Quando Rivelata:</b> Fai ritornare nell’area di allestimento il nemico ingaggiato con il valore di ingaggio più alto. Se nessun nemico ritorna nell’area di allestimento a causa di questo effetto, Caos nelle Colline guadagna impeto.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Dopo questo attacco, il nemico attaccante ingaggia il giocatore successivo e compie un attacco immediato.</p>");
            AddHtml("Looted-Weapons-CoE", "<p class='main-text'>Fintanto che è aggregata ad un nemico, conta come un aggregato <b><i>Condizione</i></b> con il testo: “Il nemico aggregato prende +1 {Threat} e +1 {Attack} per ogni aggregato nella sua pila di saccheggio.”</p><p class='main-text'><b>Quando Rivelata:</b> Aggregala al nemico in gioco con il valore di ingaggio più alto. Poi, ogni giocatore aggiunge un aggregato <b><i>Oggetto</i></b> che controlla alla pila di saccheggio di quel nemico, se possibile. Se non ci sono nemici in gioco Armi Rubate guadagna impeto.</p>");
            AddHtml("Stockpile-Barricade-CoE", "<p class='flavor-text'>Carri e carretti rovesciati formano una robusta barricata che attraversa la strada.</p><p class='main-text'>Impeto.</p><p class='main-text'>Ogni nemico prende +1 {Defense} per ogni aggregato nella propria pila di saccheggio.</p><p class='main-text'><b>Obbligato:</b> Dopo aver risolto la parola chiave Saccheggiare di un nemico, se non ci sono aggregati nella pila di saccheggio di quel nemico, esso compie un attacco immediato contro il giocatore ingaggiato.</p>{victory:10}");
            AddHtml("Erelas-Beacon-CoE", "<p class='main-text'><b>Obbligato:</b> Alla fine della fase di combattimento, infliggi 1 danno a Faro di Erelas per ogni nemico nell’area di allestimento.</p><p class='main-text'><b>Faro di Erelas ha X punti ferita, dove X è uguale a 2, più 3{PerPlayer}. Se Faro di Erelas non ha più punti ferita rimanenti, i giocatori hanno perso la partita. </b></p><p class='flavor-text'>Ogni cosa era grigia e immobile, mentre innanzi a loro l’ombra diveniva sempre più tenebrosa ed ogni speranza impallidiva.<br/>—Il Ritorno del Re</p>");
            AddHtml("Host-of-Rohan-CoE", "<p class='main-text'>Sentinella.</p><p class='main-text'><b>Azione:</b> I giocatori come gruppo possono spendere 2 risorse per rendere pronta Schiera di Rohan. (Limite una volta per fase.)</p><p class='flavor-text'>“La guerra infierisce, ed i Rohirrim, i Signori dei Cavalli, non dormono.”<br/>—Gandalf, Le Due Torri</p>");
            AddHtml("Enraged-Dunlending-CoE", "<p class='main-text'><b>Obbligato:</b> Dopo che Dunlandiano Furioso ti ha ingaggiato, infliggi 1 danno a Faro di Erelas.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Ogni danno inflitto al personaggio in difesa da questo attacco è invece inflitto a Faro di Erelas.</p>");
            AddHtml("Bald-Hill-CoE", "<p class='main-text'>Fintanto che i giocatori controllano il faro, Collina Spoglia prende +3 {Threat}.</p><p class='main-text'><b>Viaggio:</b> Se i giocatori controllano il faro, infliggi 2 danni a Faro di Erelas.</p><p class='flavor-text'>Giunsero infine in cima al colle grigio, e una brezza improvvisa soffiò tra i loro capelli agitando i loro manti: il frizzante vento dell’alba.<br/>—Le Due Torri</p>");
            AddHtml("Wooden-Barricade-CoE", "<p class='main-text'>Fintanto che Barricata di Legno è nell’area di allestimento guadagna: “<b>Obbligato:</b> Alla fine della fase di combattimento, infliggi 1 danno a Faro di Erelas e piazza 1 segnalino progresso su Barricata di Legno.”</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Infliggi 1 danno a Faro di Erelas oppure scarta 2 aggregati che controlli.</p>");
            AddHtml("Rocky-Slope-CoE", "<p class='main-text'>Dopo che l’effetto di una carta degli incontri è stato cancellato, infliggi 1 danno a Faro di Erelas.</p><p class='main-text'><b>Viaggio:</b> Il primo giocatore deve scartare tutte le carte evento dalla sua mano.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Infliggi 1 danno a Faro di Erelas oppure scarta tutte le risorse dagli eroi che controlli.</p>");
            AddHtml("Surrounding-Grassland-CoE", "<p class='main-text'>Arciere 1.</p><p class='main-text'><b>Obbligato:</b> Dopo che Prateria Sconfinata lascia il gioco come luogo esplorato, se non ci sono copie di Prateria Sconfinata nell’area di allestimento, fai tornare questo luogo nell’area di allestimento.</p><p class='flavor-text'>Scorgevano al di là del fiume le verdi praterie di Rohan sbiadire in un lontano grigio.<br/>—Le Due Torri</p>");
            AddHtml("Narrow-Stair-CoE", "<p class='main-text'>Ogni giocatore non può dichiarare più di 1 attacco e non può dichiarare più di 1 attaccante durante ciascuna fase.</p><p class='main-text'>Fintanto che Scala Angusta è il luogo attivo, ogni giocatore non può difendere più di 1 attacco durante ciascuna fase (gli altri attacchi sono considerati incontrastati).</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Infliggi 1 danno a Faro di Erelas oppure il nemico attaccante prende +3 {Attack}.</p>");
            AddHtml("From-All-Sides-CoE", "<p class='main-text'><b>Quando Rivelata:</b> Ogni giocatore sceglie: infliggi 1 danno al Faro di Erelas oppure infliggi 1 danno a 4 personaggi differenti che controlli.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Dopo questo attacco, infliggi 1 danno a Faro di Erelas oppure scarta il personaggio dichiarato come difensore.</p>");
            AddHtml("Another-Wave-CoE", "<p class='main-text'>Impeto. Condanna 1.</p><p class='main-text'><b>Quando Rivelata:</b> Se i giocatori controllano il faro, infliggi 2 danni a Faro di Erelas oppure rivela una carta degli incontri.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Infliggi 1 danno a Faro di Erelas oppure il nemico attaccante compie un attacco addizionale dopo questo.</p>");
            AddHtml("Assault-Upon-Erelas-CoE", "<p class='main-text'><b>Quando Rivelata:</b> Il primo giocatore sceglie un nemico ingaggiato con un giocatore e lo fa tornare nell’area di allestimento. Se nessun nemico viene fatto tornare nell’area di allestimento da questo effetto, infliggi 1 danno a Faro di Erelas oppure Assalto ad Erelas guadagna impeto.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Infliggi 1 danno a Faro di Erelas oppure incrementa di 4 la tua minaccia.</p>");
            AddHtml("Reclaim-the-Beacon-CoE", "<p class='flavor-text'>Riconquistate il faro, prima che i Dunlandiani lo danneggino o peggio.</p><p class='main-text'>I Dunlandiani controllano il faro. I nemici nell’area di allestimento non possono essere ingaggiati intenzionalmente né subire danni.</p><p class='main-text'>Non può avere più di X segnalini progresso, dove X è pari a 2, più 1{PerPlayer}. Se Riconquistare il Faro ha X segnalini progresso giralo (mantenendo tutti i segnalini progresso).</p>");
            AddHtml("Stolen-Steeds-CoE", "<p class='main-text'>Gli obiettivi <b><i>Cavalcatura</i></b> non possono essere scartati o lasciare il gioco. Se un obiettivo <b><i>Cavalcatura</i></b> non è aggregato, un giocatore deve prenderne il controllo e aggregarlo ad un eroe che controlla.</p><p class='main-text'><b>Obbligato:</b> Alla fine del turno, aggiungi 1 segnalino progresso alla ricerca principale per ogni obiettivo <b><i>Cavalcatura</i></b> controllato dai giocatori. Poi, incrementa di 1 la minaccia di ciascun giocatore per ogni obiettivo <b><i>Cavalcatura</i></b> in gioco che non è controllato dai giocatori.</p>");
            AddHtml("Flinthoof-CoE", "<p class='main-text'>Protetto (nemico). Limitato.</p><p class='main-text'>Il nemico che protegge Zoccoselce prende +1 {Attack}.</p><p class='main-text'>L’eroe aggregato prende +1 {Attack}.</p><p class='main-text'>Azione di Combattimento: Esaurisci Zoccoselce per infliggere 1 danno ad un nemico in gioco.</p><p class='flavor-text'>Le lunghe code svolazzanti al vento, le criniere sciolte sui loro colli orgogliosi.<br/>—Le Due Torri</p>");
            AddHtml("Brightmane-CoE", "<p class='main-text'>Protetto (luogo). Limitato.</p><p class='main-text'>Il luogo o il nemico che protegge Folgocrino prende +1 {Threat}.</p><p class='main-text'>L’eroe aggregato prende +1 {Willpower}.</p><p class='main-text'>Azione di Ricerca: Esaurisci Folgocrino per guardare la prima carta del mazzo incontri. Se è un luogo, puoi scartarla.</p>");
            AddHtml("Felarof-CoE", "<p class='main-text'>Protetto (nemico). Limitato.</p><p class='main-text'><b>Obbligato:</b> Quando il nemico che protegge Felaróf attacca, assegnagli 2 carte ombra addizionali.</p><p class='main-text'>Azione di Combattimento: Esaurisci Felaróf per scartare dal gioco una carta ombra a faccia in giù.</p><p class='flavor-text'>“Ma ne scorgo uno cavalcare innanzi: un cavallo assai grande. Mai ne ho veduto uno simile.”<br/>—Legolas, Le Due Torri</p>");
            AddHtml("Thruk-CoE", "<p class='main-text'>Immune agli effetti delle carte dei giocatori. Thruk ingaggia il primo giocatore.</p><p class='main-text'><b>Obbligato:</b> All’inizio della fase di combattimento, se Thruk non sta proteggendo un obiettivo, Thruk compie un attacco immediato contro il giocatore ingaggiato.</p>{victory:10}");
            AddHtml("Saddle-slasher-CoE", "<p class='main-text'>Arciere X. X è pari a 2, più 1 per ogni obiettivo <b><i>Cavalcatura</i></b> controllato dai giocatori.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Se controlli un obiettivo <b><i>Cavalcatura</i></b>, aggregalo al nemico attaccante come obiettivo protetto oppure scarta il personaggio difensore dopo aver risolto questo attacco.</p>");
            AddHtml("Rein-ripper-CoE", "<p class='main-text'><b>Obbligato:</b> Quando Strappa-redini attacca, assegnagli 2 carte ombra addizionali. Puoi aggregare un obiettivo <b><i>Cavalcatura</i></b> che controlli a Strappa-redini come obiettivo protetto per cancellare questo effetto.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Il nemico attaccante prende +1 {Attack} per ogni aggregato <b><i>Cavalcatura</i></b> controllato dai giocatori.</p>");
            AddHtml("Bridle-biter-CoE", "<p class='main-text'>Impeto.</p><p class='main-text'><b>Quando Rivelata:</b> Recidi-briglie ingaggia il giocatore che controlla più obiettivi <b><i>Cavalcatura</i></b>.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Metti Recidi-briglie in gioco ingaggiato con te e assegnagli una carta ombra.</p>");
            AddHtml("Pommel-pouncer-CoE", "<p class='main-text'>Fintanto che è nell’area di allestimento senza un obiettivo <b><i>Cavalcatura</i></b> aggregato, Balza-pomello guadagna: “<b>Obbligato:</b> Quando un giocatore guadagnerebbe il controllo di un obiettivo <b><i>Cavalcatura</i></b>, invece aggrega quell’obiettivo <b><i>Cavalcatura</i></b> a Balza-pomello come obiettivo protetto.”</p>");
            AddHtml("Spoiled-Pasture-CoE", "<p class='main-text'>X è pari a 2, più 2 per ogni obiettivo <b><i>Cavalcatura</i></b> controllato dai giocatori.</p><p class='main-text'><b>Obbligato:</b> Dopo che un giocatore perde il controllo di un obiettivo <b><i>Cavalcatura</i></b>, incrementa di 1 la minaccia di quel giocatore.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Incrementa di 1 la tua minaccia per ogni obiettivo <b><i>Cavalcatura</i></b> che controlli.</p>");
            AddHtml("Horse-Thieves'-Hovel-CoE", "<p class='main-text'>Quando hanno la possibilità di viaggiare, i giocatori devono viaggiare verso un luogo non vasto, se possibile.</p><p class='main-text'><b>Obbligato:</b> Dopo che Stamberga dei Ladri di Cavalli ha lasciato il gioco come luogo esplorato, se ci sono meno di 1{PerPlayer} nemici in gioco, scarta carte dalla cima del mazzo incontri fino a che non viene scartato un nemico. Aggiungi quel nemico all’area di allestimento.</p>{victory:2}");
            AddHtml("Horse-Lord's-Lodge-CoE", "<p class='main-text'>Quando hanno la possibilità di viaggiare, i giocatori devono viaggiare verso un luogo non vasto, se possibile.</p><p class='main-text'><b>Obbligato:</b> Dopo che Loggia del Signore dei Cavalli ha lasciato il gioco come luogo esplorato, se ci sono meno di 1{PerPlayer} luoghi in gioco, scarta carte dalla cima del mazzo incontri fino a che non viene scartato un luogo. Aggiungi quel luogo all’area di allestimento.</p>{victory:2}");
            AddHtml("Thirst-for-Horse-Blood-CoE", "<p class='main-text'><b>Quando Rivelata:</b> Ogni giocatore che controlla un obiettivo <b><i>Cavalcatura</i></b> sceglie: rivela una carta incontro oppure scegli il nemico con il valore di ingaggio più alto senza un aggregato e aggrega un obiettivo <b><i>Cavalcatura</i></b> che controlli a quel nemico come obiettivo protetto. Se nessun giocatore controlla un obiettivo <b><i>Cavalcatura</i></b>, Sete di Sangue di Cavallo guadagna impeto.</p><p class='flavor-text'>“Vedrete che pasteggeremo con carne di cavallo, o con qualcosa di meglio ancora.” —Uglúk, Le Due Torri</p>");
            AddHtml("Overburdened-CoE", "<p class='main-text'><b>Quando Rivelata:</b> Ogni giocatore che controlla più di 2 aggregati deve scartare 1 aggregato che controlla, se possibile (deve scartare 2 aggregati invece, se possibile, se controlla più di 5 aggregati). Se non vengono scartati aggregati tramite questo effetto, Oberato guadagna impeto.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Scarta 1 carta casuale dalla tua mano.</p>");
            AddHtml("The-Last-Alliance-CoE", "<p class='main-text'>Scegli due tratti, <b><i>A</i></b> e <b><i>B</i></b>. Ciascuno dei tuoi eroi iniziali e ciascun alleato nel tuo mazzo deve avere o il tratto stampato<i>A</i></b> o il tratto stampato <b><i>B</i></b>, ma non può averli entrambi. Devi avere almeno 1 eroe iniziale e 10 alleati nel tuo mazzo che possiedano ciascuno dei due tratti.</p><p class='main-text'><b>Preparazione:</b> Gira questa carta.</p><p class='flavor-text'>“Molto tempo fa la forza degli Elfi era più potente di adesso, e ancora non tutti gli Uomini erano ridotti in schiavitù.”<br/>—Gandalf, La Compagnia dell’Anello</p>");
            AddHtml2("The-Last-Alliance-CoE", "<p class='main-text'>Fintanto che controlli più personaggi <b><i>A</i></b> che personaggi <b><i>B</i></b>, riduci di 1 il costo del primo alleato <b><i>B</i></b> che giochi questo turno, e viceversa.</p><p class='main-text'><b>Azione:</b> Esaurisci L’Ultima Alleanza per scegliere una carta nella tua mano o una carta in gioco che controlli. Sostituisci ogni istanza stampata di “<b><i>A</i></b>” o “<b><i>B</i></b>” nel testo di gioco di quella carta con “<b><i>A</i></b> oppure <b><i>B</i></b>” fino alla fine del turno.</p>");
            AddHtml("Drive-them-Away-CoE", "<p class='flavor-text'>Seguendo la Grande Strada Occidentale, siete giunti ad un accampamento dei Rohirrim all’ombra della verde e spoglia collina di Erelas. Ma la quiete del vostro viaggio è stata interrotta da un improvviso attacco dalle ombre! Il rumore stridente dell’acciaio e le grida degli uomini raggiungono presto le vostre orecchie. Dovete respingerli e impedire che l’accampamento venga distrutto!</p><p class='main-text'><b></b> <b>Preparazione:</b> Metti Capobanda dei Briganti da parte, fuori dal gioco. Aggiungi Beni Trafugati all’area di allestimento. Ogni giocatore cerca nel mazzo incontri un nemico differente e lo aggiunge all’area di allestimento. Rimescola il mazzo incontri.</p>");
            AddHtml2("Drive-them-Away-CoE", "<p class='main-text'><b>Obbligato:</b> Dopo che un nemico viene distrutto, piazza 1 segnalino risorsa su questa scena.</p><p class='main-text'>Azione di Incontri: Rimuovi 5 segnalini progresso da questa ricerca per scegliere una carta in una pila di saccheggio e scartarla. Massimo una volta per turno per giocatore.</p><p class='main-text'><b>I giocatori non possono avanzare a meno che non ci siano X segnalini risorsa su questa scena, dove X è pari a 1, più 2{PerPlayer} (2 per giocatore).</b></p>");
            AddHtml("Ambush-CoE", "<p class='flavor-text'>Vi siete fatti strada combattendo fino al cuore dell’accampamento, ma i briganti si sono radunati intorno al loro capo e lanciano un feroce contrattacco.</p><p class='main-text'><b></b> <b>Quando Rivelata:</b> Termina la fase attuale. Aggiungi Capobanda dei Briganti all’area di allestimento. Ogni giocatore non ingaggiato con un nemico cerca nel mazzo incontri e nella pila degli scarti degli incontri un nemico e lo mette in gioco ingaggiato con sé. Rimescola il mazzo incontri.</p>");
            AddHtml2("Ambush-CoE", "<p class='main-text'><b>Obbligato:</b> Dopo che un nemico entra in gioco, compie delle verifiche di ingaggio.</p><p class='main-text'>Azione di Pianificazione: Rimuovi 5 segnalini progresso da questa scena per scegliere una carta in una pila di saccheggio. Il proprietario di quella carta la può scartare o la può giocare senza pagarne il costo. Massimo quattro volte per turno per il gruppo.</p><p class='main-text'><b>Quando Capobanda dei Briganti viene sconfitto, i giocatori vincono la partita.</b></p>");
            AddHtml("Battle-for-the-Hill-CoE", "<p class='flavor-text'>Dei Dunlandiani e dei Rohirrim traditori hanno catturato il faro di Erelas. Dovete riconquistare la collina a tutti i costi!</p><p class='main-text'><b></b> <b>Preparazione:</b> Metti Schiera di Rohan da parte, fuori dal gioco. Aggiungi Faro di Erelas e 1{PerPlayer} Dunlandiano Furioso all’area di allestimento. Aggiungi la ricerca secondaria a due facce all’area di allestimento, con la faccia Riconquistare il Faro rivolta verso l’alto. Cerca nel mazzo incontri 1 copia di Prateria Sconfinata e rendila il luogo attivo. Rimescola il mazzo incontri.</p>");
            AddHtml2("Battle-for-the-Hill-CoE", "<p class='main-text'><b>Obbligato:</b> Dopo aver piazzato un qualsiasi ammontare di segnalini progresso su questa scena, ogni giocatore deve scegliere: esaurisci un personaggio che controlli oppure infliggi 1 danno a Faro di Erelas.</p><p class='main-text'><b>Questa scena non può essere completata fintanto che i Dunlandiani controllano il faro.</b></p>");
            AddHtml("Hold-the-Hilltop-CoE", "<p class='flavor-text'>Mentre i vostri nemici si preparano per assalire nuovamente il faro, potete vedere le luci delle torce delle truppe di Gálmód che si stanno radunando tra le colline sottostanti.</p><p class='main-text'><b></b> <b>Quando Rivelata:</b> Piazza segnalini progresso su Difendere il Faro (ignorando il luogo attivo) fino a che non ha X segnalini progresso, dove X è pari a 2 più 1{PerPlayer}. Rimescola la pila degli scarti degli incontri nel mazzo incontri. Ogni giocatore scarta carte dal mazzo incontri fino a che non viene scartato un nemico e aggiunge quel nemico all’area di allestimento.<p>");
            AddHtml2("Hold-the-Hilltop-CoE", "<p class='main-text'>Tempo 3.</p><p class='main-text'><b>Obbligato:</b> Alla fine del turno, infliggi 1 danno a Faro di Erelas (2 danni invece se ci sono 3 o più giocatori nella partita). Poi, se non ci sono più segnalini tempo su questa scena, avanza immediatamente alla scena 3.</p><p class='main-text'><b>Obbligato:</b> Dopo che Difendere il Faro viene scelta come ricerca attuale, rivela una carta degli incontri.</p><p class='main-text'><b>Se i Dunlandiani controllano il faro, i giocatori perdono la partita.</b></p>");
            AddHtml("The-Last-Push-CoE", "<p class='flavor-text'>Dovete prepararvi a resistere ad un ultimo assalto mentre le forze di Gálmód si apprestano a fiancheggiare i nemici.</p><p class='main-text'><b></b> <b>Quando Rivelata:</b> Rimuovi tutti i segnalini progresso da Difendere il Faro e girala. Metti Schiera di Rohan in gioco sotto il controllo del primo giocatore. Rimescola la pila degli scarti degli incontri nel mazzo incontri. Ogni giocatore scarta carte dalla cima del mazzo incontri fino a che non scarta un nemico e aggiunge quel nemico all’area di allestimento.</p>");
            AddHtml2("The-Last-Push-CoE", "<p class='main-text'></b> Aggiungi 2 alla {Threat} totale dell’area di allestimento per ogni danno su Faro di Erelas.</p><p class='main-text'><b>Obbligato:</b> All’inizio della fase di combattimento, ogni nemico ingaggiato compie un attacco immediato.</p><p class='main-text'><b>Obbligato:</b> Dopo che un personaggio lascia il gioco, infliggi 1 danno a Faro di Erelas.</p><p class='main-text'><b>Alla fine del turno, se i Dunlandiani controllano il faro, i giocatori perdono la partita, mentre se i giocatori controllano il faro, vincono la partita.</b></p>");
            AddHtml("Thieves-and-Coursers-CoE", "<p class='flavor-text'>Gli orchi hanno invaso l’Estfalda, razziando queste terre e catturando i preziosi cavalli da guerra di Re Thengel.</p><p class='main-text'><b></b> <b>Preparazione:</b> Aggiungi Destrieri Rubati all’area di allestimento. Metti Thruk, gli obiettivi <b><i>Cavalcatura</i></b> e gli 8 nemici del set degli incontri Armata degli Orchi da parte, fuori dal gioco. Rimescola il mazzo incontri. Il primo giocatore rivela Zoccoselce e Folgocrino e li aggiunge all’area di allestimento. Poi, ogni altro giocatore scarta carte dalla cima del mazzo incontri fino a che non viene scartato un nemico o un luogo e aggiunge quella carta all’area di allestimento.</p>");
            AddHtml2("Thieves-and-Coursers-CoE", "<p class='flavor-text'>Se riuscirete a recuperare Zoccoselce e Folgocrino, potreste riuscire a fermare l’armata principale degli Orchi prima che raggiunga Aldburg.</p><p class='main-text'>Questa scena non può essere completata a meno che i giocatori non controllino 2 obiettivi <b><i>Cavalcatura</i></b>.</p>");
            AddHtml("The-King's-Stallion-CoE", "<p class='flavor-text'>Il condottiero Thruk si è unito alla battaglia, in groppa al poderoso destriero Felaróf, gridando ordini ai suoi sottoposti. “A raccolta!” sbraita. “Stasera banchetteremo con carne di cavallo!”</p><p class='main-text'><b></b> <b>Quando Rivelata:</b> Se è la fase di combattimento, termina la fase. Rimescola la pila degli scarti degli incontri nel mazzo incontri. Il primo giocatore aggiunge Thruk all’area di allestimento e gli aggrega Felaróf come obiettivo protetto. Ogni altro giocatore scarta carta dalla cima del mazzo incontri fino a che non scarta un nemico e mette quel nemico in gioco ingaggiato con sé.</p>");
            AddHtml2("The-King's-Stallion-CoE", "<p class='main-text'></b> Thruk non può subire danni a meno che non ci siano almeno 4 segnalini progresso su questa scena.</p><p class='main-text'><b>Questa scena non può essere completata a meno che i giocatori non controllino 3 obiettivi <b><i>Cavalcatura</i></b>.</b></p>");
            AddHtml("The-Sweeping-Tide-CoE", "<p class='flavor-text'>La piena potenza dell’armata degli orchi ha risposto al richiamo urlato da Thruk. Dovete prepararvi a compiere un’ultima carica per respingerli nelle colline.</p><p class='main-text'><b></b> <b>Quando Rivelata:</b> Se è la fase di combattimento, termina la fase. Rimescola la pila degli scarti degli incontri nel mazzo incontri. Cerca Thruk nel riquadro vittoria e aggiungilo all’area di allestimento. Ogni giocatore sceglie 2 nemici dal set degli incontri Armata degli Orchi messo da parte e li mette in gioco ingaggiati con sé. Uno dei nemici messo in gioco da questo effetto deve essere Battaglione Orchesco.</p>");
            AddHtml2("The-Sweeping-Tide-CoE", "<p class='main-text'></b> Salta il passo di allestimento. Se in un qualsiasi momento il mazzo incontri è vuoto, rimescola la pila degli scarti degli incontri nel mazzo incontri.</p><p class='main-text'><b>Obbligato:</b> Quando un nemico compie un attacco, assegnagli una carta ombra addizionale.</p><p class='main-text'><b>Se un obiettivo <b><i>Cavalcatura</i></b> viene aggregato ad un nemico, i giocatori perdono la partita. Se non ci sono nemici in gioco, i giocatori vincono la partita.</b></p>");
            //AddHtml("Reclaim-the-Beacon-CoE", "<p class='flavor-text'>Riconquistate il faro, prima che i Dunlandiani lo danneggino o peggio.</p><p class='main-text'>I Dunlandiani controllano il faro. I nemici nell'area di allestimento non possono essere ingaggiati intenzionalmente né subire danni.</p><p class='main-text'>Non puo avere più di X segnalini progresso, dove X è pari a 2, più 1{PerPlayer}. Se Riconquistare il Faro ha X segnalini progresso giralo (mantenendo tutti i sengalini progresso).</p>");
        }

        private void TheAldburgPlot()
        {
            AddHtml("Dunlending-Agents-TAP", "<p class='main-text'>Se i giocatori controllano 2 o più obiettivi <b><i>Indizio</i></b>, Agenti del Dunland prende +2 {Attack}.</p><p class='main-text'><b>Obbligato:</b> Dopo che Agenti del Dunland attacca e distrugge un personaggio che controlli, infliggi 1 danno ad ogni eroe che controlli con un obiettivo <b><i>Indizio</i></b> aggregato.</p><p class='flavor-text'>I Dunlandiani e molti altri uomini della guarnigione del Torrione lavoravano… –Il Ritorno del Re</p>");
            AddHtml("There-and-Back-Again-TAP", "<p class='main-text'>Segretezza 1.</p><p class='main-text'><b>Risposta:</b> Dopo che un alleato <b><i>Hobbit</i></b> ha lasciato il gioco, cerca nella tua mano e nella tua pila degli scarti una copia di quell’alleato e mettilo in gioco, sotto il controllo di un qualsiasi giocatore.</p><p class='flavor-text'>In realtà le seccature legali si trascinarono per anni. Ci volle un bel pò di tempo prima che il signor Baggins fosse effettivamente riammesso nel mondo dei vivi. – Lo Hobbit</p>");
            AddHtml("Raven-Signet-Ring-TAP", "<p class='main-text'><b>Quando Rivelata:</b> Aggiungi L’uomo dal Labbro Spaccato all’area di allestimento.</p><p class='main-text'>Se non è aggregato, aggregalo all’eroe con meno obiettivi <b><i>Indizio</i></b> aggregati.</p><p class='main-text'><b>Obbligato:</b> Dopo che l’eroe aggregato diventa pronto, scarta tutte le risorse dalla sua riserva.</p><p class='flavor-text'>Il prigioniero ha riferito alle guardie di una persona chiamata “il capo delle spie”. L’unico indizio al momento è questo anello, che si pensa appartenga a costui.</p>");
            AddHtml("Decrepit-Hovel-TAP", "<p class='main-text'>I costi in risorse degli alleati devono essere pagati dalla riserva di un singolo eroe.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Scarta 1 risorsa da un eroe che controlli.</p><p class='flavor-text'>“Vanno in giro, rubando o ’raccogliendo’, come dicono loro.” –Tom Cotton, Il Ritorno del Re</p>");
            AddHtml("Garrison-Barracks-TAP", "<p class='main-text'>Immune agli effetti delle carte dei giocatori. X è pari al doppio del numero di obiettivi <b><i>Indizio</i></b> controllati dai giocatori.</p><p class='main-text'>Viaggio: Mescola nel mazzo incontri tutti i nemici <b><i>Dunland</i></b> messi da parte.</p><p class='main-text'><b>Obbligato:</b> Quando Caserma della Guarnigione viene esplorata, gira questa carta e rivelala.</p><p class='flavor-text'>La guardia cittadina ha occhi e orecchie in tutta Aldburg. Ma chi sorveglia le guardie?</p>");
            AddHtml("Prison-Key-TAP", "<p class='main-text'><b>Quando Rivelata:</b> Aggiungi Scoppia un Incendio e Il Prigioniero in Bianco all’area di allestimento.</p><p class='main-text'>Se non è aggregato, aggregalo all’eroe con meno obiettivi <b><i>Indizio</i></b> aggregati.</p><p class='main-text'><b>Obbligato:</b> Dopo che l’eroe aggregato diventa pronto, scarta un aggregato non <b><i>Indizio</i></b> da esso.</p><p class='flavor-text'>Ferito e ormai in fuga, il Capo delle Spie si è lasciato sfuggire un dettaglio fondamentale: uno dei suoi agenti è già in movimento, diretto verso la prigione cittadina.</p>");
            AddHtml("The-Whisperer-TAP", "<p class='main-text'><b>Obbligato:</b> Alla fine del turno, fai ritornare Colei che Sussurra nell’area di allestimento, se possibile. Colei che Sussurra compie un attacco immediato contro il primo giocatore. Il danno che verrebbe inflitto da questo attacco viene invece assegnato come incremento della minaccia del primo giocatore.</p><p class='main-text'><b>Obbligato:</b> Quando Colei che Sussurra viene sconfitta, gira questa carta e rivelala.</p>");
            AddHtml("Whisperer-in-the-Darkness-TAP", "<p class='flavor-text'>La taverna era piena di voci riguardo una assassina che viene chiamata “Colei che Sussurra”. Se volete svelare la sua identità, dovrete cercarla e fermarla prima che colpisca.</p><p class='main-text'><b>Obbligato:</b> Dopo che un luogo viene esplorato, incrementa di 1 la minaccia di ogni giocatore.</p><p class='main-text'><b>Obbligato:</b> Dopo che questa ricerca viene completata, aggiungi Colei che Sussurra all’area di allestimento.</p>{victory:10}");
            AddHtml("Traitor's-Confession-TAP", "<p class='main-text'>Se non è aggregato, aggregalo all’eroe con meno obiettivi <b><i>Indizio</i></b> aggregati.</p><p class='main-text'><b>Obbligato:</b> Dopo che l’eroe aggregato diventa pronto, scarta una carta casuale dalla tua mano.</p><p class='flavor-text'>Con il suo ultimo respiro, il traditore Ossian vi ha rivelato che Gálmód gli ha ordinato di uccidere uno dei membri della sua stessa éored: il testimone di un terribile tradimento.</p>");
            AddHtml("False-Lead-TAP", "<p class='main-text'>Condanna 1.</p><p class='main-text'><b>Quando Rivelata:</b> Ogni giocatore rimuove dalla ricerca un eroe che controlla con un obiettivo <b><i>Indizio</i></b> aggregato, se possibile. Poi, ogni giocatore esaurisce un eroe che controlla e che non è stato coinvolto nella ricerca.</p><p class='flavor-text'>“…state ora cercando di ingannarmi con una vostra invenzione?” –Frodo Baggins, Le Due Torri</p>");
            AddHtml("Red-Fletched-Arrow-TAP", "<p class='main-text'>Se non è aggregato, aggregalo all’eroe con meno obiettivi <b><i>Indizio</i></b> aggregati.</p><p class='main-text'><b>Obbligato:</b> Dopo che l’eroe aggregato diventa pronto, incrementa di 1 la tua minaccia.</p><p class='flavor-text'>Il quartiermastro dell’armeria vi informa che ha forgiato lui stesso il pugnale, così come questa freccia inusuale, per conto di Lord Gálmód.</p>");
            AddHtml("Weep-No-More-TAP", "<p class='main-text'>Solo 3 copie di Freniamo il Pianto! nel riquadro vittoria.</p><p class='main-text'><b>Risposta:</b> Dopo che l’effetto “Quando Rivelata” o l’effetto ombra di una carta perfidia è stato risolto, aggiungi Freniamo il Pianto! al riquadro vittoria per aggiungere quella carta perfidia al riquadro vittoria.</p><p class='flavor-text'>“Può darsi che un giorno almeno saremo vendicati. Facciamoci coraggio, e freniamo il pianto!”<br/>–Aragorn, La Compagnia dell’Anello</p>");
            AddHtml("Gavin-TAP", "<p class='main-text'>Non può avere aggregati.</p><p class='main-text'><b>Risposta:</b> Dopo aver risolto l’allestimento di una carta degli incontri senza Impeto, esaurisci Gavin per dare Impeto a quella carta e aggiungere 1 risorsa alla riserva di un eroe <b><i>Esploratore</i></b> che controlli. Se la prossima carta degli incontri rivelata non è un luogo, scartala senza risolverne gli effetti.</p>");
            AddHtml("Young-Tom-TAP", "<p class='main-text'>Durante il turno in cui Giovane Tom è entrato in gioco, i nemici prendono +5 al valore di ingaggio.</p><p class='flavor-text'>“Non tutto funziona perfettamente,” rispose Frodo. “Ma certo i giardinieri sono assai stimati.” –Le Due Torri</p>");
            AddHtml("Great-Mead-Hall-TAP", "<p class='main-text'>Immune agli effetti delle carte dei giocatori.</p><p class='main-text'>Viaggio: I giocatori devono incrementare di X la propria minaccia, dove X è il numero di nemici nell’area di allestimento.</p><p class='main-text'><b>Obbligato:</b> Quando Grande Sala dei Banchetti viene esplorata, gira questa carta e rivelala.</p><p class='flavor-text'>Avete seguito le tracce degli agenti di Colei che Sussurra fino alle sale del Maresciallo Éomund. Fino a dove si insinua questa cospirazione?</p>");
            AddHtml("Fatty-Bolger-TAP", "<p class='main-text'><b>Preparazione:</b> Riduci di 2 la tua minaccia.</p><p class='main-text'><b>Risposta:</b> Incrementa di 4 la tua minaccia ed esaurisci Grassotto Bolgeri per cancellare una carta nemico non unico appena rivelata dal mazzo degli incontri e aggiungerla al riquadro vittoria. (Solo una volta per partita per il gruppo.)</p><p class='flavor-text'>Grassotto Bolgeri non aveva perso tempo. Appena vide strisciare le forme scure dal giardino verso la casa, seppe che non aveva altra scelta: correre o perire. –La Compagnia dell’Anello</p>");
            AddHtml("Buckland-Shirriff-TAP", "<p class='main-text'>Devozione. Gittata.</p><p class='main-text'><b>Risposta:</b> Dopo che un nemico che condivide un <b><i>Tratto</i></b> con un nemico nel riquadro vittoria viene aggiunto all’area di allestimento, spendi 1 risorsa {sphere:Lore} per mettere in gioco Guardacontea della Terra di Buck dalla tua mano, sotto il tuo controllo.</p>");
            AddHtml("Town-Guard-TAP", "<p class='main-text'>Se i giocatori controllano 2 o più obiettivi <b><i>Indizio</i></b>, Guardia Cittadina prende +2 {Threat}.</p><p class='main-text'><b>Obbligato:</b> Dopo che Guardia Cittadina attacca e distrugge un personaggio che controlli, scarta 1 risorsa da ogni eroe che controlli con un obiettivo <b><i>Indizio</i></b> aggregato.</p><p class='flavor-text'>“É vana la mia speranza che tu sia stato inviato in mio aiuto in quest’ora di dubbio e di bisogno?” –Éomer, Le Due Torri</p>");
            AddHtml("The-Spy-Master-TAP", "<p class='main-text'><b>Obbligato:</b> Quando viene assegnata una carta ombra senza alcun effetto ombra a Il Capo delle Spie, infliggi 1 danno al personaggio in difesa e assegna un’altra carta ombra a Il Capo delle Spie. (Solo 2 volte per attacco.)</p><p class='main-text'><b>Obbligato:</b> Quando Il Capo delle Spie viene sconfitto, gira questa carta e rivelala.</p>");
            AddHtml("Evil-Under-the-Moon-TAP", "<p class='flavor-text'>Sapendo che una fazione di ribelli si è insediata ad Aldburg, Ealdwulf vi chiede aiuto per snidarli. </p><p class='main-text'> <b>Preparazione:</b> Metti da parte, fuori dal gioco, tutte le ricerche secondarie degli incontri, ogni copia di Guardia Cittadina e ogni copia di Agenti del Dunland. Metti da parte, fuori dal gioco, ogni carta incontri a due facce, con la faccia <b><i>Indizio</i></b> rivolta verso il basso. Aggiungi Caserma della Guarnigione e La Coda del Cavallo all’area di allestimento. Cerca nel mazzo incontri un nemico e aggiungilo all’area di allestimento (2 nemici differenti se ci sono 3 o più giocatori). Rimescola il mazzo incontri.</p>");
            AddHtml2("Evil-Under-the-Moon-TAP", "<p class='flavor-text'>Da dove cominciare le indagini? Meglio chiedere l’aiuto delle guardie cittadine oppure investigare il mondo sommerso dei criminali partendo da una sordida taverna?</p><p class='main-text'> I nemici non possono essere ingaggiati intenzionalmente.</p><p class='main-text'><b>Obbligato:</b> Alla fine del turno, incrementa di 1 la minaccia di ogni giocatore.</p><p class='main-text'><b>Se i giocatori controllano 1 o più obiettivi <i>Indizio</i>, avanza alla scena successiva.</b></p>");
            AddHtml("The-Prisoner-in-White-TAP", "<p class='flavor-text'>Per cercare di coprire le sue tracce, l’agente del Capo delle Spie si è infiltrato nella prigione.</p><p class='main-text'>I giocatori non possono viaggiare verso i luoghi né rendere attivi dei luoghi tramite gli effetti delle carte dei giocatori.</p><p class='main-text'><b>Obbligato:</b> Dopo che questa ricerca viene completata, aggiungi Ossian all’area di allestimento.</p>{victory:10}");
            AddHtml("Sudden-Ambush-TAP", "<p class='main-text'><b>Quando Rivelata:</b> Il primo giocatore deve scegliere: incrementa di 4 la tua minaccia oppure il nemico più in alto nella pila degli scarti degli incontri ti ingaggia e compie un attacco immediato. Devi dichiarare il personaggio pronto con la {Willpower} più alta che controlli come difensore contro questo attacco, se possibile.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Risolvi l’effetto “quando rivelata” di questa carta.</p>");
            AddHtml("Spreading-Inferno-TAP", "<p class='main-text'>Protetto (luogo).</p><p class='main-text'><b>Obbligato:</b> All’inizio della fase di viaggio, scarta il luogo aggregato, incrementa di 1 la minaccia di ogni giocatore e scarta l’alleato con il costo stampato più basso controllato da ciascun giocatore. Rivela Incendio Dilagante.</p><p class='main-text'><b>Obbligato:</b> Quando il luogo aggregato viene esplorato, gira questa carta e rivelala.</p>");
            AddHtml("The-Armoury-TAP", "<p class='main-text'>Immune agli effetti delle carte dei giocatori.</p><p class='main-text'>Viaggio: Ogni giocatore deve esaurire il personaggio pronto che controlla con la {Defense} più alta.</p><p class='main-text'><b>Obbligato:</b> Quando L’Armeria viene esplorata, gira questa carta e rivelala.</p><p class='flavor-text'>Colei che Sussura impugnava un pugnale molto particolare e dalla foggia a voi familiare. Forse il quartiermastro dell’armeria potrebbe dirvi di più in merito.</p>");
            AddHtml("The-Man-with-the-Twisted-Lip-TAP", "<p class='flavor-text'>Avete iniziato a seguire le tracce del misterioso capo delle spie di Aldburg, anche se è difficile capire chi è il gatto e chi il topo in questo gioco.</p><p class='main-text'><b>Obbligato:</b> Alla fine del turno, incrementa di 1 la minaccia di ogni giocatore.</p><p class='main-text'><b>Obbligato:</b> Dopo che questa ricerca viene completata, aggiungi Il Capo delle Spie all’area di allestimento.</p>{victory:10}");
            AddHtml("The-Horse's-Tail-TAP", "<p class='main-text'>Immune agli effetti delle carte dei giocatori. X è pari al doppio del numero di obiettivi <b><i>Indizio</i></b> controllati dai giocatori.</p><p class='main-text'>Viaggio: Mescola nel mazzo incontri tutti i nemici <b><i>Guarnigione</i></b> messi da parte.</p><p class='main-text'><b>Obbligato:</b> Quando La Coda del Cavallo viene esplorata, gira questa carta e rivelala.</p><p class='flavor-text'>Recarvi qui potrebbe attirare su di voi i sospetti delle guardie cittadine, ma questo mistero non può essere risolto con mezzi ordinari.</p>");
            AddHtml("Letter-from-Dunland-TAP", "<p class='main-text'>Se non è aggregato, aggregalo all’eroe con meno obiettivi <b><i>Indizio</i></b> aggregati.</p><p class='main-text'><b>Obbligato:</b> Dopo che l’eroe aggregato diventa pronto, scarta 2 segnalini progresso da ciascun luogo e da ciascuna carta ricerca.</p><p class='flavor-text'>Trovate, ben nascosta, una lettera sigillata e scritta in una versione incerta della lingua dei Rohirrim. Porta la firma “Grimrede” ed è destinata ad un uomo che pensavate fosse un vostro amico, Gálmód.</p>");
            AddHtml("Lily-Cotton-TAP", "<p class='main-text'><b>Azione:</b> Esaurisci Lily Cotton per rendere pronto un alleato <b><i>Hobbit</i></b> differente e aggiungere la {Willpower}, {Attack} e {Defense} di Lily Cotton rispettivamente alla {Willpower}, {Attack} e {Defense} di quell’alleato fino alla fine della fase. Alla fine della fase, se quell’alleato è ancora in gioco, fallo tornare nella mano del suo proprietario. (Solo una volta per fase.)</p><p class='flavor-text'>Del resto, poco o niente di magico c’è in loro tranne il modo comunissimo con cui spariscono silenziosamente e velocemente…<br/>–Lo Hobbit</p>");
            AddHtml("Rohirric-Coins-TAP", "<p class='main-text'><b>Quando Rivelata:</b> Aggiungi Colei che Sussurra nelle Tenebre all’area di allestimento.</p><p class='main-text'>Se non è aggregato, aggregalo all’eroe con meno obiettivi <b><i>Indizio</i></b> aggregati.</p><p class='main-text'><b>Obbligato:</b> Dopo che l’eroe aggregato diventa pronto, esaurisci un altro personaggio che controlli.</p><p class='flavor-text'>Una figura sconosciuta sbatte contro il vostro tavolo. Dopo essersi scusato a lungo, l’uomo svanisce nella notte. Quando notate il sacchetto di monete che ha lasciato dietro di sé, l’uomo è ormai sparito.</p>");
            AddHtml("Ossian-TAP", "<p class='main-text'>Immune agli effetti delle carte dei giocatori. Ossian non può subire più di X danni ogni turno, dove X è il numero di obiettivi <b><i>Indizio</i></b> controllati dai giocatori.</p><p class='main-text'><b>Obbligato:</b> Quando Ossian viene sconfitto, gira questa carta e rivelala.</p><p class='flavor-text'>Trovate Ossian chinato sopra un corpo vestito di bianco. Sentendovi avvicinare, Ossian si volta.</p>");
            AddHtml("Fear-Fire-Foes-TAP", "<p class='main-text'>Segretezza 1. Gioca solo se non controlli un eroe <b><i>Guerriero</i></b>.</p><p class='main-text'><b>Azione:</b> Scegli un nemico non unico e un attributo ({Threat}, {Attack} o {Defense}). Fino alla fine della fase, riduci a 0 l’attributo scelto di quel nemico.</p><p class='flavor-text'>Si sentivano in lontananza rispondere altri corni. Il grido d’allarme si diffondeva.<br/>–La Compagnia dell’Anello</p>");
            AddHtml("Taken-by-the-Night-TAP", "<p class='main-text'><b>Quando Rivelata:</b> Ogni giocatore deve scartare l’alleato con il costo stampato più basso che controlla. Se non viene scartato almeno un alleato con costo 2 o superiore tramite questo effetto, Preso dalla Notte guadagna Impeto.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Il nemico attaccante prende +1 {Attack} (+2 {Attack} invece se questo nemico sta attaccando dall’area di allestimento).</p>");
            AddHtml("Silver-Dagger-TAP", "<p class='main-text'><b>Quando Rivelata:</b> Aggiungi Un Caso di Identità e Un Raccolto Rosso all’area di allestimento.</p><p class='main-text'>Se non è aggregato, aggregalo all’eroe con meno obiettivi <b><i>Indizio</i></b> aggregati.</p><p class='main-text'><b>Obbligato:</b> Dopo che l’eroe aggregato diventa pronto, infliggi 1 danno a quell’eroe.</p><p class='flavor-text'>Colei che Sussurra è scappata tra le strade di Aldburg, ma si è lasciata alle spalle un’arma peculiare e di valore.</p>");
            AddHtml("Gathering-Clues-TAP", "<p class='flavor-text'>Sembra che la cittadina di Aldburg sia piena di misteri. Dietro ogni angolo vi aspetta un nuovo intrigo, e dietro ogni intrigo una lama affilata e disperata. Dovrete scavare a fondo, per scoprire la verità prima che sorga il sole. </p>");
            AddHtml2("Gathering-Clues-TAP", "<p class='flavor-text'>Sembra che Aldburg faccia da sfondo ad un mistero a due teste. Se solo riusciste ad afferrare un singolo filo di questa fitta trama potreste essere in grado di disfare questa ragnatela di corruzione e astio. La mezzanotte è ormai passata. Il tempo inizia a scarseggiare.</p><p class='main-text'> <b>Risposta</b>: Alla fine del turno, se i giocatori controllano almeno 4 obiettivi <b><i>Indizio</i></b>, possono avanzare alla scena 3A.</p>");
            AddHtml("Horn-call-of-Buckland-TAP", "<p class='main-text'>Aggrega ad un eroe {sphere:Lore} o <b><i>Hobbit</i></b>.</p><p class='main-text'><b>Risposta:</b> Dopo che hai ingaggiato un nemico che condivide un <b><i>Tratto</i></b> con un nemico nel riquadro vittoria, esaurisci Richiamo del Corno di Buck per rendere pronto l’eroe aggregato. Poi, quel nemico prende −1 {Attack} e −1 {Defense} fino alla fine del turno.</p><p class='flavor-text'>In quel momento dagli alberi vicini giunse il suono di un corno. Lacerò la notte come un incendio su di una collina.<br/>–La Compagnia dell’Anello</p>");
            AddHtml("Well-lit-Hall-TAP", "<p class='main-text'>Fintanto che Sala Illuminata è nell’area di allestimento, non si possono piazzare più di 3 segnalini progresso su una carta ricerca come conseguenza di aver avuto successo nella ricerca.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Il nemico attaccante prende +1 {Attack} (+2 {Attack} invece se il personaggio in difesa ha un aggregato <b><i>Indizio</i></b>).</p>");
            AddHtml("A-Fire-Rises-TAP", "<p class='flavor-text'>C’è qualcosa di marcio ad Aldburg. Mentre state raccogliendo indizi, vedete fumo e fiamme sollevarsi da un edificio lì vicino.</p><p class='main-text'>Incrementa di 1 il costo per giocare ciascun evento.</p><p class='main-text'><b>Obbligato:</b> Dopo che questa ricerca viene completata, rivela Incendio Dilagante.</p>{victory:10}");
            AddHtml("Aldburg-Malcontent-TAP", "<p class='main-text'><b>Quando Rivelata:</b> Se i giocatori controllano 3 o più obiettivi <b><i>Indizio</i></b>, Sovversivo di Aldburg guadagna Impeto.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Se i personaggi controllano 2 o più obiettivi <b><i>Indizio</i></b>, fai ritornare il nemico attaccante nell’area di allestimento dopo questo attacco.</p>");
            AddHtml("Ruby-Cloak-pin-TAP", "<p class='main-text'>Se non è aggregato, aggregalo all’eroe con meno obiettivi <b><i>Indizio</i></b> aggregati.</p><p class='main-text'><b>Obbligato:</b> Dopo che l’eroe aggregato diventa pronto, scarta le prime 4 carte del tuo mazzo.</p><p class='flavor-text'>Anche in mezzo alla cenere riconoscete immediatamente questa spilla. Appartiene al vile Gálmód, il “fido” consigliere della regina Morwen.</p>");
            AddHtml("Misty-Street-TAP", "<p class='main-text'><b>Quando Rivelata:</b> Scarta tutti gli aggreati e i segnalini dall’alleato con il costo più alto controllato dal primo giocatore e aggregalo a faccia in giù sotto Strada Nebbiosa.</p><p class='main-text'><b>Obbligato:</b> Dopo che Strada Nebbiosa lascia il gioco, fai ritornare in gioco l’alleato aggregato ad esso, sotto il controllo del suo proprietario, a faccia in su e pronto.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Scarta un aggregato non <b><i>Indizio</i></b> che controlli.</p>");
            AddHtml("A-Case-of-Identity-TAP", "<p class='flavor-text'>Il mistero sulla vera identità di Colei che Sussurra rimane fitto. Ma dovete essere sulla pista giusta, vista la presenza di vari dei suoi sicari che vi aspettano nei vicoli.</p><p class='main-text'><b>Obbligato:</b> Alla fine del passo di allestimento, scarta carte dalla cima del mazzo incontri fino a che non viene scartato un nemico. Rivela quel nemico.</p><p class='main-text'><b>Obbligato:</b> Dopo che questa ricerca viene completata, aggiungi Grande Sala dei Banchetti all’area di allestimento.</p>{victory:10}");
            AddHtml("Red-Harvest-TAP", "<p class='flavor-text'>Con rapidità brutale gli uomini di Colei che Sussurra vi sono addosso. Dovrete combattere aspramente per farvi strada fino all’armeria.</p><p class='main-text'><b>Obbligato:</b> Dopo che un nemico ha ingaggiato un giocatore, quel giocatore infligge 2 danni ad un personaggio che controlla.</p><p class='main-text'><b>Obbligato:</b> Dopo che questa ricerca viene completata, rimescola la pila degli scarti del mazzo incontri nel mazzo incontri. Ogni giocatore scarta carte dal mazzo incontri finché non viene scartato un nemico e lo aggiunge all’area di allestimento. Aggiungi L’Armeria all’area di allestimento.</p>{victory:10}");
            AddHtml("The-Uncertain-Dawn-TAP", "<p class='flavor-text'>Quando il sole si alza sopra le strade di Aldburg, i vostri nemici, ormai messi all’angolo, si palesano. Avete raccolto i vostri indizi. Ora dovete metterli a frutto mentre combattete per le vostre vite - e per l’anima di Rohan.</p><p class='main-text'> <b>Quando Rivelata:</b> Rimuovi tutte le carte <b><i>Sospetto</i></b> e le ricerche secondarie degli incontri dal gioco. Aggiungi ogni nemico <b><i>Dunland</i></b> o <b><i>Guarnigione</i></b> messo da parte all’area di allestimento, se possibile.</p>");
            AddHtml2("The-Uncertain-Dawn-TAP", "<p class='main-text'> Un’Alba Incerta prende –5 punti ricerca per ogni obiettivo <b><i>Indizio</i></b> controllato dai giocatori.</p><p class='main-text'><b>Obbligato:</b> All’inizio della fase ricerca, scarta carte dalla cima del mazzo incontri fino a che non viene scartato un nemico. Rivela quel nemico e aggiungilo all’area di allestimento.</p><p class='main-text'><b>Questa scena non può essere superata se non alla fine del turno. Se i giocatori superano questa scena vincono la partita.</b></p>");
            AddHtml("A-Lesson-in-Caution-TAP", "<p class='main-text'>Segretezza 1.</p><p class='main-text'><b>Azione:</b> Pesca 1 carta. Poi, riduci di 2 la tua minaccia oppure la prossima carta che giochi in questa fase guadagna Segretezza 1.</p><p class='flavor-text'>Le impronte degli Hobbit sono leggere e difficili a rintracciarsi anche per un Ramingo… –Le Due Torri</p>");
            AddHtml("Widfast-TAP", "<p class='main-text'><b>Preparazione:</b> Cerca nella tua collezione un alleato <b><i>Creatura</i></b> e aggiungilo alla tua mano. Durante il primo turno, riduci di 2 il costo per giocare quell’alleato.</p><p class='main-text'><b>Azione di Viaggio:</b> Se c’è 1 solo luogo attivo, spendi 1 risorsa dalla riserva di Widfast per viaggiare verso un altro luogo, risolvendone gli effetti di <b>Viaggio</b>. (Ora ci sono 2 luoghi attivi.)</p>");
        }

        private void FireOnTheEastemnet()
        {
            AddHtml("Alagos-FotE", "<p class='main-text'>Non può avere aggregati limitati.</p><p class='main-text'><b>Risposta:</b> Dopo che un alleato <b><i>Aquila</i></b> è entrato nell’area di gioco di un giocatore, aggiungi 1 risorsa alla riserva di un eroe controllato da quel giocatore. (Solo una volta per turno.)</p><p class='flavor-text'>“Che cos’è tutto questo strepito nella foresta, stanotte?”<br/>—Il Signore delle Aquile, Lo Hobbit</p>");
            AddHtml("White-Mountain-Beastmaster-FotE", "<p class='main-text'><b>Obbligato:</b> Dopo che Ammaestratore dei Monti Bianchi ti ha ingaggiato, compie un attacco immediato oppure cattura i propri destrieri.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Il nemico attaccante compie un attacco addizionale, oppure cattura i propri destrieri.</p>");
            AddHtml("Veteran-Eagle-FotE", "<p class='main-text'>Non può avere aggregati limitati.</p><p class='main-text'><b>Risposta:</b> Dopo che Aquila Veterana è entrata nella tua area di gioco, ogni copia di Aquila Veterana che controlli prende +1 {Willpower}, +1 {Attack} e +1 {Defense} fino alla fine del turno.</p><p class='flavor-text'>Ma le aquile hanno occhi acuti e possono vedere cose piccole a grande distanza. —Lo Hobbit</p>");
            AddHtml("Hidden-Orc-Camp-FotE", "<p class='main-text'>Destrieri 1.</p><p class='main-text'>Campo Nascosto degli Orchi prende +1 {Threat} per ogni destriero su di esso.</p><p class='main-text'><b>Obbligato:</b> Alla fine della fase di combattimento, sposta un destriero da un luogo non <b><i>Campo</i></b> a Campo Nascosto degli Orchi.</p><p class='main-text'>Viaggio: Ogni giocatore esaurisce un personaggio che controlla.</p>{victory:1}");
            AddHtml("Chieftain-of-the-Skies-FotE", "<p class='main-text'>Devozione.</p><p class='main-text'>Non può avere aggregati limitati.</p><p class='main-text'><b>Risposta:</b> Dopo che Capitano dei Cieli è entrato nella tua area di gioco, rendi pronto un alleato che controlli.</p><p class='flavor-text'>Dietro di essi arrivarono tutti i loro vassalli dei monti del Nord… —Il Ritorno del Re</p>");
            AddHtml("Panicked-Horses-FotE", "<p class='main-text'><b>Quando Rivelata:</b> Sposta su ciascun nemico in gioco un destriero da Destrieri Selvaggi. Se ci sono meno di 1{PerPlayer} nemici in gioco, cerca nel mazzo incontri e nella pila degli scarti degli incontri un nemico e aggiungilo all’area di allestimento. Se hai cercato nel mazzo incontri, rimescolalo.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Il nemico attaccante prende +1 {Attack} (+2 {Attack} invece se ci sono più destrieri su Destrieri Selvaggi che su Destrieri Catturati).</p>");
            AddHtml("Golden-Crown-FotE", "<p class='main-text'>Aggregala ad un eroe <b><i>Aquila</i></b>. L’eroe aggregato guadagna il tratto <b><i>Nobile</i></b>.</p><p class='main-text'><b>Azione:</b> Esaurisci Corona Dorata per scegliere un alleato <b><i>Aquila</i></b> di cui sei il proprietario e sotto il controllo di un qualsiasi giocatore. Scarta quell’alleato dal gioco per aggiungere 1 risorsa alla riserva dell’eroe aggregato, oppure dai il controllo di quell’alleato ad un giocatore differente.</p>");
            AddHtml("Captured-Steeds-FotE", "<p class='main-text'><b>Obbligato:</b> Quando un nemico lascia il gioco in un qualsiasi modo che non sia l’essere stato sconfitto, sposta tutti i destrieri da quel nemico a Destrieri Catturati.</p><p class='main-text'><b>Obbligato:</b> Quando un luogo lascia il gioco in un qualsiasi modo che non sia l’essere stato esplorato, sposta tutti i destrieri da quel luogo a Destrieri Catturati.</p><p class='flavor-text'>I loro cavalli erano imponenti, forti e dall’andatura decisa…<br/>—Le Due Torri</p>");
            AddHtml("Wild-Steeds-FotE", "<p class='main-text'><b>Obbligato:</b> Dopo che un nemico è entrato in gioco, sposta un destriero da Destrieri Selvaggi a quel nemico.</p><p class='main-text'><b>Alla fine del turno, se non ci sono destrieri su Destrieri Selvaggi, i giocatori perdono la partita.</b></p><p class='flavor-text'>“Scommetto che c’è un sacco di buona roba che cresce selvatica in questo paese.” —Sam, Le Due Torri</p>");
            AddHtml("Roving-Herbmaster-FotE", "<p class='main-text'><b>Risposta:</b> Dopo che un giocatore ha ridotto la propria minaccia, cura 1 danno a un personaggio che controlli. Poi, puoi dare il controllo di Erborista Errante ad un altro giocatore. (Solo una volta per turno.)</p><p class='flavor-text'>“Spremine una nell’acqua, e lava la ferita finché non sia perfettamente pulita e io possa fasciarla. E ora tocca a te.”<br/>—Aragorn, La Compagnia dell’Anello</p>");
            AddHtml("Faunith-FotE", "<p class='main-text'>Non può avere aggregati limitati.</p><p class='main-text'>Azione di Ricerca: Gioca un alleato <b><i>Aquila</i></b> dalla tua mano. Poi, puoi esaurire quell’alleato per coinvolgerlo nella ricerca oppure per infliggere 1 danno ad un nemico nell’area di allestimento.</p><p class='flavor-text'>Puntarono diritti sui Nazgûl, emergendo all’improvviso dalle zone alte dell’aria… —Il Ritorno del Re</p>");
            AddHtml("Cracking-Horse-Whip-FotE", "<p class='main-text'><b>Quando Rivelata:</b> Per ogni personaggio pronto che un giocatore controlla, quel giocatore deve scegliere: esaurisci quel personaggio oppure infliggi 1 danno a quel personaggio. Se ci sono più destrieri su Destrieri Selvaggi che su Destrieri Catturati, Frusta Schioccante guadagna impeto.</p><p class='flavor-text'>“Dove c’è una frusta c’è una volontà, fannulloni.”<br/>—Il Ritorno del Re</p>");
            AddHtml("Entwash-Crossings-FotE", "<p class='main-text'>Destrieri 3.</p><p class='main-text'>Viaggio: Cerca nella pila degli scarti degli incontri un nemico e metti quel nemico in gioco ingaggiato con il primo giocatore.</p><p class='flavor-text'>In quella regione si incontravano molti mandriani, che vivevano all’aperto e sotto tende persino d’inverno. Ora tutta la regione era vuota… —Le Due Torri</p>{victory:1}");
            AddHtml("Guided-by-Fate-FotE", "<p class='main-text'>Aggregala ad un eroe.</p><p class='main-text'><b>Risposta:</b> Quando dovresti mescolare nel mazzo incontri una carta con la parola chiave Incontri messa da parte, mescolala invece nelle prime 5 carte del mazzo incontri.</p><p class='main-text'><b>Azione:</b> Se il mazzo incontri ha ancora al suo interno 5 o più carte, esaurisci Guidato dal Fato e incrementa di 1 la tua minaccia per mescolare la carta in cima alla pila degli scarti del mazzo incontri nelle prime 5 carte del mazzo incontri.</p>");
            AddHtml("Exhausted-Pursuit-FotE", "<p class='main-text'><b>Quando Rivelata:</b> Tutti i nemici in gioco catturano i propri destrieri, oppure esaurisci tutti i personaggi danneggiati.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Esaurisci un personaggio danneggiato che controlli.</p><p class='flavor-text'>Intontito com’era dal dolore e dalla stanchezza, Sam si destò… —Il Ritorno del Re</p>");
            AddHtml("Poachers-in-the-Night-FotE", "<p class='flavor-text'>Armati di fuoco e torce, gli orchi dell’armata si sono riorganizzati e stanno per rubare i preziosi destrieri di Rohan. </p><p class='main-text'> <b>Preparazione:</b> Metti Rive dell’Anduin, Battaglione Orchesco e Morgahk da parte fuori dal gioco. Rendi una copia di Stalle Saccheggiate il luogo attivo. Aggiungi all’area di allestimento Destrieri Selvaggi, Destrieri Catturati e una copia di Pascoli dell’Estemnet. Piazza su Destrieri Selvaggi 1 più 3{PerPlayer} destrieri. Ogni giocatore cerca nel mazzo incontri un nemico differente e lo aggiunge all’area di allestimento. Rimescola il mazzo incontri.</p>");
            AddHtml2("Poachers-in-the-Night-FotE", "<p class='flavor-text'>Usciti dalle stalle, affrontate a viso aperto i ladri di cavalli! Dovete respingerli nei campi prima che possano fuggire con gli animali rubati.</p><p class='main-text'> Destrieri 3{PerPlayer}.</p><p class='main-text'><b>Obbligato:</b> Alla fine del turno, sposta 1{PerPlayer} destrieri da questa scena a Destrieri Catturati. Se non rimangono più destrieri su questa scena, avanza alla scena successiva.</p>");
            AddHtml("Wolf-FotE", "<p class='main-text'>Solo 1 per mazzo. Non può avere aggregati. Ogni nemico in gioco prende +1 al proprio valore di ingaggio.</p><p class='main-text'><b>Risposta:</b> Dopo che Lupo è entrato in gioco, cerca nel tuo mazzo un alleato <b><i>Creatura</i></b> e aggiungilo alla tua mano. Fino alla fine della fase, quell’alleato non richiede una corrispondenza di risorse. Rimescola il tuo mazzo.</p><p class='flavor-text'>…il più feroce dei tre… —La Compagnia dell’Anello</p>");
            AddHtml("Orcish-Wrangler-FotE", "<p class='main-text'>Impeto.</p><p class='main-text'><b>Obbligato:</b> Quando Mandriano Orchesco attacca, sposta un destriero dal luogo attivo a Mandriano Orchesco.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Se ci sono più destrieri su Destrieri Selvaggi che su Destrieri Catturati, il nemico attaccante cattura i propri destrieri.</p>");
            AddHtml("Messenger-of-Manwe-FotE", "<p class='main-text'>Non può difendere né avere aggregati limitati.</p><p class='main-text'><b>Risposta:</b> Dopo che Messaggero di Manwë è entrato nella tua area di gioco, dai +1 {Willpower} a un eroe <b><i>Aquila</i></b> o <b><i>Nobile</i></b> che controlli fino alla fine del turno.</p><p class='main-text'><b>Risposta:</b> Dopo che Messaggero di Manwë è stato coinvolto nella ricerca, danne il controllo ad un altro giocatore.</p>");
            AddHtml("Morgahk-FotE", "<p class='main-text'>Immune agli effetti delle carte dei giocatori.</p><p class='main-text'><b>Obbligato:</b> All’inizio della fase di ricerca, Morgahk compie un attacco immediato contro il primo giocatore.</p><p class='main-text'><b>Obbligato:</b> Dopo che Morgahk ha attaccato e distrutto un personaggio, ogni nemico nell’area di allestimento cattura i propri destrieri.</p>{victory:5}");
            AddHtml("Grip-FotE", "<p class='main-text'>Solo 1 per mazzo. Non può avere aggregati. Ogni nemico in gioco prende +1 al proprio valore di ingaggio.</p><p class='main-text'><b>Risposta:</b> Dopo che Morso è entrato in gioco, cerca nel tuo mazzo un alleato <b><i>Creatura</i></b> e aggiungilo alla tua mano. Fino alla fine della fase, quell’alleato non richiede una corrispondenza di risorse. Rimescola il tuo mazzo.</p>");
            AddHtml("Hidden-Roosts-FotE", "<p class='main-text'><b>Risposta:</b> Quando un alleato <b><i>Aquila</i></b> lascia il gioco, il controllore di quell’alleato riduce di X la propria minaccia, dove X è il costo stampato di quell’alleato. Se X è 2 o meno, quel giocatore inoltre pesca 1 carta.</p><p class='flavor-text'>“Che il vento sotto le vostre ali vi sostenga fin dove il sole salpa e la luna cammina.”<br/>—Gandalf, Lo Hobbit</p>");
            AddHtml("Eastemnet-Pasture-FotE", "<p class='main-text'>Destrieri 1{PerPlayer}.</p><p class='main-text'>Non possono essere piazzati segnalini progresso sui luoghi nell’area di allestimento.</p><p class='flavor-text'>Gonfie praterie ondulate come un grande mare grigio.<br/>—Le Due Torri</p>{victory:1}");
            AddHtml("Open-Meadows-FotE", "<p class='main-text'>Destrieri 2.</p><p class='main-text'>Fintanto che Praterie Aperte si trova nell’area di allestimento guadagna: “<b>Obbligato:</b> Alla fine della fase di combattimento, aggiungi all’area di allestimento il nemico più in alto nella pila degli scarti degli incontri.”</p>{victory:1}");
            AddHtml("Breath-of-Arda-FotE", "<p class='main-text'>Azione di Incontri: Fai ritornare X alleati <b><i>Aquila</i></b> di cui sei il proprietario (sotto il controllo di qualsiasi giocatore) nella tua mano. Poi, metti in gioco ciascuno di quegli alleati uno alla volta sotto il controllo di un qualsiasi giocatore. Rimuovi Respiro di Arda dal gioco.</p><p class='flavor-text'>“Dobbiamo essere più veloci di qualunque vento…”<br/>—Gandalf, Il Ritorno del Re</p>");
            AddHtml("Expanded-Search-FotE", "<p class='flavor-text'>Gli orchi sembrano aver percepito un qualche odore e hanno iniziato a perlustrare il terreno vicino a dove vi eravate fermati a riposare.</p><p class='main-text'><b>Obbligato:</b> Dopo che un nemico è stato ingaggiato, muovi un destriero da Destrieri Selvaggi a Destrieri Catturati oppure quel nemico prende +3 {Attack} fino alla fine del turno.</p>{victory:10}");
            AddHtml("Shores-of-Anduin-FotE", "<p class='main-text'>Immune agli effetti delle carte dei giocatori.</p><p class='main-text'>Fintanto che Rive dell’Anduin è nel riquadro vittoria, la ricerca attuale guadagna “<b>Risposta:</b> Quando un nemico viene distrutto, sposta X destrieri da Destrieri Catturati a Destrieri Selvaggi. X è la {Threat} stampata del nemico appena distrutto.”</p><p class='main-text'>Viaggio: Ogni nemico nell’area di allestimento cattura i propri destrieri.</p>{victory:5}");
            AddHtml("Box-of-Earth-FotE", "<p class='main-text'>Giocala solo se controlli un eroe <b><i>Noldor</i></b> o <b><i>Silvano</i></b>.</p><p class='main-text'>Aggregala ad un eroe.</p><p class='main-text'><b>Azione:</b> Esaurisci Scatola di Terra per scegliere un luogo. Piazza 1 segnalino progresso su quel luogo e dagli il tratto <b><i>Foresta</i></b> fino alla fine del turno.</p><p class='flavor-text'>“Non ti aiuterà a percorrere con costanza la giusta via, né ti difenderà contro le insidie; ma se tu la conservi…allora forse sarai ricompensato.”<br/>—Galadriel, La Compagnia dell’Anello</p>");
            AddHtml("Orc-Lookout-FotE", "<p class='main-text'><b>Obbligato:</b> Alla fine della fase di combattimento, Sentinella Orchesca cattura i propri destrieri.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Il nemico attaccante non può subire danni in questo turno, oppure cattura i propri destrieri.</p>");
            AddHtml("Pillaged-Stables-FotE", "<p class='main-text'>Destrieri 4.</p><p class='main-text'><b>Obbligato:</b> Alla fine del turno, se ci sono ancora destrieri rimanenti su Stalle Saccheggiate, sposta 1 destriero da Stalle Saccheggiate a Destrieri Catturati. Il primo giocatore può incrementare di 2 la propria minaccia per cancellare questo effetto.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Se questo attacco distrugge un personaggio, il nemico attaccante cattura i propri destrieri.</p>{victory:1}");
            AddHtml("Relentless-Forces-FotE", "<p class='flavor-text'>Infuriati dopo la sconfitta del loro capo Thruk, gli orchi dell’armata si sono radunati nelle pianure, causando devastazione. “Avanti, avanti!” gridano, bramando carne di cavallo. </p><p class='main-text'> <b>Quando Rivelata:</b> Rimescola la pila degli scarti degli incontri nel mazzo incontri. Il primo giocatore aggiunge Battaglione Orchesco all’area di allestimento. Ogni altro giocatore scarta carte dalla cima del mazzo incontri fino a che non viene scartato un nemico e aggiunge quel nemico all’area di allestimento.</p>");
            AddHtml2("Relentless-Forces-FotE", "<p class='flavor-text'>Gli orchi non si daranno pace fino a che i campi non saranno stati svuotati da tutti gli stalloni e le giumente. Dovete fiancheggiarli e respingerli verso est!</p><p class='main-text'> Destrieri 3{PerPlayer}.</p><p class='main-text'><b>Obbligato:</b> Alla fine del turno, se ci sono nemici in gioco, sposta 1{PerPlayer} destrieri da questa scena a Destrieri Catturati.</p><p class='main-text'><b>Questa scena non può essere superata fintanto che Battaglione Orchesco è in gioco.</b></p>");
            AddHtml("To-the-River-FotE", "<p class='flavor-text'>Gli ultimi orchi si radunano intorno al loro capo, il guerriero Morgahk. Questo è il momento giusto per spezzare i loro ranghi e respingerli con la forza delle armi e la fermezza di spirito.</p><p class='main-text'> <b>Quando Rivelata:</b> Rimescola la pila degli scarti degli incontri nel mazzo incontri. Il primo giocatore aggiunge Rive dell’Anduin e Morgahk all’area di allestimento. Ogni altro giocatore scarta carte dalla cima del mazzo incontri fino a che non viene scartato un nemico e aggiunge quel nemico all’area di allestimento.</p>");
            AddHtml2("To-the-River-FotE", "<p class='main-text'> Morgahk non può lasciare l’area di allestimento a meno che non vi siano almeno 15 segnalini progresso su questa scena.</p><p class='main-text'><b>Obbligato:</b> Alla fine del turno, fai ritornare nell’area di allestimento tutti i nemici ingaggiati. Se ci sono più destrieri su Destrieri Selvaggi che su Destrieri Catturati, ogni nemico nell’area di allestimento cattura i propri destrieri.</p><p class='main-text'><b>I giocatori non possono superare questa scena fintanto che Morgahk è in gioco. Dopo che Morgahk è stato distrutto, se ci sono più destrieri su Destrieri Selvaggi che su Destrieri Catturati, i giocatori vincono la partita. Altrimenti i giocatori perdono la partita.</b></p>");
            AddHtml("Fang-FotE", "<p class='main-text'>Solo 1 per mazzo. Non può avere aggregati. Ogni nemico in gioco prende +1 al proprio valore di ingaggio.</p><p class='main-text'><b>Risposta:</b> Dopo che Zanna è entrato in gioco, cerca nel tuo mazzo un alleato <b><i>Creatura</i></b> e aggiungilo alla tua mano. Fino alla fine della fase, quell’alleato non richiede una corrispondenza di risorse. Rimescola il tuo mazzo.</p>");
        }

        private void TheGapOfRohan()
        {
            AddHtml("Giving-Chase-TGoR", "<p class='flavor-text'>Avete inseguito il traditore Gálmód fino alla Breccia di Rohan. Ma delle intense nevicate fuori stagione vi stanno rendendo difficile la marcia. </p><p class='main-text'> <b>Preparazione:</b> Metti Scorta di Gálmód da parte, fuori dal gioco. Aggiungi Gálmód e 1{PerPlayer} copia di Banco di Neve all’area di allestimento.</p>");
            AddHtml2("Giving-Chase-TGoR", "<p class='flavor-text'>Anche se la tormenta si sta avvicinando, dovete catturare il traditore Gálmód ad ogni costo.</p><p class='main-text'> Congelamento 1.</p><p class='main-text'>I personaggi non possono essere resi pronti dagli effetti delle carte dei giocatori.</p>");
            AddHtml("Snowbank-TGoR", "<p class='main-text'><b>Obbligato:</b> Quando dei segnalini progresso dovrebbero essere piazzati su una carta ricerca o sul luogo attivo, invece piazzali su una copia di Banco di Neve.</p><p class='main-text'>Azione di Ricerca: Esaurisci un eroe che controlli per piazzare X segnalini progresso su questo luogo, dove X è la {Attack} stampata di quell’eroe.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Aggiungi Banco di Neve all’area di allestimento.</p>");
            AddHtml("Bob-TGoR", "<p class='main-text'><b>Risposta:</b> Dopo che Bob è entrato in gioco, cerca nel tuo mazzo una carta con il tratto <b><i>Cavalcatura</i></b> o <b><i>Pony</i></b> e aggiungila alla tua mano. Rimescola il tuo mazzo.</p><p class='flavor-text'>“Una povera bestia vecchia e mezzo morta di fame, ma non ve la darà per meno del triplo del suo valore.”<br/>—La Compagnia dell’Anello</p>");
            AddHtml("Recapture-the-Traitor-TGoR", "<p class='flavor-text'>Mentre il cavallo del traditore supera barcollante il passo montano, l’ultima scorta di Gálmód si fa avanti per fargli da scudo e proteggerlo dalla cattura. </p><p class='main-text'> <b>Quando Rivelata:</b> Aggiungi 1 segnalino tempo a Gálmód. Il primo giocatore aggiunge Scorta di Gálmód all’area di allestimento e aggrega Gálmód ad essa come obiettivo protetto. Ogni altro giocatore cerca nel mazzo incontri, nella pila degli scarti degli incontri e nel riquadro vittoria una copia di Banco di Neve e la aggiunge all’area di allestimento. Rimescola il mazzo incontri.</p>");
            AddHtml2("Recapture-the-Traitor-TGoR", "<p class='flavor-text'>Gálmód e la sua scorta sono quasi riusciti a raggiungere le terre selvagge! Ben presto saranno fuori dalla vostra portata una volta per tutte.</p><p class='main-text'> Congelamento 3.</p><p class='main-text'>Scorta di Gálmód non può subire danni a meno che Catturare il Traditore non abbia almeno 6 segnalini progresso.</p><p class='main-text'><b>Quando Gálmód è libero da carte incontro, i giocatori lo hanno catturato e vincono la partita.</b></p>");
            AddHtml("Frozen-Solid-TGoR", "<p class='main-text'><b>Quando Rivelata:</b> Esaurisci tutti gli aggregati controllati dai giocatori. Gli aggregati non possono essere resi pronti fino alla fine del turno. Se non vengono esauriti aggregati tramite questo effetto, Congelato guadagna impeto.</p><p class='flavor-text'>“O fermarci qui dove ci troviamo, o tornare indietro. Inutile proseguire.”<br/>—Gandalf, La Compagnia dell’Anello</p>");
            AddHtml("Scheming-Staddler-TGoR", "<p class='main-text'><b>Risposta:</b> Dopo che un attacco incontrastato ha danneggiato un eroe che controlli, infliggi 1 danno al nemico attaccante. (Solo una volta per fase.)</p><p class='flavor-text'>“Venderebbe l’anima e farebbe cattiverie per puro divertimento.”<br/>—Aragorn, La Compagnia dell’Anello</p>");
            AddHtml("Exposed-Ridge-TGoR", "<p class='main-text'>Fintanto che Cresta Esposta si trova nell’area di allestimento, non possono essere piazzati segnalini progresso su di essa.</p><p class='main-text'>Viaggio: Infliggi 1 danno a ciascun personaggio esausto controllato dal primo giocatore.</p><p class='flavor-text'>“Sapeste come odio guardare dall’alto! Ma guardare è meglio che scendere.”<br/>—Sam Gamgee, Le Due Torri</p>");
            AddHtml("Bree-land-Protector-TGoR", "<p class='main-text'>Devozione.</p><p class='main-text'><b>Risposta:</b> Esaurisci Custode della Terra di Brea per cancellare un effetto ombra appena attivato durante un attacco e assegnare un’altra carta ombra a quel nemico. Se questo attacco non infligge danni, fai ritornare Custode della Terra di Brea nella mano del suo proprietario dopo l’attacco.</p>");
            AddHtml("Gift-of-Foresight-TGoR", "<p class='main-text'>Aggregalo a un eroe con almeno 2 {Willpower} stampata. Solo 1 per eroe.</p><p class='main-text'>L’eroe aggregato prende +2 {Defense} se non è stato coinvolto nella ricerca in questo turno.</p><p class='flavor-text'>“Non credere che siano sufficienti i canti fra gli alberi, o le svelte frecce degli archi elfici, per custodire e difendere dal Nemico la terra di Lothlórien.”<br/>–Galadriel, La Compagnia dell’Anello</p>");
            AddHtml("Frantic-Dunlending-TGoR", "<p class='main-text'>Impeto.</p><p class='main-text'><b>Obbligato:</b> Alla fine del turno, fai ritornare Dunlandiano Forsennato nell’area di allestimento.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Il nemico attaccante prende +X {Attack} per questo attacco. X è il valore di Congelamento della ricerca principale.</p>");
            AddHtml("Seasoned-Forager-TGoR", "<p class='main-text'>Aggregalo a un eroe che controlli. Solo 1 per giocatore.</p><p class='main-text'>I tuoi eroi non possono guadagnare risorse dagli effetti delle carte dei giocatori.</p><p class='main-text'><b>Azione:</b> Esaurisci Foraggiatore Esperto per ridurre di 1 il costo della prossima carta che giochi in questa fase e la cui sfera corrisponde a quella dell’eroe aggregato.</p>");
            AddHtml("Galmod-TGoR", "<p class='main-text'>Tempo 7.</p><p class='main-text'><b>Quando l’ultimo segnalino tempo viene rimosso da Gálmód, i giocatori perdono la partita.</b></p><p class='flavor-text'>Il traditore è fuggito verso ovest, nel pieno di una tormenta furibonda.</p>");
            AddHtml("Unnatural-Cold-TGoR", "<p class='main-text'>Fintanto che è aggregata ad una ricerca, conta come un aggregato <b><i>Condizione</i></b> con il testo: “<b>Obbligato:</b> Dopo che un nemico o un alleato entra in gioco, infliggi 1 danno ad esso. Se si tratta di un alleato, il suo controllore può esaurirlo per cancellare questo effetto.”</p><p class='main-text'><b>Quando Rivelata:</b> Aggregala alla ricerca principale se quest’ultima non ha già una copia di Gelo Innaturale aggregata.</p>");
            AddHtml("Harry-Goatleaf-TGoR", "<p class='main-text'><b>Risposta:</b> Cancella 1 danno da combattimento appena inflitto ad un eroe che controlli. (Solo una volta per turno per eroe.)</p><p class='flavor-text'>“I vostri affari sono affari vostri, senza dubbio, ma è affar mio porre domande dopo il calar della notte.”<br/>—La Compagnia dell’Anello</p>");
            AddHtml("Frozen-Lake-TGoR", "<p class='main-text'>Fintanto che Lago Ghiacciato è il luogo attivo, tratta il riquadro di testo di ogni copia di Banco di Neve nell’area di allestimento come se fosse vuoto.</p><p class='main-text'><b>Obbligato:</b> Dopo che Lago Ghiacciato lascia il gioco come luogo esplorato, scarta una carta dalla cima del mazzo incontri. Se viene scartata una perfidia, ciascun giocatore scarta un alleato che controlla.</p>");
            AddHtml("Half-pint-TGoR", "<p class='main-text'>Aggregalo a un personaggio.</p><p class='main-text'>Azione di Riordino: Scarta Mezza Pinta per curare fino a 2 danni dal personaggio aggregato. Poi, metti in cima al tuo mazzo un aggregato <b><i>Oggetto</i></b> dalla tua pila degli scarti.</p><p class='flavor-text'>“Ho una birra specialissima, se tu ed i tuoi amici volete favorire in casa.”<br/>—Il Vecchio Maggot, La Compagnia dell’Anello</p>");
            AddHtml("White-Wall-TGoR", "<p class='flavor-text'>Questa grande e potente tormenta sembra non voler smettere. Le raffiche di vento che scendono ululando dalle montagne portano con sé insormontabili muri di neve.</p><p class='main-text'>Impeto.</p><p class='main-text'>Muro Bianco prende +4{PerPlayer} punti ricerca.</p><p class='main-text'><b>Obbligato:</b> All’inizio della fase di ricerca, cerca nella pila degli scarti degli incontri una copia di Banco di Neve e aggiungila all’area di allestimento.</p>{victory:1}");
            AddHtml("Nob-TGoR", "<p class='main-text'>Riduci di 1 il costo del primo aggregato <b><i>Oggetto</i></b> che giochi ogni turno su un altro personaggio <b><i>Brea</i></b> o <b><i>Hobbit</i></b>.</p><p class='main-text'><b>Azione:</b> Esaurisci Nob per pescare 1 carta. Se quella carta è un aggregato <b><i>Oggetto</i></b>, rendi pronto un personaggio <b><i>Brea</i></b>. (Solo una volta per turno.)</p><p class='flavor-text'>“Dove sei, specie di infingardo trottapiano?”<br/>—Omorzo Cactaceo, La Compagnia dell’Anello</p>");
            AddHtml("Barliman-Butterbur-TGoR", "<p class='main-text'><b>Risposta:</b> Dopo che Omorzo Cactaceo ha subito danni da un attacco incontrastato, pesca 1 carta.</p><p class='flavor-text'>“Non so più dove mettere le mani, ma vedrò cosa posso fare per voi.”<br/>—La Compagnia dell’Anello</p>");
            AddHtml("Archet-Alekeeper-TGoR", "<p class='main-text'>Devozione.</p><p class='main-text'><b>Risposta:</b> Dopo che un attacco incontrastato ha danneggiato un eroe che controlli, rendi pronto Oste di Arceto e dagli +1 {Attack} fino alla fine della fase.</p><p class='flavor-text'>“Era un modo per girare il paese e vedere gente, e sentire le notizie, e sapere dov’era la buona birra.”<br/> —Robin Tanabuca, Il Ritorno del Re</p>");
            AddHtml("Slippery-Hill-TGoR", "<p class='main-text'>X è 1 in più del numero di alleati controllati dal giocatore che controlla più alleati.</p><p class='flavor-text'>“Passerete da dove sono passato io.”<br/>—Gandalf, Lo Hobbit</p>");
            AddHtml("Pint-TGoR", "<p class='main-text'>Giocala solo se controlli un personaggio <b><i>Brea</i></b> unico.</p><p class='main-text'>Aggregalo a un eroe.</p><p class='main-text'><b>Azione:</b> Esaurisci Pinta per curare 1 danno dall’eroe aggregato.</p><p class='main-text'><b>Azione:</b> Scarta Pinta per curare 5 danni dall’eroe aggregato.</p><p class='flavor-text'>“Ebbene, gente felice, volete bere un altro sorso insieme con me prima di partire?” —Barbalbero, Il Ritorno del Re</p>");
            AddHtml("Bree-Pony-TGoR", "<p class='main-text'>Aggregalo a un personaggio <b><i>Brea</i></b> o <b><i>Hobbit</i></b>. Limitato.</p><p class='main-text'>Il personaggio aggregato prende +1 punto ferita.</p><p class='main-text'><b>Risposta:</b> Quando il personaggio aggregato subisce danni da un attacco incontrastato, esaurisci Pony di Brea per ridurre di 1 quei danni.</p><p class='flavor-text'>Né con oro né con amore avrebbe potuto procurarsi un cavallo o un pony in tutto il paese: eccetto uno.<br/>—La Compagnia dell’Anello</p>");
            AddHtml("Dunlending-Rearguard-TGoR", "<p class='main-text'><b>Obbligato:</b> Alla fine della fase degli incontri, se Retroguardia Dunlandiana si trova nell’area di allestimento, rimuovi 1 segnalino tempo da Gálmód.</p><p class='flavor-text'>“Morte ai Testapaglia! Morte ai ladri del Nord!”<br/>—Le Due Torri</p>");
            AddHtml("Galmod's-Escort-TGoR", "<p class='main-text'>Immune agli effetti delle carte dei giocatori. Non può lasciare l’area di allestimento (se non venendo distrutto) ed è considerato ingaggiato con tutti i giocatori.</p><p class='main-text'>I danni che sarebbero inflitti a Scorta di Gálmód devono prima essere inflitti come segnalini progresso alle copie di Banco di Neve nell’area di allestimento, se possibile.</p>{victory:5}");
            AddHtml("Stumble-in-the-Snow-TGoR", "<p class='main-text'><b>Quando Rivelata:</b> Ogni giocatore esaurisce un personaggio che controlla e gli infligge X danni, dove X è il valore di Congelamento della ricerca principale.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Il personaggio in difesa non può essere reso pronto fino alla fine del turno.</p>");
            AddHtml("Snow-Squall-TGoR", "<p class='main-text'><b>Quando Rivelata:</b> Aggiungi 2 copie di Banco di Neve dalla pila degli scarti degli incontri all’area di allestimento. Se meno di 2 copie di Banco di Neve vengono aggiunte all’area di allestimento da questo effetto, Tormenta di Neve guadagna impeto.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Cerca 1 copia di Banco di Neve nella pila degli scarti degli incontri e aggiungila all’area di allestimento.</p>");
            AddHtml("A-Stout-Heart-TGoR", "<p class='main-text'><b>Risposta:</b> Cancella un effetto ombra appena attivato durante un attacco incontrastato. Dopo che questo attacco ha danneggiato un singolo eroe, il controllore di quell’eroe può spendere 1 risorsa dalla riserva di quell’eroe per ridurre di 3 la propria minaccia.</p><p class='flavor-text'>“Stiamo ormai giungendo all’orlo del burrone, ove speranza e disperazione sono sorelle. Esitare significa cadere.”<br/>—Aragorn, Il Ritorno del Re</p>");
            AddHtml("A-Dangerous-Pursuit-TGoR", "<p class='flavor-text'>Continuando ad avanzare, Gálmód è stato raggiunto da un piccolo gruppo di ribelli suoi alleati. Mentre Gálmód prosegue, i ribelli chiudono le fila dietro di lui per ostacolare il vostro inseguimento. </p><p class='main-text'> <b>Quando Rivelata:</b> Aggiungi 1 segnalino tempo a Gálmód. Mescola la pila degli scarti degli incontri nel mazzo incontri. Scarte carte dalla cima del mazzo incontri fino a che non vengono scartati 1{PerPlayer} nemici. Aggiungi all’area di allestimento ciascun nemico scartato in questo modo.</p>");
            AddHtml2("A-Dangerous-Pursuit-TGoR", "<p class='flavor-text'>Una tormenta ulula nel passo montano e anche i vostri assalitori sembrano sconcertati dalla ferocia della tempesta. La coltre bianca della neve spinta dal vento vi acceca mentre vi preparate ad affrontare i vostri nemici.</p><p class='main-text'> Congelamento 2.</p><p class='main-text'><b>Obbligato:</b> All’inizio della fase di ricerca, infliggi 1 danno a ciascun personaggio esausto.</p>");
            AddHtml("Biting-Winds-TGoR", "<p class='flavor-text'>Mentre Gálmód corre attraverso i venti sferzanti, il clima non fa altro che peggiorare. Lo vedete spingere il suo cavallo oltre ogni limite, determinato a sfuggire alla giustizia violenta riservata ai traditori. </p><p class='main-text'> <b>Quando Rivelata:</b> Aggiungi 1 segnalino tempo a Gálmód. Il primo giocatore cerca nel mazzo incontri, nella pila degli scarti degli incontri e nel riquadro vittoria una copia di Gelo Innaturale e la aggrega alla ricerca principale. Rimescola il mazzo incontri. Ogni altro giocatore rivela una carta incontro.</p>");
            AddHtml2("Biting-Winds-TGoR", "<p class='flavor-text'>Ben presto anche il vostro gruppo inizia a rallentare e ad avere difficoltà. Se il traditore dovesse fuggire attraverso il passo montano si troverà fuori dalla portata di Rohan.</p><p class='main-text'> Congelamento 2.</p><p class='main-text'>Considera il riquadro di testo di ogni personaggio danneggiato come se fosse vuoto (ad eccezione dei <b><i>Tratti</i></b>).</p><p class='main-text'>Gelo Innaturale è immune agli effetti delle carte dei giocatori e guadagna impeto.</p>");
        }

        private void TheGlitteringCaves()
        {
            AddHtml("Balin-TGC", "<p class='main-text'>Devozione.</p><p class='main-text'><b>Risposta:</b> Dopo che una carta viene scartata dalla cima del tuo mazzo, rendi pronto Balin. (Solo 1 volta per turno.)</p><p class='flavor-text'>Gli altri non fecero neanche finta di offrirsi, tutti tranne il vecchio Balin. —Lo Hobbit</p>");
            AddHtml("Jewelled-Cavern-TGC", "<p class='main-text'>Fintanto che ti trovi in questa caverna, le carte che giochi costano 1 risorsa corrispondente addizionale.</p><p class='main-text'><b>Azione di Viaggio:</b> Scarta tutte le risorse dalla riserva di ciascuno dei tuoi eroi per cercare in questa caverna.</p>");
            AddHtml("Radiant-Caverns-Jewelled-Cavern-TGC", "<p class='main-text'>Immune agli effetti delle carte dei giocatori.</p><p class='main-text'><b>Obbligato:</b> Dopo che Caverne Radiose viene esplorato, assegnalo a faccia in giù come carta ombra a Helm Mandimartello.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Il nemico attaccante prende +1 {Attack} per ogni carta ombra assegnata ad esso. Se questo attacco distrugge un personaggio, incrementa di 2 la tua minaccia.</p>");
            AddHtml("Radiant-Caverns-Vast-Cave-TGC", "<p class='main-text'>Immune agli effetti delle carte dei giocatori.</p><p class='main-text'><b>Obbligato:</b> Dopo che Caverne Radiose viene esplorato, assegnalo a faccia in giù come carta ombra a Helm Mandimartello.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Il nemico attaccante prende +1 {Attack} per ogni carta ombra assegnata ad esso. Se questo attacco distrugge un personaggio, incrementa di 2 la tua minaccia.</p>");
            AddHtml("Radiant-Caverns-Crossroads-TGC", "<p class='main-text'>Immune agli effetti delle carte dei giocatori.</p><p class='main-text'><b>Obbligato:</b> Dopo che Caverne Radiose viene esplorato, assegnalo a faccia in giù come carta ombra a Helm Mandimartello.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Il nemico attaccante prende +1 {Attack} per ogni carta ombra assegnata ad esso. Se questo attacco distrugge un personaggio, incrementa di 2 la tua minaccia.</p>");
            AddHtml("Helm's-Horn-TGC", "<p class='main-text'>I segnalini dei giocatori non si possono muovere.</p><p class='main-text'><b>Quando Rivelata:</b> Rimescola la pila degli scarti del mazzo incontri nel mazzo incontri. Se Helm Mandimartello non si trova nella caverna non danneggiata, muovilo lì. Poi, gira Helm Mandimartello.</p><p class='flavor-text'>Una notte si udì suonare il corno, ma Helm non tornò.<br/>—Il Ritorno del Re</p>");
            AddHtml("Fork-TGC", "<p class='main-text'><b>Obbligato:</b> Dopo esserti mosso in questa caverna, scarta carte dalla cima del mazzo degli incontri fino a che non viene scartato un nemico. Ingaggia quel nemico.</p><p class='main-text'><b>Azione di Viaggio:</b> Scarta l’alleato o l’aggregato che controlli con il costo stampato più alto per cercare in questa caverna.</p>");
            AddHtml("Echoes-in-the-Deep-Dark-Pit-TGC", "<p class='main-text'><b>Quando Rivelata:</b> Muovi Helm Mandimartello di 1 caverna <b>adiacente</b> verso il giocatore più vicino, se possibile. Poi, Helm Mandimartello compie un attacco immediato contro il giocatore che si trova nella sua caverna e che ha la minaccia più alta. Se non viene compiuto nessun attacco in questo modo, Eco nelle Profondità guadagna impeto.</p>");
            AddHtml("Echoes-in-the-Deep-Secret-Passage-TGC", "<p class='main-text'><b>Quando Rivelata:</b> Muovi Helm Mandimartello di 1 caverna <b>adiacente</b> verso il giocatore più vicino, se possibile. Poi, Helm Mandimartello compie un attacco immediato contro il giocatore che si trova nella sua caverna e che ha la minaccia più alta. Se non viene compiuto nessun attacco in questo modo, Eco nelle Profondità guadagna impeto.</p>");
            AddHtml("Salvaged-Supplies-TGC", "<p class='main-text'><b>Risposta:</b> Alla fine della fase di riordino, se nessuno dei tuoi eroi ha guadagnato risorse da effetti delle carte dei giocatori questo turno, aggiungi 1 risorsa alla riserva di un eroe che controlli.</p><p class='main-text'><b>Azione:</b> Esaurisci un personaggio che controlli per riprendere in mano Equipaggiamento Recuperato dalla pila degli scarti.</p>");
            AddHtml("Legacy-of-Gondolin-TGC", "<p class='main-text'>Aggregalo ad un eroe <b><i>Noldor</i></b> che controlli. Solo 1 per eroe.</p><p class='main-text'>Non puoi giocare alleati o aggregati dalla tua mano, a meno che tu non abbia 2 o meno carte nella tua mano.</p><p class='main-text'><b>Azione:</b> Esaurisci Eredità di Gondolin per pescare 1 carta oppure per scartare Eredità di Gondolin.</p>");
            AddHtml("Search-the-Caves-TGC", "<p class='flavor-text'>Mentre fuggite dalla violenta tempesta, siete inseguiti dallo spettro di Helm Mandimartello. Egli vi insegue implacabile, cercando di reclamare le ossa dei vostri prigionieri Dunlandiani. Dovete esplorare le caverne sotto il Fosso di Helm per trovare il grande corno da guerra di Helm stesso. Suonarlo potrebbe essere la vostra sola speranza per far tornare in sé lo spettro.</p><p class='main-text'> <b>Preparazione:</b> Metti Corno di Helm da parte, fuori dal gioco. Aggiungi Helm Mandimartello all’area di allestimento, con il lato <b><i>Furioso</i></b> rivolto verso l’alto. Prepara la Mappa delle Caverne e gli Indizi (vedi inserto).</p>");
            AddHtml2("Search-the-Caves-TGC", "<p class='main-text'> I segnalini progresso che dovrebbero essere piazzati qui vanno invece distribuiti tra le caverne che contengono dei segnalini giocatore.</p><p class='main-text'><b>Azione di Viaggio:</b> Un qualsiasi giocatore può rimuovere 4 segnalini progresso dalla propria caverna attuale per muoversi in una caverna <b>connessa</b>. Non c’è limite al numero di volte che questo effetto può essere attivato da un giocatore.</p><p class='main-text'><b>Quando è rimasta esattamente 1 caverna senza segnalini danno, avanzate immediatamente alla Scena 2</b>.</p>");
            AddHtml("Dark-Pit-TGC", "<p class='main-text'>I personaggi che si trovano in questa caverna prendono –1 {Attack} e –1 {Defense} e sono rimossi dal gioco quando lasciano il gioco.</p><p class='main-text'><b>Azione di Viaggio:</b> Rivela una carta incontro per cercare in questa caverna.</p>");
            AddHtml("Gaeronel-TGC", "<p class='main-text'>Fintanto che c’è una carta in cima alla tua pila degli scarti,</p><p class='main-text'>Gaeronel prende +2 {Willpower} se è un evento;</p><p class='main-text'>Gaeronel prende +2 {Attack} se è un alleato;</p><p class='main-text'>Gaeronel prende +2 {Defense} se è un aggregato.</p><p class='flavor-text'>“Io dimorerò presso le rive grigie, a guardia dei Porti, finché l’ultima nave non sia salpata.”<br/>—Círdan, Il Silmarillion</p>");
            AddHtml("Lurking-Ghul-TGC", "<p class='main-text'>Mentre si trova nell’area di allestimento, Ghûl in Agguato guadagna: “<b>Obbligato:</b> Dopo che un giocatore si è mosso, Ghûl in Agguato compie un attacco immediato contro quel giocatore dall’area di allestimento. (Solo 1 volta per turno.)”</p><p class='flavor-text'>“Vattene, orrido foul dwimmerlaik, signore delle carogne! Lascia in pace i morti!” —Éowyn, Il Ritorno del Re</p>");
            AddHtml("Glorfindel-TGC", "<p class='main-text'>Glorfindel prende +2 {Attack} mentre attacca da solo.</p><p class='main-text'>Glorfindel prende +2 {Attack} mentre attacca un nemico unico.</p><p class='flavor-text'>Allora Glorfindel balzò in avanti e gli fu addosso e la sua argentea armatura brillò stranamente al chiaro di luna…<br/>—La Caduta di Gondolin</p>");
            AddHtml("Vast-Cave-TGC", "<p class='main-text'>I personaggi che si trovano in questa caverna non possono essere resi pronti dagli effetti delle carte dei giocatori.</p><p class='main-text'><b>Azione di Viaggio:</b> Rimuovi 8 segnalini progresso da questa caverna per cercare in questa caverna.</p>");
            AddHtml("Helm-Hammerhand-A-TGC", "<p class='main-text'>Immune agli effetti delle carte dei giocatori. Non può essere ingaggiato.</p><p class='main-text'><b>Obbligato:</b> Alla fine della fase di combattimento, muovi Helm Mandimartello di 1 caverna <b>adiacente</b> verso il giocatore più vicino, se possibile. Poi, Helm Mandimartello compie un attacco immediato contro ciascun giocatore che si trova nella sua stessa caverna. I giocatori nelle altre caverne non possono usare dei personaggi con la parola chiave sentinella per difendere questi attacchi.</p>");
            AddHtml("Helm-Hammerhand-B-TGC", "<p class='main-text'>Immune agli effetti delle carte dei giocatori. Non può lasciare l’area di allestimento (se non venendo distrutto), ma è considerato ingaggiato con ciascun giocatore. Assegna a Helm Mandimartello una carta ombra addizionale ogni volta che attacca.</p><p class='main-text'><b>Obbligato:</b> Quando a Helm Mandimartello viene assegnato un nemico <b><i>Dunland</i></b> come carta ombra, cancellane l’effetto ombra (se presente) e assegna a Helm Mandimartello un’altra carta ombra.</p>{victory:10}");
            AddHtml("Crossroads-TGC", "<p class='main-text'><b>Obbligato:</b> Dopo esserti mosso in questa caverna, incrementa di 3 la tua minaccia.</p><p class='main-text'><b>Azione di Viaggio:</b> Incrementa di 3 la tua minaccia per cercare in questa caverna.</p>");
            AddHtml("Cave-Opening-TGC", "<p class='main-text'><b>Obbligato:</b> Dopo esserti mosso in questa caverna, scarta un alleato o un aggregato che controlli con un costo stampato di 2 o più.</p><p class='main-text'><b>Azione di Viaggio:</b> Incrementa di 1 la tua minaccia per ogni indizio in gioco per cercare in questa caverna.</p>");
            AddHtml("The-Horn's-Discovery-TGC", "<p class='flavor-text'>Eppure la gente diceva ancora di udire a volte il suono del corno nel Fosso, mentre lo spettro di Helm vagava fra i nemici di Rohan uccidendone molti con la sola paura. —Il Ritorno del Re</p><p class='main-text'> <b>Quando Rivelata:</b> Aggrega Corno di Helm a faccia in giù come indizio alla caverna rimasta senza segnalini danno.</p>");
            AddHtml2("The-Horn's-Discovery-TGC", "<p class='main-text'> Tutti i giocatori devono trovarsi nella caverna senza segnalini danno prima che un giocatore possa cercare quella caverna. I segnalini progresso che dovrebbero essere piazzati qui vanno invece distribuiti sulle caverne in gioco (anche su quelle che non hanno segnalini giocatore).</p><p class='main-text'><b>Azione di Viaggio:</b> Un qualsiasi giocatore può rimuovere 4 segnalini progresso dalla propria caverna attuale per muoversi in una caverna <b>connessa</b>. Non c’è limite al numero di volte che questo effetto può essere attivato da un giocatore.</p><p class='main-text'><b>Quando Helm Mandimartello è stato sconfitto, i giocatori vincono la partita.</b></p>");
            AddHtml("Lay-of-the-Nauglamir-TGC", "<p class='main-text'>Devozione.</p><p class='main-text'><b>Azione:</b> Scarta 10 carte dalla cima del tuo mazzo. Scegli fino a due alleati <b><i>Noldor</i></b> o <b><i>Nano</i></b> differenti scartati da questo effetto e mettili in gioco. Ogni alleato così scelto deve avere un costo stampato di 2 o inferiore. Puoi rimescolare nel tuo mazzo un qualsiasi numero delle altre carte scartate da questo effetto. Rimuovi Lai della Nauglamír dal gioco.</p>");
            AddHtml("Wonders-of-Aglarond-TGC", "<p class='main-text'><b>Obbligato:</b> Dopo che Meraviglie di Aglarond ha lasciato il gioco, piazza 4 segnalini progresso su 1 qualsiasi caverna senza un segnalino giocatore e rivela una carta incontro.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Muovi il segnalino di Helm Mandimartello di 1 caverna <b>adiacente</b> verso il giocatore più vicino, oppure incrementa di 2 la minaccia del giocatore in difesa.</p>");
            AddHtml("Forgotten-Shade-Fork-TGC", "<p class='main-text'>Mentre compie delle verifiche di ingaggio contro un giocatore che non si è mosso questo turno, Ombra Dimenticata prende –30 al proprio valore di ingaggio.</p><p class='main-text'>Mentre è ingaggiata con un giocatore che non si è mosso questo turno, Ombra Dimenticata non può subire danni.</p>");
            AddHtml("Forgotten-Shade-Dead-End-TGC", "<p class='main-text'>Mentre compie delle verifiche di ingaggio contro un giocatore che non si è mosso questo turno, Ombra Dimenticata prende –30 al proprio valore di ingaggio.</p><p class='main-text'>Mentre è ingaggiata con un giocatore che non si è mosso questo turno, Ombra Dimenticata non può subire danni.</p>");
            AddHtml("Forgotten-Shade-Tunnel-TGC", "<p class='main-text'>Mentre compie delle verifiche di ingaggio contro un giocatore che non si è mosso questo turno, Ombra Dimenticata prende –30 al proprio valore di ingaggio.</p><p class='main-text'>Mentre è ingaggiata con un giocatore che non si è mosso questo turno, Ombra Dimenticata non può subire danni.</p>");
            AddHtml("Alluring-Paths-TGC", "<p class='main-text'>Quando hanno l’opzione di viaggiare, i giocatori devono viaggiare verso Passaggi Affascinanti se non c’è un luogo attivo.</p><p class='main-text'><b>Obbligato:</b> Dopo che Passaggi Affascinanti ha lasciato il gioco, se Helm Mandimartello si trova nella stessa caverna di un giocatore, muovilo nella caverna del giocatore seguente.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Se non ti sei mosso questo turno, il nemico attaccante compie un attacco addizionale contro di te dopo questo.</p>");
            AddHtml("Secret-Passage-TGC", "<p class='main-text'><b>Obbligato:</b> Dopo esserti mosso in questa caverna, scegli una perfidia nella pila degli scarti e rivelala.</p><p class='main-text'><b>Azione di Viaggio:</b> Rimuovi 6 segnalini progresso da questa caverna per cercare in questa caverna oppure per muoverti direttamente ad una caverna che sia entro 2 caverne <b>adiacenti</b>.</p>");
            AddHtml("Small-Stream-TGC", "<p class='main-text'>Viaggio: Ogni giocatore deve scartare una carta casuale dalla propria mano per poter viaggiare qui.</p><p class='main-text'><b>Azione di Viaggio:</b> Se questa carta si trova nel riquadro vittoria, un giocatore in una caverna <b><i>Torrente</i></b> può rimuovere questa carta dal gioco per muoversi in una caverna <b>adiacente</b>.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Il nemico attaccante prende +1 {Attack}. Se non ti sei mosso questo turno, assegnagli un’altra carta ombra.</p>{victory:1}");
            AddHtml("Locked-Door-TGC", "<p class='main-text'><b>Obbligato:</b> Dopo esserti mosso in questa caverna, esaurisci 2 personaggi che controlli.</p><p class='main-text'><b>Azione di Viaggio:</b> Dichiara un tipo di carta e una sfera e poi scarta una carta casuale dalla tua mano. Se la carta scartata ha il tipo e sfera che hai dichiarato, cerca in questa caverna.</p>");
            AddHtml("Relic-of-Nargothrond-TGC", "<p class='main-text'>Aggregalo ad un personaggio <b><i>Noldor</i></b> o <b><i>Nano</i></b>. Limitato. Solo 1 per personaggio. Reliquia di Nargothrond può essere giocato dalla tua pila degli scarti.</p><p class='main-text'>Il personaggio aggregato prende +1 {Defense}.</p><p class='main-text'><b>Azione:</b> Scarta Reliquia di Nargothrond per dare +2 {Defense} al personaggio aggregato fino alla fine della fase.</p><p class='flavor-text'>In codesta opera, Finrod fu aiutato dai Nani dei Monti Azzurri.<br/>—Il Silmarillion</p>");
            AddHtml("Stairwell-TGC", "<p class='main-text'><b>Obbligato:</b> Dopo esserti mosso in questa caverna, scarta aggregati che controlli fino a che nessun personaggio che controlli abbia più di 2 aggregati.</p><p class='main-text'><b>Azione di Viaggio:</b> Scarta 1 aggregato da ogni personaggio che controlli che abbia un aggregato, se possibile. Poi, cerca in questa caverna.</p>");
            AddHtml("Twisting-Paths-TGC", "<p class='main-text'>Impeto.</p><p class='main-text'><b>Quando Rivelata:</b> Aggregala a faccia in su ad una caverna. Conta come un aggregato con il testo: “<b>Azione di Viaggio:</b> Un giocatore che si trova nella caverna aggregata può scartare Sentieri Tortuosi per scambiare la caverna aggregata (assieme a tutti i segnalini e aggregati) con una caverna <b>adiacente</b>.”</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Risolvi l’effetto “Quando Rivelata” di questa carta.</p>");
            AddHtml("Guard-the-Entrance-TGC", "<p class='main-text'><b>Quando Rivelata:</b> Ogni giocatore incrementa di X la propria minaccia, dove X è il numero di caverne <b>connesse</b> alla propria caverna.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Scarta una risorsa dalla riserva di ciascuno dei tuoi eroi.</p>");
            AddHtml("Wandering-Spirit-TGC", "<p class='main-text'>Non può entrare in gioco.</p><p class='main-text'><b>Azione:</b> Se Spirito Errante è nella tua pila degli scarti, puoi muoverlo in su o in giù di 1 o 2 carte. Poi, se Spirito Errante si trova accanto ad un alleato <b><i>Noldor</i></b> con un costo stampato di 2 o inferiore, puoi spendere 1 risorsa {sphere:Spirit} e rimuovere Spirito Errante dal gioco per mettere in gioco quell’alleato (Solo 1 volta per turno.)</p>");
            AddHtml("Guarded-Hall-TGC", "<p class='main-text'>Fintanto che ti trovi in questa caverna, assegna una carta ombra addizionale ai nemici ingaggiati con te quando attaccano.</p><p class='main-text'><b>Azione di Viaggio:</b> Ingaggia il nemico in cima alla pila degli scarti del mazzo incontri per cercare in questa caverna.</p>");
            AddHtml("Tunnel-TGC", "<p class='main-text'><b>Obbligato:</b> Dopo esserti mosso in questa caverna, scarta personaggi che controlli fino a che non controlli 6 o meno personaggi.</p><p class='main-text'><b>Azione di Viaggio:</b> Esaurisci ogni personaggio pronto che controlli per cercare in questa caverna.</p>");
            AddHtml("Killing-With-Fear-Guarded-Hall-TGC", "<p class='main-text'><b>Quando Rivelata:</b> Ogni giocatore che si trova a 1 o meno caverne <b>adiacenti</b> di distanza da Helm Mandimartello infligge 1 danno ad ogni personaggio esausto che controlla e ad ogni nemico <b><i>Dunland</i></b> ingaggiato con sé. Muovi Helm Mandimartello di 1 caverna <b>adiacente</b> verso il giocatore più vicino.</p>");
            AddHtml("Killing-With-Fear-Stairwell-TGC", "<p class='main-text'><b>Quando Rivelata:</b> Ogni giocatore che si trova a 1 o meno caverne <b>adiacenti</b> di distanza da Helm Mandimartello infligge 1 danno ad ogni personaggio esausto che controlla e ad ogni nemico <b><i>Dunland</i></b> ingaggiato con sé. Muovi Helm Mandimartello di 1 caverna <b>adiacente</b> verso il giocatore più vicino.</p>");
            AddHtml("Killing-With-Fear-Cave-Opening-TGC", "<p class='main-text'><b>Quando Rivelata:</b> Ogni giocatore che si trova a 1 o meno caverne <b>adiacenti</b> di distanza da Helm Mandimartello infligge 1 danno ad ogni personaggio esausto che controlla e ad ogni nemico <b><i>Dunland</i></b> ingaggiato con sé. Muovi Helm Mandimartello di 1 caverna <b>adiacente</b> verso il giocatore più vicino.</p>");
            AddHtml("Harlond-Lookout-TGC", "<p class='main-text'>Devozione.</p><p class='main-text'><b>Risposta:</b> Dopo che Vedetta di Harlond viene distrutto, rendi pronto un personaggio che controlli.</p><p class='flavor-text'>Le flotte svanirono alla vista delle scolte nei porti, e le loro lanterne impallidirono. —Il Silmarillion</p>");
            AddHtml("Onward-Into-Battle-TGC", "<p class='main-text'>Aggregalo ad un eroe <b><i>Guerriero</i></b>.</p><p class='main-text'>Azione di Incontri: Esaurisci Verso la Battaglia per rendere pronto l’eroe aggregato.</p><p class='flavor-text'>“…e pur convinti che un guerriero debba possedere altre doti di intelligenza e destrezza oltre l’abilità nel maneggiare armi e uccidere, tuttavia lo consideriamo superiore agli Uomini di altri mestieri. Sono i tempi che vogliono così.”<br/>—Faramir, Le Due Torri</p>");
            AddHtml("Dead-End-TGC", "<p class='main-text'>Fintanto che ti trovi in questa caverna, non puoi pescare carte e i tuoi eroi non possono guadagnare risorse.</p><p class='main-text'><b>Azione di Viaggio:</b> Rimuovi dal gioco tutti gli eventi nella tua mano e nella tua pila degli scarti per cercare in questa caverna.</p>");
        }

        private void MusterinfOfTheRohirrim()
        {
            AddHtml("Woodmen's-Outpost-MotR", "<p class='main-text'>Aggregalo a un luogo. Solo 1 per luogo.</p><p class='main-text'><b>Risposta:</b> Dopo che il luogo aggregato è stato esplorato, ogni personaggio controllato dal primo giocatore prende +1 {Attack} e +1 {Defense} fino alla fine del turno.</p><p class='flavor-text'>…e nemmeno i Mannari osavano attaccarli se erano in molti, o in pieno giorno.<br/> —Lo Hobbit</p>");
            AddHtml("Birna-MotR", "<p class='main-text'><b>Azione:</b> Esaurisci Birna e spendi 1 risorsa {sphere:Tactics} per mettere in gioco un alleato <b><i>Beorning</i></b> dalla tua pila degli scarti. Alla fine della fase, se quell’alleato è ancora in gioco o nella pila degli scarti, rimescolalo nel tuo mazzo.</p><p class='flavor-text'>Ma all’ultima ora era apparso Beorn in persona - nessuno sapeva come o da dove.<br/>—Lo Hobbit</p>");
            AddHtml("Spiteful-Hunter-MotR", "<p class='main-text'>Cacciatore Iracondo prende +1 {Attack} per ogni eroe esaurito controllato dal giocatore in difesa.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Piazza 1 segnalino danno su Schieramento all’Isen oppure il nemico attaccante prende +3 {Attack}.</p>");
            AddHtml("Raven-Clan-Warchief-MotR", "<p class='main-text'>Non può avere aggregati.</p><p class='main-text'><b>Obbligato:</b> Quando Capo del Clan del Corvo attacca, piazza 1 segnalino danno su Schieramento all’Isen.</p><p class='main-text'><b>Obbligato:</b> Quando Capo del Clan del Corvo viene sconfitto, mescolalo nel mazzo incontri assieme alla pila degli scarti degli incontri.</p>");
            AddHtml("Cutthroat's-Strike-MotR", "<p class='main-text'>Impeto.</p><p class='main-text'>Fintanto che è aggregata ad un nemico, conta come un aggregato <b><i>Condizione</i></b> con il testo: “<b>Obbligato:</b> Quando il nemico aggregato attacca, prende +5 {Attack} per questo attacco. Poi, scarta Colpo del Tagliagole.”</p><p class='main-text'><b>Quando Rivelata:</b> Aggregala al nemico in gioco con il costo di ingaggio più basso e senza un’altra copia di Colpo del Tagliagole aggregata.</p>");
            AddHtml("Coup-MotR", "<p class='main-text'><b>Quando Rivelata:</b> Ogni giocatore deve scegliere: incrementare la propria minaccia di 6 oppure scartare dal gioco un eroe che controlla. Aggiungi Colpo di Stato al riquadro vittoria. Questo effetto non può essere cancellato.</p><p class='flavor-text'>“Quando seppi che Ombromanto era tornato senza cavaliere, mi rallegrai del suo ritorno, ma ancor più dell’assenza del cavaliere.”<br/>—Théoden, Le Due Torri</p>{victory:1}");
            AddHtml("Sniveling-Courtier-MotR", "<p class='main-text'>Indistruttibile.</p><p class='main-text'><b>Obbligato:</b> Dopo che Cortigiano Lamentoso ha compiuto un attacco incontrastato contro di te, piazza 1 segnalino danno su Schieramento all’Isen oppure incrementa di 3 la tua minaccia.</p><p class='main-text'>Azione di Riordino: Il giocatore ingaggiato può spendere 2 risorse per scartare Cortigiano Lamentoso.</p>");
            AddHtml("Pernicious-Plotter-MotR", "<p class='main-text'><b>Obbligato:</b> Alla fine del turno, piazza 1 segnalino danno su Schieramento all’Isen.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Piazza 1 segnalino danno su Schieramento all’Isen oppure il nemico attaccante ritorna nell’area di allestimento dopo questo attacco.</p>");
            AddHtml("Curuwyn-The-Whisperer-MotR", "<p class='main-text'><b>Obbligato:</b> All’inizio della fase di incontri, Curuwyn Colei Che Sussurra ingaggia il giocatore con la minaccia più alta.</p><p class='main-text'><b>Obbligato:</b> Dopo che Curuwyn Colei Che Sussurra ha attaccato, il giocatore in difesa deve piazzare 1 segnalino danno su Schieramento all’Isen oppure incrementare di 4 la propria minaccia.</p>{victory:5}");
            AddHtml("Descendants-of-Beorn-MotR", "<p class='main-text'><b>Risposta:</b> Quando un alleato <b><i>Beorning</i></b> che controlli lascia il gioco, aggiungi 1 risorsa alla riserva di un eroe <b><i>Beorning</i></b> per ogni segnalino danno che si trovava su quell’alleato (fino ad un massimo pari ai punti ferita stampati di quell’alleato).</p><p class='flavor-text'>Egli spazzò via dalla sua strada lupi e orchi come fossero piume e pagliuzze.<br/> —Lo Hobbit</p>");
            AddHtml("Felberen-Scion-of-Freca-MotR", "<p class='main-text'><b>Obbligato:</b> All’inizio della fase di incontri, Felberen Discendente di Freca ingaggia il giocatore con il maggior numero di carte in mano.</p><p class='main-text'><b>Obbligato:</b> Dopo che Felberen Discendente di Freca ha attaccato, il giocatore in difesa deve piazzare 1 segnalino danno su Schieramento all’Isen oppure scartare la propria mano.</p>{victory:5}");
            AddHtml("Oaths-of-the-Rohirrim-MotR", "<p class='flavor-text'>Il traditore Ealdwulf sta radunando un’armata per usurpare il trono di Re Thengel. </p><p class='main-text'> <b>Preparazione:</b> Mescola assieme le 5 carte con il tratto <b><i>Schieramento</i></b> a formare il mazzo Schieramento. Cerca nel mazzo incontri Írensaga, Resti dell’Armata degli Orchi, tutti i nemici <b><i>Assassino</i></b> e tutte le ricerche secondarie <b><i>Adunata</i></b> e ponile da parte, fuori dal gioco. Il primo giocatore aggiunge Schieramento all’Isen all’area di allestimento e rende Torrente Fossato il luogo attivo. Ogni giocatore cerca nel mazzo incontri un nemico <b><i>Traditore</i></b> differente e lo aggiunge all’area di allestimento. Rimescola il mazzo incontri.</p>");
            AddHtml2("Oaths-of-the-Rohirrim-MotR", "<p class='flavor-text'>Se volete sperare di affrontare la sfida di Ealdwulf dovreste passare i prossimi mesi a radunare le schiere disperse di Rohan. Il suo complotto lungamente orchestrato gli fornisce un vantaggio notevole, ma i vostri rapidi destrieri vi potranno portare attraverso molte leghe per avvisare coloro che rimangono fedeli al vessillo di Thengel.</p><p class='main-text'> <b>Quando Rivelata:</b> Ogni giocatore deve scegliere: piazza 1 segnalino danno su Schieramento all’Isen oppure rivela una carta incontro.</p>");
            AddHtml("Beorn's-Welcome-MotR", "<p class='main-text'>Giocalo solo se controlli un eroe <b><i>Beorning</i></b>. Aggregalo ad un eroe. Solo 1 per eroe.</p><p class='main-text'><b>Risposta:</b> Dopo che spendi un qualsiasi numero di risorse dalla riserva dell’eroe aggregato per uno scopo che non sia il pagare per giocare una carta, esaurisci Il Benvenuto di Beorn per aggiungere 1 risorsa alla riserva dell’eroe aggregato.</p><p class='flavor-text'>“Io sono Gandalf” disse lo stregone. “Mai sentito nominare” ringhiò l’uomo.<br/>—Lo Hobbit</p>");
            AddHtml("Seeking-Support-MotR", "<p class='flavor-text'>Viaggiando attraverso Rohan notate come essa sia nel caos. Fino a che non ritroverete l’éored che è stata inviata verso i Monti Bianchi, non richiederete l’aiuto di Thengel e non vi occuperete una volta per tutte dei ladri di cavalli orcheschi che vagano in queste terre, nessuno potrà cavalcare in vostro soccorso. E dovunque andate, gli agenti di Ealdwulf cercano di rallentarvi e distrarvi. </p><p class='main-text'> <b>Quando Rivelata:</b> Il primo giocatore sceglie una ricerca secondaria <b><i>Adunata</i></b> messa da parte e la rivela.</p>");
            AddHtml2("Seeking-Support-MotR", "<p class='flavor-text'>Con ogni alba che passa, l’armata di Ealdwulf è sempre più preparata. Dovete affrettarvi.</p><p class='main-text'> In Cerca di Sostegno non può essere scelta come ricerca attuale durante la fase di ricerca.</p><p class='main-text'><b>Obbligato:</b> Quando una ricerca secondaria <b><i>Adunata</i></b> viene superata, girala e rivelala. Poi, il primo giocatore sceglie un’altra ricerca secondaria <b><i>Adunata</i></b> e la rivela. <b>Se il primo giocatore non è in grado di farlo, avanzate alla scena 3A.</b></p>");
            AddHtml("Winter-in-Westemnet-MotR", "<p class='flavor-text'>Gli esploratori di Éomund hanno vagato in lungo e in largo nella loro ricerca di Gálmód. Divulgare la notizia della sua cattura porterà sicuramente nuovi alleati alla vostra causa.</p><p class='main-text'><b>Quando Rivelata:</b> Il primo giocatore aggiunge Írensaga all’area di allestimento. Ogni altro giocatore rivela una carta incontro.</p><p class='main-text'>I nemici prendono +1 {Threat} e non compiono verifiche di ingaggio.</p><p class='main-text'><b>Questa scena non può essere superata a meno che Írensaga non si trovi nel riquadro vittoria.</b></p>");
            AddHtml("Irensaga-MotR", "<p class='main-text'>Immune agli effetti delle carte dei giocatori.</p><p class='main-text'>Írensaga prende +2{PerPlayer} punti ricerca. Fintanto che Írensaga è il luogo attivo, i giocatori non possono giocare alleati.</p><p class='main-text'><b>Obbligato:</b> Alla fine del turno, se Írensaga è nell’area di allestimento, piazza 1 segnalino danno su Schieramento all’Isen.</p>{victory:5}");
            AddHtml("The-Far-Bank-MotR", "<p class='main-text'>Immune agli effetti delle carte dei giocatori. I giocatori non possono viaggiare verso La Riva Lontana.</p><p class='main-text'>I nemici in gioco prendono +5 al loro valore di ingaggio.</p><p class='main-text'><b>Obbligato:</b> Dopo che un giocatore ha ingaggiato intenzionalmente un nemico, quel nemico compie un attacco immediato contro quel giocatore.</p>");
            AddHtml("Road-to-Ruin-MotR", "<p class='flavor-text'>I resti dell’armata orchesca si stanno raggruppando e minacciano di nuovo gli allevatori di cavalli già allo stremo.</p><p class='main-text'><b>Quando Rivelata:</b> Il primo giocatore aggiunge Resti dell’Armata degli Orchi all’area di allestimento e gli infligge 4 danni. Ogni altro giocatore rivela una carta incontro.</p><p class='main-text'><b>Obbligato:</b> Alla fine del turno, infliggi 1 danno a Resti dell’Armata degli Orchi.</p><p class='main-text'><b>Questa scena non può essere superata a meno che Resti dell’Armata degli Orchi non si trovi nel riquadro vittoria.</b></p>");
            AddHtml("Halls-of-Ealdwulf-MotR", "<p class='flavor-text'>Siete tornati ad Aldburg per affrontare Ealdwulf. Purtroppo l’armata del traditore si è già allontanata, ma quest’ultimo ha lasciato dietro di sé un regalo d’addio: due truci assassini, armati di lame scintillanti ed affilate. </p><p class='main-text'> <b>Quando Rivelata:</b> Piazza 1{PerPlayer} segnalino danno su Schieramento all’Isen. Aggiungi all’area di allestimento i nemici <b><i>Assassino</i></b> messi da parte rimanenti.</p>");
            AddHtml2("Halls-of-Ealdwulf-MotR", "<p class='flavor-text'>Gli assassini di Ealdwulf vi assalgono ferocemente accecati da un freddo odio, colpendo e ringhiando.</p><p class='main-text'> I nemici <b><i>Assassino</i></b> sono immuni agli effetti delle carte dei giocatori.</p><p class='main-text'><b>Questa scena non può essere superata fintanto che un nemico <b><i>Assassino</i></b> è in gioco. Quando questa scena viene superata, i giocatori vincono la partita.</b></p>");
            AddHtml("Osbera-SideA-MotR", "<p class='main-text'>Riduci di 1 la {Threat} totale nell’area di allestimento per ogni personaggio <b><i>Beorning</i></b> danneggiato che controlli (limite –5 {Threat}).</p><p class='main-text'><b>Risposta:</b> Dopo che hai ingaggiato un nemico, spendi 1 risorsa dalla riserva di Osbera per girarla.</p><p class='flavor-text'>“Non ho bisogno dei vostri servizi, grazie! Ma ho l’impressione che voi abbiate bisogno dei miei.”<br/>—Beorn, Lo Hobbit</p>");
            AddHtml("Osbera-SideB-MotR", "<p class='main-text'>Non può avere aggregati limitati. Se non sei ingaggiato con un nemico, gira Osbera.</p><p class='main-text'><b>Risposta:</b> Infliggi 1 danno a un personaggio <b><i>Beorning</i></b> che controlli per cancellare 2 punti di danno appena inflitti a un personaggio che controlli. (Solo 1 volta per turno.)</p><p class='flavor-text'>“Senza essere stati ancora mangiati dai Mannari o dagli orchi o da orsi malvagi, mi pare.”<br/> —Beorn, Lo Hobbit</p>");
            AddHtml("Lie-of-the-Land-MotR", "<p class='main-text'>Aggregalo a un luogo.</p><p class='main-text'><b>Azione:</b> Esaurisci un personaggio <b><i>Esploratore</i></b> per piazzare 1 segnalino progresso sul luogo aggregato. Se quel luogo non viene esplorato da questo effetto, puoi aggregare Perlustrare il Territorio ad un altro luogo.</p><p class='flavor-text'>“Dovremo perlustrare tutto il territorio nel giro di molte leghe prima di compiere qualsiasi movimento.”<br/>—Gandalf, La Compagnia dell’Anello</p>");
            AddHtml("Plains-of-Strife-MotR", "<p class='main-text'>Vasto.</p><p class='main-text'>Fintanto che Pianure Contese si trova nell’area di allestimento, i nemici <b><i>Rohan</i></b> prendono +1 {Threat}.</p><p class='main-text'>Fintanto che Pianure Contese è il luogo attivo, i nemici <b><i>Rohan</i></b> prendono +1 {Attack}.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Se il nemico attaccante ha il tratto <b><i>Rohan</i></b>, compie un attacco addizionale contro di te dopo di questo.</p>");
            AddHtml("Beorning-Pony-MotR", "<p class='main-text'>Non può avere aggregati.</p><p class='main-text'><b>Risposta:</b> Dopo che Pony Beorniano è entrato in gioco, scarta le prime 5 carte del tuo mazzo. Poi, puoi spendere 1 risorsa {sphere:Tactics} per aggiungere alla tua mano una delle carte scartate da questo effetto.</p><p class='flavor-text'>C’era appena posto per farci passare con un po’ di fatica i pony, dopo aver tolto loro il carico e le selle.<br/> —Lo Hobbit</p>");
            AddHtml("Aldburg-Provocateur-MotR", "<p class='main-text'>Ogni carta giocata dal giocatore ingaggiato guadagna Condanna 1.</p><p class='flavor-text'>“Ma vi è una terza razza: raccoglitori di ossa che si impicciano dei dispiaceri altrui, uccelli avidi di carogne che ingrassano in tempo di guerra.”<br/>—Gríma, Le Due Torri</p>");
            AddHtml("Orc-host-Remnant-MotR", "<p class='main-text'>X è il numero di segnalini danno su Resti dell’Armata degli Orchi. Non può avere aggregati. Solo il giocatore ingaggiato può dichiarare attaccanti o difensori contro Resti dell’Armata degli Orchi.</p><p class='main-text'><b>Obbligato:</b> Dopo che Resti dell’Armata degli Orchi subisce un qualsiasi ammontare di danni, fallo ritornare nell’area di allestimento.</p>{victory:5}");
            AddHtml("Traitor's-Redoubt-MotR", "<p class='main-text'>Immune agli effetti delle carte dei giocatori.</p><p class='main-text'>Viaggio: Ogni giocatore cerca nel mazzo incontri e nella pila degli scarti degli incontri un nemico <b><i>Traditore</i></b> e lo mette in gioco ingaggiato con sé.</p><p class='main-text'><b>Risposta:</b> Quando Rifugio dei Traditori lascia il gioco come luogo esplorato, rimuovi 1 segnalino danno da Schieramento all’Isen.</p>{victory:1}");
            AddHtml("Rowan-MotR", "<p class='main-text'>Sentinella.</p><p class='main-text'><b>Risposta:</b> Dopo aver giocato un aggregato su un luogo, aggiungi 1 risorsa alla riserva di un eroe che controlli. (Solo 1 volta per turno.)</p><p class='main-text'>Azione di Ricerca: Scegli un giocatore che prenda il controllo del segnalino del primo giocatore. Ogni giocatore può essere scelto solo una volta per partita.</p>");
            AddHtml("Orator's-Hall-MotR", "<p class='main-text'>I giocatori non possono viaggiare verso Salone degli Oratori. I segnalini progresso non possono essere piazzati qui dagli effetti delle carte dei giocatori.</p><p class='main-text'>Azione di Pianificazione: Esaurisci un eroe che controlli per piazzare segnalini progresso su Salone degli Oratori pari alla {Willpower} stampata di quell’eroe. Se Salone degli Oratori viene esplorato tramite questo effetto, rimuovi 1 segnalino danno da Schieramento all’Isen. Ogni giocatore può attivare questo effetto.</p>{victory:1}");
            AddHtml("Host-of-Rohan-MotR", "<p class='main-text'>Immune agli effetti delle carte dei giocatori. Sentinella.</p><p class='main-text'>Il primo giocatore prende il controllo di Schiera di Rohan.</p><p class='main-text'><b>Azione:</b> I giocatori come gruppo possono spendere 2 risorse per rendere pronta Schiera di Rohan. (Solo 1 volta per fase.)</p><p class='main-text'><b>Se Schiera di Rohan lascia il gioco, rimuovila dal gioco.</b></p><p class='flavor-text'>Al sicuro dagli attentati, il re è ora libero di inviare i suoi uomini.</p>");
            AddHtml("Massing-at-the-Isen-MotR", "<p class='main-text'><b>Obbligato:</b> Alla fine del turno, piazza 1 segnalino danno qui.</p><p class='main-text'><b>Se ci sono 5 o più segnalini danno qui, rimuovi 5 di essi e rivela la prima carta del mazzo Schieramento. Se non è possibile farlo, i giocatori perdono la partita.</b></p><p class='flavor-text'>Ealdwulf sta preparando alla guerra la sua armata sulle rive del Fiume Isen.</p>");
            AddHtml("Hunting-Dogs-MotR", "<p class='main-text'>Devozione. Non può avere aggregati.</p><p class='main-text'><b>Risposta:</b> Dopo che un luogo con un aggregato viene esplorato, rendi pronto Segugi da Caccia.</p><p class='flavor-text'>“Cane affamato, coniglio spaventato.”<br/>—Barbalbero, Le Due Torri</p>");
            AddHtml("Without-My-Hat-MotR", "<p class='main-text'>Solo 1 copia di Senza Cappello nel riquadro vittoria.</p><p class='main-text'><b>Azione:</b> Aggiungi Senza Cappello al riquadro vittoria per cercare un aggregato nella tua collezione. Se durante la preparazione della partita nessuna copia di quell’aggregato si trovava nel tuo mazzo, aggiungi quella carta alla tua mano.</p>{victory:1}");
            AddHtml("Smeohbrand-Rogue-of-Orthanc-MotR", "<p class='main-text'><b>Obbligato:</b> All’inizio della fase di incontri, Smeóhbrand Canaglia di Orthanc ingaggia il giocatore che controlla il minor numero di personaggi.</p><p class='main-text'><b>Obbligato:</b> Dopo che Smeóhbrand Canaglia di Orthanc ha attaccato per la prima volta ciascun turno, compie un attacco addizionale. Il giocatore ingaggiato può piazzare 1 segnalino danno su Schieramento all’Isen per cancellare questo effetto.</p>{victory:5}");
            AddHtml("Subdued-MotR", "<p class='main-text'>Aggregalo ad un nemico danneggiato ingaggiato con te. Solo 1 per nemico.</p><p class='main-text'>Il nemico aggregato prende –4 {Attack}.</p><p class='flavor-text'>Il suo misero nemico si contorceva piagnucolando sulle pietre.<br/>—Le Due torri</p>");
            AddHtml("Scattered-to-the-Winds-MotR", "<p class='main-text'><b>Quando Rivelata:</b> Ogni giocatore sceglie metà degli alleati non obiettivo che controlla (arrotondando per eccesso) e li rimescola nel mazzo del loro proprietario. Aggiungi Sparsi ai Quattro Venti al riquadro vittoria. Questo effetto non può essere cancellato.</p><p class='flavor-text'>“Ci confonderemo e ci perderemo.”<br/>—Aragorn, La Compagnia dell’Anello</p>{victory:1}");
            AddHtml("Raven-Clan-Sharpshooter-MotR", "<p class='main-text'>Fintanto che almeno un giocatore ha 3 o più carte nella propria mano, Tiratore del Clan del Corvo guadagna Arciere 3.</p><p class='main-text'>Fintanto che almeno un giocatore ha 5 o più carte nella propria mano, Tiratore del Clan del Corvo non può essere ingaggiato intenzionalmente.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Il nemico attaccante prende +2 {Attack}.</p>");
            AddHtml("Deeping-stream-MotR", "<p class='main-text'>Torrente Fossato prende +3{PerPlayer} punti ricerca.</p><p class='main-text'><b>Obbligato:</b> Alla fine del turno, piazza 1 segnalino danno su Schieramento all’Isen.</p><p class='flavor-text'>Dovete lasciare rapidamente il vostro misero campo vicino alle Caverne Scintillanti, prima che Ealdwulf distrugga il regno di Rohan con le sue ambizioni.</p>{victory:5}");
            AddHtml("Assassin-at-Edoras-MotR", "<p class='flavor-text'>Con la minaccia di un assassino in circolazione, Thengel ha messo Edoras in allerta.</p><p class='main-text'><b>Quando Rivelata:</b> Il primo giocatore sceglie casualmente uno dei nemici <b><i>Assassino</i></b> messi da parte e lo aggiunge all’area di allestimento. Ogni altro giocatore rivela una carta incontro.</p><p class='main-text'>I nemici <b><i>Assassino</i></b> sono immuni agli effetti delle carte dei giocatori. Fintanto che Un Assassino ad Edoras hai dei punti ricerca rimanenti, i nemici <b><i>Assassino</i></b> non possono subire danni.</p><p class='main-text'><b>Questa scena non può essere superata fintanto che un nemico <b><i>Assassino</i></b> è in gioco.</b></p>");
            AddHtml("Bitter-Betrayal-MotR", "<p class='main-text'><b>Quando Rivelata:</b> L’alleato con il costo più alto che ciascun giocatore controlla compie un attacco immediato contro il giocatore che lo controlla. Fino alla fine di questi attacchi, ciascuna di queste carte perde il tipo di carta alleato e ottiene il tipo di carta nemico. Poi, rimuovi quegli alleati dal gioco. Aggiungi Un Tradimento Amaro al riquadro vittoria. Questo effetto non può essere cancellato.</p>{victory:1}");
        }

        private void BloodInTheIsen()
        {
            AddHtml("Anduril-BitI", "<p class='main-text'>Aggregalo a un eroe con 4 o più <b><i>Tratti</i></b>.</p><p class='main-text'>Limitato. Se l’eroe aggregato è Aragorn, guadagna un’icona risorsa {sphere:Leadership}.</p><p class='main-text'>L’eroe aggregato prende +1 {Willpower}, +1 {Attack} e +1 {Defense}.</p><p class='main-text'><b>Azione:</b> Esaurisci Andúril per rendere pronto l’eroe aggregato.</p><p class='flavor-text'>“Non verrai più riposta nel fodero finché non sarà stata combattuta l’ultima battaglia.”<br/>—Aragorn, Il Ritorno del Re</p>");
            AddHtml("Aragorn-BitI", "<p class='main-text'>Aragorn guadagna un’icona risorsa corrispondente a quella di ciascun aggregato <b><i>Titolo</i></b> a lui aggregato.</p><p class='main-text'><b>Preparazione:</b> Cerca nella tua collezione un aggregato <b><i>Titolo</i></b> con un costo stampato di 1 e aggregalo a Aragorn.</p><p class='main-text'><b>Azione:</b> Rendi pronto un aggregato <b><i>Titolo</i></b> con un costo stampato di 1 aggregato a Aragorn. (Solo 1 volta per turno.)</p>");
            AddHtml("Dunland-Axe-hand-BitI", "<p class='main-text'><b>Obbligato:</b> Dopo che Asciere del Dunland ti ha ingaggiato, rimuovi 1 segnalino risorsa dall’obiettivo <b><i>Guadi</i></b> oppure infliggi 2 danni suddivisi tra i personaggi che controlli.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Il nemico attaccante prende +3 {Attack}. Puoi scartare X carte dalla tua mano per ridurre di X questo ammontare.</p>");
            AddHtml("Coordinated-Assault-BitI", "<p class='main-text'><b>Quando Rivelata:</b> Il primo giocatore deve scegliere 3 cose differenti che controlla da scartare: 1 risorsa, 1 alleato, 1 aggregato da un personaggio, 1 aggregato da un nemico, 1 aggregato da un luogo, 1 ricerca secondaria dei giocatori dall’area di allestimento oppure 1 carta casuale dalla propria mano.</p>");
            AddHtml("Narrow-Crossing-BitI", "<p class='main-text'><b>Obbligato:</b> Alla fine della fase di pianificazione, il primo giocatore piazza 3 segnalini risorsa su Attraversamento Ostico.</p><p class='main-text'><b>Obbligato:</b> Dopo aver risolto l’effetto <b><i>Obbligato</i></b> sovrastante, se il costo stampato totale delle carte che controlli è maggiore del numero di segnalini risorsa su questa carta, incrementa la tua minaccia della differenza. (Ogni giocatore deve risolvere questo effetto separatamente.)</p>");
            AddHtml("Hasty-Pursuit-BitI", "<p class='main-text'><b>Obbligato:</b> Alla fine della fase di pianificazione, il primo giocatore rimuove 1 segnalino risorsa da Inseguimento Avventato.</p><p class='main-text'><b>Obbligato:</b> Dopo aver risolto l’effetto <b><i>Obbligato</i></b> sovrastante, se il costo stampato totale delle carte che controlli è maggiore del numero di risorse su questa carta, incrementa la tua minaccia della differenza. (Ogni giocatore deve risolvere questo effetto separatamente.)</p>");
            AddHtml("Crossing-the-Fords-BitI", "<p class='flavor-text'>Il traditore Ealdwulf ha manipolato un’orda di Dunlandiani e Rohirrim traditori per tentare di usurpare il trono di Rohan. Le sue armate si sono ammassate sulle rive dell’Isen, dove i Dunlandiani si preparano a guadare. </p><p class='main-text'> <b>Preparazione:</b> Metti Ealdwulf da parte, fuori dal gioco. Aggiungi l’obiettivo <b><i>Guadi</i></b> all’area di allestimento, con il lato Attraversamento Ostico verso l’alto. Il primo giocatore aggiunge Grimrede e Sponda Occidentale all’area di allestimento. Ogni altro giocatore cerca nel mazzo incontri un nemico differente e lo aggiunge all’area di allestimento. Rimescola il mazzo incontri.</p>");
            AddHtml2("Crossing-the-Fords-BitI", "<p class='flavor-text'>Ealdwulf deve essere fermato. Ora è il momento di guadare il fiume e porre fine alla sua folle campagna, prima che Rohan anneghi nel sangue.</p><p class='main-text'> Grimrede non può essere ingaggiato intenzionalmente o subire danni.</p><p class='main-text'><b>Questa scena non può essere superata fintanto che Riva Occidentale è in gioco.</b></p>");
            AddHtml("Contested-Battleground-BitI", "<p class='main-text'>Condanna 1.</p><p class='main-text'><b>Obbligato:</b> Dopo che Campo di Battaglia Conteso è diventato il luogo attivo, rimuovi 1 segnalino risorsa dall’obiettivo <b><i>Guadi</i></b> oppure la ricerca corrente guadagna battaglia fino a che Campo di Battaglia Conteso non lascia il gioco.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Dopo questo attacco, incrementa di X la tua minaccia, dove X è l’ammontare di danni inflitti da questo attacco.</p>");
            AddHtml("Chaos-and-Confusion-BitI", "<p class='flavor-text'>Le voci e il fragore di armi e bardature erano assai vicini. Frodo e Sam aprirono i foderi delle loro piccole spade. La fuga era impossibile.<br>—Le Due Torri</p><p class='main-text'><b>Obbligato:</b> Dopo che un nemico <b><i>Dunland</i></b> ha attivato il suo effetto <b><i>Obbligato</i></b> per aver ingaggiato un giocatore, risolvi quell’effetto <b><i>Obbligato</i></b> una seconda volta. (Solo una volta per nemico per fase.)</p>{victory:10}");
            AddHtml("Chieftain-of-the-North-BitI", "<p class='main-text'>Aggregalo a un eroe <b><i>Ranger</i></b> o a Aragorn. Il personaggio aggregato guadagna il tratto <b><i>Guerriero</i></b>.</p><p class='main-text'><b>Azione:</b> Esaurisci Capitano del Nord per scegliere un alleato che sta attaccando e che condivide un <b><i>Tratto</i></b> con il personaggio aggregato. Rendi pronto quell’alleato oppure dagli +1 {Attack} per quell’attacco.</p><p class='flavor-text'>“Ecco Aragorn figlio di Arathorn, capo dei Dúnedain…”<br/>—Faramir, Il Ritorno del Re</p>");
            AddHtml("Fleet-footed-Dunlending-BitI", "<p class='main-text'><b>Obbligato:</b> Dopo che Dunlandiano Lesto ti ha ingaggiato, scarta un aggregato che controlli.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Scarta l’aggregato con il costo più alto aggregato al personaggio in difesa.</p>");
            AddHtml("Ealdwulf-BitI", "<p class='main-text'>Immune agli effetti delle carte dei giocatori.</p><p class='main-text'>Ealdwulf prende +2 punti ferita per ogni segnalino risorsa su di esso.</p><p class='main-text'><b>Obbligato:</b> Quando un segnalino risorsa dovrebbe essere rimosso da Inseguimento Avventato, invece spostalo su Ealdwulf.</p>{victory:15}");
            AddHtml("Elendilmir-BitI", "<p class='main-text'>Protetto (luogo).</p><p class='main-text'>Aggregalo a un eroe.</p><p class='main-text'>Sostituisci ogni istanza di “Solo 1 volta per fase” o “Solo 1 volta per turno” nel riquadro di testo del personaggio aggregato rispettivamente con “Solo 2 volte per fase” o “Solo 2 volte per turno”.</p><p class='main-text'><b>Azione:</b> Esaurisci Aragorn per scartare il luogo che protegge Elendilmir. Poi, aggrega Elendilmir a Aragorn.</p>");
            AddHtml("Elessar-BitI", "<p class='main-text'>Aggregalo a un eroe <b><i>Nobile</i></b> o a Aragorn. Il personaggio aggregato guadagna il tratto <b><i>Gondor</i></b>. Se il personaggio aggregato è Aragorn, guadagna il tratto <b><i>Nobile</i></b>.</p><p class='main-text'><b>Azione:</b> Esaurisci Elessar per scegliere un alleato che sta difendendo un attacco e che condivide un <b><i>Tratto</i></b> con il personaggio aggregato. Rendi pronto quell’alleato oppure dagli +1 {Defense} per quell’attacco.</p><p class='flavor-text'>“Non dite I Signori di Gondor. Dite Re Elessar.”<br/>—Principe Imrahil, Il Ritorno del Re</p>");
            AddHtml("Estel-BitI", "<p class='main-text'>Aggregalo a un eroe <b><i>Dúnedain</i></b> o a Aragorn. Il personaggio aggregato guadagna il tratto <b><i>Noldor</i></b>.</p><p class='main-text'><b>Azione:</b> Esaurisci Estel e scarta 1 carta dalla tua mano per cercare 1 carta nelle prime X carte del tuo mazzo e aggiungerla alla tua mano. Rimescola il tuo mazzo. X è il numero di <b><i>Tratti</i></b> del personaggio aggregato.</p>");
            AddHtml("Gilraen-BitI", "<p class='main-text'><b>Preparazione:</b> Puoi scegliere di dare –2 {Willpower} a Gilraen e +2 {Willpower} a altri due eroi <b><i>Dúnedain</i></b> fino alla fine della partita.</p><p class='main-text'><b>Azione:</b> Gira a faccia in su una carta ombra assegnata ad un nemico. Poi, puoi assegnare a quel nemico una carta ombra addizionale a faccia in su per pescare 1 carta. (Solo 1 volta per fase.)</p><p class='flavor-text'>“Ho dato la speranza ai Dúnedain, non ne ho conservata per me.”<br/>—Il Ritorno del Re</p>");
            AddHtml("Grey-Companion-BitI", "<p class='main-text'>Sentinella.</p><p class='main-text'><b>Risposta:</b> Quando giochi Grigio Compagno dalla tua mano, esaurisci X aggregati <b><i>Titolo</i></b> che controlli per ridurne di X il costo.</p><p class='flavor-text'>“Eppure io non vi ho invocati, se non con il pensiero.” disse Aragorn.<br/>—Il Ritorno del Re</p>");
            AddHtml("Grimrede-BitI", "<p class='main-text'>X è 1{PerPlayer}. Immune agli effetti delle carte dei giocatori. Ciascun giocatore non può ridurre la propria minaccia di più di 1 in ciascun turno.</p><p class='main-text'><b>Obbligato:</b> Quando un qualsiasi numero di segnalini risorsa dovrebbe essere piazzato sull’obiettivo <b><i>Guadi</i></b>, riduci quel numero di 1, a meno che ciascun giocatore non infligga 1 danno ad un personaggio non danneggiato che controlla.</p>{victory:5}");
            AddHtml("The-Renewer-BitI", "<p class='main-text'>Aggregalo a un eroe <b><i>Gondor</i></b> o a Aragorn. Il personaggio aggregato guadagna il tratto <b><i>Guaritore</i></b>.</p><p class='main-text'><b>Azione:</b> Esaurisci Il Rinnovatore per scegliere un alleato coinvolto nella ricerca e che condivide un <b><i>Tratto</i></b> con il personaggio aggregato. Rendi pronto quell’alleato e guariscigli 1 danno oppure dagli +1 {Willpower} fino alla fine della fase.</p><p class='flavor-text'>“Le mani di re sono mani di guaritore.”<br/>—Ioreth, Il Ritorno del Re</p>");
            AddHtml("The-Battle-is-Joined-BitI", "<p class='flavor-text'>Mentre vi addentrate nel fiume, i vostri nemici cercano di respingervi con fionde e lance. Il capitano Dunlandiano Grimrede prepara la sua ascia e si pone davanti a voi, ringhiando. “La terra delle verdi colline sarà di nuovo nostra. Ci è stata sottratta con ferro e fuoco. Così ce la riprenderemo.”</p><p class='main-text'> <b>Quando Rivelata:</b> Rimescola la pila degli scarti degli incontri nel mazzo incontri e scarta carte da esso fino a che non vengono scartati X nemici. X è 1 in meno del numero di giocatori. Aggiungi ogni nemico scartato in questo modo all’area di allestimento.</p>");
            AddHtml2("The-Battle-is-Joined-BitI", "<p class='main-text'> Grimrede ingaggia il primo giocatore. I nemici prendono –10 al proprio valore di ingaggio e non possono essere ingaggiati intenzionalmente.</p><p class='main-text'>Attraversamento Ostico non può avere più di 15 segnalini risorsa su di sé.</p><p class='main-text'><b>Grimrede non può essere sconfitto fintanto che questa scena ha dei punti ricerca rimanenti. Questa scena non può essere superata a meno che Grimrede non si trovi nel riquadro vittoria.</b></p>");
            AddHtml("The-Flight-of-Ealdwulf-BitI", "<p class='flavor-text'>Ora che le sorti della battaglia sono mutate, il traditore Ealdwulf si dà alla fuga. Il signore della guerra si dirige di gran corsa verso le terre selvagge, lasciando i suoi uomini a combattere e morire per la sua crociata disperata. </p><p class='main-text'> <b>Quando Rivelata:</b> Termina la fase attuale. Il primo giocatore aggiunge Ealdwulf all’area di allestimento. Ogni altro giocatore cerca nel mazzo degli incontri e nella pila degli scarti degli incontri una copia di Rinnegato di Rohan e lo ingaggia, se possibile. Mescola il mazzo incontri. Gira Attraversamento Ostico, mantenendo ogni segnalino risorsa su di esso.</p>");
            AddHtml2("The-Flight-of-Ealdwulf-BitI", "<p class='flavor-text'>Il cavallo del traditore è rapido e la battaglia infuria su entrambe le rive del fiume. Ealdwulf deve essere catturato e portato di fronte a Re Thengel per affrontare la giustizia.</p><p class='main-text'> Ealdwulf non può essere ingaggiato intenzionalmente fintanto che questa scena ha dei punti ricerca rimanenti.</p><p class='main-text'><b>Obbligato:</b> Dopo che un giocatore ha ridotto la propria minaccia di un qualsiasi ammontare, quel giocatore deve distribuire un pari ammontare di danni tra i personaggi che controlla.</p><p class='main-text'><b>Questa scena non può essere superata fintanto che Ealdwulf è in gioco. Quando Ealdwulf viene sconfitto, i giocatori hanno sventato i piani del traditore e vincono la partita.</b></p>");
            AddHtml("Heavy-Losses-BitI", "<p class='main-text'><b>Quando Rivelata:</b> Ogni giocatore può scartare un qualsiasi numero di personaggi che controlla. Poi, ciascun giocatore incrementa di 1 la propria minaccia per ogni personaggio danneggiato che controlla. Ogni giocatore che non incrementa la propria minaccia per questo effetto deve infliggere 1 danno a 3 personaggi differenti che controlla.</p>");
            AddHtml("Defensive-Outcrop-BitI", "<p class='main-text'>I nemici <b><i>Dunland</i></b> prendono +1 {Defense}. Questa abilità non si somma ad altre copie di Postazione Difensiva.</p><p class='main-text'>Fintanto che Postazione Difensiva è il luogo attivo, i nemici <b><i>Dunland</i></b> prendono +1 {Attack}.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Fino alla fine della fase, quando un personaggio viene dichiarato come attaccante contro questo nemico, infliggi 1 danno a quel personaggio.</p>");
            AddHtml("Open-Rebellion-BitI", "<p class='main-text'><b>Quando Rivelata:</b> Il primo giocatore infligge 1 danno a ciascun eroe che controlla e che abbia almeno 1 risorsa nella propria riserva, poi incrementa di 1 la propria minaccia per ogni eroe <b><i>Nobile</i></b> danneggiato da questo effetto.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Il nemico attaccante prende +1 {Attack} (+2 {Attack} invece se il personaggio in difesa è <b><i>Nobile</i></b>).</p>");
            AddHtml("Rohan-Renegade-BitI", "<p class='main-text'>Quando un eroe difende contro Rinnegato di Rohan, quell’eroe non conta la propria {Defense}.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Dopo questo attacco, il nemico attaccante ingaggia il giocatore successivo, poi compie un attacco immediato.</p>");
            AddHtml("Spurned-Hillman-BitI", "<p class='main-text'>Condanna 1.</p><p class='main-text'><b>Obbligato:</b> Dopo che Selvaggio Disprezzato ti ha ingaggiato, incrementa di 1 la tua minaccia.</p><p class='flavor-text'>“Morte ai Forgoil!”<br/>—Le Due Torri</p>");
            AddHtml("West-Bank-BitI", "<p class='main-text'>Immune agli effetti delle carte dei giocatori.</p><p class='main-text'>Attraversamento Ostico non può avere più di 10 segnalini risorsa su di sé.</p><p class='main-text'><b>Obbligato:</b> Alla fine del turno, incrementa di 1 la minaccia di ciascun giocatore.</p><p class='flavor-text'>“Non andare ai Guadi dell’Isen e non fermarti nella pianura.”<br/> — Gandalf, Le Due Torri</p>{victory:1}");
            AddHtml("North-South-Road-BitI", "<p class='main-text'>Viaggio: Ogni giocatore infligge 1 danno ad un personaggio che controlla.</p><p class='main-text'><b>Risposta:</b> Dopo che Strada Nord-Sud ha lasciato il gioco come luogo esplorato, piazza 1 segnalino risorsa sull’obiettivo <b><i>Guadi</i></b>.</p><p class='flavor-text'>…ove usciva il torrente fiancheggiato dalla strada…<br/>—Le Due Torri</p>");
            AddHtml("A-Fair-Exchange-BitI", "<p class='main-text'><b>Azione:</b> Scarta una carta unica dalla tua mano che condivide il titolo con una carta in gioco per cercare nel tuo mazzo una carta unica differente e aggiungerla alla tua mano. Rimescola il tuo mazzo.</p><p class='flavor-text'>“Se volete trovare qualcosa, non c’è niente di meglio che cercare.”<br/>—Thorin Scudodiquercia, Lo Hobbit</p>");
        }

        private void TheScouringOfTheShire()
        {
            AddHtml("Bright-Mail-TSotS", "<p class='main-text'>Aggregalo ad un eroe.</p><p class='main-text'>L’eroe aggregato prende +1 {Defense} (+2 {Defense} invece se è un <b><i>Hobbit</i></b>).</p><p class='flavor-text'>“Non oserebbero nulla contro gente armata con spade, elmi, scudi e tutto il resto. Ci penserebbero prima due volte.” —Omorzo Cactaceo, Il Ritorno del Re</p>");
            AddHtml("Gang-of-Ruffians-TSotS", "<p class='main-text'><b>Risposta:</b> Quando Banda di Furfanti infliggerebbe danni durante un attacco, distribuisci invece quei danni tra i personaggi controllati dal giocatore in difesa.</p><p class='main-text'><b>Obbligato:</b> Dopo che Banda di Furfanti ha attaccato, se l’attacco ha distrutto almeno 1 dei tuoi personaggi, scarta 1 carta a caso dalla tua mano e incrementa di 1 la tua minaccia.</p>");
            AddHtml("Highway-Robber-TSotS", "<p class='main-text'><b>Obbligato:</b> Quando Bandito di Strada ti attacca, scarta 1 risorsa da un eroe che controlli oppure Bandito di Strada prende +2 {Attack} per questo attacco.</p><p class='flavor-text'>Il loro lavoro sinora era stato quello di spaventare i contadini di Brea e di maltrattare gli Hobbit stupefatti.<br/>–Il Ritorno del Re</p>");
            AddHtml("Brandywine-Gate-TSotS", "<p class='main-text'>Immune agli effetti delle carte dei giocatori.</p><p class='main-text'><b>Obbligato:</b> Dopo che Cancello del Brandivino è stato esplorato, ogni giocatore cerca nel mazzo incontri 1 copia di Guardacontea dal Cappello Piumato e la mette in gioco ingaggiata con sé. Rimescola il mazzo incontri.</p><p class='flavor-text'>Ad ambedue le estremità del Ponte si ergeva un grande cancello sormontato da lunghe punte aguzze…<br/>–Il Ritorno del Re</p>{victory:1}");
            AddHtml("Wooden-Shed-TSotS", "<p class='main-text'>Fintanto che Capanno di Legno si trova nell’area di allestimento, i giocatori non possono ridurre la propria minaccia di più di 1 durante ciascun turno.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Incrementa di 1 la tua minaccia oppure esaurisci un personaggio che controlli.</p><p class='flavor-text'>Vi erano dappertutto pile d’immondizia. La porta era piena di fessure e crepe; il campanello penzolava e non emetteva alcun suono. –Il Ritorno del Re</p>");
            AddHtml("Shirriff-House-TSotS", "<p class='main-text'><b>Obbligato:</b> Dopo che Casa dei Guardacontea diventa il luogo attivo, esaurisci ogni personaggio con 2 o più {Attack} stampata.</p><p class='main-text'><b>Obbligato:</b> Dopo che Casa dei Guardacontea lascia il gioco, il primo giocatore cerca nel mazzo incontri e nella pila degli scarti degli incontri un nemico <b><i>Guardacontea</i></b> e lo mette in gioco ingaggiato con sé, se possibile. Rimescola il mazzo incontri.</p><p class='flavor-text'>L’interno era umido e squallido… –Il Ritorno del Re</p>");
            AddHtml("Scatha's-Horn-TSotS", "<p class='main-text'>Aggregalo ad un eroe.</p><p class='main-text'><b>Azione:</b> Esaurisci Corno di Scatha per scegliere un nemico attaccante. Fino alla fine della fase, quel nemico prende −1 {Attack} (−2 {Attack} invece se il personaggio in difesa è un eroe <b><i>Hobbit</i></b>).</p><p class='flavor-text'>“Colui che lo suona nell’ora del bisogno, desterà paura nei cuori dei nemici…” —Éowyn, Il Ritorno del Re</p>");
            AddHtml("Rouse-the-Cottons-TSotS", "<p class='flavor-text'>“Ma ora andiamo, ragazzi! Lungacque si ribella! Dobbiamo dare una mano!” –Tom Cotton, Il Ritorno del Re</p><p class='main-text'>Fintanto che Destare i Cotton è la ricerca in corso, gli alleati non possono essere coinvolti nella ricerca.</p><p class='main-text'><b>Obbligato:</b> Quando questa ricerca viene superata, girala e rivelala.</p>");
            AddHtml("Brick-Building-TSotS", "<p class='main-text'>Fintanto che Edificio di Mattoni si trova nell’area di allestimento, i personaggi non possono essere curati.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Infliggi 2 danni al personaggio in difesa oppure incrementa di 2 la tua minaccia.</p><p class='flavor-text'>Passando davanti a lunghe file di squallide case nuove, videro il nuovo mulino in tutta la sua sporca bruttezza.<br/>–Il Ritorno del Re</p>");
            AddHtml("Set-up-Barriers-TSotS", "<p class='flavor-text'>Altri, seguendo gli ordini di Merry, ergevano barricate attraverso la strada ad ambedue le estremità del paesino. –Il Ritorno del Re</p><p class='main-text'>I giocatori non possono pescare carte.</p><p class='main-text'><b>Azione:</b> I giocatori come gruppo possono spendere 1{PerPlayer} risorse per piazzare 3 segnalini progresso su Ergere Barricate.</p><p class='main-text'><b>Obbligato:</b> Dopo che questa ricerca viene superata, il primo giocatore scarta tutte le risorse da ciascun eroe che controlla. Poi, gira questa carta e rivelala.</p>");
            AddHtml("Thieving-Ruffian-TSotS", "<p class='main-text'><b>Obbligato:</b> Quando Farabutto ti attacca, incrementa la tua minaccia di 1 per ogni aggregato sul personaggio difensore.</p><p class='flavor-text'>“La prendete su questo tono? Fareste bene a cambiarlo, o ve lo faremo cambiare noi.” –Il Ritorno del Re</p>");
            AddHtml("Frodo-Baggins-TSotS", "<p class='main-text'><b>Risposta:</b> Dopo che Frodo Baggins è stato coinvolto nella ricerca, spendi 1 risorsa dalla sua riserva per rendere pronto un altro personaggio unico coinvolto nella ricerca. Se hai successo nella ricerca in questa fase, riduci di 1 la tua minaccia.</p>");
            AddHtml("Frodo-Baggins-Fellowship-TSotS", "<p class='main-text'>Il primo giocatore prende il controllo di Frodo Baggins.</p><p class='main-text'><b>Risposta:</b> Dopo che Frodo Baggins è stato dichiarato come difensore, spendi 1 risorsa [fellowship] per ridurre la tua minaccia della {Threat} del nemico attaccante. Fino alla fine del turno, quel nemico non può subire danni.</p><p class='main-text'><b>Se Frodo Baggins lascia il gioco, i giocatori perdono la partita.</b></p>");
            AddHtml("Star-like-Gem-TSotS", "<p class='main-text'>Aggregalo ad un’eroe.</p><p class='main-text'>L’eroe aggregato prende +1 {Willpower}.</p><p class='main-text'>Se l’eroe aggregato è un <b><i>Hobbit</i></b>, esso non si esaurisce per essere coinvolto nella ricerca.</p><p class='flavor-text'>“Quando ti sentirai turbato dal ricordo della paura e dell’oscurità, questo ti sarà d’aiuto.”<br/>–Arwen, Il Ritorno del Re</p>");
            AddHtml("Feather-capped-Shirriff-TSotS", "<p class='main-text'>Indistruttibile. Il danno inflitto ai tuoi personaggi da Guardacontea dal Cappello Piumato viene invece assegnato come incremento alla tua minaccia.</p><p class='main-text'>Fintanto che la minaccia del giocatore ingaggiato è più alta del valore di ingaggio di Guardacontea dal Cappello Piumato, esso prende +1 {Attack}.</p>");
            AddHtml("Roused-Hobbits-TSotS", "<p class='main-text'>Fintanto che è aggregata, conta come un aggregato <b><i>Condizione</i></b> con il testo: “La carta aggregata perde indistruttibile e il tipo di carta nemico e guadagna il tipo di carta alleato con {Willpower} pari alla propria {Threat}. Se Hobbit Destati o la carta a cui è aggregata lasciano il gioco, rimuovi entrambe le carte dalla partita.”</p><p class='main-text'><b>Quando Rivelata:</b> Scegli 1 giocatore, quel giocatore prende il controllo di un nemico <b><i>Guardacontea</i></b> ingaggiato con sé e aggrega Hobbit Destati a quel nemico (se non ci sono nemici <b><i>Guardacontea</i></b> ingaggiati, rimuovi Hobbit Destati dalla partita).</p>");
            AddHtml("Roused-Hobbits-2-TSotS", "<p class='main-text'>Fintanto che è aggregata, conta come un aggregato <b><i>Condizione</i></b> con il testo: “La carta aggregata perde indistruttibile e il tipo di carta nemico e guadagna il tipo di carta alleato con {Willpower} pari alla propria {Threat}. Se Hobbit Destati o la carta a cui è aggregata lasciano il gioco, rimuovi entrambe le carte dalla partita.”</p><p class='main-text'><b>Quando Rivelata:</b> Scegli 1 giocatore, quel giocatore prende il controllo di un nemico <b><i>Guardacontea</i></b> ingaggiato con sé e aggrega Hobbit Destati a quel nemico (se non ci sono nemici <b><i>Guardacontea</i></b> ingaggiati, rimuovi Hobbit Destati dalla partita).</p>");
            AddHtml("Roused-Hobbits-3-TSotS", "<p class='main-text'>Fintanto che è aggregata, conta come un aggregato <b><i>Condizione</i></b> con il testo: “La carta aggregata perde indistruttibile e il tipo di carta nemico e guadagna il tipo di carta alleato con {Willpower} pari alla propria {Threat}. Se Hobbit Destati o la carta a cui è aggregata lasciano il gioco, rimuovi entrambe le carte dalla partita.”</p><p class='main-text'><b>Quando Rivelata:</b> Scegli 1 giocatore, quel giocatore prende il controllo di un nemico <b><i>Guardacontea</i></b> ingaggiato con sé e aggrega Hobbit Destati a quel nemico (se non ci sono nemici <b><i>Guardacontea</i></b> ingaggiati, rimuovi Hobbit Destati dalla partita).</p>");
            AddHtml("Roused-Hobbits-4-TSotS", "<p class='main-text'>Fintanto che è aggregata, conta come un aggregato <b><i>Condizione</i></b> con il testo: “La carta aggregata perde indistruttibile e il tipo di carta nemico e guadagna il tipo di carta alleato con {Willpower} pari alla propria {Threat}. Se Hobbit Destati o la carta a cui è aggregata lasciano il gioco, rimuovi entrambe le carte dalla partita.”</p><p class='main-text'><b>Quando Rivelata:</b> Scegli 1 giocatore, quel giocatore prende il controllo di un nemico <b><i>Guardacontea</i></b> ingaggiato con sé e aggrega Hobbit Destati a quel nemico (se non ci sono nemici <b><i>Guardacontea</i></b> ingaggiati, rimuovi Hobbit Destati dalla partita).</p>");
            AddHtml("Roused-Hobbits-5-TSotS", "<p class='main-text'>Fintanto che è aggregata, conta come un aggregato <b><i>Condizione</i></b> con il testo: “La carta aggregata perde indistruttibile e il tipo di carta nemico e guadagna il tipo di carta alleato con {Willpower} pari alla propria {Threat}. Se Hobbit Destati o la carta a cui è aggregata lasciano il gioco, rimuovi entrambe le carte dalla partita.”</p><p class='main-text'><b>Quando Rivelata:</b> Scegli 1 giocatore, quel giocatore prende il controllo di un nemico <b><i>Guardacontea</i></b> ingaggiato con sé e aggrega Hobbit Destati a quel nemico (se non ci sono nemici <b><i>Guardacontea</i></b> ingaggiati, rimuovi Hobbit Destati dalla partita).</p>");
            AddHtml("Roused-Hobbits-6-TSotS", "<p class='main-text'>Fintanto che è aggregata, conta come un aggregato <b><i>Condizione</i></b> con il testo: “La carta aggregata perde indistruttibile e il tipo di carta nemico e guadagna il tipo di carta alleato con {Willpower} pari alla propria {Threat}. Se Hobbit Destati o la carta a cui è aggregata lasciano il gioco, rimuovi entrambe le carte dalla partita.”</p><p class='main-text'><b>Quando Rivelata:</b> Scegli 1 giocatore, quel giocatore prende il controllo di un nemico <b><i>Guardacontea</i></b> ingaggiato con sé e aggrega Hobbit Destati a quel nemico (se non ci sono nemici <b><i>Guardacontea</i></b> ingaggiati, rimuovi Hobbit Destati dalla partita).</p>");
            AddHtml("Roused-Hobbits-7-TSotS", "<p class='main-text'>Fintanto che è aggregata, conta come un aggregato <b><i>Condizione</i></b> con il testo: “La carta aggregata perde indistruttibile e il tipo di carta nemico e guadagna il tipo di carta alleato con {Willpower} pari alla propria {Threat}. Se Hobbit Destati o la carta a cui è aggregata lasciano il gioco, rimuovi entrambe le carte dalla partita.”</p><p class='main-text'><b>Quando Rivelata:</b> Scegli 1 giocatore, quel giocatore prende il controllo di un nemico <b><i>Guardacontea</i></b> ingaggiato con sé e aggrega Hobbit Destati a quel nemico (se non ci sono nemici <b><i>Guardacontea</i></b> ingaggiati, rimuovi Hobbit Destati dalla partita).</p>");
            AddHtml("The-Very-Last-Stroke-TSotS", "<p class='flavor-text'>Ora che le forze di Sharkey sono state sconfitte, Frodo si incammina per confrontarsi con lui. Con sua grande sorpresa scopre che si tratta nientemeno che di Saruman, con Gríma Vermilinguo terrorizzato al suo fianco. </p><p class='main-text'> <b>Quando Rivelata:</b> Se è la fase di combattimento, termina la fase. Aggiungi Saruman e La Scelta di Gríma all’area di allestimento.</p>");
            AddHtml2("The-Very-Last-Stroke-TSotS", "<p class='flavor-text'>“Sei saggio, e crudele. Hai rubato la dolcezza della mia vendetta, e ora devo partire con amarezza…” –Saruman, Il Ritorno del Re</p><p class='main-text'> X è pari a 6{PerPlayer}. Questa scena non può avere un numero di segnalini progresso maggiore del numero di segnalini risorsa presenti su Saruman.</p><p class='main-text'><b>Azione:</b> Esaurisci un eroe e scarta 1 risorsa dalla sua riserva. Poi, aggiungi 1 risorsa a Saruman.</p><p class='main-text'><b>Questa scena non può essere superata fintanto che Saruman è in gioco. Quando questa scena viene superata, i giocatori vincono la partita.</i></b></p>");
            AddHtml("The-Battle-of-Bywater-TSotS", "<p class='main-text'> <b>Quando Rivelata:</b> Rimescola la pila degli scarti degli incontri nel mazzo incontri. Rimuovi dal gioco ogni nemico <b><i>Guardacontea</i></b> in gioco e nel mazzo incontri. Il primo giocatore cerca nel mazzo incontri e nel riquadro vittoria 1 copia di Banda di Furfanti e la mette in gioco ingaggiata con sé. Poi, ogni giocatore non ingaggiato con un nemico <b><i>Furfante</i></b> cerca un nemico <b><i>Furfante</i></b> nel mazzo incontri e lo mette in gioco ingaggiato con sé. Rimescola il mazzo incontri. Scarta carte dal mazzo incontri fino a che non vengono scartati X nemici e aggiungili all’area di allestimento. X è il numero di ricerche secondarie degli incontri in gioco. Poi, rimuovi dal gioco quelle ricerche secondarie.</p>");
            AddHtml2("The-Battle-of-Bywater-TSotS", "<p class='flavor-text'>Ma non era facile ormai intimidire i furfanti. Alcuni di essi obbedirono, ma furono immediatamente costretti dai loro compagni a non cedere. Una ventina o più cercò di evadere assaltando i carri. –Il Ritorno del Re</p><p class='main-text'> Fintanto che almeno un nemico <b><i>Furfante</i></b> in gioco ha ancora dei punti ferita rimanenti, tutti i nemici <b><i>Furfante</i></b> guadagnano indistruttibile.</p><p class='main-text'><b>Se non ci sono nemici in gioco, avanza alla prossima scena della ricerca.</i></b></p>");
            AddHtml("Grima's-Choice-TSotS", "<p class='flavor-text'>“Vermilinguo!” gridò Frodo. “Non sei obbligato a seguirlo. Non so di alcun male che tu mi abbia fatto. Troverai qui riparo e cibo, finché sarai più forte e potrai andartene per i fatti tuoi.” –Il Ritorno del Re</p><p class='main-text'>X è pari a 3{PerPlayer}. Questa scena non può essere scelta come la ricerca in corso fintanto che Il Colpo Definitivo ha ancora dei punti ricerca rimanenti.</p><p class='main-text'><b>Obbligato:</b> Quando questa ricerca viene superata, scarta Saruman dal gioco.</p>");
            AddHtml("The-Lockholes-TSotS", "<p class='main-text'>Pericolo.</p><p class='main-text'><b>Quando Rivelata:</b> Rimuovi tutti gli aggregati e scarta tutti i segnalini dall’alleato con il costo più alto che controlli e aggrega quell’alleato a faccia in giù sotto Le Cellechiuse.</p><p class='main-text'><b>Obbligato:</b> Dopo che Le Cellechiuse è stato esplorato, fai ritornare l’alleato aggregato ad essa sotto il controllo del suo proprietario, a faccia in su e pronto.</p><p class='flavor-text'>“Quelle che chiamano le Cellechiuse: i vecchi magazzini in forma di tunnel, a Pietraforata: li hanno trasformati in prigioni per coloro che si ribellano.” –Tom Cotton, Il Ritorno del Re</p>");
            AddHtml("Once-Beloved-Place-TSotS", "<p class='main-text'>Impeto.</p><p class='main-text'><b>Quando Rivelata:</b> Ogni luogo prende +1 {Threat} fino alla fine del turno. Se ci sono meno di 1{PerPlayer} luoghi nell’area di allestimento, scarta carte dal mazzo incontri fino a che non viene scartato un luogo ed aggiungilo all’area di allestimento.</p><p class='flavor-text'>Tutti i castagni erano scomparsi. Gli argini e le siepi erano rotti. –Il Ritorno del Re</p>");
            AddHtml("Curse-Upon-the-Shire-TSotS", "<p class='main-text'><b>Quando Rivelata:</b> Ogni giocatore deve scegliere: Incrementa di 1 la tua minaccia per ogni alleato che controlli oppure scarta 2 alleati che controlli.</p><p class='flavor-text'>“Vi auguro che nella vostra terra la foglia da pipa possa mancare per molto tempo!”<br/>–Saruman, Il Ritorno del Re</p>");
            AddHtml("Many-Partings-TSotS", "<p class='flavor-text'>Quando al calar del sole uscirono di nuovo in aperta campagna, raggiunsero un vecchio curvo su di un bastone, vestito di stracci grigi o bianchi sporchi, seguito alle calcagna da un altro mendico, che camminava strisciando e gemendo. –Il Ritorno del Re </p><p class='main-text'> <b>Preparazione:</b> Crea il mazzo Sharkey (vedi inserto). Metti da parte, fuori dal gioco, Saruman, Cancello del Brandivino, Pietra dei Tre Decumani e tutte le ricerche secondarie degli incontri. Mescola il mazzo incontri.</p>");
            AddHtml2("Many-Partings-TSotS", "<p class='flavor-text'>“Non v’importa la miseria di un mendicante, a voi! Perché avete tutto ciò che volete, cibo e bei vestiti, e l’erba migliore per le vostre pipe. Oh sì, lo so! So da dove proviene. Ma certo non ne dareste un pizzico ad un mendicante, vero?”<br>–Saruman, Il Ritorno del Re</p><p class='main-text'> <b>Azione:</b> Esaurisci un eroe non [fellowship] e scarta una risorsa dalla sua riserva. Poi, aggiungi 2 risorse alla carta Saruman messa da parte.</p><p class='main-text'><b>Alla fine della fase di pianificazione, avanza alla prossima scena.</i></b></p>");
            AddHtml("The-Scouring-of-the-Shire-Campaign-TSotS", "<p class='main-text'> State giocando in Modalità Campagna.</p><p class='main-text'><b>Preparazione:</b> Ogni giocatore può cambiare gli eroi che controlla senza ricevere la penalità di minaccia +1. I giocatori hanno guadagnato le seguenti carte vantaggio: Gemma a Stella, Armatura Lucente, Corno di Scatha e Statura Incrementata. Ogni giocatore ne sceglie una e la aggiunge alla propria mano. Se una o più di queste carte vantaggio non vengono scelte, un giocatore può scegliere una di esse e mescolarla nel proprio mazzo. Rimuovi dal gioco le carte che non sono state scelte. Rimuovi Gwaihir e tutti gli svantaggi dalla Riserva della Campagna. Aggrega a Frodo Baggins Ferita Dolorosa da La Strada si Oscura.</p><p class='flavor-text'>Dopo il calar della notte, stanchi e bagnati, i viaggiatori giunsero infine al Brandivino, e trovarono la strada sbarrata.<br/>–Il Ritorno del Re </p>");
            AddHtml2("The-Scouring-of-the-Shire-Campaign-TSotS", "<p class='main-text'> Risoluzione: Ogni giocatore ha guadagnato la carta contratto Verso L’Ovest e può ora usarla nei propri mazzi.</p><p class='flavor-text'>Allora Elrond e Galadriel ripresero il cammino; la Terza Era era infatti finita, e i Giorni degli Anelli ormai passati, e si concludevano così la storia e i canti di quei tempi. E con essi se ne andavano molti Elfi di Alto Lignaggio che non volevano più dimorare nella Terra di Mezzo; e in mezzo a loro, pieni di una tristezza benedetta e priva di ogni amarezza, cavalcavano Sam, e Frodo, e Bilbo, e gli Elfi erano felici di poterli onorare.<br/>–Il Ritorno del Re</p>");
            AddHtml("Three-Farthing-Stone-TSotS", "<p class='main-text'>X è pari a 3{PerPlayer}. Immune agli effetti delle carte dei giocatori. I giocatori non possono viaggiare qui a meno che non ci siano 12 segnalini progresso su Scortare i “Prigionieri”.</p><p class='main-text'><b>Risposta:</b> Dopo che Pietra dei Tre Decumani lascia il gioco come luogo esplorato, ogni giocatore riduce di 3 la propria minaccia.</p><p class='flavor-text'>Erano affamati e indolenziti, e non riuscivano più a tenere il passo. –Il Ritorno del Re</p>{victory:1}");
            AddHtml("Take-Him-Lads-TSotS", "<p class='main-text'><b>Quando Rivelata:</b> Incrementa di 4 la minaccia di ciascun giocatore. Poi, ogni giocatore scarta un alleato che controlla e riduce di X la propria minaccia, dove X è il costo stampato dell’alleato scartato.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Se la tua minaccia è superiore al valore di ingaggio del nemico attaccante, esso prende +2 {Attack}. Altrimenti, incrementa di 2 la tua minaccia.</p>");
            AddHtml("Muster-the-Tooks-TSotS", "<p class='flavor-text'>“Non sono più di quattordici miglia prendendo per i campi. Vi riporterò un esercito di Tuc domattina.” –Pipino, Il Ritorno del Re</p><p class='main-text'>X è pari a 4{PerPlayer}. Fintanto che Radunare i Tuc è la ricerca in corso, gli eroi non possono essere coinvolti nella ricerca.</p><p class='main-text'><b>Obbligato:</b> Dopo che questa ricerca viene superata, girala e rivelala.</p>");
            AddHtml("Saruman-TSotS", "<p class='main-text'>Immune agli effetti delle carte dei giocatori. Non può essere ingaggiato. Gli effetti delle carte <b><i>Sharkey</i></b> non possono essere cancellati.</p><p class='main-text'><b>Obbligato:</b> Alla fine del passo di allestimento, aggiungi 1{PerPlayer} segnalini risorsa a Saruman e rivela la prima carta del mazzo Sharkey. Poi, metti la carta rivelata in fondo al mazzo Sharkey.</p>");
            AddHtml("Escorted-Prisoners-TSotS", "<p class='flavor-text'>Dopo un breve incontro sul cammino con Saruman, ormai caduto in disgrazia, Frodo e i suoi compagni incontrano ostilità e resistenza da parte dei Guardacontea. </p><p class='main-text'> <b>Quando Rivelata:</b> Rendi Cancello del Brandivino il luogo attivo e aggiungi Pietra dei Tre Decumani all’area di allestimento.</p>");
            AddHtml2("Escorted-Prisoners-TSotS", "<p class='flavor-text'>“Per ordine del Capo, dovete seguirci in silenzio. Vi porteremo a Lungacque e vi affideremo alle guardie del Capo.” –Il Ritorno del Re</p><p class='main-text'> Questa scena non può avere più di 12 segnalini progresso.</p><p class='main-text'><b>Obbligato:</b> Alla fine della fase di ricerca, se non sono stati piazzati dei segnalini progresso su questa scena per aver avuto successo nella ricerca, il primo giocatore incrementa di 3 la propria minaccia. Se sono stati piazzati più di 3 segnalini progresso, ogni giocatore ingaggiato con 2 o più nemici <b><i>Guardacontea</i></b> può scartarne 1 di essi.</p><p class='main-text'><b>I giocatori non possono superare questa scena fintanto che Pietra dei Tre Decumani è in gioco.</i></b></p>");
            AddHtml("Raise-the-Shire-TSotS", "<p class='flavor-text'>La Contea è caduta nelle mani di un gruppo di banditi guidati da un uomo noto come “Sharkey”. Frodo e i suoi amici devono preparare Lungacque e raccogliere supporto per affrontarlo. </p><p class='main-text'> <b>Quando Rivelata:</b> Scegli casualmente 3 delle ricerche secondarie non <b><i>Fato</i></b> messe da parte e mettile in gioco (4 ricerche secondarie invece se ci sono 3 o 4 giocatori nella partita).</p>");
            AddHtml2("Raise-the-Shire-TSotS", "<p class='flavor-text'>Ma non appena videro come stavano le cose, i più si tolsero le piume e si unirono ai rivoltosi. –Il Ritorno del Re</p><p class='main-text'> <b>Obbligato:</b> Dopo che una ricerca secondaria viene superata, cerca nel mazzo incontri un nemico <b><i>Guardacontea</i></b> e aggiungilo all’area di allestimento. Rimescola il mazzo incontri.</p><p class='main-text'><b>Obbligato:</b> Alla fine del turno, piazza 1 segnalino risorsa su questa scena e aumenta la minaccia di ciascun giocatore del numero totale di segnalini risorsa presenti su questa scena. Poi, i giocatori possono scegliere di avanzare alla prossima scena della ricerca.</p>");
            AddHtml("Watch-the-East-Road-TSotS", "<p class='flavor-text'>“Vi è una grossa banda a circa quattro miglia di distanza…Sono circa un centinaio, e bruciano ogni cosa lungo la strada.” —Merry, Il Ritorno del Re</p><p class='main-text'>Non può essere scelta come la ricerca in corso. X è pari a 1 più 1{PerPlayer}.</p><p class='main-text'><b>Risposta:</b> All’inizio del passo di allestimento, piazza un qualsiasi numero di segnalini progresso su questa scena e rivela un pari numero di carte degli incontri.</p><p class='main-text'><b>Obbligato:</b> Quando questa ricerca viene superata, girala e rivelala.</p>");
            AddHtml("Heightened-Stature-TSotS", "<p class='main-text'>Aggregalo ad un eroe.</p><p class='main-text'>L’eroe aggregato ottiene +2 punti ferita (+3 punti ferita invece se l’eroe aggregato è un <b><i>Hobbit</i></b>).</p><p class='main-text'><b>Azione:</b> Esaurisci Statura Incrementata per guarire 1 danno dall’eroe aggregato.</p><p class='flavor-text'>“I mortali non possono bere le pozioni degli Ent e credere che abbiano lo stesso effetto di un bicchiere di birra!”<br/>–Gimli, Il Ritorno del Re</p>");
            AddHtml("Hobbiton-Road-TSotS", "<p class='main-text'><b>Obbligato:</b> Dopo che Strada di Hobbiville lascia il gioco, metti in gioco ingaggiato con un giocatore un nemico <b><i>Furfante</i></b> dalla pila degli scarti.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Infliggi 1 danno al personaggio in difesa oppure incrementa di 1 la tua minaccia.</p>");
            AddHtml("To-the-Road-Again-Worm-TSotS", "<p class='main-text'><b>Quando Rivelata:</b> Scarta carte dal mazzo incontri fino a che non viene scartato un luogo e aggiungi quel luogo all’area di allestimento. Infliggi X danni tra i personaggi in gioco, dove X è il doppio del numero di luoghi in gioco (3 volte il numero di luoghi in gioco se ci sono 3 o più giocatori nella partita).</p><p class='flavor-text'>Saruman rise. “Tu fai quello che dice Sharkey, vero, Verme?” –Il Ritorno del Re</p>");
            AddHtml("Awake-Fire-Foes-Awake-TSotS", "<p class='flavor-text'>Aveva percorso un breve tratto, quando udì all’improvviso il chiaro suono di un corno squillare in cielo. Echeggiò per colli e campi… –Il Ritorno del Re</p><p class='main-text'>Azione di Incontri: Ingaggia un nemico <b><i>Furfante</i></b> dalla pila degli scarti degli incontri. Ogni giocatore può attivare questo effetto.</p><p class='main-text'><b>Risposta:</b> Dopo che il primo giocatore ha distrutto un nemico, piazza 2 segnalini progresso su questa ricerca.</p><p class='main-text'><b>Obbligato:</b> Quando questa ricerca viene superata, girala e rivelala.</p>");
            AddHtml("Ambush-Sharkey's-Men-TSotS", "<p class='flavor-text'>“Ti avevo avvertito di non tornare. Ti avverto di nuovo: sei in piena luce…”<br>–Merry, Il Ritorno del Re</p><p class='main-text'><b>Obbligato:</b> Dopo che 1 copia di Banda di Furfanti è stata sconfitta, gira questa carta e rivelala.</p><p class='main-text'><b>Obbligato:</b> Alla fine della fase di ricerca, se non sono stati piazzati segnalini progresso su Tendere un Agguato in questo turno, cerca nel mazzo incontri e nella pila degli scarti degli incontri un nemico <b><i>Furfante</i></b> e aggiungilo all’area di allestimento. Rimescola il mazzo incontri.</p>");
            AddHtml("Find-the-Gaffer-TSotS", "<p class='flavor-text'>“Ma voglio vederlo. Quel Capo e quel Sharkey di cui parlavano potrebbero combinare dei guai lassù prima che venga il mattino.” –Sam, Il Ritorno del Re</p><p class='main-text'><b>Azione di Viaggio:</b> Esaurisci un eroe che controlli per piazzare 2 segnalini progresso su Trovare il Gaffiere. Non puoi dichiarare difensori o attaccanti questo turno. Ogni giocatore può attivare questo effetto.</p><p class='main-text'><b>Obbligato:</b> Quando questa ricerca viene superata, girala e rivelala.</p>");
            AddHtml("One-Ill-Turn-TSotS", "<p class='main-text'><b>Quando Rivelata:</b> Infliggi 1 danno ad ogni personaggio esausto. Fino alla fine del turno, i personaggi non possono essere curati.</p><p class='flavor-text'>“E sarebbe stata una lezione ancora più severa, se mi aveste lasciato più tempo…”<br/>–Saruman, Il Ritorno del Re</p>");
            AddHtml("Hidden-Blade-TSotS", "<p class='main-text'><b>Quando Rivelata:</b> Saruman compie un attacco immediato contro ciascun giocatore in ordine di gioco. Gli alleati non possono essere dichiarati come difensori contro Saruman.</p><p class='flavor-text'>Una lama scintillò fra le sue mani ed egli colpì rapido come un baleno. –Il Ritorno del Re</p>");
            AddHtml("Sharkey's-Men-TSotS", "<p class='main-text'><b>Quando Rivelata:</b> Ogni nemico in gioco prende +1 {Attack} fino alla fine del turno. Se ci sono meno di 1{PerPlayer} nemici <b><i>Furfante</i></b> in gioco, scarta carte dal mazzo incontri fino a che non viene scartato un nemico <b><i>Furfante</i></b> e aggiungilo all’area di allestimento.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Se il nemico attaccante è un <b><i>Furfante</i></b>, esso compie un attacco addizionale. Il giocatore in difesa può scartare un alleato che controlla per cancellare questo effetto.</p>");
            AddHtml("Into-the-West-TSotS", "<p class='main-text'> Non puoi usare questo contratto a meno che tu non abbia distrutto l’Unico Anello e vinto <i>Percorrendo la Contea</i> in modalità campagna.</p><p class='main-text'>Il tuo mazzo deve contenere un egual numero di carte con un costo stampato pari a 1, 2, 3, 4 e 5.</p><p class='main-text'><b>Preparazione:</b> Cerca nelle prime 10 carte del tuo mazzo un alleato con un costo stampato di 3 o inferiore e mettilo in gioco. Mescola il tuo mazzo e gira questa carta.</p><p class='flavor-text'>“Ho tentato di salvare la Contea, ed è stata salvata, ma non per merito mio.” –Frodo Baggins, Il Ritorno del Re</p>");
            AddHtml2("Into-the-West-TSotS", "<p class='main-text'> Verso L’Ovest è immune agli effetti delle altre carte e non può lasciare il tuo controllo o lasciare il gioco. Gli alleati che giochi dalla tua mano non possono avere il proprio costo ridotto di più di 2.</p><p class='main-text'><b>Obbligato:</b> All’inizio della fase di ricerca, aggrega Verso L’Ovest ad un alleato che controlli, se possibile.</p><p class='main-text'><b>Obbligato:</b> Alla fine della fase di riordino, distribuisci X risorse tra gli eroi che controlli, dove X è il costo stampato della carta aggregata. Rimuovi la carta aggregata dal gioco e pesca 1 carta.</p>");
            AddHtml("East-Road-TSotS", "<p class='main-text'>Pericolo.</p><p class='main-text'><b>Quando Rivelata:</b> Incrementa di 2 la tua minaccia oppure Via Orientale guadagna Impeto.</p><p class='flavor-text'>Dopo una svolta, a un paio di centinaia di metri dalla strada maestra, incontrarono una robusta barricata fatta di vecchi carri capovolti. Furono costretti a fermarsi.<br/>–Il Ritorno del Re</p>");
            AddHtml("Against-the-Rules-TSotS", "<p class='main-text'><b>Quando Rivelata:</b> Esaurisci ogni personaggio con 3 o più {Attack} stampata. Ogni giocatore che non ha esaurito alcun personaggio per via di questo effetto deve scegliere ed esaurire 1 personaggio che controlla.</p>{shadow}<p class='shadow-text'><b>Ombra:</b> Se la tua minaccia è superiore al valore di ingaggio del nemico attaccante, esso prende +2 {Attack}. Altrimenti, incrementa di 2 la tua minaccia.</p>");
            AddHtml("Voice-of-Saruman-TSotS", "<p class='main-text'><b>Quando Rivelata:</b> Scegli casualmente 1 alleato controllato da ciascun giocatore, se possibile. Ogni giocatore infligge X danni ad uno dei suoi eroi, dove X è la {Attack} del proprio alleato scelto tramite questo effetto, poi scarta quell’alleato. Se un giocatore non controlla alleati, quel giocatore invece scarta tutti gli aggregati dall’eroe che controlla con il maggiore numero di aggregati.</p><p class='flavor-text'>“Ha perso tutto il suo potere, eccetto la sua voce, che può ancora intimorirvi e illudervi, se glielo permettete.”<br/>—Frodo Baggins, Il Ritorno del Re</p>");
        }
    }
}