{
    public class PolishTemplateRepository
        : TemplateRepository
    {
        protected override void Initialize()
        {
            ChildrenOfEorl();
            TheAldburgPlot();
            TheScouringOfTheShire();
        }

        private void ChildrenOfEorl()
        {
            AddHtml("Ingold-CoE", "<p class='main-text'><b>Odpowiedź:</b> Kiedy sprzymierzeniec z cechą <b><i>Gondor</i></b> lub <b><i>Wojownik</i></b> wchodzi do gry pod twoją kontrolą, dobierz 1 kartę. Następnie, wydaj 1 zasób z puli zasobów Ingolda lub zwiększ swój poziom zagrożenia o 1. (Limit raz na fazę.)</p><p class='flavor-text'>“Nie chcemy w dzisiejszych czasach mieć w swoim kraju obcych gości, chyba że są to mężni i zbrojni wojownicy, na których pomocy i wierności możemy polegać.” — Powrót Króla</p>");
            AddHtml("Celador-CoE", "<p class='main-text'>{keyword:Dystans.}</p><p class='main-text'>Zredukuj o 1 całkowity {Threat} strefy przeciwności za każdą kontrolowaną przez ciebie i nie dołączoną do wroga <b><i>Pułapkę</i></b>.</p><p class='main-text'><b>Odpowiedź:</b> Kiedy kontrolowana przez ciebie karta z cechą <b><i>Pułapka</i></b> zostanie dołączona do jakiegoś wroga, przygotuj Celadora lub zmniejsz swój poziom zagrożenia o 1.</p><p class='flavor-text'>“Rozum wzbrania ufać zbytnio obcym, spotkanym przypadkowo w tych okolicach.” — Faramir, Dwie Wieże</p>");
            AddHtml("Thengel-CoE", "<p class='main-text'><b>Odpowiedź:</b> Kiedy sprzymierzeniec zostanie odrzucony z gry, właściciel tego sprzymierzeńca przeszukuje 5 wierzchnich kart swojej talii i umieszcza w grze sprzymierzeńca, który posiada przynajmniej 1 <b><i>Cechę</i></b> odrzuconego sprzymierzeńca. Umieść wybranego sprzymierzeńca w grze wyczerpanego. Następnie dany gracz tasuje swoją talię. (Limit raz na rundę.)</p><p class='flavor-text'>“Przyjacielu! — rzekł Gandalf — Ty miałeś konie, zbrojne wyprawy, swobodę otwartych stepów…” — Powrót Króla</p>");
            AddHtml("Herubrand-CoE", "<p class='main-text'>Herubrand otrzymuje +1 {Willpower} za każdego kontrolowanego przez ciebie bohatera z dołączonym co najmniej 1 dodatkiem ze słowem kluczowym Ograniczenie.</p><p class='main-text'><b>Odpowiedź:</b> Kiedy w strefie przeciwności zostanie zabity wróg, dobierz 1 kartę.</p><p class='flavor-text'>“Kogo niesiecie, przyjaciele z Rohanu?” — Imrahil, Powrót Króla</p>");
            AddHtml("Deeping-Bowman-CoE", "<p class='main-text'>{keyword:Oddany.} {keyword:Dystans.}</p><p class='main-text'><b>Akcja:</b> Odrzuć Helmowego Łucznika, aby wybrać wroga. Do końca fazy ten wróg nie wlicza swojej {Defense} kiedy przydzielane są obrażenia.</p><p class='flavor-text'>“Nie mamy już ani jednej strzały w kołczanach, trupami orków wypełniła się fosa.” — Dwie Wieże</p>");
            AddHtml("Open-the-Gates-CoE", "<p class='main-text'><b>Akcja:</b> Przeszukaj 5 wierzchnich kart swojej talii, wybierz sprzymierzeńca dzielącego <b><i>Cechę</i></b> z każdym z twoich bohaterów i umieść go w grze. Potasuj swoją talię. Jeśli ten sprzymierzeniec pod koniec fazy nadal znajduje się grze, cofnij go na swoją rękę.</p><p class='flavor-text'>“Wieść o przybyciu gości musiała ich zapewne wyprzedzić, bo otwarto bramę natychmiast, o nic nie pytając.” — Powrót Króla</p>");
            AddHtml("Wealth-of-Adventure-CoE", "<p class='main-text'><b>Akcja:</b> Przeszukaj swoją talię, wybierz kartę z cechą <b><i>Umiejętność</i></b> i dodaj ją do swojej ręki. Potasuj swoją talię.</p><p class='flavor-text'>“Niebezpiecznie wychodzić za własny próg, mój Frodo! — powiadał nieraz. — Trafisz na gościniec i jeżeli nie powstrzymasz swoich nóg, ani się spostrzeżesz, kiedy cię poniosą.”<br/>— Frodo, Drużyna Pierścienia</p>");
            AddHtml("Feast-of-Plenty-CoE", "<p class='main-text'>Święto Obfitości musi zostać opłacone z puli zasobów jednego bohatera.</p><p class='main-text'><b>Akcja:</b> Przygotuj do 3 swoich bohaterów następnie dodaj 1 żeton zasobów każdemu z tych bohaterów. (Limit raz na rundę na grupę.)</p><p class='flavor-text'>“…uczta była wesoła, a sute dania mogły zadowolić najgłodniejszego.” — Drużyna Pierścienia</p>");
            AddHtml("Deeping-Defender-CoE", "<p class='main-text'>{keyword:Oddany.}</p><p class='main-text'><b>Odpowiedź:</b> Kiedy Helmowy Obrońca obroni się przed atakiem, cofnij atakującego wroga do strefy przeciwności i zadaj mu 1 obrażenie.</p><p class='flavor-text'>“Zachodzące słońce złociło ich włócznie i hełmy, połyskiwało na jasnych rozwianych włosach.”<br/>— Dwie Wieże</p>");
            AddHtml("Morwen-Steelsheen-CoE", "<p class='main-text'><b>Akcja:</b> Umieść Morwenę Kwiat ze Stali w grze ze swojej ręki. Odrzuć kontrolowanego przez ciebie sprzymierzeńca z cechą <b><i>Rohan</i></b> lub <b><i>Gondor</i></b>.</p><p class='main-text'><b>Odpowiedź:</b> Kiedy sprzymierzeniec z cechą <b><i>Rohan</i></b> lub <b><i>Gondor</i></b> opuści grę, wyczerp Morwenę Kwiat ze Stali, aby wyleczyć 2 obrażenia dowolnej postaci lub zmniejszyć swój poziom zagrożenia o 1.</p>");
            AddHtml("It-Should-Be-Spared-CoE", "<p class='main-text'><b>Odpowiedź:</b> Kiedy kontrolowana przez ciebie karta z cechą <b><i>Pułapka</i></b> zostanie dołączona do jakiegoś wroga, cofnij tę kartę do swojej talii. Następnie zagraj tę kartę nie płacąc jej kosztu.</p><p class='flavor-text'>“Nie zabijam jednak ani ludzi, ani zwierząt bez potrzeby, a nawet gdy muszę, nie sprawia mi to przyjemności.” — Faramir, Dwie Wieże</p>");
            AddHtml("Spring-the-Trap-CoE", "<p class='main-text'><b>Odpowiedź:</b> Kiedy kontrolowana przez ciebie karta z cechą <b><i>Pułapka</i></b> zostanie dołączona do jakiegoś wroga, umieść w grze ze swojej ręki sprzymierzeńca z cechą <b><i>Strażnik</i></b>.</p><p class='flavor-text'>“Nie przejdą, póki kapitan Faramir stoi na czele swego oddziału.” — Mablung, Dwie Wieże</p>");
            AddHtml("Squire-of-the-Mark-CoE", "<p class='main-text'><b>Odpowiedź:</b> Kiedy kontrolowany przez ciebie sprzymierzeniec z cechą <b><i>Rohan</i></b> zostanie odrzucony z gry, dodaj Giermka Marchii do puli zwycięstwa, aby umieścić w grze odrzuconego sprzymierzeńca i wyczerpać go.</p><p class='flavor-text'>“Niewielkie usługi mogę oddać waszemu Władcy, lecz przez pamięć dzielnego Boromira zrobię wszystko, co w mojej mocy.”<br/>— Pippin, Powrót Króla</p>");
            AddHtml("Rammas-Lookout-CoE", "<p class='main-text'>Oddany.</p><p class='main-text'>Kiedy każdy kontrolowany przez ciebie bohater posiada w swojej puli zasobów co najmniej 1 żeton zasobów, Czujka Rammasu otrzymuje +1{Willpower} i nie zostaje wyczerpana, gdy zostaje przydzielona do wyprawy.</p><p class='flavor-text'>“Cokolwiek bowiem się zdarzy, zbliża się koniec Gondoru takiego, jaki znaliście dotychczas. Nie zatrzymujcie mnie!”<br/>— Gandalf, Powrót Króla</p>");
            AddHtml("Theodwyn-CoE", "<p class='main-text'><b>Odpowiedź:</b> Kiedy Théodwyna wchodzi do gry, dobierz kartę lub dodaj 1 żeton zasobów do puli zasobów bohatera z cechą <b><i>Szlachcic</i></b>.</p><p class='flavor-text'>“To serce nieulękłe i szlachetne. Wszyscy ją miłują. Niech ona panuje plemieniu Eorla przez czas twojej nieobecności, królu!” — Háma, Dwie Wieże</p>");
            AddHtml("Light-footed-Steed-CoE", "<p class='main-text'>Dołącz do bohatera z cechą <b><i>Rohan</i></b>. Ograniczenie.</p><p class='main-text'><b>Odpowiedź:</b> Kiedy kontrolowana przez ciebie postać opuści grę, wyczerp Chyżego Rumaka, aby przygotować bohatera, do którego dołączono Chyżego Rumaka. Jeśli postać, która opuściła grę była unikatowa, bohater, do którego dołączono Chyżego Rumaka otrzymuje +1 {Attack} do końca rundy.</p><p class='flavor-text'>“Nie zapominaj przy tym o ich koniach! Te zwierzaki podobno dostrzegają nawet drżenie powietrza w ciemnościach.”<br/>— Ugluk, Dwie Wieże</p>");
            AddHtml("Rammas-Sentry-CoE", "<p class='main-text'>Oddany.</p><p class='main-text'>Kiedy każdy kontrolowany przez ciebie bohater posiada w swojej puli zasobów co najmniej 1 zasób, Strażnik Rammasu otrzymuje: “<b>Odpowiedź:</b> Anuluj 1 obrażenie właśnie zadane postaci z cechą <b><i>Gondor</i></b>. (Limit raz na fazę.)”</p><p class='flavor-text'>“Jeśli my się załamiemy, któż się ostoi?” — Beregond, Powrót Króla</p>");
            AddHtml("Live-Bait-CoE", "<p class='main-text'>Zagraj tylko jeśli kontrolujesz niedołączony do żadnej karty dodatek z cechą <b><i>Pułapka</i></b>.</p><p class='main-text'><b>Akcja:</b> Wybierz nieunikatowego wroga znajdującego się w grze. Do końca rundy traktuj pole tekstowe tego wroga tak, jakby było puste (za wyjątkiem <b><i>Cech</i></b>). Następnie dołącz do tego wroga kontrolowany przez ciebie i niedołączony do żadnej karty dodatek z cechą <b><i>Pułapka</i></b>.</p>");
            AddHtml("Worthy-of-Remembrance-CoE", "<p class='main-text'><b>Akcja Walki:</b> Odrzuć kontrolowanego przez ciebie sprzymierzeńca z cechą <b><i>Rohan</i></b> i wybierz wroga. Zadaj 2 obrażenia temu wrogowi (4 obrażenia jeśli odrzucony sprzymierzeniec był unikatowy).</p><p class='flavor-text'>“Nagle serce jej odmieniło się, a może tylko w tym momencie zrozumiała, że odmieniło się już przedtem. W nim także zima ustąpiła wiośnie.” — Powrót Króla</p>");
            AddHtml("Need-Brooks-No-Delay-CoE", "<p class='main-text'>Zredukuj koszt Lepiej późno niż nigdy o 1 za każdego kontrolowanego przez ciebie bohatera z cechą <b><i>Rohan</i></b>.</p><p class='main-text'>Akcja Planowania: Przeszukaj 10 wierzchnich kart swojej talii, wybierz kartę o koszcie 2 lub mniej (3 lub mniej jeśli wybrana karta jest unikatowa) i umieść ją na bok, poza grę. Potasuj swoją talię. Zagraj odłożoną kartę nie płacąc jej kosztu tak jakby była na twojej ręce.</p>");
            AddHtml("Wild-Country-CoE", "<p class='main-text'>Zguba 1. Rozległy.</p><p class='main-text'><b>Wymuszony:</b> Pod koniec fazy Podróży, jeśli aktywny obszar nie posiada słowa kluczowego Rozległy, każdy gracz zadaje 1 obrażenie kontrolowanej przez siebie postaci.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Jeśli aktywny obszar posiada słowo kluczowe Rozległy, zadaj 1 obrażenie broniącej się postaci.</p>");
            AddHtml("Endless-Grasslands-CoE", "<p class='main-text'>Rozległy.</p><p class='main-text'><b>Wymuszony:</b> Pod koniec fazy Podróży, jeśli aktywny obszar nie posiada słowa kluczowego Rozległy, każdy gracz wyczerpuje 1 kontrolowanego przez siebie sprzymierzeńca.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Jeśli aktywny obszar posiada słowo kluczowe Rozległy, atakujący wróg atakuje ponownie.</p>");
            AddHtml("Windworn-Villages-CoE", "<p class='main-text'>Rozległy.</p><p class='main-text'><b>Wymuszony:</b> Pod koniec fazy Podróży, jeśli aktywny obszar nie posiada słowa kluczowego Rozległy, każdy gracz odrzuca 1 kartę ze swojej ręki.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Jeśli aktywny obszar posiada słowo kluczowe Rozległy, odrzuć 1 dodatek z broniącej się postaci.</p>");
            AddHtml("Humpbacked-Downs-CoE", "<p class='main-text'>Rozległy.</p><p class='main-text'><b>Wymuszony:</b> Pod koniec fazy Podróży, jeśli aktywny obszar nie posiada słowa kluczowego Rozległy, każdy gracz zwiększa swój poziom zagrożenia o 1.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Jeśli aktywny obszar posiada słowo kluczowe Rozległy, podnieś swój poziom zagrożenia o 2.</p>");
            AddHtml("Ride-Through-the-Night-CoE", "<p class='main-text'><b>Po odkryciu:</b> Wyczerp każdy kontrolowany przez graczy dodatek ze słowem kluczowym Ograniczenie. Jeśli nie wyczerpano w ten sposób żadnego dodatku, Nocna przejażdżka otrzymuje mroczną falę.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Jeśli aktywny obszar posiada słowo kluczowe Rozległy, broniąca się postać do końca rundy nie może zostać przygotowana.</p>");
            AddHtml("The-Green-Sea-CoE", "<p class='flavor-text'>Ziemie Rohanu są rozległe i bezkresne, wyczerpujące nawet dla najsilniejszych koni Zachodniej Bruzdy.</p><p class='main-text'>Obszary ze słowem kluczowym Rozległy otrzymują +3 punkty wyprawy i są odporne na efekty kart graczy.</p><p class='main-text'><b>Wymuszony:</b> Jeśli na początku fazy Podróży nie ma żadnego obszaru ze słowem kluczo-wym Rozległy, odrzucaj wierzchnią kartę z talii Spotkań, dopóki nie odrzucisz obszaru ze słowem kluczowym Rozległy. Dodaj ten obszar do strefy przeciwności.</p>{victory:10}");
            AddHtml("Cold-from-the-Mountains-CoE", "<p class='main-text'><b>Po odkryciu:</b> Każdy gracz kontrolujący więcej niż 2 sprzymierzeńców musi odrzucić kontrolowanego przez siebie sprzymierzeńca (2 sprzymierzeńców jeśli kontroluje więcej niż 5 sprzymierzeńców).</p>{shadow}<p class='shadow-text'><b>Cień:</b> Wyczerp kontrolowaną przez ciebie postać.</p><p class='flavor-text'>“Niech sobie ktoś nazywa to zawieją, jeśli taka jego wola.”<br/>— Boromir, Drużyna Pierścienia</p>");
            AddHtml("Exhausting-Winds-CoE", "<p class='main-text'><b>Po odkryciu:</b> Zadaj 1 obrażenie każdej postaci z co najmniej 2 punktami wytrzymałości. Wyczerp każdą postać, która nie otrzymała w ten sposób obrażeń.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Po tym ataku przydziel X obrażeń broniącej się postaci. X to pozostałe punkty wytrzymałości tej postaci minus 1.</p>");
            AddHtml("Tired-and-Hungry-CoE", "<p class='main-text'>Kiedy Zmęczony i Głodny jest dołączony do bohatera, traktuj tę kartę jako dodatek z cechą <b><i>Stan</i></b> o treści: “<b>Wymuszony:</b> Kiedy obszar zostanie zbadany, wyczerp bohatera, do którego dołączono tę kartę.”</p><p class='main-text'><b>Po odkryciu:</b> Dołącz do przygotowanego bohatera, który nie ma dołączonej karty Zmęczony i Głodny. W przeciwnym przypadku, Zmęczony i Głodny otrzymuje mroczną falę.</p><p class='flavor-text'>“Przed nami wrogi, za nami — strach,<br/>Pod niebem nieraz przyjdzie nam spać” — Drużyna Pierścienia</p>");
            AddHtml("Exposed-to-the-Elements-CoE", "<p class='flavor-text'>Twoja drużyna jest zdana na łaskę deszczu i głodu. Musisz szukać schronienia dla choćby odrobiny odpoczynku.</p><p class='main-text'><b>Wymuszony:</b> Pod koniec kroku odkrywania przeciwności, odrzucaj karty z talii Spotkań, dopóki nie odrzucisz podstępu. Odkryj ten podstęp.</p>{victory:10}");
            AddHtml("Old-Farmhouse-CoE", "<p class='main-text'><b>Wymuszony:</b> Kiedy Stara Farma opuści grę jako zbadany obszar, odrzucaj karty z talii Spotkań, dopóki nie odrzucisz obszaru. Dodaj ten obszar do strefy przeciwności.</p><p class='flavor-text'>“…o niespełna ćwierć mili od gościńca, zbójcy natknęli się na przeszkodę: drogę zagradzał ciężki przewrócony wóz chłopski. To wstrzymało pochód.” — Powrót Króla</p>");
            AddHtml("Abandoned-Mill-CoE", "<p class='main-text'>Zguba 1. Odporny na efekty kart Graczy.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Opuszczony Młyn opuści grę jako zbadany obszar, odrzucaj karty z talii Spotkań, dopóki nie odrzucisz wroga. Dodaj tego wroga do strefy przeciwności.</p><p class='flavor-text'>“…żeby mleć, trzeba mieć ziarno, a do nowego młyna nie dostarczano go więcej niż do starego.” — Powrót Króla</p>");
            AddHtml("Dunland-Chieftain-CoE", "<p class='main-text'><b>Wymuszony:</b> Kiedy Dunlandzki Wódz wejdzie z tobą w zwarcie, odrzuć losową kartę ze swojej ręki. Następnie odrzuć wszystkie kopie tej karty ze swojej ręki lub znajdujące się pod twoją kontrolą.</p><p class='flavor-text'>“Nienawidzą nas i cieszą się…” – Gamling, Dwie Wieże</p>{victory:1}");
            AddHtml("Wild-Ravager-CoE", "<p class='main-text'><b>Wymuszony:</b> Na początku fazy Spotkań, losowo wybierz gracza. Dziki Grabieżca wchodzi w zwarcie z tym graczem.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Atakujący wróg do końca rundy nie może otrzymać obrażeń.</p>");
            AddHtml("Dunlending-Berserker-CoE", "<p class='main-text'><b>Wymuszony:</b> Kiedy Dunlandzki Berserker wejdzie z tobą w zwarcie, zadaj 1 obrażenie kontrolowanemu przez ciebie bohaterowi oraz nieunikatowemu wrogowi znajdującemu się z tobą w zwarciu.</p><p class='flavor-text'>“Nie ustąpią ani o zmierzchu, ani o świcie…”<br/>— Gamling, Dwie Wieże</p>");
            AddHtml("Hillman-Scout-CoE", "<p class='main-text'>Łucznictwo 2.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Zwiadowca ze wzgórz wejdzie z tobą w zwarcie, wyczerp kontrolowanego przez ciebie sprzymierzeńca o najwyższym koszcie.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Wyczerp kontrolowaną przez ciebie postać z 3 lub więcej {Attack}.</p>");
            AddHtml("Surprise-Attack-CoE", "<p class='main-text'>Mroczna fala.</p><p class='main-text'><b>Po odkryciu:</b> Następny wróg, który wejdzie w zwarcie w tej rundzie, do końca rundy nie może otrzymywać obrażeń i cofa się do strefy przeciwności po wykonaniu ataku.</p><p class='flavor-text'>“…nim dwaj prześladowcy, którzy wypadli z zasadzki, odetną mu drogę. Widział ich teraz wyraźnie…”<br/>— Drużyna Pierścienia</p>");
            AddHtml("A-History-of-Strife-CoE", "<p class='flavor-text'>Dunlendingowie żywią zamierzchłą urazę do obcych. Chcą was zaskoczyć, plądrując wasze zapasy, zanim zostawią was na śmierć.</p><p class='main-text'>Mroczna fala.</p><p class='main-text'>Każdy wróg z cechą <b><i>Dunland</i></b> otrzymuje: “<b>Wymuszony:</b> Kiedy ten wróg wejdzie z tobą w zwarcie, odrzuć 1 żeton zasobów z puli zasobów kontrolowanego przez ciebie bohatera.”</p>{victory:10}");
            AddHtml("Recreant-Rider-CoE", "<p class='main-text'><b>Wymuszony:</b> Kiedy Zdradliwy Jeździec wchodzi do strefy przeciwności, wykonuje natychmiastowy atak przeciwko pierwszemu graczowi.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Po tym ataku cofnij atakującego wroga do strefy przeciwności.</p>");
            AddHtml("Deadly-Defector-CoE", "<p class='main-text'><b>Wymuszony:</b> Pod koniec fazy Walki, jeśli Zabójczy Dezerter znajduje się w strefie przeciwności, wykonuje natychmiastowy atak przeciwko pierwszemu graczowi.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Jeśli atakujący wróg znajduje się w strefie przeciwności, wykonuje dodatkowy atak.</p>");
            AddHtml("Orc-Battalion-CoE", "<p class='main-text'>Zastęp Orków nie może otrzymywać obrażeń jeśli atakują go mniej niż 3 postacie.</p><p class='main-text'><b>Wymuszony:</b> Kiedy postać zostanie zadeklarowana jako atakujący przeciwko Zastępowi Orków, zadaj 1 obrażenie tej postaci.</p>");
            AddHtml("Servant-of-the-Eye-CoE", "<p class='main-text'>Zguba 1.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Sługa Oka cię atakuje, podnieś swój poziom zagrożenia o 1.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Podnieś swój poziom zagrożenia o 2.</p>");
            AddHtml("Black-Land-Scouts-CoE", "<p class='main-text'><b>Wymuszony:</b> Na początku fazy Walki podnieś swój poziom zagrożenia o 1 lub rozdziel 4 obrażenia znajdującym się w grze postaciom.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Zadaj 2 obrażenia kontrolowanej przez ciebie postaci.</p>");
            AddHtml("Orcish-Horse-butcher-CoE", "<p class='main-text'><b>Wymuszony:</b> Kiedy Orkowy Rzeźnik Koni zada obrażenia postaci, odrzuć dodatek dołączony do tej postaci lub podnieś o 1 poziom zagrożenia gracza, który kontroluje tę postać.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Odrzuć dodatek, który kontrolujesz lub podnieś swój poziom zagrożenia o 3.</p>");
            AddHtml("Crimson-Banner-CoE", "<p class='main-text'>Mroczna fala.</p><p class='main-text'>Kiedy Szkarłatny Sztandar jest dołączony do wroga, traktuj tę kartę jako dodatek z cechą <b><i>Stan</i></b> o treści: “<b>Wymuszony:</b> Pod koniec rundy każdy gracz podnosi swój poziom zagrożenia o 1 za każdego wroga z cechą <b><i>Ork</i></b> znajdującego się w grze.”</p><p class='main-text'><b>Po odkryciu:</b> Dołącz do nieunikatowego wroga z cechą <b><i>Ork</i></b> o najwyższym koszcie zwarcia.</p>");
            AddHtml("White-Mountain-Reinforcements-CoE", "<p class='flavor-text'>Orkowie zaczęli wypływać ze swoich górskich jaskiń, wzmacniając ataki waszych wrogów. Jeśli ich nie pokonacie, mogą wkrótce was przytłoczyć.</p><p class='main-text'><b>Po odkryciu:</b> Każdy gracz, który nie znajduje się w zwarciu z żadnym wrogiem przeszukuje talię Spotkań i stos odrzuconych kart Spotkań, wybiera wroga i umieszcza go w zwarciu ze sobą. Następnie potasuj talię Spotkań.</p><p class='main-text'>Każda karta cienia bez żadnego efektu cienia otrzymuje: “<b>Cień:</b> Podnieś swój poziom zagrożenia o 1.”</p>{victory:10}");
            AddHtml("Brigand-Ringleader-CoE", "<p class='main-text'>Złupienie 3. Odporny na efekty kart Graczy. Nie może otrzymać więcej niż 5 obrażeń w jednej rundzie.</p><p class='main-text'><b>Wymuszony:</b> Pod koniec rundy gracz, z którym Herszt Rozbójników znajduje się w zwarciu, dodaje wierzchnią kartę ze swojej talii do stosu łupów Herszta Rozbójników i leczy 1 jego obrażenie za każdy różny koszt kart w jego stosie łupów.</p>{victory:10}");
            AddHtml("Plundered-Goods-CoE", "<p class='main-text'>Dodaj 1 do {Threat} strefy przeciwności za każdą kartę w stosie łupów Splądrowanych Dóbr.</p><p class='main-text'><b>Wymuszony:</b> Kiedy wróg opuszcza grę, dodaj 1 kartę ze stosu łupów tego wroga do stosu łupów Splądrowanych Dóbr, następnie dodaj inną kartę ze stosu łupów tego wroga na rękę właściciela. Odrzuć resztę kart.</p><p class='flavor-text'>Rozbójnicy zbrojnie zaatakowali wioskę oraz zwrócili broń przeciwko wieśniakom.</p>");
            AddHtml("Pillaging-Swordsman-CoE", "<p class='main-text'>Złupienie 1.</p><p class='main-text'>Szermierz Grabieżca otrzymuje +X {Attack}, gdzie X to najwyższy wydrukowany koszt karty z jego stosu łupów.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Dodaj 2 wierzchnie karty ze swojej talii do stosu łupów atakującego wroga.</p>");
            AddHtml("Grasping-Slaver-CoE", "<p class='main-text'>Złupienie 2.</p><p class='main-text'><b>Wymuszony:</b> Kiedy sprzymierzeniec otrzyma obrażenia lub zostaje zabity przez atak Łowcy Niewolników, dodaj tego sprzymierzeńca do stosu łupów Łowcy Niewolników.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Łowca Niewolników atakuje, przydziel X obrażeń postaciom, które kontrolujesz. X to liczba sprzymierzeńców w stosie łupów Łowcy Niewolników.</p>");
            AddHtml("Vengeful-Bowman-CoE", "<p class='main-text'>Złupienie 2. Łucznictwo X.</p><p class='main-text'>X to liczba różnych typów kart gracza w stosie łupów Mściwego Łucznika.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Jeśli ten atak zada obrażenia, dodaj wierzchnią kartę ze swojej talii do stosu łupów atakującego wroga.</p>");
            AddHtml("Sneaky-Thief-CoE", "<p class='main-text'>Złupienie 0. Mroczna fala.</p><p class='main-text'><b>Wymuszony:</b> Jeśli Podstępny Złodziej ma karty w swoim stosie łupów, zamiast przydzielać mu obrażenia odrzuć wszystkie te karty.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Jeśli ten atak zabije postać, atakujący wróg atakuje ponownie.</p>");
            AddHtml("Erelas-Gates-CoE", "<p class='main-text'><b>Podróż:</b> Każdy gracz musi wybrać wroga i dodać do jego stosu łupów wierzchnią kartę ze swojej talii.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Podnieś swój poziom zagrożenia o 1 za każdą kartę w stosie łupów atakującego wroga.</p><p class='flavor-text'>“Pogięte, wyłamane filary sterczały rozszczepionymi głowicami…” — Dwie Wieże</p>");
            AddHtml("Crossroads-CoE", "<p class='main-text'><b>Podróż:</b> Wtasuj stos odrzuconych kart Spotkań do talii Spotkań. Odrzucaj karty, dopóki nie odrzucisz wroga. Umieść tego wroga w zwarciu z pierwszym graczem.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Atakujący wróg otrzymuje +X {Attack}, gdzie X to liczba różnych sfer wśród kart w stosie łupów atakującego wroga.</p>");
            AddHtml("Erelas-Stables-CoE", "<p class='main-text'><b>Podróż:</b> Cofnij wroga będącego w zwarciu z graczem do strefy przeciwności.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Jeśli atakujący wróg nie jest unikatowy, po wykonaniu ataku cofnij go do strefy przeciwności.</p>");
            AddHtml("Chaos-in-the-Hills-CoE", "<p class='main-text'><b>Po odkryciu:</b> Cofnij wroga o najwyższym koszcie zwarcia do strefy przeciwności. Jeśli w ten sposób żaden wróg nie został cofnięty do strefy przeciwności, Chaos Wśród Wzgórz otrzymuje mroczną falę.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Po tym ataku, atakujący wróg wchodzi w zwarcie z następnym graczem i wykonuje natychmiastowy atak.</p>");
            AddHtml("Looted-Weapons-CoE", "<p class='main-text'>Kiedy Zrabowana Broń jest dołączona do wroga, traktuj tę kartę jako dodatek z cechą <b><i>Stan</i></b> o treści: “Wróg, do którego dołączono tę kartę otrzymuje +1{Threat} oraz +1{Attack} za każdy dodatek w jego stosie łupów.”</p><p class='main-text'><b>Po odkryciu:</b> Dołącz do wroga o najwyższym koszcie zwarcia. Każdy gracz dodaje kontrolowany przez siebie dodatek z cechą <b><i>Przedmiot</i></b> do stosu łupów tego wroga. Jeśli nie ma żadnych wrogów w grze, Zrabowana Broń otrzymuje mroczną falę.</p>");
            AddHtml("Stockpile-Barricade-CoE", "<p class='flavor-text'>Przewrócone wozy i wózki tworzyły mocną barykadę w poprzek drogi.</p><p class='main-text'>Mroczna fala.</p><p class='main-text'>Każdy wróg otrzymuje +1{Defense} za każdy dodatek w swoim stosie łupów.</p><p class='main-text'><b>Wymuszony:</b> Jeśli podczas rozpatrywania słowa kluczowego Złupienie z karty wroga nie ma żadnych dodatków w stosie łupów tego wroga, to ten wróg wykonuje natych-miastowy atak przeciwko graczowi, z którym znajduje się w zwarciu.</p>{victory:10}");
            AddHtml("Erelas-Beacon-CoE", "<p class='main-text'><b>Wymuszony:</b> Pod koniec fazy Walki Wzgórze Sygnałowe Erelas otrzymuje 1 obrażenie za każdego wroga w strefie przeciwności.</p><p class='main-text'><b>Wzgórze Sygnałowe Erelas posiada X punktów wytrzymałości, gdzie X to 2+3{PerPlayer}. Jeśli Wzgórze Sygnałowe Erelas straci wszystkie punkty wytrzymałości, gracze przegrywają grę.</b></p><p class='flavor-text'>“Kraj wokół był szary i cichy, tylko w miarę jak się posuwali, cień gęstniał, a nadzieja słabła we wszystkich sercach.”<br/>— Powrót Króla</p>");
            AddHtml("Host-of-Rohan-CoE", "<p class='main-text'>Wartownik.</p><p class='main-text'><b>Akcja:</b> Gracze jako grupa mogą wydać 2 żetony zasobów, aby przygotować Zastęp Rohanu. (Limit raz na fazę.)</p><p class='flavor-text'>“…w tych stronach trwa wojna, a Rohirrimowie, hodowcy i mistrzowie koni, nie śpią…”<br/>— Gandalf, Dwie Wieże</p>");
            AddHtml("Enraged-Dunlending-CoE", "<p class='main-text'><b>Wymuszony:</b> Kiedy Wściekły Dunlending wejdzie z tobą w zwarcie, zadaj 1 obrażenie Wzgórzu Sygnałowemu Erelas.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Obrażenia zadane tym atakiem przydziel Wzgórzu Sygnałowemu Erelas.</p>");
            AddHtml("Bald-Hill-CoE", "<p class='main-text'>Kiedy gracze kontrolują Wzgórze Sygnałowe, Łyse Wzgórze otrzymuje +3{Threat}.</p><p class='main-text'><b>Podróż:</b> Jeśli gracze kontrolują Wzgórze Sygnałowe, zadaj 2 obrażenia Wzgórzu Sygnałowemu Erelas.</p><p class='flavor-text'>“Kiedy w końcu dotarli na grań szarego wzgórza, nagły powiew rozburzył im włosy i załopotał połami płaszczy. Świt dmuchnął im chłodem w twarze.” — Dwie Wieże</p>");
            AddHtml("Wooden-Barricade-CoE", "<p class='main-text'>Kiedy Drewniana Barykada znajduje się w strefie przeciwności, otrzymuje: “<b>Wymuszony:</b> Pod koniec fazy Walki zadaj 1 obrażenie Wzgórzu Sygnałowemu Erelas i umieść 1 żeton postępu na Drewnianej Barykadzie.”</p>{shadow}<p class='shadow-text'><b>Cień:</b> Odrzuć 2 kontrolowane przez ciebie dodatki lub zadaj 1 obrażenie Wzgórzu Sygnałowemu Erelas.</p>");
            AddHtml("Rocky-Slope-CoE", "<p class='main-text'>Kiedy efekt karty Spotkania zostanie anulowany, zadaj 1 obrażenie Wzgórzu Sygnałowemu Erelas.</p><p class='main-text'><b>Podróż:</b> Pierwszy gracz musi odrzucić wszystkie karty wydarzeń ze swojej ręki.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Odrzuć wszystkie żetony zasobów z puli zasobów wszystkich kontrolowanych przez ciebie bohaterów lub przydziel 1 obrażenie Wzgórzu Sygnałowemu Erelas.</p>");
            AddHtml("Surrounding-Grassland-CoE", "<p class='main-text'>Łucznictwo 1.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Okoliczne Pastwiska opuszczą grę jako zbadany obszar, jeśli nie ma żadnych kopii Okolicznych Pastwisk w strefie przeciwności, cofnij ten Obszar do strefy przeciwności.</p><p class='flavor-text'>“…otwierał się ponad potokiem widok na zielony step Rohanu ginący w szarej dali.” — Dwie Wieże</p>");
            AddHtml("Narrow-Stair-CoE", "<p class='main-text'>Każdy gracz może zadeklarować nie więcej niż 1 atak oraz nie więcej niż 1 atakującego w każdej fazie.</p><p class='main-text'>Kiedy Wąskie Schody są aktywnym obszarem, każdy gracz może obronić nie więcej niż 1 atak na fazę (pozostałe ataki są niebronione).</p>{shadow}<p class='shadow-text'><b>Cień:</b> Atakujący wróg otrzymuje +3{Attack} lub zadaj 1 obrażenie Wzgórzu Sygnałowemu Erelas.</p>");
            AddHtml("From-All-Sides-CoE", "<p class='main-text'><b>Po odkryciu:</b> Każdy gracz musi wybrać: przydzielić po 1 obrażeniu 4 różnym kontrolowanym przez siebie postaciom lub zadać 1 obrażenie Wzgórzu Sygnałowemu Erelas.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Po tym ataku, odrzuć broniącą się postać lub zadaj 1 obrażenie Wzgórzu Sygnałowemu Erelas.</p>");
            AddHtml("Another-Wave-CoE", "<p class='main-text'>Mroczna fala. Zguba 1.</p><p class='main-text'><b>Po odkryciu:</b> Jeśli gracze kontrolują Wzgórze Sygnalowe, zadaj 2 obrażenia Wzgórzu Sygnałowemu Erelas lub odkryj kartę Spotkania.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Atakujący wróg atakuje ponownie lub zadaj 1 obrażenie Wzgórzu Sygnałowemu Erelas.</p>");
            AddHtml("Assault-Upon-Erelas-CoE", "<p class='main-text'><b>Po odkryciu:</b> Pierwszy gracz wybiera wroga będącego w zwarciu z dowolnym graczem i cofa go do strefy przeciwności. Jeśli żaden wróg nie został w ten sposób cofnięty do strefy przeciwności, zadaj 1 obrażenie Wzgórzu Sygnałowemu Erelas lub Atak na Erelas otrzymuje mroczną falę.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Podnieś swój poziom zagrożenia o 4 lub zadaj 1 obrażenie Wzgórzu Sygnałowemu Erelas.</p>");
            AddHtml("Reclaim-the-Beacon-CoE", "<p class='flavor-text'>Przejmij Wzgórze Sygnałowe, żeby Dunlendingowie go nie zniszczyli albo gorzej!</p><p class='main-text'>Dunlendingowie kontrolują Wzgórze Sygnałowe. Wrogowie w strefie przeciw-ności nie mogą opcjonalnie wchodzić w zwarcie lub otrzymywać obrażeń.</p><p class='main-text'>Nie może mieć więcej niż X żetonów postępu, gdzie X to 2+1{PerPlayer}. Jeśli Odzyskać Wzgórze Sygnałowe ma na sobie X żetonów postępu, odwróć tę kartę (zachowując wszystkie żetony postępu).</p>");
            AddHtml("Stolen-Steeds-CoE", "<p class='main-text'>Cele z cechą <b><i>Wierzchowiec</i></b> nie mogą zostać odrzucone lub opuścić gry. Jeśli cel z cechą <b><i>Wierzchowiec</i></b> nie jest dołączony do żadnej karty, gracze muszą ją zdobyć i dołączyć do bohatera, którego kontrolują.</p><p class='main-text'><b>Wymuszony:</b> Pod koniec rundy dodaj na główną wyprawę 1 żeton postępu za każdy kontrolowany przez graczy cel z cechą <b><i>Wierzchowiec</i></b>. Następnie podnieś poziom zagrożenia każdego gracza o 1 za każdy cel z cechą <b><i>Wierzchowiec</i></b>, którego gracze nie kontrolują.</p>");
            AddHtml("Flinthoof-CoE", "<p class='main-text'>Strzeżony (wróg). Ograniczenie.</p><p class='main-text'>Wróg strzegący Krzemyka otrzymuje +1{Attack}.</p><p class='main-text'>Bohater, do którego dołączono tę kartę, otrzymuje +1{Attack}.</p><p class='main-text'><b>Akcja Walki:</b> Wyczerp Krzemyka i zadaj 1 obrażenie wrogowi znajdującemu się w grze.</p><p class='flavor-text'>“…długie ogony rozwiewały się na wietrze, splecione grzywy zdobiły dumne karki.” — Dwie Wieże</p>");
            AddHtml("Brightmane-CoE", "<p class='main-text'>Strzeżony (obszar). Ograniczenie.</p><p class='main-text'>Obszar lub wróg strzegący Jasnogrzywego otrzymuje +1{Threat}.</p><p class='main-text'>Bohater, do którego dołączono tę kartę, otrzymuje +1{Willpower}.</p><p class='main-text'><b>Akcja Wyprawy:</b> Wyczerp Jasnogrzywego, aby podejrzeć wierzchnią kartę talii Spotkań. Jeśli to obszar, możesz go odrzucić.</p>");
            AddHtml("Felarof-CoE", "<p class='main-text'>Strzeżony (wróg). Ograniczenie.</p><p class='main-text'><b>Wymuszony:</b> Kiedy wróg strzegący Felarófa atakuje, rozdaj mu 2 dodatkowe karty cienia.</p><p class='main-text'><b>Akcja:</b> Wyczerp Felarófa, aby odrzucić zakrytą kartę cienia.</p><p class='flavor-text'>“…na przodzie cwałuje inny jeszcze koń, ogromny. Nie spotkałem jeszcze w życiu takiego rumaka.”<br/>— Legolas, Dwie Wieże</p>");
            AddHtml("Thruk-CoE", "<p class='main-text'>Odporny na efekty kart graczy. Thruk wchodzi w zwarcie z pierwszym graczem.</p><p class='main-text'><b>Wymuszony:</b> Na początku fazy Walki, jeśli Thruk nie strzeże żadnego celu, wykonuje natychmiastowy atak przeciwko graczowi, z którym znajduje się w zwarciu.</p>{victory:10}");
            AddHtml("Saddle-slasher-CoE", "<p class='main-text'>Łucznictwo X. X to 2+1 za każdy cel z cechą <b><i>Wierzchowiec</i></b> kontrolowany przez graczy.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Jeśli kontrolujesz cel z cechą <b><i>Wierzchowiec</i></b>, dołącz ten cel do atakującego wroga lub odrzuć broniącą się postać po rozstrzygnięciu ataku.</p>");
            AddHtml("Rein-ripper-CoE", "<p class='main-text'><b>Wymuszony:</b> Kiedy Rozpruwacz Cugli atakuje, rozdaj mu 2 dodatkowe karty cienia. Jeżeli kontrolujesz cel z cechą <b><i>Wierzchowiec</i></b>, możesz go dołączyć do Rozpruwacza Cugli jako strzeżony cel, aby anulować ten efekt.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Atakujący wróg otrzymuje +1{Attack} za każdy cel z cechą <b><i>Wierzchowiec</i></b> kontrolowany przez graczy.</p>");
            AddHtml("Bridle-biter-CoE", "<p class='main-text'>Mroczna fala.</p><p class='main-text'><b>Po odkryciu:</b> Kąsacz Uzd wchodzi w zwarcie z graczem, który kontroluje najwięcej celów z cechą <b><i>Wierzchowiec</i></b>.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Umieść Kąsacza Uzd w grze z tobą w zwarciu i rozdaj mu kartę cienia.</p>");
            AddHtml("Pommel-pouncer-CoE", "<p class='main-text'>Kiedy Wysadzacz Jeźdźców znajduje się w strefie przeciwności bez dołączonego do niego celu z cechą <b><i>Wierzchowiec</i></b>, otrzymuje: “<b>Wymuszony:</b> Kiedy gracz miałby przejąć kontrolę nad celem z cechą <b><i>Wierzchowiec</i></b>, dołącz ten cel z cechą <b><i>Wierzchowiec</i></b> do Wysadzacza Jeźdźców jako strzeżony cel.”</p>");
            AddHtml("Spoiled-Pasture-CoE", "<p class='main-text'>X to 2+2 za każdy cel z cechą <b><i>Wierzchowiec</i></b> kontrolowany przez graczy.</p><p class='main-text'><b>Wymuszony:</b> Kiedy gracz straci kontrolę nad celem z cechą <b><i>Wierzchowiec</i></b>, podnieś jego poziom zagrożenia o 1.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Podnieś swój poziom zagrożenia o 1 za każdy kontrolowany przez ciebie cel z cechą <b><i>Wierzchowiec</i></b>.</p>");
            AddHtml("Horse-Thieves'-Hovel-CoE", "<p class='main-text'>Kiedy gracze mają możliwość Podróży, muszą podróżować na obszar bez słowa kluczowego Rozległy.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Nora Koniokradów opuści grę jako zbadany obszar, jeśli w grze znajduje się mniej niż 1{PerPlayer} wrogów, odrzucaj karty z talii Spotkań, dopóki nie odrzucisz wroga. Dodaj tego wroga do strefy przeciwności.</p>{victory:2}");
            AddHtml("Horse-Lord's-Lodge-CoE", "<p class='main-text'>Kiedy gracze mają możliwość Podróży, muszą podróżować na obszar bez słowa kluczowego Rozległy.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Kwatera Władcy Koni opuści grę jako zbadany obszar, jeśli w grze znajduje się mniej niż 1{PerPlayer} obszary, odrzucaj karty z talii Spotkań, dopóki nie odrzucisz obszaru. Dodaj ten obszar do strefy przeciwności.</p>{victory:2}");
            AddHtml("Thirst-for-Horse-Blood-CoE", "<p class='main-text'><b>Po odkryciu:</b> Każdy gracz, który kontroluje cel z cechą <b><i>Wierzchowiec</i></b> wybiera: odkryj kartę Spotkań lub wybierz wroga o najwyższym koszcie zwarcia bez dołączonego dodatku i dołącza cel z cechą <b><i>Wierzchowiec</i></b> do tego wroga jako strzeżony cel. Jeśli żaden gracz nie kontroluje celu z cechą <b><i>Wierzchowiec</i></b>, Pragnienie Końskiej Krwi otrzymuje mroczną falę.</p><p class='flavor-text'>“Będziemy wkrótce ucztować i zakosztujemy końskiego mięsa, a może też innego, lepszego jeszcze.” — Uglúk, Dwie Wieże</p>");
            AddHtml("Overburdened-CoE", "<p class='main-text'><b>Po odkryciu:</b> Każdy gracz, który kontroluje więcej niż 2 dodatki musi odrzucić kontrolowany przez siebie dodatek (2 dodatki jeśli kontroluje więcej niż 5 dodatków). Jeśli w ten sposób żaden dodatek nie został odrzucony, Przeciążony otrzymuje mroczną falę.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Odrzuć 1 losową kartę ze swojej ręki.</p>");
            AddHtml("The-Last-Alliance-CoE", "<p class='main-text'>Wybierz dwie cechy, <b><i>A</i></b> i <b><i>B</i></b>. Każdy z twoich startowych bohaterów i każdy sojusznik w twojej talii musi mieć wydrukowaną cechę <b><i>A</i></b> lub <b><i>B</i></b>, ale nie może mieć ich obu. Musisz mieć co najmniej 1 startowego bohatera i 10 sprzymierzeńców w swojej talii każdej z wybranych cech.</p><p class='main-text'><b>Przygotowanie:</b> Odwróć tę kartę. </p><p class='flavor-text'>“Dawnymi czasy elfowie byli silniejsi i stawiali mu opór; ludzie wówczas także nie wszyscy stronili od elfów.” — Gandalf, Drużyna Pierścienia</p>");
            AddHtml2("The-Last-Alliance-CoE", "<p class='main-text'>Kiedy kontrolujesz więcej postaci z cechą <b><i>A</i></b> niż postaci z cechą <b><i>B</i></b>, obniż o 1 koszt pierwszego sprzymierzeńca z cechą <b><i>B</i></b> zagrywanego przez ciebie podczas każdej rundy i vice versa.</p><p class='main-text'><b>Akcja:</b> Wyczerp Ostatni Sojusz, aby wybrać kartę w twojej ręce lub pod twoją kontrolą. Aż do końca rundy zamień każde wystąpienie “<b><i>A</i></b>” lub “<b><i>B</i></b>” w treści tej karty na “<b><i>A</i></b> lub <b><i>B</i></b>”.</p>");
            AddHtml("Drive-them-Away-CoE", "<p class='flavor-text'>Podróżując wzdłuż Wielkiego Gościńca Zachodniego natrafiasz na obóz Rohirrimów w cieniu zielonego, łysego wzgórza Erelas. Ale spokój twojej podróży przerywa nagły atak z cienia! Brzęk stali i krzyki ludzi wkrótce docierają do twoich uszu. Musisz wypędzić najeźdźców i ochronić obóz przed dalszymi zniszczeniami!</p><p class='main-text'><b></b> <b>Przygotowanie:</b> Odłóż Herszta Rozbójników na bok, poza grę. Dodaj Splądrowane Dobra do strefy przeciwności. Każdy gracz przeszukuje talię Spotkań w poszukiwaniu innego wroga i dodaje go do strefy przeciwności. Potasuj talię Spotkań. </p>");
            AddHtml2("Drive-them-Away-CoE", "<p class='main-text'></b> <b>Wymuszony:</b> Po zabiciu wroga umieść tutaj 1 żeton zasobów.</p><p class='main-text'><b>Akcja Spotkania:</b> Usuń 5 żetonów postępu z tego etapu, aby wybrać kartę ze stosu łupów i odrzucić ją. Limit raz na rundę na gracza.</p><p class='main-text'><b>Gracze nie mogą przejść dalej, dopóki na tym etapie nie ma X żetonów zasobów, gdzie X to 1+2{PerPlayer}.</b></p>");
            AddHtml("Ambush-CoE", "<p class='flavor-text'>Walczyłeś w samym sercu obozowiska, ale bandyci gromadzą się wokół swojego przywódcy i przypuszczają zaciekły atak.</p><p class='main-text'><b></b> <b>Po odkryciu:</b> Zakończ bieżącą fazę. Dodaj Herszta Rozbójników do strefy przeciwności. Każdy gracz, który nie jest w zwarciu z wrogiem, przeszukuje talię Spotkań oraz stos odrzuconych kart Spotkań w poszukiwaniu wroga, który wchodzi z nim w zwarcie. Potasuj talię Spotkań. </p>");
            AddHtml2("Ambush-CoE", "<p class='main-text'></b> <b>Wymuszony:</b> Każdy wróg w momencie wejścia do gry wykonuje natychmiastowy test zwarcia.</p><p class='main-text'><b>Akcja Planowania:</b> Usuń 5 żetonów postępu z obecnej karty wyprawy, aby wybrać kartę ze stosu łupów. Właściciel tej karty może ją odrzucić lub zagrać bez płacenia za jej koszt. Limit cztery razy na rundę dla grupy.</p><p class='main-text'><b>Kiedy Herszt Rozbójników zostanie pokonany, gracze wygrywają.</b></p>");
            AddHtml("Battle-for-the-Hill-CoE", "<p class='flavor-text'>Dunlendingowie i zdradzieccy Rohirrimowie zdobyli wzgórze sygnałowe Erelas. Odzyskaj wzgórze za wszelką cenę!</p><p class='main-text'><b></b> <b>Przygotowanie:</b> Odłóż Zastęp Rohanu na bok, poza grę. Dodaj Wzgórze Sygnałowe Erelas i 1{PerPlayer} Wściekły Dunlending do strefy przeciwności. Dodaj poboczną wyprawę Odzyskać Wzgórze Sygnałowe do strefy przeciwności. Przeszukaj talię Spotkań w poszukiwaniu 1 kopii karty Okoliczne Pastwiska i uczyń ją aktywnym obszarem. Potasuj talię Spotkań.</p>");
            AddHtml2("Battle-for-the-Hill-CoE", "<p class='main-text'></b> <b>Wymuszony:</b> Po umieszczeniu dowolnej liczby żetonów postępu na tym etapie, każdy gracz musi wybrać: albo wyczerpuje kontrolowaną przez siebie postać, albo zadaje 1 obrażenie Wzgórzu Sygnałowemu Erelas.</p><p class='main-text'><b>Ten etap nie może zostać ukończony, jeżeli Dunlendingowie kontrolują Wzgórze Sygnałowe.</b></p>");
            AddHtml("Hold-the-Hilltop-CoE", "<p class='flavor-text'>Gdy twoi wrogowie zbierają się do kolejnego ataku na wzgórze sygnałowe, możesz dostrzec pochodnie żołnierzy Gálmóda gromadzących się pośród wzgórz poniżej.</p><p class='main-text'><b></b> <b>Po odkryciu:</b> Umieść żetony postępu na karcie Obronić Wzgórze Sygnałowe (pomijając aktywny obszar), dopóki nie będzie na niej X żetonów postępu, gdzie X to 2+1{PerPlayer}. Wtasuj stos odrzuconych kart Spotkań do talii Spotkań. Każdy gracz odrzuca karty z wierzchu talii Spotkań, dopóki nie zostanie odrzucony wróg, a następnie dodaje go do strefy przeciwności. </p>");
            AddHtml2("Hold-the-Hilltop-CoE", "<p class='main-text'></b> Czas 3.</p><p class='main-text'><b>Wymuszony:</b> Na koniec rundy zadaj 1 obrażenie Wzgórzu Sygnałowym Erelas (jeśli w grze jest 3 lub więcej graczy zadaj 2 obrażenia). Następnie, jeśli na tym etapie nie ma znacznika czasu, natychmiast przejdź do etapu 3.</p><p class='main-text'><b>Wymuszony:</b> Po wybraniu opcji Obronić Wzgórze Sygnałowe jako bieżącej przygody odkryj kartę spotkania.</p><p class='main-text'><b>Jeśli Dunlendingowie kontrolują Wzgórze Sygnałowe, gracze przegrywają.</b></p>");
            AddHtml("The-Last-Push-CoE", "<p class='flavor-text'>Gdy siły Gálmóda śpieszą by oflankować wroga, musicie przygotować się na ostatnią walkę.</p><p class='main-text'><b></b> <b>Po odkryciu:</b> Usuń wszystkie żetony postępu z karty Obronić Wzgórze Sygnałowe i odwróć ją. Umieść Zastęp Rohanu w grze pod kontrolą pierwszego gracza. Wtasuj stos odrzuconych kart Spotkań do talii Spotkań. Każdy gracz odrzuca karty z wierzchu talii Spotkań, dopóki nie zostanie odrzucony wróg, a następnie dodaje go do strefy przeciwności. </p>");
            AddHtml2("The-Last-Push-CoE", "<p class='main-text'></b> Dodaj 2 do całkowitego {Threat} w strefie przeciwności za każdy żeton obrażeń znajdujący się na karcie Wzgórze Sygnałowe Erelas.</p><p class='main-text'><b>Wymuszony:</b> Na początku fazy walki każdy wróg będący w zwarciu wykonuje natychmiastowy atak.</p><p class='main-text'><b>Wymuszony:</b> Po opuszczeniu gry przez postać zadaj 1 obrażenie Wzgórzu Sygnałowemu Erelas.</p><p class='main-text'><b>Pod koniec rundy, jeśli Dunlendingowie kontrolują Wzgórze Sygnałowe – gracze przegrywają grę, natomiast jeśli gracze kontrolują Wzgórze Sygnałowe – wygrywają.</b></p>");
            AddHtml("Thieves-and-Coursers-CoE", "<p class='flavor-text'>Orkowie zaatakowali Wschodnią Bruzdę, szalejąc po całej krainie i porywając cenne konie bojowe króla Thengela.</p><p class='main-text'><b></b> <b>Przygotowanie:</b> Dodaj Skradzione Rumaki do strefy przeciwności. Odłóż Thruka, karty celów z cechą <b><i>Wierzchowiec</i></b> oraz 8 wrogów z zestawu Spotkań Oddział Orków poza grę. Potasuj talię Spotkań. Pierwszy gracz odkrywa karty Krzemyk oraz Jasnogrzywy i dodaje je do strefy przeciwności. Następnie każdy z pozostałych graczy odrzuca karty z wierzchu talii Spotkań, dopóki nie zostanie odrzucony obszar lub wróg, a następnie dodaje go do strefy przeciwności. </p>");
            AddHtml2("Thieves-and-Coursers-CoE", "<p class='flavor-text'>Jeśli uda ci się odzyskać Krzemyka oraz Jasnogrzywego, być może będziesz w stanie powstrzymać główne siły orków, zanim dotrą do Aldburga.</p><p class='main-text'></b> <b>Ten etap nie może zostać ukończony, jeśli gracze nie kontrolują 2 kart celu z cechą <b><i>Wierzchowiec</i></b>.</b></p>");
            AddHtml("The-King's-Stallion-CoE", "<p class='flavor-text'>Wódz orków – Thruk, przybył na pole bitwy, jadąc na potężnym koniu Felarófie i wydając rozkazy swoim pobratyńcom. — Do mnie! — krzyknął doniosłym głosem. — Dziś wieczorem zasmakujemy końskiego mięsa!</p><p class='main-text'><b></b> <b>Po odkryciu:</b> Jeśli jest to faza walki, zakończ tę fazę. Wtasuj stos odrzuconych kart Spotkań do talii Spotkań. Pierwszy gracz dodaje Thruka do strefy przeciwności, jako wroga strzegącego Felarófa. Następnie każdy z pozostałych graczy odrzuca karty z wierzchu talii Spotkań, dopóki nie zostanie odrzucony wróg. Gracz umieszcza tego wroga w grze, w zwarciu ze sobą. </p>");
            AddHtml2("The-King's-Stallion-CoE", "<p class='main-text'></b> Thruk nie może otrzymywać obrażeń, jeśli na tym etapie nie ma co najmniej 4 żetonów postępu.</p><p class='main-text'><b>Ten etap nie może zostać ukończony, chyba że gracze kontrolują 3 karty celu z cechą <b><i>Wierzchowiec</i></b>.</b></p>");
            AddHtml("The-Sweeping-Tide-CoE", "<p class='flavor-text'>Cały zastęp orków odpowiedział na wezwanie Thruka. Musicie się zjednoczyć, by wykonać ostatnie natarcie i rozgromić ich na wzgórzach.</p><p class='main-text'><b></b> <b>Po odkryciu:</b> Jeśli jest to faza walki, zakończ tę fazę. Wtasuj stos odrzuconych kart Spotkań do talii Spotkań. Przeszukaj pulę zwycięstwa w poszukiwaniu Thruka i dodaj go do strefy przeciwności. Każdy gracz wybiera 2 wrogów z odłożonych na bok kart z zestawu Spotkań Oddział Orków i umieszcza tych wrogów w grze w zwarciu ze sobą. Jednym z wrogów dodanych do gry w ten sposób musi być Zastęp Orków. </p>");
            AddHtml2("The-Sweeping-Tide-CoE", "<p class='main-text'></b> Pomiń krok odkrywania przeciwności (Faza wyprawy, krok 2). Jeśli w dowolnym momencie gry talia Spotkań się skończy, należy potasować stos odrzuconych kart Spotkań w nową talię Spotkań.</p><p class='main-text'><b>Wymuszony:</b> Kiedy wróg wykonuje atak, otrzymuje dodatkową kartę cienia.</p><p class='main-text'><b>Jeśli do wroga dołączona jest karta celu z cechą <b><i>Wierzchowiec</i></b>, gracze przegrywają grę. Jeśli w grze nie ma wrogów, gracze wygrywają.</b></p>");
            AddHtml("Defend-the-Beacon-CoE", "<p class='main-text'>Gracze kontrolują Wzgórze Sygnałowe. Karty cienia są odporne na efekty kart Graczy.</p><p class='main-text'>Obronić Wzgórze Sygnałowe nie może mieć więcej niż X żetonów postępu, gdzie X to 2+1{PerPlayer}.</p><p class='main-text'><b>Odpowiedź:</b> Usuń dowolną liczbę żetonów postępu z karty Obronić Wzgórze Sygnałowe, aby anulować tę samą liczbę obrażeń zadanych Wzgórzu Sygnałowemu Erelas. Następnie, jeśli na karcie Obronić Wzgórze Sygnałowe nie ma żadnych żetonów postępu, odwróć tę kartę.</p>");
        }

        private void TheAldburgPlot()
        {
            AddHtml("Fatty-Bolger-TAP", "<p class='main-text'><b>Przygotowanie:</b> Obniż o 2 swój poziom zagrożenia.</p><p class='main-text'><b>Odpowiedź:</b> Podnieś o 4 swój poziom zagrożenia i wyczerp Grubasa Bolgera, aby anulować właśnie odkrytą z talii Spotkań nie-unikatową kartę wroga i dodać ją do puli zwycięstwa. (Limit raz na grę na grupę.)</p><p class='flavor-text'>“Grubas Bolger nie próżnował. Kiedy spostrzegł czarne cienie sunące przez ogród, zrozumiał od razu, że musi umknąć przed nimi albo zginie.” — Drużyna Pierścienia</p>");
            AddHtml("Widfast-TAP", "<p class='main-text'><b>Przygotowanie:</b> Przeszukaj swoją kolekcję kart i weź na rękę sprzymierzeńca z cechą <b><i>Istota</i></b>. W pierwszej rundzie obniż koszt tego sprzymierzeńca o 2.</p><p class='main-text'><b>Akcja Podróży:</b> Jeśli jest jeden aktywny obszar, wydaj 1 żeton zasobów z puli zasobów Widfasty, aby podróżować na inny obszar rozpatrując jego efekt <b>Podróży</b>. Teraz są dwa aktywne obszary.</p>");
            AddHtml("Lily-Cotton-TAP", "<p class='main-text'><b>Akcja:</b> Wyczerp Lili Cotton, aby przygotować innego sprzymierzeńca z cechą <b><i>Hobbit</i></b> oraz do końca fazy odpo-wiednio dodać {Willpower}, {Attack} i {Defense} Lili Cotton do {Willpower}, {Attack} i {Defense} tego sprzymierzeńca. Pod koniec fazy cofnij tego sprzymie-rzeńca na rękę jego właścicela. (Limit raz na fazę.)</p>");
            AddHtml("A-Lesson-in-Caution-TAP", "<p class='main-text'>{keyword:Tajność 1.}</p><p class='main-text'><b>Akcja:</b> Dobierz 1 kartę. Następnie obniż swój poziom zagrożenia o 2 lub następna karta, którą zagrasz w tej fazie otrzymuje Tajność 1.</p><p class='flavor-text'>“Hobbici mają lekki chód i niełatwo nawet Strażnikowi odszukać ich trop…” — Dwie Wieże</p>");
            AddHtml("There-and-Back-Again-TAP", "<p class='main-text'>{keyword:Tajność 1.}</p><p class='main-text'><b>Odpowiedź:</b> Kiedy sprzymierzeniec z cechą <b><i>Hobbit</i></b> opuści grę, przeszukaj swoją rękę i stos odrzuconych kart w poszukiwaniu 1 kopii tego sprzymierzeńca i umieść go w grze pod kontrolą dowolnego gracza.</p><p class='flavor-text'>“Ceregiele prawne trwały kilka lat. Wiele wody upłynęło w rzece, nim pana Bagginsa naprawdę i formalnie uznano z powrotem za żyjącego.” — Hobbit</p>");
            AddHtml("Buckland-Shirriff-TAP", "<p class='main-text'>{keyword:Oddany.} {keyword:Dystans.}</p><p class='main-text'><b>Odpowiedź:</b> Kiedy do strefy przeciwności zostaje dodany wróg dzielący <b><i>Cechę</i></b> z wrogiem znajdującym się w puli zwycięstwa, wydaj 1 żeton zasobów {sphere:Lore}, aby umieścić Szeryfa Bucklandu w grze z ręki, pod twoją kontrolą.</p>");
            AddHtml("Weep-No-More-TAP", "<p class='main-text'>W puli zwycięstwa mogą się znajdować maksymalnie 3 kopie Otrzyjmy Łzy!</p><p class='main-text'><b>Odpowiedź:</b> Kiedy z karty podstępu zostanie rozpatrzony efekt “po odkryciu” lub efekt cienia, dodaj Otrzyjmy Łzy! do puli zwycięstwa, aby dodać kartę rozpatrzonego efektu do puli zwycięstwa.</p><p class='flavor-text'>“Może będziemy kiedyś pomszczeni. A teraz uzbrójmy się w męstwo i otrzyjmy łzy.” — Aragorn, Drużyna Pierścienia</p>");
            AddHtml("Fear-Fire-Foes-TAP", "<p class='main-text'>{keyword:Tajność 1.} Zagraj tylko wtedy, gdy nie kontrolujesz żadnego bohatera z cechą <b><i>Wojownik</i></b>.</p><p class='main-text'><b>Akcja:</b> Wybierz nie-unikatowego wroga i jego atrybut ({Threat}, {Attack} lub {Defense}). Do końca fazy obniż wybrany atrybut tego wroga do 0.</p><p class='flavor-text'>“Z dala odezwały się w odpowiedzi inne rogi. Alarm się szerzył.” — Drużyna Pierścienia</p>");
            AddHtml("Horn-call-of-Buckland-TAP", "<p class='main-text'>Dołącz do bohatera {sphere:Lore} lub bohatera z cechą {trait:Hobbit.}</p><p class='main-text'><b>Odpowiedź:</b> Kiedy wejdziesz w zwarcie z wrogiem posiadającym wspólną cechę z wrogiem z puli zwycięstwa, wyczerp Sygnał Alarmowy Bucklandu, aby przygotować bohatera, do którego dołączono tę kartę. Następnie do końca rundy ten wróg otrzymuje –1 {Attack} i –1 {Defense}.</p><p class='flavor-text'>“W tej samej sekundzie wśród pobliskich drzew zagrał róg. Jego głos rozdarł noc jak płomień buchający ze szczytu góry.”<br/>— Drużyna Pierścienia</p>");
            AddHtml("Young-Tom-TAP", "<p class='main-text'>Podczas rundy, w której Młody Tom wszedł do gry, wrogowie otrzymują +5 kosztu zwarcia.</p><p class='flavor-text'>“Nie wszystko jest u nas doskonałe — odparł Frodo — lecz ogrodników rzeczywiście bardzo szanujemy.”<br/>— Dwie Wieże</p>");
            AddHtml("Gavin-TAP", "<p class='main-text'>Nie może posiadać dodatków.</p><p class='main-text'><b>Odpowiedź:</b> Po rozpatrzeniu karty przeciwności bez słowa kluczowego mroczna fala, wyczerp Gavina, aby rozpatrzona karta otrzymała słowo kluczowe mroczna fala i dodaj 1 żeton zasobów do puli zasobów kontrolo-wanego przez ciebie bohatera z cechą <b><i>Zwiadowca</i></b>. Jeśli następna odkryta karta nie jest obszarem, odrzuć ją bez rozpatrywania jej efektów.</p>");
            AddHtml("Red-Harvest-TAP", "<p class='flavor-text'>Ludzie Szepczącej zaatakowali was z zadziwiającą brutalnością i zawziętością. Musicie zaciekle walczyć, aby dostać się do zbrojowni.</p><p class='main-text'><b>Wymuszony:</b> Kiedy wróg wejdzie z graczem w zwarcie, ten gracz przydziela 2 obrażenia kontrolowanej przez siebie postaci.</p><p class='main-text'><b>Wymuszony:</b> Kiedy ten etap zostanie pokonany, wtasuj stos odrzuconych kart Spotkań do talii Spotkań. Każdy gracz odrzuca karty z talii Spotkań, aż zostanie odrzucony wróg, a następnie dodaje go do strefy przeciwności. Dodaj Zbrojownię do strefy przeciwności.</p>{victory:10}");
            AddHtml("The-Prisoner-in-White-TAP", "<p class='flavor-text'>Agent mistrza szpiegów, próbując zatrzeć swoje ślady, zinfiltrował więzienie.</p><p class='main-text'>Gracze nie mogą podróżować na obszary ani czynić obszary aktywnymi w wyniku efektów kart graczy.</p><p class='main-text'><b>Wymuszony:</b> Kiedy ten etap zostanie pokonany, dodaj Osjana do strefy przeciwności.</p>{victory:10}");
            AddHtml("The-Man-with-the-Twisted-Lip-TAP", "<p class='flavor-text'>Zaczęliście śledzić sekretnego przywódcę szpiegów z Aldburga, choć trudno jest powiedzieć, które z was jest łowcą, a które zwierzyną.</p><p class='main-text'><b>Wymuszony:</b> Na koniec rundy podnieś o 1 poziom zagrożenia każdego gracza.</p><p class='main-text'><b>Wymuszony:</b> Kiedy ten etap zostanie pokonany, dodaj Mistrza Szpiegów do strefy przeciwności.</p>{victory:10}");
            AddHtml("A-Fire-Rises-TAP", "<p class='flavor-text'>Coś złego szykuje się w Aldburgu. Kiedy zaczęliście składać kawałki układanki w całość, widzicie dym i płomienie wydobywające się z sąsiedniego budynku.</p><p class='main-text'>Zwiększ koszt zagrania każdego wydarzenia o 1.</p><p class='main-text'><b>Wymuszony:</b> Kiedy ten etap zostanie pokonany, odkryj Szalejącą Pożogę.</p>{victory:10}");
            AddHtml("A-Case-of-Identity-TAP", "<p class='flavor-text'>Tajemnica prawdziwej tożsamości Szepczącej pozostaje nierozwikłana. Musicie być na właściwym tropie, sądząc po jej rzezimieszkach przyczajonych w bocznej uliczce.</p><p class='main-text'><b>Wymuszony:</b> Pod koniec kroku odkrywania przeciwności odrzucaj karty z talii Spotkań, dopóki nie odrzucisz wroga. Odkryj tego wroga i dodaj go do strefy przeciwności.</p><p class='main-text'><b>Wymuszony:</b> Kiedy ten etap zostanie pokonany, dodaj Wielką Halę Miodów Pitnych do strefy przeciwności.</p>{victory:10}");
            AddHtml("Whisperer-in-the-Darkness-TAP", "<p class='flavor-text'>Tawerna była pełna plotek o zabójcy zwanym “Szepczącą”. Jeśli zamierzacie wyjaśnić tę tajemnicę, musicie ją odnaleźć i zatrzymać zanim zaatakuje.</p><p class='main-text'><b>Wymuszony:</b> Kiedy obszar zostanie zbadany, podnieś o 1 poziom zagrożenia każdego gracza.</p><p class='main-text'><b>Wymuszony:</b> Kiedy ten etap zostanie pokonany, dodaj Szepczącą do strefy przeciwności.</p>{victory:10}");
            AddHtml("Spreading-Inferno-TAP", "<p class='main-text'>Strzeżony (obszar).</p><p class='main-text'><b>Wymuszony:</b> Na początku fazy podróży odrzuć dołączony obszar, następnie każdy gracz podnosi o 1 poziom zagrożenia oraz odrzuca sprzymierzeńca o najniższym koszcie. Następnie ponownie odkryj Szalejącą Pożogę.</p><p class='main-text'><b>Wymuszony:</b> Kiedy dołączony obszar zostanie zbadany, odwróć tę kartę i odkryj ją.</p>");
            AddHtml("The-Whisperer-TAP", "<p class='main-text'><b>Wymuszony:</b> Pod koniec rundy cofnij Szepczącą do strefy przeciwności (jeśli to możliwe). Szepcząca wykonuje natychmiastowy atak przeciwko pierwszemu graczowi. Wszystkie obrażenia, które zadałby ten atak, należy anulować i zamiast tego podnieść o tę samą wartość poziom zagrożenia pierwszego gracza.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Szepcząca zostanie pokonana, odwróć tę kartę i odkryj ją.</p>");
            AddHtml("The-Spy-Master-TAP", "<p class='main-text'><b>Wymuszony:</b> Kiedy Mistrz Szpiegów otrzyma kartę cienia bez efektu cienia, zadaj 1 obrażenie broniącej się postaci i przydziel Mistrzowi Szpiegów kolejną kartę cienia (limit dwa razy na atak).</p><p class='main-text'><b>Wymuszony:</b> Kiedy Mistrz Szpiegów zostanie pokonany, odwróć tę kartę i odkryj ją.</p>");
            AddHtml("Ossian-TAP", "<p class='main-text'>Odporny na efekty kart graczy. W każdej rundzie Osjan nie może otrzymać więcej niż X obrażeń, gdzie X to liczba celów z cechą <b><i>Wskazówka</i></b> kontrolowanych przez graczy.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Osjan zostanie pokonany, odwróć tę kartę i odkryj ją.</p><p class='flavor-text'>Znaleźliście Osjana pochylonego nad biało odzianymi zwłokami. Słysząc wasze kroki odwraca się.</p>");
            AddHtml("Garrison-Barracks-TAP", "<p class='main-text'>Odporny na efekty kart graczy. X to dwukrotność liczby celów z cechą <b><i>Wskazówka</i></b> kontrolowanych przez graczy.</p><p class='main-text'><b>Podróż:</b> Wtasuj odłożonych na bok nie-unikatowych wrogów z cechą <b><i>Dunland</i></b> do talii Spotkań.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Koszary Garnizonu zostaną zbadane, odwróć tę kartę i odkryj ją.</p><p class='flavor-text'>Straż miejska Aldburga ma oczy i uszy wszędzie. Ale kto pilnuje strażników?</p>");
            AddHtml("The-Horse's-Tail-TAP", "<p class='main-text'>Odporny na efekty kart graczy. X to dwukrotność liczby celów z cechą <b><i>Wskazówka</i></b> kontrolowanych przez graczy.</p><p class='main-text'><b>Podróż:</b> Wtasuj odłożonych na bok wrogów z cechą <b><i>Garnizon</i></b> do talii Spotkań.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Pod Końskim Ogonem zostanie zbadana, odwróć tę kartę i odkryj ją.</p><p class='flavor-text'>Może to wzbudzić podejrzenia straży miejskiej, ale rozwiązanie tej tajemnicy wymaga użycia niestandardowych metod.</p>");
            AddHtml("The-Armoury-TAP", "<p class='main-text'>Odporny na efekty kart graczy.</p><p class='main-text'><b>Podróż:</b> Każdy gracz musi wyczerpać postać o najwyższej {Defense}.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Zbrojownia zostanie zbadana, odwróć tę kartę i odkryj ją.</p><p class='flavor-text'>Szepcząca dzierżyła charakterystyczny, znajomo wyglądający sztylet. Możliwe, że kwatermistrz zbrojowni mógłby coś o nim powiedzieć.</p>");
            AddHtml("Great-Mead-Hall-TAP", "<p class='main-text'>Odporny na efekty kart graczy.</p><p class='main-text'><b>Podróż:</b> Gracze muszą podnieść swój poziom zagrożenia o X, gdzie X to liczba wrogów w strefie przeciwności.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Wielka Hala Miodów Pitnych zostanie zbadana, odwróć tę kartę i odkryj ją.</p><p class='flavor-text'>Śledziliście agentów Szepczącej aż do sal Marszałka Éomunda. Jak głęboko sięga ta konspiracja?</p>");
            AddHtml("Town-Guard-TAP", "<p class='main-text'>Jeśli gracze kontrolują co najmniej 2 cele z cechą <b><i>Wskazówka</i></b>, Strażnik Miejski otrzymuje +2 {Threat}.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Strażnik Miejski zaatakuje i zabije kontrolowaną przez Ciebie postać, odrzuć 1 żeton zasobów z puli zasobów każdego kontrolowanego przez ciebie bohatera posiadającego cel z cechą <b><i>Wskazówka</i></b>.</p><p class='flavor-text'>“Czy też łudzę się tylko nadzieją, że przysłano cię tutaj, abyś mnie poratował w rozterce i ciężkiej potrzebie?” — Éomer, Dwie Wieże</p>");
            AddHtml("Dunlending-Agents-TAP", "<p class='main-text'>Jeśli gracze kontrolują co najmniej 2 cele z cechą <b><i>Wskazówka</i></b>, Agenci Dunlendingów otrzymują +2 {Attack}.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Agenci Dunlendingów zaatakują i zabiją kontrolowaną przez ciebie postać, zadaj 1 obrażenie każdemu kontrolowanemu przez ciebie bohaterowi posiadającemu cel z cechą <b><i>Wskazówka</i></b>.</p><p class='flavor-text'>“Dunlendingowie i inni ludzie pracowali…” — Powrót Króla</p>");
            AddHtml("Aldburg-Malcontent-TAP", "<p class='main-text'><b>Po odkryciu:</b> Jeśli gracze kontrolują co najmniej 3 cele z cechą <b><i>Wskazówka</i></b>, Malkontent z Aldburga otrzymuje mroczną falę.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Jeśli gracze kontrolują co najmniej 2 cele z cechą <b><i>Wskazówka</i></b>, po tym ataku cofnij atakującego wroga do strefy przeciwności.</p>");
            AddHtml("Misty-Street-TAP", "<p class='main-text'><b>Po odkryciu:</b> Odrzuć wszystkie dodatki i żetony z kontrolo-wanego przez pierwszego gracza sprzymierzeńca o najwyż-szym koszcie i dołącz go zakrytego do Mglistej Ulicy.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Mglista Ulica opuści grę, przygotuj i zwróć dołączonego sprzymierzeńca pod kontrolę właściciela.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Odrzuć kontrolowany przez ciebie dodatek nieposiadający cechy <b><i>Wskazówka</i></b>.</p>");
            AddHtml("Well-lit-Hall-TAP", "<p class='main-text'>Kiedy Oświetlona Sala znajduje się w strefie przeciwności, na karcie wyprawy w wyniku powodzenia wyprawy mogą być umieszczone nie więcej niż 3 żetony postępu.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Atakujący wróg otrzymuje +1 {Attack} (+2 {Attack} jeśli broniąca się postać posiada dodatek z cechą <b>Wskazówka</b>).</p>");
            AddHtml("Decrepit-Hovel-TAP", "<p class='main-text'>Koszt sprzymierzeńca musi zostać opłacony wyłącznie z puli jednego bohatera.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Odrzuć 1 żeton zasobów z puli zasobów kontrolowanego przez ciebie bohatera.</p><p class='flavor-text'>“…stamtąd rozłażą się po okolicy, kradnąc czy też, jak to nazywają, »rekwirując«.” — Tom Cotton, Powrót Króla</p>");
            AddHtml("Taken-by-the-Night-TAP", "<p class='main-text'><b>Po odkryciu:</b> Każdy gracz musi odrzucić sprzymierzeńca o najniższym wydrukowanym koszcie. Jeśli nie został odrzucony sprzymierzeniec o wydrukowanym koszcie 2 lub wyższym, Zabrany przez Noc otrzymuje mroczną falę.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Atakujący wróg otrzymuje +1{Attack} (+2{Attack} jeśli atakujący wróg atakuje ze strefy przeciwności).</p>");
            AddHtml("False-Lead-TAP", "<p class='main-text'>Zguba 1.</p><p class='main-text'><b>Po odkryciu:</b> Każdy gracz usuwa z wyprawy bohatera posiadającego cel z cechą <b><i>Wskazówka</i></b> (jeśli to możliwe). Następnie każdy gracz wyczerpuje kontrolowanego przez siebie bohatera, który nie został przydzielony do wyprawy.</p><p class='flavor-text'>“A może chcesz mnie kłamstwem usidlić?”<br/>— Frodo Baggins, Dwie Wieże</p>");
            AddHtml("Sudden-Ambush-TAP", "<p class='main-text'><b>Po odkryciu:</b> Pierwszy gracz musi wybrać: podnieść swój poziom zagrożenia o 4 lub wejść w zwarcie z wrogiem najbliżej wierzchu odrzuconych kart spotkań, który następnie wykonuje natychmiastowy atak. Jako obrońcę tego ataku musisz zadeklarować kontrolowaną przez ciebie postać o najwyższej {Willpower}.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Rozpatrz efekt “po odkryciu” tej karty.</p>");
            AddHtml("Evil-Under-the-Moon-TAP", "<p class='flavor-text'>Wiedząc, że w Aldburgu zamieszkała frakcja rebeliantów, Ealdwulf zachęca bohaterów do połączenia sił i pozbycia się ich raz na zawsze.</p><p class='main-text'><b>Przygotowanie:</b> Odłóż na bok poza grę wszystkie karty pobocznych wypraw talii Spotkań, każdą kopię Strażnika Miejskiego oraz każdą kopię kart Agenci Dunlendingów. Odłóż na bok poza grę każdą dwustronną kartę spotkania zakrytą stroną karty celu <b><i>Wskazówka</i></b>. Dodaj Koszary Garnizonu i Pod Końskim Ogonem do strefy przeciwności. Przeszukaj talię Spotkań w poszukiwaniu wroga i dodaj go do strefy przeciwności (2 różnych wrogów, jeśli jest 3 lub więcej graczy). Potasuj talię Spotkań. </p>");
            AddHtml2("Evil-Under-the-Moon-TAP", "<p class='flavor-text'>Od czego zacząć polowanie? Poszukasz pomocy u strażników miejskich czy zbadasz przestępcze podziemie mieszczące się w obskurnej tawernie?</p><p class='main-text'>Gracze nie mogą dobrowolnie wchodzić w zwarcie z wrogami znajdującymi się w strefie przeciwności.</p><p class='main-text'><b>Wymuszony:</b> Na koniec rundy podnieś o 1 poziom zagrożenia każdego gracza.</p><p class='main-text'>Jeżeli gracze kontrolują co najmniej jedną kartę celu z cechą <b><i>Wskazówka</i></b> przejdź do kolejnego etapu.</p>");
            AddHtml("Gathering-Clues-TAP", "<p class='flavor-text'>Miasto Aldburg jest pełne tajemnic. Za każdym rogiem czeka nowa zagadka, a za każdą zagadką kryje się naostrzone, zdesperowane ostrze. Zanim wzejdzie słońce, będziecie musieli poszukać głębiej, aby odkryć prawdę.</p>");
            AddHtml2("Gathering-Clues-TAP", "<p class='main-text'>Wygląda na to, że Aldburg kryje niejedną mroczną tajemnicę. Jeśli tylko uchwycicie choć jeden wątek, być może uda się wam rozwikłać zagadkę tej skorumpowanej pajęczyny powiązań. Minęła północ. Czasu jest coraz mniej.</p><p class='main-text'><b>Odpowiedź:</b> Na koniec rundy, jeśli gracze kontrolują co najmniej 4 karty celu z cechą <b><i>Wskazówka</i></b>, mogą przejść do etapu 3A.</p>");
            AddHtml("The-Uncertain-Dawn-TAP", "<p class='flavor-text'>Gdy słońce zachodzi nad ulicami Aldburga, twoi osaczeni wrogowie ujawniają się. Zebrałeś swoje wskazówki. Wykorzystaj je w walce o swoje życie – i duszę Rohanu.</p><p class='main-text'><b></b> Po odkryciu: Usuń z gry wszystkie karty z cechą <b><i>Podejrzany</i></b> oraz karty pobocznych wypraw talii Spotkań. Jeśli to możliwe, umieść w strefie przeciwności wszystkich odłożonych na bok poza grę wrogów z cechą <b><i>Dunland</i></b> lub <b><i>Garnizon</i></b>. </p>");
            AddHtml2("The-Uncertain-Dawn-TAP", "<p class='main-text'></b> Niepewny Świt dostaje –5 punktów wyprawy za każdą kontrolowaną przez graczy kartę celu z cechą <b><i>Wskazówka</i></b>.</p><p class='main-text'><b>Wymuszony:</b> Na początku fazy Wyprawy odrzucaj karty z wierzchu talii Spotkań, dopóki nie odrzucisz wroga. Odkryj tego wroga i dodaj go do strefy przeciwności.</p><p class='main-text'><b>Ten etap może zostać pokonany jedynie na końcu rundy. Jeżeli gracze pokonają ten etap, wygrywają grę.</b></p>");
            AddHtml("Ruby-Cloak-pin-TAP", "<p class='main-text'>Jeśli Rubinowa Klamra nie jest dołączona do żadnej karty, dołącz ją do bohatera posiadającego najmniej celów z cechą <b><i>Wskazówka</i></b>.</p><p class='main-text'><b>Wymuszony:</b> Kiedy bohater, do którego dołączono tę kartę, zostanie przygotowany, odrzuć 4 wierzchnie karty ze swojej talii.</p><p class='flavor-text'>Wśród popiołu, natychmiast rozpoznaliście klamrę. Należy do Gálmóda, zaufanego doradcy królowej Morweny.</p>");
            AddHtml("Silver-Dagger-TAP", "<p class='main-text'><b>Po odkryciu:</b> Dodaj Sprawę Tożsamości i Krwawe Żniwo do strefy przeciwności.</p><p class='main-text'>Jeśli Srebrny Sztylet nie jest dołączony do żadnej karty, dołącz go do bohatera posiadającego najmniej celów z cechą <b><i>Wskazówka</i></b>.</p><p class='main-text'><b>Wymuszony:</b> Kiedy bohater, do którego dołączono tę kartę, zostanie przygotowany, zadaj mu 1 obrażenie.</p><p class='flavor-text'>Szepcząca uciekła między uliczki Aldburga, jednakże zostawiła charakterystyczny, kosztowny sztylet.</p>");
            AddHtml("Prison-Key-TAP", "<p class='main-text'><b>Po odkryciu:</b> Dodaj Ogień Płonie i Więzień w Bieli do strefy przeciwności.</p><p class='main-text'>Jeśli Więzienny Klucz nie jest dołączony do żadnej karty, dołącz go do bohatera posiadającego najmniej celów z cechą <b><i>Wskazówka</i></b>.</p><p class='main-text'><b>Wymuszony:</b> Kiedy bohater, do którego dołączono tę kartę, zostanie przygotowany, odrzuć dodatek niebędący celem dołączony do tego bohatera.</p><p class='flavor-text'>Ranny i walczący o ucieczkę Mistrz Szpiegów zdradza kilka kluczowych szczegółów: jeden z jego agentów jest już w ruchu i zmierza po więźnia.</p>");
            AddHtml("Traitor's-Confession-TAP", "<p class='main-text'>Jeśli Wyznanie Zdrajcy nie jest dołączone do żadnej karty, dołącz je do bohatera posiadającego najmniej celów z cechą <b><i>Wskazówka</i></b>.</p><p class='main-text'><b>Wymuszony:</b> Kiedy bohater, do którego dołączono tę kartę, zostanie przygotowany, odrzuć losową kartę z twojej ręki.</p><p class='flavor-text'>Wraz z ostatnim tchnieniem Osjan wyznał, że Gálmód rozkazał mu zabić żołnierza z własnego éoredu: świadka straszliwej zbrodni.</p>");
            AddHtml("Raven-Signet-Ring-TAP", "<p class='main-text'><b>Po odkryciu:</b> Dodaj Mężczyznę z Wywiniętą Wargą do strefy przeciwności.</p><p class='main-text'>Jeśli Sygnet Kruka nie jest dołączony do żadnej karty, dołącz go do bohatera posiadającego najmniej celów z cechą <b><i>Wskazówka</i></b>.</p><p class='main-text'><b>Wymuszony:</b> Kiedy bohater, do którego dołączono tę kartę, zostanie przygotowany, odrzuć wszystkie żetony zasobów z puli zasobów tego bohatera.</p><p class='flavor-text'>Więzień opowiedział strażnikom o agencie zwanym “mistrzem szpiegów”. Jedynym tropem jest ten sygnet, który prawdopodobnie do niego należy.</p>");
            AddHtml("Rohirric-Coins-TAP", "<p class='main-text'><b>Po odkryciu:</b> Dodaj Szepczącą w Ciemności do strefy przeciwności.</p><p class='main-text'>Jeśli Monety Rohirrimów nie są dołączone do żadnej karty, dołącz je do bohatera posiadającego najmniej celów z cechą <b><i>Wskazówka</i></b>.</p><p class='main-text'><b>Wymuszony:</b> Kiedy bohater, do którego dołączono tę kartę, zostanie przygotowany, wyczerp inną kontrolowaną przez ciebie postać.</p><p class='flavor-text'>Nieznana osoba wpada na wasz stół. Przepraszając was wylewnie, wkracza w ciemność nocy. Po chwili zauważacie sakiewkę z pieniędzmi pozostawionymi przez nieznajomego.</p>");
            AddHtml("Red-Fletched-Arrow-TAP", "<p class='main-text'>Jeśli Czerwona Strzała nie jest dołączona do żadnej karty, dołącz ją do bohatera posiadającego najmniej celów z cechą <b><i>Wskazówka</i></b>.</p><p class='main-text'><b>Wymuszony:</b> Kiedy bohater, do którego dołączono tę kartę, zostanie przygotowany, podnieś o 1 swój poziom zagrożenia.</p><p class='flavor-text'>Kwatermistrz zbrojowni poinformował was, że nie tylko osobiście wykuł sztylet dla Lorda Gálmóda, ale również wykonał tę niezwykłą strzałę.</p>");
            AddHtml("Letter-from-Dunland-TAP", "<p class='main-text'>Jeśli List z Dunlandu nie jest dołączony do żadnej karty, dołącz go do bohatera posiadającego najmniej celów z cechą <b><i>Wskazówka</i></b>.</p><p class='main-text'><b>Wymuszony:</b> Kiedy bohater, do którego dołączono tę kartę, zostanie przygotowany, odrzuć 2 żetony postępu z każdego obszaru i karty wyprawy.</p><p class='flavor-text'>Odnaleźliście głęboko schowany zapieczętowany list napisany kiepskim rohirrimskim, podpisany przez niejakiego “Grimrede”. Jest zaadresowany do, jak mniemaliście, waszego przyjaciela Gálmóda.</p>");
        }

        private void TheScouringOfTheShire()
        {
            AddHtml("Set-up-Barriers-TSotS", "<p class='flavor-text'>“Inni na rozkaz Meriadoka ustawili w poprzek drogi zapory na obu końcach wsi.”— Powrót Króla</p><p class='main-text'>Gracze nie mogą dobierać kart.</p><p class='main-text'><b>Akcja:</b> Wydaj 1{PerPlayer} żetonów zasobów jako grupa, aby umieścić 3 żetony postępu na karcie Barykadować Drogę.</p><p class='main-text'><b>Wymuszony:</b> Po pokonaniu tej wyprawy pierwszy gracz odrzuca wszystkie żetony zasobów z każdego kontrolowanego przez siebie bohatera. Następnie odwróć tę kartę.</p>");
            AddHtml("The-Battle-of-Bywater-TSotS", "<p class='main-text'> <b>Po odkryciu:</b> Wtasuj stos odrzuconych kart spotkań do talii spotkań. Usuń każdego wroga z cechą <b><i>Szeryf</i></b> z gry oraz talii spotkań. Pierwszy gracz przeszukuje talię spotkań i pulę zwycięstwa w poszukiwaniu 1 kopii Gangu Zbójów i wchodzi z tym wrogiem w zwarcie. Następnie każdy gracz, który nie znajduje się w zwarciu z wrogiem <b><i>Zbój</i></b>, przeszukuje talię spotkań w poszukiwaniu wroga <b><i>Zbój</i></b> i wchodzi z nim w zwarcie. Potasuj talię spotkań. Odrzuć karty z talii spotkań, aż odrzucisz X wrogów i dodaj tych wrogów do strefy przeciwności. X to liczba kart pobocznych wypraw w grze. Następnie usuń te poboczne wyprawy z gry.</p>");
            AddHtml2("The-Battle-of-Bywater-TSotS", "<p class='flavor-text'>“Ale zbójcy nie dali się tak łatwo nastraszyć. Kilku chciało posłuchać wezwania, lecz natychmiast współtowarzysze wybili im to z głowy. Ze dwudziestu zawróciło i rzuciło się na wozy tarasujące drogę.” — Powrót Króla</p><p class='main-text'> Jeżeli któryś z wrogów z cechą <b><i>Zbój</i></b> w grze ma jakiekolwiek pozostałe punkty wytrzymałości, wszyscy wrogowie <b><i>Zbój</i></b> zyskują słowo kluczowe niezniszczalny.</p><p class='main-text'><b>Jeśli w grze nie ma wrogów, przejdź do następnego etapu.</b></p>");
            AddHtml("Bright-Mail-TSotS", "<p class='main-text'>Dołącz do bohatera.</p><p class='main-text'>Bohater, do którego dołączono tę kartę otrzymuje +1 {Defense} (+2 {Defense} jeśli bohater do którego dołączono tę kartę posiada cechę <b><i>Hobbit</i></b>).</p><p class='flavor-text'>“Woleli nie porywać się na zbrojnych, którzy mają miecze, hełmy, tarcze i tak dalej. Każdy by się dwa razy namyślił, zanimby was zaczepił.”<br/>— Barliman Butterbur, Powrót Króla</p>");
            AddHtml("Take-Him-Lads-TSotS", "<p class='main-text'><b>Po odkryciu:</b> Podnieś o 4 poziom zagrożenia każdego gracza. Następnie każdy gracz odrzuca kontrolowanego przez siebie sprzymierzeńca i obniża o X swój poziom zagrożenia, gdzie X to wydrukowany koszt odrzuconego sprzymierzeńca.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Jeśli twój poziom zagrożenia jest wyższy niż koszt zwarcia atakującego wroga, atakujący wróg otrzymuje +2 {Attack}. W przeciwnym przypadku podnieś o 2 swój poziom zagrożenia.</p>");
            AddHtml("Brandywine-Gate-TSotS", "<p class='main-text'>Odporny na efekty kart Graczy.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Brama na Brandywinie zostanie zbadana, każdy gracz przeszukuje talię Spotkań, wybiera Szeryfa z Piórem na Czapce i wchodzi z nim w zwarcie. Potasuj talię Spotkań.</p><p class='flavor-text'>“U dwóch końców mostu jeżyły się ostrokołowe bramy.”<br/>— Powrót Króla</p>{victory:1}");
            AddHtml("Brick-Building-TSotS", "<p class='main-text'>Kiedy Ceglany Budynek znajduje się w strefie przeciwności, postacie nie mogą być leczone.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Przydziel 2 obrażenia broniącej się postaci lub podnieś o 2 swój poziom zagrożenia.</p><p class='flavor-text'>“…pomiędzy dwoma rzędami nowych, lichych domów, zobaczyli nowy młyn w całej jego złowrogiej i brudnej brzydocie…” — Powrót Króla</p>");
            AddHtml("Shirriff-House-TSotS", "<p class='main-text'><b>Wymuszony:</b> Kiedy Dom Szeryfów stanie się aktywnym obszarem, wyczerp każdą postać z wydrukowanym 2 lub więcej {Attack}.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Dom Szeryfów opuści grę, pierwszy gracz przeszukuje talię Spotkań i stos odrzuconych kart Spotkań, bierze wroga z cechą <b><i>Szeryf</i></b> i wchodzi z nim w zwarcie. Potasuj talię Spotkań.</p><p class='flavor-text'>“Wnętrze okazało się wilgotne i ponure…” — Powrót Króla</p>");
            AddHtml("Wooden-Shed-TSotS", "<p class='main-text'>Kiedy Drewniana Szopa znajduje się w strefie przeciwności, gracze nie mogą obniżać swojego poziomu zagrożenia o więcej niż 1 na rundę.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Podnieś o 1 swój poziom zagrożenia lub wyczerp kontrolowaną przez siebie postać.</p><p class='flavor-text'>“Wszędzie piętrzyły się kupy śmieci i odpadków. Drzwi były odrapane, sznur od dzwonka zwisał oderwany, a dzwonek nie dzwonił.” — Powrót Króla</p>");
            AddHtml("Hobbiton-Road-TSotS", "<p class='main-text'><b>Wymuszony:</b> Kiedy Droga do Hobbitonu opuści grę, umieść wroga z cechą <b><i>Zbój</i></b> ze stosu odrzuconych kart Spotkań w zwarciu z graczem.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Przydziel 1 obrażenie broniącej się pos-taci lub podnieś o 1 swój poziom zagrożenia.</p>");
            AddHtml("Escorted-Prisoners-TSotS", "<p class='flavor-text'>Po niespodziewanym spotkaniu na drodze z okrytym hańbą Sarumanem, Frodo i jego towarzysze napotykają opór i wrogość ze strony Szeryfów z Shire. </p><p class='main-text'> <b>Po odkryciu:</b> Uczyń Bramę na Brandywinie aktywnym obszarem i dodaj Kamień Trzech Ćwiartek do strefy przeciwności.</p>");
            AddHtml2("Escorted-Prisoners-TSotS", "<p class='flavor-text'>“Zgodnie z rozkazem Wodza macie iść tam z nami bez oporu. Zaprowadzimy was Nad Wodę i oddamy w ręce Dużych Ludzi Wodza.” — Powrót Króla</p><p class='main-text'> Na tym etapie nie może znajdować się więcej niż 12 żetonów postępu.</p><p class='main-text'><b>Wymuszony:</b> Na końcu fazy Wyprawy, jeśli nie położono tu żadnego żetonu postępu w wyniku udanej wyprawy, pierwszy gracz podnosi swój poziom zagrożenia o 3. Jeśli na karcie wyprawy położono więcej niż 3 żetony postępu, każdy gracz będący w zwarciu z więcej niż 1 wrogiem z cechą <b><i>Szeryf</i></b> może odrzucić 1 z nich.</p><p class='main-text'><b>Gracze nie mogą przejść do kolejnego etapu, dopóki w grze znajduje się Kamień Trzech Ćwiartek.</b></p>");
            AddHtml("Escorted-Prisoners-TSotS", "<p class='main-text'><b>Odpowiedź:</b> Kiedy Frodo Baggins zostanie przydzielony do wy-prawy, wydaj 1 żeton zasobu z jego puli zasobów, aby przygotować inną przydzieloną do wyprawy postać. Jeśli w tej fazie wyprawa zakończy się sukcesem, obniż o 1 swój poziom zagrożenia.</p>");
            AddHtml("Frodo-Baggins-Fellowship-TSotS", "<p class='main-text'>Pierwszy gracz obejmuje kontrolę nad Frodem Bagginsem.</p><p class='main-text'><b>Odpowiedź:</b> Kiedy Frodo Baggins zostanie zadeklarowany jako obrońca, wydaj 1 [fellowship] żeton zasobu, aby obniżyć swój poziom zagrożenia o {Threat} atakowanego wroga. Do końca rundy ten wróg nie może otrzymać obrażeń.</p><p class='main-text'><b>Jeśli Frodo Baggins opuści grę, gracze przegrywają.</b></p>");
            AddHtml("Gang-of-Ruffians-TSotS", "<p class='main-text'><b>Odpowiedź:</b> Kiedy Gang Zbójów zadałby obrażenia, zamiast tego rozdziel te obrażenia między postaciami kontrolowanymi przez broniącego się gracza.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Gang Zbójów atakuje, jeśli ten atak zniszczył co najmniej 1 z twoich postaci, odrzuć 1 losową kartę z ręki oraz podnieś o 1 swój poziom zagrożenia.</p>");
            AddHtml("Voice-of-Saruman-TSotS", "<p class='main-text'><b>Po odkryciu:</b> Wybierz losowo po 1 sprzymierzeńcu kontrolowanym przez każdego z graczy. Każdy gracz zadaje X obrażeń swojemu bohaterowi, gdzie X to {Attack} wybranego sprzymierzeńca, następnie odrzuca tego sprzymierzeńca. Jeśli gracz nie kontroluje żadnych sprzymierzeńców, to odrzuca wszystkie dodatki z bohatera posiadającego najwięcej dodatków.</p><p class='flavor-text'>“Stracił moc, zachował tylko głos, który będzie was straszył i oszukiwał, jeśli mu się poddacie.”<br/>— Frodo Baggins, Powrót Króla</p>");
            AddHtml("Three-Farthing-Stone-TSotS", "<p class='main-text'>X to 3{PerPlayer}. Odporny na efekty kart Graczy. Gracze nie mogą podróżować na ten obszar, dopóki na karcie Eskortowani “Więźniowie” nie ma 12 żetonów postępu.</p><p class='main-text'><b>Odpowiedź:</b> Kiedy Kamień Trzech Ćwiartek opuści grę jako zbadany obszar, każdy gracz obniża o 3 swój poziom zagrożenia.</p><p class='flavor-text'>“Głodni, z poobijanymi nogami, nie mogli dłużej dotrzymać kroku kucykom.” — Powrót Króla</p>{victory:1}");
            AddHtml("Curse-Upon-the-Shire-TSotS", "<p class='main-text'><b>Po odkryciu:</b> Każdy gracz musi wybrać: podnieść o 1 swój poziom zagrożenia za każdego kontrolowanego przez siebie sprzymierzeńca lub odrzucić 2 kontrolo-wanych sprzymierzeńców.</p><p class='flavor-text'>“Przez długie lata niełatwo będzie w Shire o fajkowe liście.” — Saruman, Powrót Króla</p>");
            AddHtml("Star-like-Gem-TSotS", "<p class='main-text'>Dołącz do bohatera.</p><p class='main-text'>Bohater, do którego dołączono tę kartę otrzymuje +1 {Willpower}.</p><p class='main-text'>Jeśli bohater do którego dołączono tę kartę posiada cechę <b><i>Hobbit</i></b>, nie zostaje wyczerpany, kiedy zostaje przydzielony do wyprawy.</p><p class='flavor-text'>“W tym znajdziesz obronę, ilekroć cię nękać będzie wspomnienie strachu i ciemności.” — Arwena, Powrót Króla</p>");
            AddHtml("The-Lockholes-TSotS", "<p class='main-text'>Opresja.</p><p class='main-text'><b>Po odkryciu:</b> Usuń wszystkie dodatki i odrzuć wszystkie żetony dołączone do kontrolowanego przez ciebie sprzymierzeńca o najwyższym koszcie. Dołącz tego sprzymierzeńca do Lochów.</p><p class='main-text'><b>Wymuszony:</b> Kiedy Lochy zostaną zbadane, przygotuj i cof-nij dołączonego sprzymierzeńca pod kontrolę właściciela.</p><p class='flavor-text'>“A poza tym są lochy, to znaczy stare podziemne składy w Michel Delving, zamienione teraz na więzienia dla tych, którzy ośmielają się zbirom przeciwstawić.” — Tom Cotton, Powrót Króla</p>");
            AddHtml("Sharkey's-Men-TSotS", "<p class='main-text'><b>Po odkryciu:</b> Do końca rundy każdy wróg znajdujący się w grze otrzymuje +1 {Attack}. Jeśli w grze znajduje się mniej wrogów z cechą <b><i>Zbój</i></b> niż liczba graczy, odrzucaj karty z talii Spotkań, dopóki nie odrzucisz wroga z cechą <b><i>Zbój</i></b>. Dodaj tego wroga do strefy przeciwności.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Jeśli atakujący wróg posiada cechę <b>Zbój</b>, wykonuje dodatkowy atak. Broniący się gracz może odrzucić sprzymierzeńca, aby anulować ten efekt.</p>");
            AddHtml("Into-the-West-TSotS", "<p class='main-text'> Nie możesz użyć tego kontraktu, dopóki nie zniszczyłeś Jedynego Pierścienia i nie wygrałeś scenariusza <i>Porządki w Shire</i> w trybie kampanii.</p><p class='main-text'>Twoja talia musi zawierać tę samą liczbę kart o wydrukowanym koszcie 1, 2, 3, 4, i 5.</p><p class='main-text'><b>Przygotowanie:</b> Przeszukaj 10 wierzchnich kart ze swojej talii w poszukiwaniu sprzymierzeńca o koszcie 3 lub mniejszym i umieść go w grze. Potasuj swoją talię i odwróć tę kartę.</p><p class='flavor-text'>“Starałem się uratować Shire i uratowałem, ale nie dla siebie.” — Frodo Baggins, Powrót Króla</p>");
            AddHtml2("Into-the-West-TSotS", "<p class='main-text'> Na Zachód jest odporne na efekty innych kart, nie może opuścić gry i nie możesz stracić nad nim kontroli. Koszt zagrania sprzymierzeńca nie może zostać zredukowany o więcej niż 2.</p><p class='main-text'><b><b>Wymuszony:</b></b> Na początku fazy Wyprawy, dołącz Na Zachód do kontrolowanego przez ciebie sprzymierzeńca, jeśli to możliwe.</p><p class='main-text'><b><b>Wymuszony:</b></b> Na końcu fazy Odpoczynku rozdziel X żetonów zasobów pomiędzy kontrolowanych przez ciebie bohaterów, gdzie X to wydrukowany koszt dołączonej karty. Usuń dołączoną kartę z gry i dobierz 1 kartę.</p>");
            AddHtml("Once-Beloved-Place-TSotS", "<p class='main-text'>Mroczna fala.</p><p class='main-text'><b>Po odkryciu:</b> Do końca rundy każdy obszar otrzymuje +1 {Threat}. Jeśli w strefie przeciwności znajduje się mniej obszarów niż 1{PerPlayer}, odrzucaj karty z talii Spotkań, dopóki nie zostanie odrzucony obszar. Dodaj ten obszar do strefy przeciwności.</p><p class='flavor-text'>“Drzewa kasztanowe zniknęły. Zielone skarpy i żywopłoty zniszczono.” — Powrót Króla</p>");
            AddHtml("Watch-the-East-Road-TSotS", "<p class='flavor-text'>“Silna banda nadciąga, są nie dalej niż o cztery mile stąd. (…) Będzie razem koło stu, a wszystko, co im się nawinie w marszu, podpalają.” — Merry, Powrót Króla</p><p class='main-text'>Nie można wybrać jako aktywnej wyprawy. X to 1+1{PerPlayer}.</p><p class='main-text'><b>Odpowiedź:</b> Na początku etapu przeciwności umieść na tej karcie dowolną liczbę żetonów postępu i odkryj taką samą liczbę kart Spotkań.</p><p class='main-text'><b>Wymuszony:</b> Po pokonaniu tej wyprawy odwróć tę kartę.</p>");
            AddHtml("Roused-Hobbits-TSotS", "<p class='main-text'>Gdy ta karta jest dołączona, traktuj ją jako dodatek z cechą <b><i>Stan</i></b> o treści: “Karta, do której dołączono tę kartę, traci słowo kluczowe niezniszczalny i typ karty wróg oraz zys-kuje typ karty sojusznik z {Willpower} równą jej {Threat}. Jeżeli Podburzeni Hobbici lub karta do której została ona dołączona opuści grę, usuń obie karty z gry.”</p><p class='main-text'><b>Po odkryciu:</b> Wybierz 1 gracza, który przejmie kontrolę nad wrogiem z cechą <b><i>Szeryf</i></b> będącym z nim w zwarciu i dołącza do niego kartę Podburzeni Hobbici (jeśli żaden wróg z cechą <b><i>Szeryf</i></b> nie jest w zwarciu, usuń Podburzeni Hobbici z gry).</p>");
            AddHtml("Roused-Hobbits-2-TSotS", "<p class='main-text'>Gdy ta karta jest dołączona, traktuj ją jako dodatek z cechą <b><i>Stan</i></b> o treści: “Karta, do której dołączono tę kartę, traci słowo kluczowe niezniszczalny i typ karty wróg oraz zys-kuje typ karty sojusznik z {Willpower} równą jej {Threat}. Jeżeli Podburzeni Hobbici lub karta do której została ona dołączona opuści grę, usuń obie karty z gry.”</p><p class='main-text'><b>Po odkryciu:</b> Wybierz 1 gracza, który przejmie kontrolę nad wrogiem z cechą <b><i>Szeryf</i></b> będącym z nim w zwarciu i dołącza do niego kartę Podburzeni Hobbici (jeśli żaden wróg z cechą <b><i>Szeryf</i></b> nie jest w zwarciu, usuń Podburzeni Hobbici z gry).</p>");
            AddHtml("Roused-Hobbits-3-TSotS", "<p class='main-text'>Gdy ta karta jest dołączona, traktuj ją jako dodatek z cechą <b><i>Stan</i></b> o treści: “Karta, do której dołączono tę kartę, traci słowo kluczowe niezniszczalny i typ karty wróg oraz zys-kuje typ karty sojusznik z {Willpower} równą jej {Threat}. Jeżeli Podburzeni Hobbici lub karta do której została ona dołączona opuści grę, usuń obie karty z gry.”</p><p class='main-text'><b>Po odkryciu:</b> Wybierz 1 gracza, który przejmie kontrolę nad wrogiem z cechą <b><i>Szeryf</i></b> będącym z nim w zwarciu i dołącza do niego kartę Podburzeni Hobbici (jeśli żaden wróg z cechą <b><i>Szeryf</i></b> nie jest w zwarciu, usuń Podburzeni Hobbici z gry).</p>");
            AddHtml("Roused-Hobbits-4-TSotS", "<p class='main-text'>Gdy ta karta jest dołączona, traktuj ją jako dodatek z cechą <b><i>Stan</i></b> o treści: “Karta, do której dołączono tę kartę, traci słowo kluczowe niezniszczalny i typ karty wróg oraz zys-kuje typ karty sojusznik z {Willpower} równą jej {Threat}. Jeżeli Podburzeni Hobbici lub karta do której została ona dołączona opuści grę, usuń obie karty z gry.”</p><p class='main-text'><b>Po odkryciu:</b> Wybierz 1 gracza, który przejmie kontrolę nad wrogiem z cechą <b><i>Szeryf</i></b> będącym z nim w zwarciu i dołącza do niego kartę Podburzeni Hobbici (jeśli żaden wróg z cechą <b><i>Szeryf</i></b> nie jest w zwarciu, usuń Podburzeni Hobbici z gry).</p>");
            AddHtml("Roused-Hobbits-5-TSotS", "<p class='main-text'>Gdy ta karta jest dołączona, traktuj ją jako dodatek z cechą <b><i>Stan</i></b> o treści: “Karta, do której dołączono tę kartę, traci słowo kluczowe niezniszczalny i typ karty wróg oraz zys-kuje typ karty sojusznik z {Willpower} równą jej {Threat}. Jeżeli Podburzeni Hobbici lub karta do której została ona dołączona opuści grę, usuń obie karty z gry.”</p><p class='main-text'><b>Po odkryciu:</b> Wybierz 1 gracza, który przejmie kontrolę nad wrogiem z cechą <b><i>Szeryf</i></b> będącym z nim w zwarciu i dołącza do niego kartę Podburzeni Hobbici (jeśli żaden wróg z cechą <b><i>Szeryf</i></b> nie jest w zwarciu, usuń Podburzeni Hobbici z gry).</p>");
            AddHtml("Roused-Hobbits-6-TSotS", "<p class='main-text'>Gdy ta karta jest dołączona, traktuj ją jako dodatek z cechą <b><i>Stan</i></b> o treści: “Karta, do której dołączono tę kartę, traci słowo kluczowe niezniszczalny i typ karty wróg oraz zys-kuje typ karty sojusznik z {Willpower} równą jej {Threat}. Jeżeli Podburzeni Hobbici lub karta do której została ona dołączona opuści grę, usuń obie karty z gry.”</p><p class='main-text'><b>Po odkryciu:</b> Wybierz 1 gracza, który przejmie kontrolę nad wrogiem z cechą <b><i>Szeryf</i></b> będącym z nim w zwarciu i dołącza do niego kartę Podburzeni Hobbici (jeśli żaden wróg z cechą <b><i>Szeryf</i></b> nie jest w zwarciu, usuń Podburzeni Hobbici z gry).</p>");
            AddHtml("Roused-Hobbits-7-TSotS", "<p class='main-text'>Gdy ta karta jest dołączona, traktuj ją jako dodatek z cechą <b><i>Stan</i></b> o treści: “Karta, do której dołączono tę kartę, traci słowo kluczowe niezniszczalny i typ karty wróg oraz zys-kuje typ karty sojusznik z {Willpower} równą jej {Threat}. Jeżeli Podburzeni Hobbici lub karta do której została ona dołączona opuści grę, usuń obie karty z gry.”</p><p class='main-text'><b>Po odkryciu:</b> Wybierz 1 gracza, który przejmie kontrolę nad wrogiem z cechą <b><i>Szeryf</i></b> będącym z nim w zwarciu i dołącza do niego kartę Podburzeni Hobbici (jeśli żaden wróg z cechą <b><i>Szeryf</i></b> nie jest w zwarciu, usuń Podburzeni Hobbici z gry).</p>");
            AddHtml("Rouse-the-Cottons-TSotS", "<p class='flavor-text'>“…dalejże, chłopcy! Nad Wodą powstanie! My z nim!”<br>— Tom Cotton, Powrót Króla</p><p class='main-text'>Kiedy Poderwać Cottonów do Walki jest aktywną wyprawą, sprzymierzeńcy nie mogą być przydzielani do wyprawy.</p><p class='main-text'><b>Wymuszony:</b> Po pokonaniu tej wyprawy, odwróć kartę.</p>");
            AddHtml("The-Scouring-of-the-Shire-Campaign-TSotS", "<p class='main-text'> Grasz w trybie kampanii.</p><p class='main-text'><b>Przygotowanie:</b> Każdy gracz może zmienić kontro-lowanych przez siebie bohaterów bez ponoszenia kary +1 do zagrożenia. Gracze zdobyli karty łaski Gwiezdny Klejnot, Błyszcząca Kolczuga, Róg Skata i Zwiększona Postura. Każdy gracz wybiera jedną i dodaje ją do swojej ręki. Jeśli któraś z tych kart łask pozostaje niewybrana, jeden gracz może wybrać jedną z nich i wtasować ją do swojej talii. Usuń wszystkie nie wybrane karty z gry. Usuń Gwaihira i wszystkie karty brzemion z puli kampanii. Dołącz Poważną Ranę z rozszerzenia Droga Ciemnieje do Froda Bagginsa.</p><p class='flavor-text'>“Już po zmroku zmoknięci i zmęczeni wędrowcy zajechali nad Brandywinę i zastali tam drogę zagrodzoną.” — Powrót Króla </p>");
            AddHtml2("The-Scouring-of-the-Shire-Campaign-TSotS", "<p class='main-text'> Następstwa: Każdy gracz zdobył kartę kontraktu Na Zachód i może teraz używać jej w swoich taliach.</p><p class='flavor-text'>“Elrond i Galadriela odjeżdżali, bo skończyła się Trzecia Era, minęły dni Pierścieni, dobiegała końca historia i pieśń tej epoki. Odchodziło z nimi mnóstwo Elfów Wysokiego Rodu, nie chcąc dłużej pozostawać w Śródziemiu. Między nimi, przepełnieni smutkiem, ale smutkiem szczęśliwym, niezatrutym goryczą, jechali Sam, Frodo i Bilbo, a elfowie odnosili się do nich z wielkim szacunkiem.” — Powrót Króla</p>");
            AddHtml("Find-the-Gaffer-TSotS", "<p class='flavor-text'>“Chcę koniecznie zobaczyć Dziadunia. Ten cały Wódz i jego Sharkey, czy jak go tam zwą, mogliby w ciągu nocy uknuć jakieś łotrostwo.” — Sam, Powrót Króla</p><p class='main-text'><b>Akcja Podróży:</b> Wyczerp kontrolowanego bohatera, aby umieścić 2 żetony postępu na karcie Poszukiwania Dziadunia. W tej rundzie nie możesz zadekla-rować obrońców ani atakujących. Każdy gracz może aktywować ten efekt.</p><p class='main-text'><b>Wymuszony:</b> Po pokonaniu tej wyprawy odwróć tę kartę.</p>");
            AddHtml("Raise-the-Shire-TSotS", "<p class='flavor-text'>Shire znalazło się we władzy gangu zbójów dowodzonych przez człowieka znanego jako “Sharkey”. Frodo i jego przyjaciele muszą przygotować wieś Nad Wodą oraz zebrać wsparcie, aby stawić mu czoła. </p><p class='main-text'> <b>Po odkryciu:</b> Wybierz losowo 3 z odłożonych na bok kart pobocznych wypraw, nie posiadających cechy <b><i>Przeznaczenie</i></b> i umieść je w grze (wybierz 4 karty jeżeli w grze znajduje się 3 lub 4 graczy).</p>");
            AddHtml2("Raise-the-Shire-TSotS", "<p class='flavor-text'>“…gdy szeryfowie zorientowali się o co chodzi, większość zdarła pióra z kapeluszy i przyłączyła się do powstania.” — Powrót Króla</p><p class='main-text'> <b>Wymuszony:</b> Po ukończeniu pobocznej wyprawy przeszukaj talię spotkań w poszukiwaniu wroga z cechą <b><i>Szeryf</i></b> i dodaj go do strefy przeciwności. Potasuj talię spotkań.</p><p class='main-text'><b>Wymuszony:</b> Na koniec rundy umieść na tej karcie 1 żeton zasobów i podnieś poziom zagrożenia każdego gracza o łączną liczbę żetonów zasobów znających się na tej karcie. Następnie gracze mogą zdecydować się na przejście do następnego etapu.</p>");
            AddHtml("Against-the-Rules-TSotS", "<p class='main-text'><b>Po odkryciu:</b> Wyczerp każdą postać z wydrukowa-nym 3 lub więcej {Attack}. Gracz, który w wyniku tego efektu nie wyczerpał żadnej postaci musi wyczerpać 1 kontrolowaną przez siebie postać.</p>{shadow}<p class='shadow-text'><b>Cień:</b> Jeśli twój poziom zagrożenia jest wyższy niż koszt zwarcia atakującego wroga, atakujący wróg otrzymuje +2 {Attack}. W przeciwnym przypadku podnieś o 2 swój poziom zagrożenia.</p>");
            AddHtml("Highway-Robber-TSotS", "<p class='main-text'><b>Wymuszony:</b> Kiedy Rabuś atakuje, odrzuć żeton zasobów z kontrolowanego przez ciebie bohatera albo Rabuś otrzymuje +2 {Attack}.</p><p class='flavor-text'>“Dotychczas łatwo sobie radzili z bezbronnymi wieśnia-kami w Bree albo z oszołomionym ludem w Shire.”<br/>— Powrót Króla</p>");
            AddHtml("Scatha's-Horn-TSotS", "<p class='main-text'>Dołącz do bohatera.</p><p class='main-text'><b>Akcja:</b> Wyczerp Róg Skata i wybierz atakującego wroga. Do końca fazy ten wróg otrzymuje –1 {Attack} (–2 {Attack} jeśli broniąca postać ma cechę <b><i>Hobbit</i></b>).</p><p class='flavor-text'>“Kto w potrzebie zadmie w ten róg, porazi strachem serca wrogów…” — Éowina, Powrót Króla</p>");
            AddHtml("To-the-Road-Again-Worm-TSotS", "<p class='main-text'><b>Po odkryciu:</b> Odrzucaj karty z talii Spotkań, dopóki nie zostanie odrzucony obszar. Dodaj ten obszar do strefy przeciwności. Przydziel łącznie X obrażeń postaciom w grze. X to dwukrotność liczby obszarów w grze (trzykrotność liczby obszarów w grze jeśli w grze bierze udział 3 lub więcej graczy).</p><p class='flavor-text'>“A ty zawsze robisz, co Sharkey każe, czy tak? — zaśmiał się Saruman.” — Powrót Króla</p>");
            AddHtml("Saruman-TSotS", "<p class='main-text'>Odporny na efekty kart Graczy. Nie może wejść w zwarcie. Efekty kart z cechą <b><i>Sharkey</i></b> nie mogą zostać anulowane.</p><p class='main-text'><b>Wymuszony:</b> Pod koniec kroku odkrywania przeciwności dodaj 1{PerPlayer} żeton zasobów do Sarumana i odkryj wierzchnią kartę z talii Sharkeya. Następnie umieść odkrytą kartę na spodzie talii Sharkeya.</p>");
            AddHtml("Muster-the-Tooks-TSotS", "<p class='flavor-text'>“Na przełaj mamy nie więcej niż czternaście mil. Jutro rano stawię się z całą armią Tuków.” — Pippin, Powrót Króla</p><p class='main-text'>X to 4{PerPlayer}. Kiedy Skrzyknąć Tuków jest aktywną wyprawą, bohaterowie nie mogą być przydzieleni do wyprawy.</p><p class='main-text'><b>Wymuszony:</b> Po pokonaniu tej wyprawy odwróć tę kartę.</p>");
            AddHtml("Feather-capped-Shirriff-TSotS", "<p class='main-text'>Niezniszczalny. Anuluj wszystkie obrażenia zadane twoim postaciom przez Szeryfa z Piórem na Czapce. Podnieś twój poziom zagrożenia o liczbę anulowanych obrażeń.</p><p class='main-text'>Kiedy poziom zagrożenia gracza, z którym Szeryf z Piórem na Czapce znajduje się w zwarciu, jest większy od jego kosztu zagrożenia, Szeryf z Piórem na Czapce otrzymuje +1 {Attack}.</p>");
            AddHtml("The-Very-Last-Stroke-TSotS", "<p class='flavor-text'>Gdy siły Sharkeya są rozproszone, Frodo postanawia stawić mu czoła. Ku zaskoczeniu Froda, wita go nie kto inny jak Saruman, wraz z jego sługą Grímą Gadzim Językiem. </p><p class='main-text'> <b>Po odkryciu:</b> Jeśli jest to faza walki, zakończ ją. Dodaj Sarumana i Wybór Grímy do strefy przeciwności.</p>");
            AddHtml2("The-Very-Last-Stroke-TSotS", "<p class='flavor-text'>“Jesteś mądry i okrutny. Odarłeś moją zemstę ze słodyczy i muszę stąd odejść z gorzkim poczuciem…” — Saruman, Powrót Króla</p><p class='main-text'> X to 6{PerPlayer}. Ten etap nie może mieć więcej żetonów postępu niż liczba żetonów zasobów na Sarumanie.</p><p class='main-text'><b>Akcja:</b> Wyczerp bohatera i odrzuć 1 żeton zasobów z jego puli, aby dodać ten żeton zasobów do Sarumana.</p><p class='main-text'><b>Gracze nie mogą pokonać tego etapu, dopóki Saruman znajduje się w grze. Kiedy ten etap zostanie pokonany, gracze wygrywają.</b></p>");
            AddHtml("Awake-Fire-Foes-Awake-TSotS", "<p class='flavor-text'>“Nie zdążył się oddalić, gdy nagle dobiegł jego uszu czysty, bijący ku niebu śpiew rogu. Daleko w górach i na polach odpowiedziały echa…” — Powrót Króla</p><p class='main-text'>Akcja Spotkania: Wejdź w zwarcie z wrogiem z cechą <b><i>Zbój</i></b> ze stosu odrzuconych kart spotkań. Każdy gracz może rozpatrzyć ten efekt.</p><p class='main-text'><b>Odpowiedź:</b> Po tym, jak pierwszy gracz zabije wroga, umieść tutaj 2 żetony postępu.</p><p class='main-text'><b>Wymuszony:</b> Po pokonaniu tej wyprawy odwróć tę kartę.</p>");
            AddHtml("Hidden-Blade-TSotS", "<p class='main-text'><b>Po odkryciu:</b> Saruman wykonuje natychmiastowy atak przeciwko każdemu graczowi. Sprzymierzeńcy nie mogą bronić ataków Sarumana.</p><p class='flavor-text'>“…w ręku Sarumana znienacka błysnął nóż. Dźgnął błyskawicznie.” — Powrót Króla</p>");
            AddHtml("One-Ill-Turn-TSotS", "<p class='main-text'><b>Po odkryciu:</b> Zadaj 1 obrażenie każdej wyczerpanej postaci. Do końca rundy postacie nie mogą być leczone.</p><p class='flavor-text'>“Nauczka byłaby jeszcze lepsza, gdybyście mi zosta-wili trochę więcej czasu…”<br/>— Saruman, Powrót Króla</p>");
            AddHtml("Many-Partings-TSotS", "<p class='flavor-text'>“Wydostając się z lasu znowu na otwartą przestrzeń, dogonili starca wspiera-jącego się na lasce i odzianego w łachmany brudnobiałej barwy; trop w trop za nim wlókł się drugi żebrak, zgarbiony i pochlipujący.” — Powrót Króla </p><p class='main-text'> <b>Przygotowanie:</b> Utwórz talię Sharkeya (patrz karta z zasadami). Odłóż Sarumana, Bramę na Brandywinie, Kamień Trzech Ćwiartek oraz karty pobocznych wypraw na bok, poza grę. Potasuj talię spotkań.</p>");
            AddHtml2("Many-Partings-TSotS", "<p class='flavor-text'>“Nie obchodzi was, że cierpię niedostatek, co? Wam za to nie brak niczego, jadła, pięknych strojów i najlepszego ziela do fajek. Tak, tak, wiem wszystko. Wiem, skąd je macie. Nie dalibyście żebrakowi choć garsteczki?” — Saruman, Powrót Króla</p><p class='main-text'> <b>Akcja:</b> Wyczerp bohatera nie należącego do strefy [fellowship] i odrzuć żeton zasobów z jego puli. Następnie dodaj 2 żetony zasobów do odłożonego poza grą Sarumana.</p><p class='main-text'><b>Pod koniec fazy planowania przejdź do następnego etapu misji.</b></p>");
            AddHtml("East-Road-TSotS", "<p class='main-text'>Opresja.</p><p class='main-text'><b>Po odkryciu:</b> Podnieś o 2 poziom zagrożenia pierwszego gracza lub Wschodni Gościniec otrzymuje mroczną falę.</p><p class='flavor-text'>“Za pierwszym skrętem, o niespełna ćwierć mili od gościńca, zbójcy natknęli się na przeszkodę: drogę zagradzał ciężki przewrócony wóz chłopski. To wstrzymało pochód.” — Powrót Króla</p>");
            AddHtml("Grima's-Choice-TSotS", "<p class='flavor-text'>“Gadzi Języku! — zawołał Frodo. — Nie jesteś zmuszony iść za nim. O ile mi wiadomo, nie wyrządziłeś mi nic złego. Możesz odpocząć i odżywić się tutaj, a gdy nabierzesz sił, pójść własną drogą.” — Powrót Króla</p><p class='main-text'>X to 3{PerPlayer}. Ten etap nie może zostać wybrany jako aktywna wyprawa, jeżeli na karcie To Był Ostatni Cios znajduje się mniej niż 6{PerPlayer} żetonów postępu.</p><p class='main-text'><b>Wymuszony:</b> Kiedy ten etap zostanie pokonany, odrzuć Sarumana z gry.</p>");
            AddHtml("Ambush-Sharkey's-Men-TSotS", "<p class='flavor-text'>“…ostrzegałem cię, żebyś się więcej tu nie pokazywał – rzekł do dowódcy. – Ostrzegam cię po raz wtóry: stoisz w pełnym świetle…” — Merry, Powrót Króla</p><p class='main-text'><b>Wymuszony:</b> Po pokonaniu Gangu Zbójów odwróć tę kartę.</p><p class='main-text'><b>Wymuszony:</b> Pod koniec fazy wyprawy, jeśli w tej rundzie nie położono żetonu postępu na karcie Zasadzka na Ludzi Sharkeya, przeszukaj talię spotkań i stos kart odrzuconych, aby znaleźć wroga z cechą <b><i>Zbój</i></b> i dodaj go do strefy przeciwności. Potasuj talię spotkań.</p>");
            AddHtml("Thieving-Ruffian-TSotS", "<p class='main-text'><b>Wymuszony:</b> Kiedy Złodziejski Zbój atakuje, podnieś o 1 swój poziom zagrożenia za każdy dodatek dołączony do broniącej się postaci.</p><p class='flavor-text'>“Radzę ci zmienić ton po dobroci, bo inaczej z tobą pogadam.” — Powrót Króla</p>");
            AddHtml("Heightened-Stature-TSotS", "<p class='main-text'>Dołącz do bohatera.</p><p class='main-text'>Bohater, do którego dołączono tę kartę otrzymuje +2 punkty wytrzymałości (+3 jeśli bohater do którego dołączono tę kartę posiada cechę <b><i>Hobbit</i></b>).</p><p class='main-text'><b>Akcja:</b> Wyczerp Zwiększoną Posturę, aby wyleczyć 1 obrażenie z bohatera, do którego dołączono tę kartę.</p><p class='flavor-text'>“Śmiertelnik nie może pić wody entów i łudzić się, że nie wyniknie z tego nic więcej niż z wychylenia kufla piwa.”<br/>— Gimli, Powrót Króla</p>");
        }
    }
}